INSERT INTO `intrebari` (`id`, `cerinta`, `imagine`, `variante`, `rasp_a`, `rasp_b`, `rasp_c`, `eticheta`) VALUES
(1, 'Indicatorul alaturat interzice:', 'uploads/3707.jpg', '<p id=\"v_a\"><b>a. </b>circulatia autovehiculelor destinate transportului de marfuri, care au distanta dintre osii mai mare de 10 m;</p>\r\n	<p id=\"v_b\"><b>b. </b>accesul autovehiculelor sau ansamblurilor de vehicule cu lungimea mai mare de 10 m;</p>\r\n	<p id=\"v_c\"><b>c. </b>accesul autovehiculelor destinate transportului de marfuri, care tracteaza remorci având o lungime mai mare de 10 m.</p>', 'nu', 'da', 'nu', ''),
(2, 'Schimbarea repetata a luminii de drum cu lumina de întâlnire se foloseste pe timp de noapte:', NULL, '<p id=\"v_a\"><b>a. </b>în intersectiile care nu sunt dirijate prin semnale luminoase sau de catre un politist rutier;</p>\r\n<p id=\"v_b\"><b>b. </b>la semnalizarea conducatorului de autovehicule care s-a angajat într-o depasire periculoasa;</p>\r\n<p id=\"v_c\"><b>c. </b>la întâlnirea vehiculelor care vin din sens opus.</p>', 'da', 'nu', 'nu', ''),
(3, 'Manevra de evitare, prin schimbarea direc&#355;iei de mers, a unui obstacol aflat pe sensul dvs. de circula&#355;ie, urmat&#259; de revenirea pe acela&#351;i sens, constituie:', '', '<p id=\"v_a\"><b>a. </b>ocolire</p><p id=\"v_b\"><b>b. </b>depasire</p><p id=\"v_c\"><b>c. </b>situatie de urgenta</p>', 'nu', 'da', 'nu', ''),
(4, 'Echipamentul de frânare nu este obligatoriu la remorcile cu masa total&#259; maxim&#259; autorizat&#259;:', '', '<p id=\"v_a\"><b>a. </b>mai mic&#259; de 750 kg</p>\r\n				<p id=\"v_b\"><b>b. </b>mai mic&#259; de 400 kg</p>\r\n				<p id=\"v_c\"><b>c. </b>mai mare de 750 kg</p>', 'da', 'nu', 'nu', ''),
(5, 'Care dintre vehiculele de mai jos nu beneficiaz&#259; de prioritate de trecere:', '', '<p id=\"v_a\"><b>a. </b>vehiculele care schimba direc&#355;ia de mers spre dreapta, fa&#355;&#259; de cele care circul&#259; din sens opus;</p>\r\n				<p id=\"v_b\"><b>b. </b>vehiculele care traverseaz&#259; o intersec&#355;ie pe direc&#355;ia înainte &#351;i întâlnesc culoarea verde a semaforului;</p>\r\n				<p id=\"v_c\"><b>c. </b>vehiculele care coboar&#259; fa&#355;&#259; de cele care urc&#259;, dac&#259; pe sensul de mers al celor care urc&#259; întâlnesc un obstacol.</p>', 'nu', 'nu', 'da', ''),
(6, 'În care dintre urm&#259;toarele situa&#355;ii nu este permis&#259; dep&#259;&#351;irea?', '', '<p id=\"v_a\"><b>a. </b>când autobuzul se afl&#259; oprit în sta&#355;ie;</p>\r\n				<p id=\"v_b\"><b>b. </b>în apropierea vârfurilor de ramp&#259;, când vizibilitatea este sub 50 m;</p>\r\n				<p id=\"v_c\"><b>c. </b>într-o intersec&#355;ie nedirijat&#259;.</p>', 'nu', 'da', 'da', ''),
(7, 'În ce condi&#355;ii pot circula pe drumurile publice autovehiculele &#351;i remorcile?', '', '<p id=\"v_a\"><b>a. </b>când au un aspect civilizat;</p>\r\n				<p id=\"v_b\"><b>b. </b>când nu dep&#259;&#351;esc greutatea &#351;i gabaritul înscrise în certificatul de înmatriculare;</p>\r\n				<p id=\"v_c\"><b>c. </b>când sunt în bun&#259; stare de func&#355;ionare &#351;i îndeplinesc condi&#355;iile tehnice stabilite prin lege.</p>', 'nu', 'nu', 'da', ''),
(8, 'Care este semnifica&#355;ia indicatorului &#351;i a panoului adi&#355;ional al&#259;turate?', 'uploads/2860.jpg', '<p id=\"v_a\"><b>a. </b>se interzice sta&#355;ionarea vehiculelor , în zilele impare (f&#259;r&#259; so&#355;), între orele men&#355;ionate;</p>\r\n		<p id=\"v_b\"><b>b. </b>se permite sta&#355;ionarea vehiculelor , în zilele impare (f&#259;r&#259; so&#355;), între orele men&#355;ionate;</p>\r\n		<p id=\"v_c\"><b>c. </b>se permite sta&#355;ionarea vehiculelor în afara orelor men&#355;ionate.</p>', 'da', 'nu', 'nu', ''),
(9, 'În care dintre situa&#355;ii este interzis s&#259; circula&#355;i pe drumurile publice?', '', '<p id=\"v_a\"><b>a. </b>atunci când certificatul de înmatriculare este deteriorat;</p>\r\n		<p id=\"v_b\"><b>b. </b>atunci când vehiculul nu este asigurat de r&#259;spundere civil&#259;, în caz de pagube materiale produse ter&#355;ilor prin accidente de circula&#355;ie, conform legii;</p>\r\n		<p id=\"v_c\"><b>c. </b>atunci când conduc&#259;torul auto nu a pl&#259;tit taxa de folosire a drumurilor publice.</p>', 'nu', 'da', 'nu', ''),
(10, 'Care este rolul termostatului?', '', '<p id=\"v_a\"><b>a. </b>permite pornirea u&#351;oar&#259; a motorului la temperaturi sc&#259;zute;</p>\r\n		<p id=\"v_b\"><b>b. </b>închide &#351;i deschide circuitul lichidului de r&#259;cire la anumite temperaturi;</p>\r\n		<p id=\"v_c\"><b>c. </b>asigur&#259; r&#259;cirea lichidului de frânare.</p>', 'nu', 'da', 'nu', ''),
(11, 'Indicatorul urm&#259;tor v&#259; informeaz&#259; despre:', 'uploads/2859.jpg', '<p id=\"v_a\"><b>a. </b>limitele generale de vitez&#259;;</p>\r\n		<p id=\"v_b\"><b>b. </b>limitarea diferen&#539;iat&#259; de vitez&#259;;</p>\r\n		<p id=\"v_c\"><b>c. </b>limit&#259;rile de vitez&#259; diferen&#539;iate pe benzi.</p>', 'da', 'nu', 'nu', ''),
(12, 'Care este rolul bateriei de acumulatori?', '', '<p id=\"v_a\"><b>a. </b>alimenteaz&#259; consumatorii cu energie electric&#259;;</p>\r\n		<p id=\"v_b\"><b>b. </b>asigur&#259; func&#355;ionarea instala&#355;iei de aprindere;</p>\r\n		<p id=\"v_c\"><b>c. </b>asigur&#259; func&#355;ionarea instala&#355;iei de r&#259;cire.</p>', 'da', 'nu', 'nu', ''),
(13, 'Care dintre indicatoare te avertizeaz&#259; c&#259; urmeaz&#259; s&#259; intersectezi un drum f&#259;r&#259; prioritate?', 'uploads/3711.jpg', '<p id=\"v_a\"><b>a. </b>ambele;</p>\r\n		<p id=\"v_b\"><b>b. </b>indicatorul 1;</p>\r\n		<p id=\"v_c\"><b>c. </b>indicatorul 2.</p>', 'nu', 'da', 'nu', ''),
(14, 'Preciza&#355;i în care dintre situa&#355;iile de mai jos este interzis&#259; dep&#259;&#351;irea:', '', '<p id=\"v_a\"><b>a. </b>la coborârile a c&#259;ror pant&#259; dep&#259;&#351;e&#351;te 10%;</p><p id=\"v_b\"><b>b. </b>pe trecerile pentru pietoni sau pe cele la nivel cu calea ferat&#259; curent&#259; &#351;i la mai pu&#355;in de 50 m de acestea;</p><p id=\"v_c\"><b>c. </b>în situa&#355;ia în care, din sens opus, un vehicul este nevoit s&#259; execute o manevr&#259;, pentru a evita coliziunea.</p>', 'nu', 'da', 'da', ''),
(15, 'Este corect s&#259; dep&#259;&#351;e&#351;ti motocicleta din fa&#355;a ta?', 'uploads/3700.jpg', '<p id=\"v_a\"><b>a. </b>da;</p><p id=\"v_b\"><b>b. </b>nu, deoarece astfel vei dep&#259;&#351;i axul drumului;</p><p id=\"v_c\"><b>c. </b>nu, deoarece nu respec&#355;i semnifica&#355;ia indicatorului.</p>', 'da', 'nu', 'nu', ''),
(16, 'Ce rol are ambreiajul?', '', '<p id=\"v_a\"><b>a. </b>asigur&#259; viteze diferite ale ro&#355;ilor motoare la parcurgerea curbelor;</p><p id=\"v_b\"><b>b. </b>comand&#259; m&#259;rirea tura&#355;iei motorului, în func&#355;ie de sarcina transportat&#259;</p><p id=\"v_c\"><b>c. </b>realizeaz&#259; cuplarea progresiv&#259; &#351;i decuplarea motorului de restul transmisiei</p>', 'nu', 'nu', 'da', ''),
(17, 'Pe drumurile publice marcate cu simbolul al&#259;turat, limita maxim&#259; de vitez&#259; în afara localit&#259;&#355;ilor, pentru autoturisme, este de:', 'uploads/3718.jpg', '<p id=\"v_a\"><b>a. </b>90 km/h;</p><p id=\"v_b\"><b>b. </b>130 km/h;</p><p id=\"v_c\"><b>c. </b>110 km/h.</p>', 'da', 'nu', 'nu', ''),
(18, 'Acvaplanarea poate fi evitata atunci cand:', '', '<p id=\"v_a\"><b>a. </b>pneurile sunt bune si viteza este redusa;</p><p id=\"v_b\"><b>b. </b>autovehiculul este dotat cu ABS;</p><p id=\"v_c\"><b>c. </b>autovehiculul este supraincarcat.</p>', 'da', 'nu', 'nu', ''),
(19, 'Dac&#259;, dup&#259; întreruperea contactului electric, motorul continu&#259; s&#259; func&#355;ioneze, fenomenul se nume&#351;te:', '', '<p id=\"v_a\"><b>a. </b>detona&#355;ie;</p><p id=\"v_b\"><b>b. </b>calare;</p><p id=\"v_c\"><b>c. </b>autoaprindere.</p>', 'nu', 'nu', 'da', ''),
(20, 'C&#259;ror categorii de autovehicule le este interzis s&#259; circule pe autostrad&#259;?', '', '<p id=\"v_a\"><b>a. </b>celor care, prin construc&#355;ie, nu pot dep&#259;&#351;i viteza de 50 km/or&#259;;</p><p id=\"v_b\"><b>b. </b>motocicletelor cu &#351;i f&#259;r&#259; ata&#351;;</p><p id=\"v_c\"><b>c. </b>tractoarelor &#351;i ma&#351;inilor agricole autopropulsate.</p>', 'da', 'nu', 'da', ''),
(21, 'Ce semnific&#259; cuvântul TUBELESS inscrip&#355;ionat pe anvelop&#259;?', '', '<p id=\"v_a\"><b>a. </b>sec&#355;iunea pneului are form&#259; tubular&#259;;</p><p id=\"v_b\"><b>b. </b>anvelopa poate func&#355;iona f&#259;r&#259; camer&#259; de aer;</p><p id=\"v_c\"><b>c. </b>anvelopa este destinat&#259; folosirii pe vreme nefavorabil&#259;.</p>', 'nu', 'da', 'nu', ''),
(22, 'Cum trebuie s&#259; procedezi la întâlnirea panoului al&#259;turat?', 'uploads/3694.jpg', '<p id=\"v_a\"><b>a. </b>respec&#355;i presemnalizarea unui loc periculos, o interzicere sau o restric&#355;ie pe un drum lateral la dreapta;</p><p id=\"v_b\"><b>b. </b>circuli f&#259;r&#259; obliga&#355;ii, deoarece intersectezi un drum f&#259;r&#259; prioritate;</p><p id=\"v_c\"><b>c. </b>circuli numai pe direc&#355;ia înainte.</p>', 'da', 'nu', 'nu', ''),
(23, 'Indicatorul al&#259;turat semnific&#259;:', 'uploads/3723.jpg', '<p id=\"v_a\"><b>a. </b>pist&#259; comun&#259; pentru pietoni &#351;i bicicli&#351;ti;</p><p id=\"v_b\"><b>b. </b>accesul interzis pietonilor &#351;i bicicli&#351;tilor;</p><p id=\"v_c\"><b>c. </b>drum închis circula&#355;iei publice.</p>', 'da', 'nu', 'nu', ''),
(24, 'Indicatorul semnific&#259;:', 'uploads/3692.jpg', '<p id=\"v_a\"><b>a. </b>presemnalizarea traseului de urmat în vederea efectu&#259;rii virajului la stânga;</p><p id=\"v_b\"><b>b. </b>ocolirea intersec&#355;iei;</p><p id=\"v_c\"><b>c. </b>interzicerea virajului la stânga pentru camioane.</p>', 'da', 'nu', 'nu', ''),
(25, 'Cum sunt semnalizate benzile de circula&#355;ie reversibil&#259;, care v&#259; permit s&#259; continua&#355;i deplasarea?', '', '<p id=\"v_a\"><b>a. </b>cu dispozitive de culoare galben&#259; intermitent&#259;;</p><p id=\"v_b\"><b>b. </b>cu semnalul verde de forma unei s&#259;ge&#355;i cu vârful în jos;</p><p id=\"v_c\"><b>c. </b>cu semnul ro&#351;u în cruce.</p>', 'nu', 'da', 'nu', ''),
(26, 'La dep&#259;&#351;irea c&#259;ror vehicule trebuie s&#259; p&#259;strezi o distan&#355;&#259; lateral&#259; mai mare?', '', '<p id=\"v_a\"><b>a. </b>la dep&#259;&#351;irea bicicletelor</p><p id=\"v_b\"><b>b. </b>la dep&#259;&#351;irea autobuzelor</p><p id=\"v_c\"><b>c. </b>la dep&#259;&#351;irea camioanelor</p>', 'da', 'nu', 'nu', ''),
(27, 'Care dintre vehicule î&#351;i pot continua deplasarea în intersec&#355;ie?', 'uploads/3686.jpg', '<p id=\"v_a\"><b>a. </b>tramvaiele, pentru c&#259; au prioritate de trecere;</p><p id=\"v_b\"><b>b. </b>autoturismul albastru;</p><p id=\"v_c\"><b>c. </b>autoturismul albastru &#351;i autoturismul galben.</p>', 'nu', 'da', 'nu', ''),
(28, 'Ac&#355;ionând o singur&#259; dat&#259; pedala de frânare, cursa acesteia este prea lung&#259;. Ac&#355;ionand de repetate ori, cursa pedalei se scurteaz&#259;. Cum trebuie s&#259; proceda&#355;i?', '', '<p id=\"v_a\"><b>a. </b>ac&#355;iona&#355;i frâna mai des;</p><p id=\"v_b\"><b>b. </b>repara&#355;i frâna într-un atelier specializat;</p><p id=\"v_c\"><b>c. </b>nu trebuie s&#259; face&#355;i nimic, acest lucru este normal.</p>', 'nu', 'da', 'nu', ''),
(29, 'Ce interzice indicatorul urm&#259;tor?', 'uploads/2414.jpg', '<p id=\"v_a\"><b>a. </b>circulatia vehiculelor destinate transportului de m&#259;rfuri</p><p id=\"v_b\"><b>b. </b>accesul ansamblurilor de vehicule sau al trenurilor auto</p><p id=\"v_c\"><b>c. </b>accesul autovehiculelor cu remorc&#259;, cu exceptia celor cu semiremorc&#259; sau cu remorc&#259; cu o osie</p>', 'nu', 'nu', 'da', ''),
(30, 'Distan&#355;a de frânare a autovehiculului depinde cel mai mult de:', '', '<p id=\"v_a\"><b>a. </b>viteza</p><p id=\"v_b\"><b>b. </b>profilul pneurilor</p><p id=\"v_c\"><b>c. </b>eficien&#355;a sistemului de direc&#355;ie</p>', 'da', 'nu', 'nu', ''),
(31, 'Indicatorul din imagine se refer&#259; la:', 'uploads/3705.jpg', '<p id=\"v_a\"><b>a. </b>o ramp&#259; de îmbarcare pe un bac;</p><p id=\"v_b\"><b>b. </b>o ie&#351;ire spre un chei sau mal abrupt;</p><p id=\"v_c\"><b>c. </b>o zon&#259; de trecere spre un pod mobil.</p>', 'nu', 'da', 'nu', ''),
(32, 'Care este ordinea de trecere prin intersec&#355;ie?', 'uploads/1017.jpg', '<p id=\"v_a\"><b>a. </b>autoturismul, autocamionul, motocicleta;</p><p id=\"v_b\"><b>b. </b>autoturismul, motocicleta, autocamionul;</p><p id=\"v_c\"><b>c. </b>autocamionul, autoturismul, motocicleta.</p>', 'nu', 'nu', 'da', ''),
(33, 'Autoturismul alb execut&#259; manevra de întoarcere. Cum vei proceda corect?', 'uploads/3708.jpg', '<p id=\"v_a\"><b>a. </b>reduci viteza &#351;i te apropii cât mai mult de acesta;</p><p id=\"v_b\"><b>b. </b>opre&#351;ti &#351;i a&#351;tep&#355;i degajarea traficului;</p><p id=\"v_c\"><b>c. </b>execu&#355;i dep&#259;&#351;irea acestuia.</p>', 'nu', 'da', 'nu', ''),
(34, 'Ce m&#259;suri trebuie s&#259; lua&#355;i atunci când circula&#355;i în condi&#355;ii atmosferice ce împiedic&#259; mult vizibilitatea?', '', '<p id=\"v_a\"><b>a. </b>s&#259; circula&#355;i pe distan&#355;e scurte &#351;i cu luminile de pozi&#355;ie în func&#355;iune;</p><p id=\"v_b\"><b>b. </b>s&#259; folosi&#355;i luminile de pozi&#355;ie &#351;i s&#259; circula&#355;i cât mai aproape de marginea din dreapta a drumului;</p><p id=\"v_c\"><b>c. </b>s&#259; folosi&#355;i luminile de întâlnire.</p>', 'nu', 'nu', 'da', ''),
(35, 'Cum vei circula la trecerea pe lâng&#259; un grup de elevi care se deplaseaz&#259; în aceea&#351;i direc&#355;ie, pe drumul na&#355;ional dintr-o localitate rural&#259;?', '', '<p id=\"v_a\"><b>a. </b>dep&#259;&#351;e&#351;ti grupul, semnalizând cu luminile de întâlnire;</p><p id=\"v_b\"><b>b. </b>preventiv, reduci viteza &#351;i ocole&#351;ti cu pruden&#355;&#259; grupul;</p><p id=\"v_c\"><b>c. </b>folose&#351;ti avertizorul sonor, pentru a îndep&#259;rta elevii de pe partea carosabil&#259;.</p>', 'nu', 'da', 'nu', ''),
(36, 'Ce interzice indicatorul al&#259;turat?', 'uploads/3697.jpg', '<p id=\"v_a\"><b>a. </b>accesul vehiculelor care transport&#259; m&#259;rfuri periculoase;</p><p id=\"v_b\"><b>b. </b>accesul vehiculelor cu gabarit dep&#259;&#351;it;</p><p id=\"v_c\"><b>c. </b>accesul vehiculelor care transport&#259; substan&#355;e explozive sau u&#351;or inflamabile.</p>', 'nu', 'nu', 'da', ''),
(37, 'Indicatorul arat&#259;:', 'uploads/3715.jpg', '<p id=\"v_a\"><b>a. </b>limitele maxime de vitez&#259; pe autostrad&#259;, în func&#355;ie de condi&#355;iile meteorologice;</p><p id=\"v_b\"><b>b. </b>limitele obligatorii de vitez&#259;;</p><p id=\"v_c\"><b>c. </b>viteza minim&#259; obligatorie pe autostrad&#259;.</p>', 'da', 'nu', 'nu', ''),
(38, 'Cum se realizeaz&#259; controlul nivelului de ulei din baie?', '', '<p id=\"v_a\"><b>a. </b>cu ajutorul unei tije metalice numite joj&#259;, introdus&#259; în blocul motor;</p><p id=\"v_b\"><b>b. </b>cu ajutorul turometrului de la bord;</p><p id=\"v_c\"><b>c. </b>cu ajutorul manometrului de la bord.</p>', 'da', 'nu', 'nu', ''),
(39, 'Dac&#259; autovehiculul de poli&#355;ie transmite mesajul „coloan&#259; oficial&#259;”, ai voie s&#259; te ata&#351;ezi la o astfel de coloan&#259;?', 'uploads/3706.jpg', '<p id=\"v_a\"><b>a. </b>nu;</p><p id=\"v_b\"><b>b. </b>da;</p><p id=\"v_c\"><b>c. </b>da, numai dac&#259; dispui de un autoturism performant.</p>', 'da', 'nu', 'nu', ''),
(40, 'Conduc&#259;torilor de autovehicule le este interzis:', '', '<p id=\"v_a\"><b>a. </b>s&#259; transporte în &#351;i pe autoturism obiecte a c&#259;ror lungime sau l&#259;&#355;ime dep&#259;&#351;e&#351;te, împreun&#259; cu înc&#259;rc&#259;tura, dimensiunile acestuia;</p><p id=\"v_b\"><b>b. </b>s&#259; aib&#259; aplicate folii sau tratamente chimice pe parbrize, lunet&#259; ori geamuri laterale, care reduc vizibilitatea;</p><p id=\"v_c\"><b>c. </b>s&#259; circule cu vehiculele avariate mai mult de 30 de zile.</p>', 'da', 'da', 'da', ''),
(41, 'Panourile al&#259;turate semnific&#259;:', 'uploads/3717.jpg', '<p id=\"v_a\"><b>a. </b>panouri suplimentare pentru intersec&#355;ia cu calea ferat&#259;;</p><p id=\"v_b\"><b>b. </b>panouri suplimentare la nodurile rutiere de pe autostr&#259;zi;</p><p id=\"v_c\"><b>c. </b>panouri succesive pentru curbe deosebit de periculoase.</p>', 'nu', 'da', 'nu', ''),
(42, 'Cum trebuie s&#259; procedezi atunci când cobori cu autoturismul o pant&#259; lung&#259; &#351;i accentuat&#259;, având ata&#351;at&#259; o remorc&#259; f&#259;r&#259; frân&#259; proprie?', '', '<p id=\"v_a\"><b>a. </b>reduci viteza, introduci într-o treapt&#259; de vitez&#259; inferioar&#259; &#351;i te preg&#259;te&#351;ti pentru frânare;</p><p id=\"v_b\"><b>b. </b>opre&#351;ti motorul în treapta a ll-a de vitez&#259;;</p><p id=\"v_c\"><b>c. </b>opre&#351;ti de mai multe ori pân&#259; cobori panta.</p>', 'da', 'nu', 'nu', ''),
(43, 'Ce semnific&#259; indicatorul din imagine?', 'uploads/3712.jpg', '<p id=\"v_a\"><b>a. </b>acces pe baz&#259; de tichet;</p><p id=\"v_b\"><b>b. </b>drum rezervat autovehiculelor cu ecuson;</p><p id=\"v_c\"><b>c. </b>punct de alimentare cu combustibil.</p>', 'da', 'nu', 'nu', ''),
(44, 'Cum trebuie s&#259; procedeze conduc&#259;torul auto la semnalul poli&#355;istului din imagine?', 'uploads/3701.jpg', '<p id=\"v_a\"><b>a. </b>s&#259; vireze spre dreapta;</p><p id=\"v_b\"><b>b. </b>s&#259; opreasc&#259;;</p><p id=\"v_c\"><b>c. </b>semnalul se adreseaz&#259; numai conduc&#259;torilor care circul&#259; din sens opus.</p>', 'nu', 'da', 'nu', ''),
(45, 'La întâlnirea unui vehicul care circul&#259; noaptea, din sens opus, pe un drum neiluminat, conduc&#259;torul de autovehicul este obligat:', '', '<p id=\"v_a\"><b>a. </b>s&#259; circule cât mai aproape de marginea din dreapta a drumului;</p><p id=\"v_b\"><b>b. </b>s&#259; reduc&#259; viteza;</p><p id=\"v_c\"><b>c. </b>s&#259; foloseasc&#259; avertizorul sonor.</p>', 'nu', 'da', 'nu', ''),
(46, 'Când nu v&#259; este permis&#259; întoarcerea vehiculului?', '', '<p id=\"v_a\"><b>a. </b>când l&#259;&#355;imea drumului public este mai mic&#259; de 7 m;</p><p id=\"v_b\"><b>b. </b>la urcarea pantelor;</p><p id=\"v_c\"><b>c. </b>pe marcajul pietonal.</p>', 'nu', 'nu', 'da', ''),
(47, 'Întoarcerea este interzis&#259;:', '', '<p id=\"v_a\"><b>a. </b>în ramp&#259;, dac&#259; vizibilitatea este sub 50 m;</p><p id=\"v_b\"><b>b. </b>în dreptul unui garaj;</p><p id=\"v_c\"><b>c. </b>la trecerile pentru pietoni.</p>', 'da', 'nu', 'da', ''),
(48, 'Punctele de penalizare se aplic&#259; pentru:', '', '<p id=\"v_a\"><b>a. </b>nerespectarea semnifica&#355;iei indicatorului „Copii“;</p><p id=\"v_b\"><b>b. </b>nerespectarea semnifica&#355;iei indicatorului „Drum cu denivel&#259;ri“;</p><p id=\"v_c\"><b>c. </b>nerespectarea semnifica&#355;iei indicatorului „Oprire“, instalat la trecerea la nivel cu o cale ferat&#259;.</p>', 'nu', 'nu', 'da', ''),
(49, 'Care dintre faptele de mai jos atrag suspendarea exercit&#259;rii dreptului de conduce pe o perioad&#259; de 90 de zile?', '', '<p id=\"v_a\"><b>a. </b>conducerea sub influen&#355;a b&#259;uturilor alcoolice;</p><p id=\"v_b\"><b>b. </b>conducerea sub influen&#355;a produselor stupefiante;</p><p id=\"v_c\"><b>c. </b>conducerea unui vehicul neînmatriculat.</p>', 'da', 'nu', 'nu', ''),
(50, 'Ce manevre sunt interzise pe autostrad&#259;?', '', '<p id=\"v_a\"><b>a. </b>oprirea &#351;i efectuarea unei pene de cauciuc;</p><p id=\"v_b\"><b>b. </b>înv&#259;&#355;area conducerii unui vehicul;</p><p id=\"v_c\"><b>c. </b>încercarea prototipurilor de &#351;asiuri &#351;i de vehicule cu motor.</p>', 'nu', 'da', 'da', ''),
(51, 'În ce situa&#355;ie, pe timp de zi, conduc&#259;torii auto sunt obliga&#355;i s&#259; foloseasc&#259; luminile de întâlnire?', '', '<p id=\"v_a\"><b>a. </b>când tracteaz&#259; alte vehicule;</p><p id=\"v_b\"><b>b. </b>când circul&#259; pe drumurile jude&#355;ene;</p><p id=\"v_c\"><b>c. </b>când circul&#259; cu vitez&#259; redus&#259;.</p>', 'da', 'nu', 'nu', ''),
(52, 'Luminile de pozi&#355;ie se folosesc, pe timp de zi, atunci când autovehiculul este oprit:', '', '<p id=\"v_a\"><b>a. </b>pentru urcarea sau coborârea pasagerilor;</p><p id=\"v_b\"><b>b. </b>din cauza unor avarii;</p><p id=\"v_c\"><b>c. </b>pentru c&#259; vizibilitatea este redus&#259;.</p>', 'nu', 'nu', 'da', ''),
(53, 'Indicatorul interzice accesul:', 'uploads/863.jpg', '<p id=\"v_a\"><b>a. </b>autoturismelor, motocicletelor f&#259;r&#259; ata&#351; &#351;i vehiculelor cu trac&#355;iune animal&#259;;</p><p id=\"v_b\"><b>b. </b>autovehiculelor &#351;i vehiculelor cu trac&#355;iune animal&#259;;</p><p id=\"v_c\"><b>c. </b>autoturismelor, motocicletelor &#351;i vehiculelor împinse sau trase cu mâna.</p>', 'nu', 'da', 'nu', ''),
(54, 'Conducerea pe drumul public a autovehicului de c&#259;tre o persoan&#259; cu o alcoolemie în sânge de peste 0,8 g/l atrage:', '', '<p id=\"v_a\"><b>a. </b>r&#259;spunderea contraven&#355;ional&#259; &#351;i re&#355;inerea permisului de conducere;</p><p id=\"v_b\"><b>b. </b>r&#259;spunderea penal&#259; &#351;i anularea permisului de conducere;</p><p id=\"v_c\"><b>c. </b>r&#259;spunderea penal&#259; &#351;i suspendarea exercit&#259;rii dreptului de a conduce pe o perioad&#259; de 90 de zile.</p>', 'nu', 'da', 'nu', ''),
(55, 'În care dintre situa&#355;ii este interzis&#259; oprirea?', '', '<p id=\"v_a\"><b>a. </b>în curbe &#351;i pe sectoarele de drum cu vizibilitate redus&#259; sub 50 m;</p><p id=\"v_b\"><b>b. </b>la mai pu&#355;in de 50 m de cel mai apropiat col&#355; al intersec&#355;iei;</p><p id=\"v_c\"><b>c. </b>în zona de ac&#355;iune a indicatorului „Sta&#355;ionarea interzis&#259;“.</p>', 'da', 'nu', 'nu', ''),
(56, 'Ce manevre sunt interzise pe autostrad&#259;?', '', '<p id=\"v_a\"><b>a. </b>folosirea benzii de accelerare pentru a schimba direc&#355;ia de mers;</p><p id=\"v_b\"><b>b. </b>circula&#355;ia sau sta&#355;ionarea pe banda de urgen&#355;&#259;, cu excep&#355;ia cazurilor justificate;</p><p id=\"v_c\"><b>c. </b>deplasarea cu o vitez&#259; inferioar&#259; limitei maxime permise.</p>', 'nu', 'da', 'nu', ''),
(57, 'Cum este recomandat s&#259; proceda&#355;i pentru a dep&#259;&#351;i, în condi&#355;ii de siguran&#355;&#259;, un tramvai oprit într-o sta&#355;ie cu refugiu?', '', '<p id=\"v_a\"><b>a. </b>s&#259; avertiza&#355;i sonor persoanele aflate în zona sta&#355;iei;</p><p id=\"v_b\"><b>b. </b>s&#259; semnaliza&#355;i din timp inten&#355;ia de dep&#259;&#351;ire a tramvaiului;</p><p id=\"v_c\"><b>c. </b>s&#259; reduce&#355;i viteza de deplasare.</p>', 'nu', 'nu', 'da', ''),
(58, 'Ce interdic&#355;ie ave&#355;i în aceast&#259; situa&#355;ie?', 'uploads/1037.jpg', '<p id=\"v_a\"><b>a. </b>s&#259; acorda&#355;i prioritate vehiculelor care intr&#259; în intersec&#355;ie;</p><p id=\"v_b\"><b>b. </b>s&#259; opri&#355;i autovehiculul;</p><p id=\"v_c\"><b>c. </b>s&#259; circula&#355;i cu o vitez&#259; mai mic&#259; de 30 km pe or&#259;.</p>', 'nu', 'da', 'nu', ''),
(59, 'Suspendarea exercit&#259;rii dreptului de a conduce pentru 90 de zile se aplic&#259; în cazul:', '', '<p id=\"v_a\"><b>a. </b>conducerii sub influen&#355;a b&#259;uturilor alcoolice;</p><p id=\"v_b\"><b>b. </b>neacord&#259;rii priorit&#259;&#355;ii de trecere vehiculelor care au acest drept;</p><p id=\"v_c\"><b>c. </b>nerespect&#259;rii semnalelor poli&#355;istului rutier aflat în exercitarea atribu&#355;iilor de serviciu.</p>', 'da', 'nu', 'nu', ''),
(60, 'În care dintre situa&#355;ii se dispune imobilizarea unui vehicul?', '', '<p id=\"v_a\"><b>a. </b>conduc&#259;torul acestuia nu are asupra sa permisul de conducere;</p><p id=\"v_b\"><b>b. </b>starea tehnic&#259; a vehiculului deterioreaz&#259; drumul public sau afecteaz&#259; mediul;</p><p id=\"v_c\"><b>c. </b>conduc&#259;torul autovehiculului nu are asupra sa certificatul de înmatriculare.</p>', 'nu', 'da', 'nu', ''),
(61, 'Conduc&#259;torilor de vehicule le este interzis:', '', '<p id=\"v_a\"><b>a. </b>s&#259; lase volanul liber;</p><p id=\"v_b\"><b>b. </b>s&#259; claxoneze;</p><p id=\"v_c\"><b>c. </b>s&#259; aplice afi&#351;e &#351;i reclame publicitare pe parbriz, pe lunet&#259; sau pe geamurile laterale.</p>', 'da', 'nu', 'da', ''),
(62, 'Hemoragia nazal&#259; se poate opri dac&#259; accidentatul:', '', '<p id=\"v_a\"><b>a. </b>st&#259; culcat cu capul pe spate;</p><p id=\"v_b\"><b>b. </b>st&#259; în picioare &#351;i î&#351;i strânge ambele n&#259;ri timp de aproximativ 5-10 minute;</p><p id=\"v_c\"><b>c. </b>ridic&#259; mâna dreapt&#259;.</p>', 'nu', 'da', 'nu', ''),
(63, 'Într-o zon&#259; reziden&#355;ial&#259; semnalizat&#259; ca atare:', '', '<p id=\"v_a\"><b>a. </b>copiii nu se pot juca pe partea carosabil&#259;;</p><p id=\"v_b\"><b>b. </b>pietonii pot folosi toat&#259; l&#259;&#355;imea p&#259;r&#355;ii carosabile;</p><p id=\"v_c\"><b>c. </b>viteza maxim&#259; admis&#259; este de 30 km/h.</p>', 'nu', 'da', 'nu', ''),
(64, 'Întoarcerea este interzis&#259;:', '', '<p id=\"v_a\"><b>a. </b>pe str&#259;zile cu sens unic;</p><p id=\"v_b\"><b>b. </b>în intersec&#355;ii &#351;i la mai pu&#355;in de 50 m de cel mai apropiat col&#355; al intersec&#355;iei;</p><p id=\"v_c\"><b>c. </b>la mai pu&#355;in de 100 m de o trecere la nivel cu calea ferat&#259;.</p>', 'da', 'nu', 'nu', ''),
(65, 'Ce v&#259; indic&#259;, de regul&#259;, faptul c&#259; bateria de acumulatori este desc&#259;rcat&#259;?', '', '<p id=\"v_a\"><b>a. </b>la aprinderea farurilor, intensitatea luminoas&#259; a acestora scade progresiv;</p><p id=\"v_b\"><b>b. </b>ac&#355;ionând claxonul, acesta emite un sunet slab, întrerupt;</p><p id=\"v_c\"><b>c. </b>demontând bateria, constat&#259;m c&#259; aceasta este mai u&#351;oar&#259; decât de obicei.</p>', 'da', 'da', 'nu', ''),
(66, 'Ave&#355;i obliga&#355;ia de a opri imediat autovehiculul la semnalele adresate de:', '', '<p id=\"v_a\"><b>a. </b>poli&#355;i&#351;tii comunitari;</p><p id=\"v_b\"><b>b. </b>personalul autorizat din cadrul serviciului examin&#259;ri;</p><p id=\"v_c\"><b>c. </b>personalul autorizat din zona lucr&#259;rilor pe drumurile publice.</p>', 'nu', 'nu', 'da', ''),
(67, 'Oprirea voluntar&#259; a unui vehicul este interzis&#259;:', '', '<p id=\"v_a\"><b>a. </b>pe drumurile cu un singur sens de circula&#355;ie;</p><p id=\"v_b\"><b>b. </b>pe drumurile cu o l&#259;&#355;ime mai mic&#259; de 6 m;</p><p id=\"v_c\"><b>c. </b>în locurile unde s-ar împiedica observarea unui indicator sau a unui semnal luminos.</p>', 'nu', 'nu', 'da', ''),
(68, 'Viteza de deplasare trebuie redus&#259;:', '', '<p id=\"v_a\"><b>a. </b>la întâlnirea indicatorului „Cedeaz&#259; trecerea“;</p><p id=\"v_b\"><b>b. </b>atunci când se circul&#259; din direc&#355;ia opus&#259; tramvaielor, troleibuzelor &#351;i autobuzelor oprite în sta&#355;ie, pe drumurile cu o singur&#259; band&#259; pe sens;</p><p id=\"v_c\"><b>c. </b>la întâlnirea indicatorului „Prioritate fa&#355;&#259; de circula&#355;ia din sens invers“.</p>', 'da', 'nu', 'nu', ''),
(69, 'Execut&#259; corect autoturismul manevra de dep&#259;&#351;ire?', 'uploads/731.jpg', '<p id=\"v_a\"><b>a. </b>nu, deoarece autobuzul are prioritate la plecarea din sta&#355;ie;</p><p id=\"v_b\"><b>b. </b>nu, deoarece aceast&#259; manevr&#259; nu este permis&#259; în dreptul sta&#355;iilor mijloacelor de transport public de persoane;</p><p id=\"v_c\"><b>c. </b>da, manevra este regulamentar&#259;.</p>', 'nu', 'nu', 'da', ''),
(70, 'Într-o zon&#259; pietonal&#259;, semnalizat&#259; ca atare, sunte&#355;i obligat s&#259; circula&#355;i cu o vitez&#259; de:', '', '<p id=\"v_a\"><b>a. </b>cel mult 30 km/h;</p><p id=\"v_b\"><b>b. </b>cel mult 20 km/h;</p><p id=\"v_c\"><b>c. </b>cel mult 5 km/h.</p>', 'nu', 'nu', 'da', ''),
(71, 'Exercitarea dreptului de a conduce se suspend&#259; pe o perioad&#259; de 60 de zile pentru:', '', '<p id=\"v_a\"><b>a. </b>circula&#355;ia pe sens opus, cu excep&#355;ia cazurilor în care se efectueaz&#259; regulamentar manevra de dep&#259;&#351;ire;</p><p id=\"v_b\"><b>b. </b>dep&#259;&#351;irea cu cel mult 30 km/h a vitezei maxime admise;</p><p id=\"v_c\"><b>c. </b>cumul de 10 puncte de penalizare.</p>', 'da', 'nu', 'nu', ''),
(72, 'Circula&#355;ia frecvent&#259; cu termostatul defect &#351;i, implicit, cu o temperatur&#259; a lichidului de r&#259;cire sub 60 grade C conduce la:', '', '<p id=\"v_a\"><b>a. </b>cre&#351;terea consumului de combustibil;</p><p id=\"v_b\"><b>b. </b>imposibilitatea înc&#259;lzirii habitaclului;</p><p id=\"v_c\"><b>c. </b>blocarea pompei de ap&#259;.</p>', 'da', 'nu', 'nu', ''),
(73, 'În intersec&#355;ii au prioritate de trecere vehiculele care întâlnesc unul dintre indicatoarele:', '', '<p id=\"v_a\"><b>a. </b>„Intersec&#355;ie cu un drum f&#259;r&#259; prioritate“;</p><p id=\"v_b\"><b>b. </b>„Înainte“;</p><p id=\"v_c\"><b>c. </b>„Circula&#355;ie în ambele sensuri“.</p>', 'da', 'nu', 'nu', ''),
(74, 'Oprirea voluntar&#259; a unui vehicul este interzis&#259;:', '', '<p id=\"v_a\"><b>a. </b>în zona de ac&#355;iune a indicatorului „Sta&#355;ionarea interzis&#259;“;</p><p id=\"v_b\"><b>b. </b>în curbe &#351;i în alte locuri unde vizibilitatea este redus&#259; sub 100 m;</p><p id=\"v_c\"><b>c. </b>în intersec&#355;iile cu sens giratoriu.</p>', 'nu', 'nu', 'da', ''),
(75, 'Purtarea centurii de siguran&#355;&#259; nu este obligatorie în localit&#259;&#355;i pentru:', '', '<p id=\"v_a\"><b>a. </b>copiii cu vârsta de pân&#259; la 12 ani;</p><p id=\"v_b\"><b>b. </b>persoanele care posed&#259; un document în acest sens, emis de o institu&#355;ie medical&#259; de specialitate;</p><p id=\"v_c\"><b>c. </b>femeia care prezint&#259; o sarcinã vizibil&#259;.</p>', 'nu', 'da', 'da', ''),
(76, 'În care dintre urm&#259;toarele situa&#355;ii este interzis&#259; oprirea?', '', '<p id=\"v_a\"><b>a. </b>pe sectoarele de drum neiluminate;</p><p id=\"v_b\"><b>b. </b>la o distan&#355;&#259; mai mic&#259; de 5 m fa&#355;&#259; de spatele unui tramvai;</p><p id=\"v_c\"><b>c. </b>pe drumurile na&#355;ionale europene.</p>', 'nu', 'nu', 'da', ''),
(77, 'În care dintre situa&#355;iile de mai jos este interzis&#259; dep&#259;&#351;irea vehiculelor?', '', '<p id=\"v_a\"><b>a. </b>în zona de ac&#355;iune a indicatorului „Alte pericole“;</p><p id=\"v_b\"><b>b. </b>la mai pu&#355;in de 50 m de o intersec&#355;ie;</p><p id=\"v_c\"><b>c. </b>în curbe, unde vizibilitatea este redus&#259; sub 50 m.</p>', 'nu', 'nu', 'da', ''),
(78, 'Autoturismul sta&#355;ioneaz&#259; regulamentar?', 'uploads/382.jpg', '<p id=\"v_a\"><b>a. </b>da, deoarece în zon&#259; nu sunt restric&#355;ii de circula&#355;ie;</p><p id=\"v_b\"><b>b. </b>nu, deoarece se afl&#259; pe un drum na&#355;ional;</p><p id=\"v_c\"><b>c. </b>nu, deoarece oblig&#259; camionul la dep&#259;&#351;irea prin triplare.</p>', 'da', 'nu', 'nu', ''),
(79, 'În care dintre urm&#259;toarele cazuri, pe lâng&#259; amenda contraven&#355;ional&#259;, se re&#355;ine &#351;i permisul de conducere?', '', '<p id=\"v_a\"><b>a. </b>neoprirea la semnalul persoanelor autorizate din zona de lucr&#259;ri la drumul public;</p><p id=\"v_b\"><b>b. </b>nerespectarea semnalelor poli&#355;i&#351;tilor la trecerea coloanelor oficiale;</p><p id=\"v_c\"><b>c. </b>neoprirea la semnalul agentului de cale ferat&#259;.</p>', 'nu', 'da', 'nu', ''),
(80, 'Unde trebuie s&#259; declara&#355;i pierderea, furtul sau distrugerea permisului de conducere:', '', '<p id=\"v_a\"><b>a. </b>la unitatea emitent&#259;;</p><p id=\"v_b\"><b>b. </b>la poli&#355;ia de care apar&#355;ine&#355;i cu domiciliul;</p><p id=\"v_c\"><b>c. </b>la cea mai apropiat&#259; sec&#355;ie de poli&#355;ie.</p>', 'da', 'nu', 'nu', ''),
(81, 'În situa&#355;ia dat&#259;, pentru a v&#259; semnaliza prezen&#355;a, ve&#355;i schimba alternativ luminile de drum cu luminile de întâlnire?', 'uploads/700.jpg', '<p id=\"v_a\"><b>a. </b>da, dup&#259; trecerea autocamionului;</p><p id=\"v_b\"><b>b. </b>nu;</p><p id=\"v_c\"><b>c. </b>se folosesc numai luminile de drum.</p>', 'da', 'nu', 'nu', ''),
(82, 'În ce ordine vor circula autovehiculele prin intersec&#355;ia prezentat&#259;, dac&#259; semafoarele nu func&#355;ioneaz&#259;?', 'uploads/1029.jpg', '<p id=\"v_a\"><b>a. </b>tramvaiul 1, tramvaiul 2, autoturismul;</p><p id=\"v_b\"><b>b. </b>autoturismul, tramvaiul 1, tramvaiul 2;</p><p id=\"v_c\"><b>c. </b>tramvaiul 1, autoturismul, tramvaiul 2.</p>', 'nu', 'nu', 'da', ''),
(83, 'În ce situa&#355;ie este interzis&#259; întoarcerea?', '', '<p id=\"v_a\"><b>a. </b>în apropierea sta&#355;iilor de tramvai;</p><p id=\"v_b\"><b>b. </b>când vizibilitatea este redus&#259; sub 100 m;</p><p id=\"v_c\"><b>c. </b>în locurile unde soliditatea drumului nu permite.</p>', 'nu', 'nu', 'da', ''),
(84, 'La folosirea pe termen lung a solu&#355;iilor antigel, trebuie aplicate urm&#259;toarele reguli:', '', '<p id=\"v_a\"><b>a. </b>se va urm&#259;ri întotdeauna nivelul optim al lichidului din vasul de expansiune;</p><p id=\"v_b\"><b>b. </b>dup&#259; cel mult 3 ani solu&#355;ia antigel va fi complet înlocuit&#259;, chiar dac&#259; densitatea acesteia corespunde cerin&#355;elor;</p><p id=\"v_c\"><b>c. </b>se va urm&#259;ri culoarea lichidului din vasul de expansiune.</p>', 'nu', 'da', 'nu', ''),
(85, 'Este interzis&#259; dep&#259;&#351;irea:', '', '<p id=\"v_a\"><b>a. </b>tuturor vehiculelor, la 100 m de trecerile la nivel cu calea ferat&#259; curent&#259;;</p><p id=\"v_b\"><b>b. </b>vehiculelor cu trac&#355;iune animal&#259;, motocicletelor f&#259;r&#259; ata&#351;, mopedelor &#351;i bicicletelor, dac&#259; vizibilitatea asupra drumului este asigurat&#259; pe o distan&#355;&#259; mai mare de 20 m, iar l&#259;&#355;imea drumului este de cel pu&#355;in 7 m</p><p id=\"v_c\"><b>c. </b>în intersec&#355;iile cu circula&#355;ie nedirijat&#259;.</p>', 'nu', 'nu', 'da', ''),
(86, 'Viteza se va reduce obligatoriu:', '', '<p id=\"v_a\"><b>a. </b> la trecerile pentru pietoni;\r\n</p><p id=\"v_b\"><b>b. </b> la semnalul poli&#355;istului de frontier&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> la întâlnirea indicatorului „Cedeaz&#259; trecerea”.</p>', 'nu', 'da', 'da', ''),
(87, 'Cum ve&#355;i semnaliza faptul c&#259; autovehiculul cu care circula&#355;i a r&#259;mas în pan&#259; pe partea carosabil&#259;?', '', '<p id=\"v_a\"><b>a. </b> prin folosirea luminilor de pozi&#355;ie;\r\n</p><p id=\"v_b\"><b>b. </b> prin instalarea triunghiurilor reflectorizante &#351;i prin folosirea luminilor de avarie;\r\n</p><p id=\"v_c\"><b>c. </b> prin purtarea vestei reflectorizante.</p>', 'da', 'da', 'nu', ''),
(88, 'În care dintre situa&#355;ii consumul de carburant al unui motor cre&#351;te?', '', '<p id=\"v_a\"><b>a. </b> atunci când motorul nu atinge temperatura de func&#355;ionare;\r\n</p><p id=\"v_b\"><b>b. </b> atunci când fumul de e&#351;apament este de culoare neagr&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> atunci când motorul func&#355;ioneaz&#259; cu întreruperi.</p>', 'da', 'da', 'da', ''),
(89, 'Nu se poate circula cu un autoturism dac&#259;:', '', '<p id=\"v_a\"><b>a. </b> se dep&#259;&#351;e&#351;te masa maxim&#259; admis&#259;, înscris&#259; în certificatul de înmatriculare;\r\n</p><p id=\"v_b\"><b>b. </b> anvelopele sunt de m&#259;rimi &#351;i caracteristici diferite fa&#355;&#259; de cele înscrise în certificatul de înmatriculare;\r\n</p><p id=\"v_c\"><b>c. </b> autovehiculul dep&#259;&#351;e&#351;te în&#259;l&#355;imea înscris&#259; în certificatul de înmatriculare.</p>', 'da', 'da', 'nu', ''),
(90, 'Neeliberarea complet&#259; a frânei de sta&#355;ionare determin&#259;:', '', '<p id=\"v_a\"><b>a. </b> zgomote în zona manetei frânei de mân&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> un consum suplimentar de carburant;\r\n</p><p id=\"v_c\"><b>c. </b> înc&#259;lzirea excesiv&#259; a butucilor ro&#355;ilor din spate.</p>', 'nu', 'da', 'da', ''),
(91, 'Pentru care dintre situa&#355;iile de mai jos se dispune re&#355;inerea permisului de conducere?', '', '<p id=\"v_a\"><b>a. </b> pentru neacordarea priorit&#259;&#355;ii de trecere;\r\n</p><p id=\"v_b\"><b>b. </b> permisul de conducere este deteriorat;\r\n</p><p id=\"v_c\"><b>c. </b> permisul de conducere prezint&#259; modific&#259;ri, &#351;ters&#259;turi sau ad&#259;ugiri.</p>', 'da', 'da', 'da', ''),
(92, 'În care dintre situa&#355;ii este interzis&#259; schimbarea direc&#355;iei de mers spre stânga?', '', '<p id=\"v_a\"><b>a. </b> când conduc&#259;torul de vehicul este încadrat pe banda din dreapta;\r\n</p><p id=\"v_b\"><b>b. </b> când drumul pe care urmeaz&#259; s&#259; se intre este semnalizat cu indicatorul „Circula&#355;ia interzis&#259; în ambele sensuri”;\r\n</p><p id=\"v_c\"><b>c. </b> când strada pe care urmeaz&#259; s&#259; se intre este semnalizat&#259; cu indicatorul „Drum f&#259;r&#259; ie&#351;ire”.</p>', 'da', 'da', 'nu', ''),
(93, 'În care dintre situa&#355;ii sunte&#355;i obligat s&#259; reduce&#355;i viteza?', '', '<p id=\"v_a\"><b>a. </b> la trecerea la nivel cu calea ferat&#259; curent&#259; cu bariere;\r\n</p><p id=\"v_b\"><b>b. </b> la trecerea la nivel cu calea ferat&#259; curent&#259; f&#259;r&#259; bariere;\r\n</p><p id=\"v_c\"><b>c. </b> la trecerea la nivel cu calea ferat&#259; curent&#259; industrial&#259;.</p>', 'da', 'nu', 'nu', ''),
(94, 'În care dintre situa&#355;ii este permis&#259; dep&#259;&#351;irea?', '', '<p id=\"v_a\"><b>a. </b> în intersec&#355;ia semnalizat&#259; cu indicatoare de prioritate;\r\n</p><p id=\"v_b\"><b>b. </b> în intersec&#355;ia dirijat&#259; prin semnale luminoase;\r\n</p><p id=\"v_c\"><b>c. </b> în intersec&#355;ia dirijat&#259; prin semnale ale poli&#355;istului.</p>', 'da', 'da', 'da', ''),
(95, 'Se aplic&#259; puncte de penalizare pentru:', '', '<p id=\"v_a\"><b>a. </b> nerespectarea semnifica&#355;iei indicatorului „Oprire”, instalat la trecerea la nivel cu o cale ferat&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> dep&#259;&#351;irea cu 31-40 km/h a vitezei maxime admise pe sectorul de drum respectiv pentru categoria din care face parte autovehiculul condus;\r\n</p><p id=\"v_c\"><b>c. </b> dep&#259;&#351;irea cu 10-20 km/h a vitezei maxime admise pe sectorul de drum respectiv pentru categoria din care face parte autovehiculul condus.</p>', 'da', 'da', 'da', ''),
(96, 'Ce categorii de vehicule au obliga&#355;ia de a folosi luminile de întâlnire pe timp de zi?', '', '<p id=\"v_a\"><b>a. </b> motocicletele &#351;i mopedele;\r\n</p><p id=\"v_b\"><b>b. </b> autovehiculele care înso&#355;esc coloane militare sau transport&#259; grupuri de persoane;\r\n</p><p id=\"v_c\"><b>c. </b> autovehiculele care tracteaz&#259; alte vehicule sau care transport&#259; produse periculoase.</p>', 'da', 'da', 'da', ''),
(97, 'Ce condi&#355;ii tehnice trebuie s&#259; îndeplineasc&#259; un autovehicul pentru a circula pe drumurile publice?', '', '<p id=\"v_a\"><b>a. </b> s&#259; nu dep&#259;&#351;easc&#259; masa total&#259; maxim&#259; autorizat&#259; înscris&#259; în certificatul de înmatriculare;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; nu dep&#259;&#351;easc&#259; gabaritul înscris în certificatul de înmatriculare;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; fie înmatriculat &#351;i s&#259; corespund&#259; condi&#355;iilor tehnice legale.</p>', 'da', 'nu', 'da', ''),
(98, 'La trecerea autovehiculelor cu regim de circula&#355;ie prioritar, care au în func&#355;iune mijloacele de avertizare sonor&#259; &#351;i luminoas&#259; de culoare albastr&#259;, sunte&#355;i obligat:', '', '<p id=\"v_a\"><b>a. </b> s&#259; reduce&#355;i viteza;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; circula&#355;i cât mai aproape de marginea drumului în sensul de deplasare;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; acorda&#355;i prioritate de trecere.</p>', 'da', 'nu', 'da', ''),
(99, 'Când este permis&#259; dep&#259;&#351;irea prin stânga a tramvaielor aflate în mers?', '', '<p id=\"v_a\"><b>a. </b> atunci când drumul public este cu sens unic;\r\n</p><p id=\"v_b\"><b>b. </b> când între &#351;ina de tramvai din dreapta &#351;i marginea trotuarului nu exist&#259; suficient spa&#355;iu pentru dep&#259;&#351;ire;\r\n</p><p id=\"v_c\"><b>c. </b> în nicio situa&#355;ie.</p>', 'da', 'da', 'nu', ''),
(100, 'Ce obliga&#355;ii v&#259; revin în timpul conducerii autovehiculului cu privire la vitez&#259;?', '', '<p id=\"v_a\"><b>a. </b> s&#259; respecta&#355;i viteza maxim&#259; admis&#259; pe sectorul de drum pe care circula&#355;i, corespunz&#259;toare categoriei din care face parte autovehiculul condus;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; v&#259; conforma&#355;i limitelor de vitez&#259; impuse prin mijloacele de semnalizare specifice;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; rula&#355;i pe cât posibil în treptele inferioare de vitez&#259;, pentru a nu polua mediul.</p>', 'da', 'da', 'nu', ''),
(101, 'Transportarea victimelor la spital se poate realiza cu:', '', '<p id=\"v_a\"><b>a. </b> o ambulan&#355;&#259; special dotat&#259; în acest sens;\r\n</p><p id=\"v_b\"><b>b. </b> o ambulan&#355;&#259; înso&#355;it&#259; de un medic;\r\n</p><p id=\"v_c\"><b>c. </b> vehiculul care a provocat accidentul, dac&#259; starea victimei permite acest lucru.</p>', 'da', 'da', 'da', ''),
(102, 'Se interzice circula&#355;ia pe drumurile publice a autovehiculelor dac&#259;:', '', '<p id=\"v_a\"><b>a. </b> echipamentul de frânare este defect;\r\n</p><p id=\"v_b\"><b>b. </b> emisia de gaze atinge limita legal&#259; admis&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> au montate dispozitive de iluminare &#351;i de semnalizare, altele decât cele omologate de autoritatea competent&#259;.</p>', 'da', 'nu', 'da', ''),
(103, 'Cum ve&#355;i proceda la întâlnirea semnalului intermitent de culoare galben&#259;?', '', '<p id=\"v_a\"><b>a. </b> acest semnal nu v&#259; permite trecerea;\r\n</p><p id=\"v_b\"><b>b. </b> ve&#355;i circula cu vitez&#259; redus&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> ve&#355;i respecta semnifica&#355;ia semnaliz&#259;rii rutiere &#351;i a regulilor de circula&#355;ie aplicabile în acel loc.</p>', 'nu', 'da', 'da', ''),
(104, 'Sunte&#355;i obligat s&#259; nu dep&#259;&#351;i&#355;i viteza de 50 km/h în afara localit&#259;&#355;ilor, în urm&#259;toarele situa&#355;ii:', '', '<p id=\"v_a\"><b>a. </b> pe poduri sau sub poduri;\r\n</p><p id=\"v_b\"><b>b. </b> la trecerea pe lâng&#259; grupuri organizate, coloane militare sau cortegii, pe drumurile cu o singur&#259; band&#259; pe sens;\r\n</p><p id=\"v_c\"><b>c. </b> în curbe deosebit de periculoase sau cu vizibilitate mai mic&#259; de 50 m.</p>', 'nu', 'da', 'da', ''),
(105, 'Circula&#355;ia autovehiculelor pe drumurile publice se interzice atunci când:', '', '<p id=\"v_a\"><b>a. </b> certificatul de înmatriculare este deteriorat;\r\n</p><p id=\"v_b\"><b>b. </b> acestea nu corespund normelor tehnice cu privire la siguran&#355;a rutier&#259; &#351;i la protec&#355;ia mediului înconjur&#259;tor;\r\n</p><p id=\"v_c\"><b>c. </b> termenul de valabilitate a inspec&#355;iei tehnice periodice a expirat.</p>', 'nu', 'da', 'da', ''),
(106, 'În ce situa&#355;ie poate fi re&#355;inut certificatul de înmatriculare al unui vehicul?', '', '<p id=\"v_a\"><b>a. </b> autovehiculul are aplicate afi&#351;e publicitare;\r\n</p><p id=\"v_b\"><b>b. </b> datele din certificatul de înmatriculare nu concord&#259; cu caracteristicile tehnice ale vehiculului;\r\n</p><p id=\"v_c\"><b>c. </b> de&#355;in&#259;torul nu face dovada încheierii asigur&#259;rii auto de r&#259;spundere civil&#259;.</p>', 'nu', 'da', 'da', ''),
(107, 'Care dintre faptele de mai jos constituie infrac&#355;iune &#351;i se pedepse&#351;te ca atare?', '', '<p id=\"v_a\"><b>a. </b> conducerea pe drumul public a unui autovehicul neînmatriculat;\r\n</p><p id=\"v_b\"><b>b. </b> conducerea pe drumul public a unui autovehicul f&#259;r&#259; certificat de înmatriculare;\r\n</p><p id=\"v_c\"><b>c. </b> conducerea pe drumul public a unui autovehicul cu num&#259;r de înmatriculare fals.</p>', 'da', 'nu', 'da', ''),
(108, 'În care dintre situa&#355;ii se re&#355;ine certificatul de înmatriculare sau înregistrare?', '', '<p id=\"v_a\"><b>a. </b> când inspec&#355;ia tehnic&#259; periodic&#259; a expirat;\r\n</p><p id=\"v_b\"><b>b. </b> dac&#259; zgomotul în mers sau sta&#355;ionare dep&#259;&#351;e&#351;te limita legal&#259; admis&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> când motorul emite noxe poluante peste limitele legale admise.</p>', 'da', 'da', 'da', ''),
(109, 'Pentru care dintre faptele de mai jos se aplic&#259; m&#259;sura re&#355;inerii permisului de conducere?', '', '<p id=\"v_a\"><b>a. </b> pentru neoprirea la semnalul regulamentar al poli&#355;istului;\r\n</p><p id=\"v_b\"><b>b. </b> pentru nerespectarea dispozi&#355;iilor legale referitoare la dep&#259;&#351;ire;\r\n</p><p id=\"v_c\"><b>c. </b> pentru nerespectarea normelor privind folosirea luminilor pe timp de noapte.</p>', 'da', 'da', 'nu', ''),
(110, 'În care dintre situa&#355;ii este interzis&#259; întoarcerea vehiculului?', '', '<p id=\"v_a\"><b>a. </b> în locul în care este instalat indicatorul cu semnifica&#355;ia „Întoarcerea interzis&#259;“;\r\n</p><p id=\"v_b\"><b>b. </b> în locurile în care oprirea este interzis&#259;, cu excep&#355;ia intersec&#355;iilor;\r\n</p><p id=\"v_c\"><b>c. </b> în zona de ac&#355;iune a indicatorului „Sta&#355;ionarea interzis&#259;“.</p>', 'da', 'da', 'nu', ''),
(111, 'În care dintre situa&#355;iile prezentate mai jos nu este permis&#259; oprirea voluntar&#259; a vehiculelor?', '', '<p id=\"v_a\"><b>a. </b> la o distan&#355;&#259; mai mic&#259; de 50 m de trecerea la nivel cu calea ferat&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> pe pistele obligatorii pentru pietoni;\r\n</p><p id=\"v_c\"><b>c. </b> pe platforma c&#259;ii ferate sau de tramvai, dac&#259; prin aceasta este stânjenit&#259; circula&#355;ia vehiculelor pe &#351;ine.</p>', 'da', 'da', 'da', ''),
(112, 'În ce situa&#355;ii se interzice circula&#355;ia pe drumurile publice a autovehiculelor?', '', '<p id=\"v_a\"><b>a. </b> atunci când zgomotul în mers sau în sta&#355;ionare dep&#259;&#351;e&#351;te limita legal&#259; admis&#259; pentru tipul respectiv de vehicul;\r\n</p><p id=\"v_b\"><b>b. </b> atunci când nu se respect&#259; regulile referitoare la transportul produselor periculoase;\r\n</p><p id=\"v_c\"><b>c. </b> atunci când sunt montate alte dispozitive de avertizare sonor&#259; decât cele omologate de autoritatea competent&#259;.</p>', 'da', 'da', 'da', ''),
(113, 'Cum ve&#355;i proceda corect pentru a intra pe autostrad&#259; folosind banda de intrare (accelerare):', '', '<p id=\"v_a\"><b>a. </b> ve&#355;i m&#259;ri viteza, întrucât banda pe care circula&#355;i este special creat&#259; în acest scop;\r\n</p><p id=\"v_b\"><b>b. </b> ceda&#355;i trecerea autovehiculelor care circul&#259; pe prima band&#259; a autostr&#259;zii &#351;i nu stânjeni&#355;i în niciun fel circula&#355;ia acestora;\r\n</p><p id=\"v_c\"><b>c. </b> nu ave&#355;i nicio obliga&#355;ie, deoarece autostrada are suficiente benzi pentru a permite circula&#355;ia tuturor vehiculelor.</p>', 'da', 'da', 'nu', ''),
(114, 'Când se recomand&#259; s&#259; opri&#355;i motorul pentru a reduce consumul de combustibil?', '', '<p id=\"v_a\"><b>a. </b> în cazul unei opriri lungi la o trecere la nivel cu calea ferat&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> la culoarea ro&#351;ie a unui semafor care, de regul&#259;, dureaz&#259; foarte mult;\r\n</p><p id=\"v_c\"><b>c. </b> la semnalul poli&#355;istului rutier f&#259;cut în acest sens.</p>', 'da', 'da', 'nu', ''),
(115, 'În care dintre situa&#355;ii este interzis&#259; dep&#259;&#351;irea unui moped?', '', '<p id=\"v_a\"><b>a. </b> pe trecerile pentru pietoni, semnalizate cu indicator sau cu marcaj;\r\n</p><p id=\"v_b\"><b>b. </b> la mai pu&#355;in de 50 m de trecerile la nivel cu calea ferat&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> în zona de ac&#355;iune a indicatorului „Dep&#259;&#351;irea autovehiculelor, cu excep&#355;ia motocicletelor f&#259;r&#259; ata&#351;, interzis&#259;“.</p>', 'da', 'da', 'nu', ''),
(116, 'Conduc&#259;torii de autovehicule au urm&#259;toarele obliga&#355;ii, înainte de a pleca la drum:', '', '<p id=\"v_a\"><b>a. </b> s&#259; verifice sistemul de lumini &#351;i semnalizare;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; verifice instala&#355;ia de climatizare;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; men&#355;in&#259; permanent curat parbrizul.</p>', 'da', 'da', 'da', ''),
(117, 'Ce se poate ata&#351;a unui autovehicul din categoria B?', '', '<p id=\"v_a\"><b>a. </b> o remorc&#259; u&#351;oar&#259;, a c&#259;rei mas&#259; s&#259; nu dep&#259;&#351;easc&#259; 750 kg;\r\n</p><p id=\"v_b\"><b>b. </b> o remorc&#259; sau o semiremorc&#259;, cu masa maxim&#259; autorizat&#259; de pân&#259; la 1 t;\r\n</p><p id=\"v_c\"><b>c. </b> o remorc&#259;, dar care, împreun&#259; cu autovehiculul tr&#259;g&#259;tor de categorie B, s&#259; nu dep&#259;&#351;easc&#259; 4250 kg.</p>', 'da', 'nu', 'da', ''),
(118, 'Când se restituie permisul de conducere re&#355;inut de c&#259;tre poli&#355;ie în vederea suspend&#259;rii exercit&#259;rii dreptului de a conduce?', '', '<p id=\"v_a\"><b>a. </b> la hot&#259;rârea agentului de circula&#355;ie;\r\n</p><p id=\"v_b\"><b>b. </b> la expirarea termenului de suspendare, dac&#259; a fost promovat testul de verificare a cuno&#351;tin&#355;elor, în situa&#355;iile prev&#259;zute de lege;\r\n</p><p id=\"v_c\"><b>c. </b> atunci când procesul verbal de contraven&#355;ie a fost anulat printr-o hot&#259;râre judec&#259;toreasc&#259; definitiv&#259;.</p>', 'nu', 'da', 'da', ''),
(119, 'În care dintre urm&#259;toarele situa&#355;ii sunte&#355;i obligat s&#259; circula&#355;i cu o vitez&#259; care s&#259; nu dep&#259;&#351;easc&#259; 30 km/h în localit&#259;&#355;i?', '', '<p id=\"v_a\"><b>a. </b> în zona de ac&#355;iune a indicatorului de avertizare \"Accident\";\r\n</p><p id=\"v_b\"><b>b. </b> când vizibilitatea este sub 100 m, în condi&#355;ii de cea&#355;&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> la intersectarea cu vehiculele care circul&#259; din sens opus.</p>', 'da', 'da', 'nu', ''),
(120, 'Viteza se reduce la semnalul:', '', '<p id=\"v_a\"><b>a. </b> personalului autorizat din zona lucr&#259;rilor drumului public;\r\n</p><p id=\"v_b\"><b>b. </b> agentului de cale ferat&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> poli&#355;istului de frontier&#259;.</p>', 'da', 'da', 'da', '');
INSERT INTO `intrebari` (`id`, `cerinta`, `imagine`, `variante`, `rasp_a`, `rasp_b`, `rasp_c`, `eticheta`) VALUES
(121, 'La întâlnirea panoului de pe stâlp ave&#355;i obliga&#355;ia:', 'uploads/1015.jpg', '<p id=\"v_a\"><b>a. </b> s&#259; v&#259; deplasa&#355;i cu aten&#355;ie pe sectorul de drum îngustat;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; acorda&#355;i prioritate numai autocamioanelor &#351;i autobuzelor care circul&#259; din sens opus;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; acorda&#355;i prioritate vehiculelor care circul&#259; din sens opus.</p>', 'da', 'nu', 'nu', ''),
(122, 'Ce rol are cutia de viteze?', '', '<p id=\"v_a\"><b>a. </b> asigur&#259; transmiterea puterii la ro&#355;i;\r\n</p><p id=\"v_b\"><b>b. </b> permite mersul înapoi, f&#259;r&#259; a inversa sensul de rota&#355;ie a motorului;\r\n</p><p id=\"v_c\"><b>c. </b> permite sta&#355;ionarea cu motorul în func&#355;iune.</p>', 'da', 'da', 'da', ''),
(123, 'Ce bunuri sunt supuse confisc&#259;rii, în conformitate cu legea circula&#355;iei?', '', '<p id=\"v_a\"><b>a. </b> mijloacele speciale de avertizare luminoas&#259; &#351;i sonor&#259; de&#355;inute ilegal;\r\n</p><p id=\"v_b\"><b>b. </b> farurile suplimentare &#351;i portbagajul suplimentar neautorizat;\r\n</p><p id=\"v_c\"><b>c. </b> mijloacele tehnice de detectare a prezen&#355;ei aparatelor radar.</p>', 'da', 'nu', 'nu', ''),
(124, 'Remorcarea unui autoturism r&#259;mas în pan&#259; se face cu:', '', '<p id=\"v_a\"><b>a. </b> o leg&#259;tur&#259; flexibil&#259; omologat&#259;, în lungime de 3-5 m, dac&#259; mecanismul de direc&#355;ie &#351;i sistemul de frânare nu sunt defecte;\r\n</p><p id=\"v_b\"><b>b. </b> o bar&#259; rigid&#259;, lung&#259; de maximum 4 m;\r\n</p><p id=\"v_c\"><b>c. </b> o bar&#259; metalic&#259;, înso&#355;it&#259; de un lan&#355; de siguran&#355;&#259;.</p>', 'da', 'da', 'nu', ''),
(125, 'Pentru a conduce ecologic un autovehiculul, este necesar:', '', '<p id=\"v_a\"><b>a. </b> s&#259; verifica&#355;i o dat&#259; pe lun&#259;, la rece, presiunea din pneuri;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; schimba&#355;i între ele pneurile, la aproximativ 10 000 km;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; înlocui&#355;i pneurile la fiecare 1000 km.</p>', 'da', 'da', 'nu', ''),
(126, 'Care dintre variantele de mai jos reprezint&#259; calit&#259;&#355;i ale unui ulei de motor?', '', '<p id=\"v_a\"><b>a. </b> culoarea;\r\n</p><p id=\"v_b\"><b>b. </b> vâscozitatea;\r\n</p><p id=\"v_c\"><b>c. </b> punctul de congelare.</p>', 'nu', 'da', 'da', ''),
(127, 'În care dintre situa&#355;ii oprirea este interzis&#259;?', '', '<p id=\"v_a\"><b>a. </b> în zona de ac&#355;iune a indicatorului care interzice dep&#259;&#351;irea;\r\n</p><p id=\"v_b\"><b>b. </b> pe str&#259;zile unde circula&#355;ia se desf&#259;&#351;oar&#259; în sens unic;\r\n</p><p id=\"v_c\"><b>c. </b> în dreptul altui vehicul care se afl&#259; oprit, dac&#259; prin aceasta se stânjene&#351;te circula&#355;ia a dou&#259; vehicule venind din sensuri opuse.</p>', 'da', 'nu', 'da', ''),
(128, 'Preciza&#355;i în care dintre situa&#355;ii este interzis&#259; sta&#355;ionarea voluntar&#259; a vehiculelor?', '', '<p id=\"v_a\"><b>a. </b> în dreptul c&#259;ilor de acces care deservesc propriet&#259;&#355;ile al&#259;turate drumurilor publice;\r\n</p><p id=\"v_b\"><b>b. </b> în zona aglomerat&#259; a pie&#355;elor;\r\n</p><p id=\"v_c\"><b>c. </b> în pante &#351;i în rampe.</p>', 'da', 'nu', 'da', ''),
(129, 'Pentru între&#355;inerea sistemului de ungere, trebuie s&#259; efectua&#355;i:', '', '<p id=\"v_a\"><b>a. </b> verificarea &#351;i completarea nivelului uleiului din baie;\r\n</p><p id=\"v_b\"><b>b. </b> înlocuirea uleiului dup&#259; expirarea termenului de utilizare;\r\n</p><p id=\"v_c\"><b>c. </b> desfundarea orificiilor de aerisire ale radiatorului.</p>', 'da', 'da', 'nu', ''),
(130, 'Poli&#355;istul rutier poate dispune imobilizarea unui vehicul atunci când:', '', '<p id=\"v_a\"><b>a. </b> vehiculul nu are efectuat&#259; inspec&#355;ia tehnic&#259; periodic&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> anvelopele au alte dimensiuni decât cele prev&#259;zute în cartea de identitate a vehiculului;\r\n</p><p id=\"v_c\"><b>c. </b> conduc&#259;torul auto refuz&#259; s&#259; se legitimeze.</p>', 'nu', 'nu', 'da', ''),
(131, 'În ce situa&#355;ie se dispune imobilizarea vehiculului?', '', '<p id=\"v_a\"><b>a. </b> când exist&#259; date sau indicii c&#259; acesta face obiectul unei contraven&#355;ii;\r\n</p><p id=\"v_b\"><b>b. </b> când are defec&#355;iuni grave la sistemul de direc&#355;ie;\r\n</p><p id=\"v_c\"><b>c. </b> când este în imposibilitatea de a se deplasa, din cauza unor defec&#355;iuni la motor.</p>', 'nu', 'da', 'nu', ''),
(132, 'Se pedepsesc penal urm&#259;toarele ac&#355;iuni:', '', '<p id=\"v_a\"><b>a. </b> instalarea de mijloace de semnalizare rutier&#259; sau modificarea pozi&#355;iei acestora f&#259;r&#259; autoriza&#355;ie legal&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> organizarea sau participarea, în calitate de conduc&#259;tor auto sau de animale, la întreceri neautorizate pe drumurile publice;\r\n</p><p id=\"v_c\"><b>c. </b> blocarea cu inten&#355;ie a drumului public, dac&#259; se pune în pericol siguran&#355;a circula&#355;iei.</p>', 'da', 'da', 'da', ''),
(133, 'Ce obliga&#355;ii ave&#355;i când sesiza&#355;i c&#259; urmeaz&#259; s&#259; fi&#355;i dep&#259;&#351;it de un alt autovehicul?', '', '<p id=\"v_a\"><b>a. </b> s&#259; reduce&#355;i viteza de mers;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; nu m&#259;ri&#355;i viteza de mers;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; circula&#355;i cât mai aproape de marginea din dreapta a carosabilului sau a benzii pe care v&#259; deplasa&#355;i.</p>', 'nu', 'da', 'da', ''),
(134, 'Care dintre urm&#259;toarele fapte constituie infrac&#355;iuni?', '', '<p id=\"v_a\"><b>a. </b> nerespectarea semnalelor poli&#355;i&#351;tilor la trecerea coloanelor oficiale;\r\n</p><p id=\"v_b\"><b>b. </b> conducerea sub influen&#355;a unor produse sau substan&#355;e stupefiante sau a unor medicamente cu efecte similare;\r\n</p><p id=\"v_c\"><b>c. </b> dep&#259;&#351;irea limitei legale de vitez&#259; cu peste 50 km/h.</p>', 'nu', 'da', 'nu', ''),
(135, 'Care dintre urm&#259;torii factori reprezint&#259; baza conduitei preventive?', '', '<p id=\"v_a\"><b>a. </b> cunoa&#351;terea normelor de circula&#355;ie &#351;i a regulilor de conducere a autovehiculului;\r\n</p><p id=\"v_b\"><b>b. </b> capacitatea de a fi politicos &#351;i disciplinat în traficul rutier;\r\n</p><p id=\"v_c\"><b>c. </b> încadrarea în baremele legale privind s&#259;n&#259;tatea.</p>', 'da', 'da', 'nu', ''),
(136, 'Când se folosesc luminile de avarie?', '', '<p id=\"v_a\"><b>a. </b> când un vehicul a r&#259;mas în pan&#259; sau a fost avariat, fiind imobilizat pe carosabil;\r\n</p><p id=\"v_b\"><b>b. </b> când vehiculul se deplaseaz&#259; foarte lent &#351;i constituie un pericol pentru ceilal&#355;i participan&#355;i la trafic;\r\n</p><p id=\"v_c\"><b>c. </b> când autovehiculul este remorcat.</p>', 'da', 'da', 'da', ''),
(137, 'În care dintre situa&#355;ii se dispune imobilizarea unui vehicul?', '', '<p id=\"v_a\"><b>a. </b> când starea tehnic&#259; a vehiculului pune în pericol grav siguran&#355;a circula&#355;iei;\r\n</p><p id=\"v_b\"><b>b. </b> când starea tehnic&#259; a vehiculului deterioreaz&#259; drumul public sau afecteaz&#259; mediul;\r\n</p><p id=\"v_c\"><b>c. </b> când conduc&#259;torul autovehiculului nu are asupra sa certificatul de înmatriculare.</p>', 'da', 'da', 'nu', ''),
(138, 'Care dintre faptele de mai jos se consider&#259; infrac&#355;iune &#351;i se pedepse&#351;te ca atare?', '', '<p id=\"v_a\"><b>a. </b> sustragerea de la obliga&#355;ia de a supune autovehiculul inspec&#355;iei tehnice periodice;\r\n</p><p id=\"v_b\"><b>b. </b> p&#259;r&#259;sirea locului accidentului care a avut ca urmare avarii;\r\n</p><p id=\"v_c\"><b>c. </b> repararea autovehicului ce prezint&#259; urme de accident, f&#259;r&#259; autoriza&#355;ia poli&#355;iei.</p>', 'nu', 'nu', 'da', ''),
(139, 'Oprirea este interzis&#259;:', '', '<p id=\"v_a\"><b>a. </b> pe sectoarele de drum unde dep&#259;&#351;irea este interzis&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> în zona de ac&#355;iune a indicatorului „Sta&#355;ionarea interzis&#259;“;\r\n</p><p id=\"v_c\"><b>c. </b> în dreptul c&#259;ilor de acces care deservesc propriet&#259;&#355;ile al&#259;turate drumurilor publice.</p>', 'da', 'nu', 'nu', ''),
(140, 'În care dintre situa&#355;ii este interzis&#259; oprirea?', '', '<p id=\"v_a\"><b>a. </b> la o distan&#355;&#259; mai mic&#259; de 25 m înainte &#351;i dup&#259; indicatorul de sta&#355;ie pentru mijloacele de transport public de persoane;\r\n</p><p id=\"v_b\"><b>b. </b> la o distan&#355;&#259; mai mic&#259; de 25 m de col&#355;ul intersec&#355;iei;\r\n</p><p id=\"v_c\"><b>c. </b> la o distan&#355;&#259; de 5 m în spatele ultimului vagon de tramvai oprit într-o sta&#355;ie f&#259;r&#259; refugiu pentru pietoni.</p>', 'da', 'da', 'nu', ''),
(141, 'Oprirea este interzis&#259;:', '', '<p id=\"v_a\"><b>a. </b> în zona în care este instalat indicatorul „Sta&#355;ionarea interzis&#259;“;\r\n</p><p id=\"v_b\"><b>b. </b> în zona indicatorului „Dep&#259;&#351;irea interzis&#259;“;\r\n</p><p id=\"v_c\"><b>c. </b> în dreptul c&#259;ilor de acces care deservesc propriet&#259;&#355;ile al&#259;turate drumurilor publice.</p>', 'nu', 'da', 'nu', ''),
(142, 'În care dintre urm&#259;toarele situa&#355;ii sunte&#355;i obligat s&#259; circula&#355;i cu o vitez&#259; care s&#259; nu dep&#259;&#351;easc&#259; 30 km/h în localit&#259;&#355;i?', '', '<p id=\"v_a\"><b>a. </b> în curbele deosebit de periculoase, semnalizate ca atare;\r\n</p><p id=\"v_b\"><b>b. </b> în curbele în care vizibilitatea este sub 50 m;\r\n</p><p id=\"v_c\"><b>c. </b> în zonele în care este interzis&#259; dep&#259;&#351;irea.</p>', 'da', 'nu', 'nu', ''),
(143, 'În sensul conducerii ecologice a unui autovehicul, se recomand&#259;:', '', '<p id=\"v_a\"><b>a. </b> s&#259; evita&#355;i transportul obiectelor inutile în portbagaj sau în habitaclu;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; nu ave&#355;i montat în permanen&#355;&#259; pe autovehicul portbagajul suplimentar;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; utiliza&#355;i spoilere &#351;i faruri suplimentare.</p>', 'da', 'da', 'nu', ''),
(144, 'Când se aplic&#259; suspendarea exercit&#259;rii dreptului de a conduce?', '', '<p id=\"v_a\"><b>a. </b> în cazul nerespect&#259;rii regulilor de circula&#355;ie la trecerea unei coloane oficiale;\r\n</p><p id=\"v_b\"><b>b. </b> în cazul nerespect&#259;rii semnifica&#355;iei culorii galbene a semaforului;\r\n</p><p id=\"v_c\"><b>c. </b> în cazul utiliz&#259;rii dispozitivelor antiradar.</p>', 'da', 'nu', 'da', ''),
(145, 'Comportamentul ecologic în conducerea unui autovehiculul presupune:', '', '<p id=\"v_a\"><b>a. </b> planificarea traseului pentru a economisi timp &#351;i combustibil;\r\n</p><p id=\"v_b\"><b>b. </b> evitarea zonelor cu circula&#355;ie dens&#259;, cu lucr&#259;ri pe carosabil sau cu teren accidentat;\r\n</p><p id=\"v_c\"><b>c. </b> vitez&#259; &#351;i accelera&#355;ie mai mari, pentru a scurta timpul de deplasare.</p>', 'da', 'da', 'nu', ''),
(146, 'Ce obliga&#355;ii v&#259; revin în ceea ce prive&#351;te regimul vitezei de circula&#355;ie?', '', '<p id=\"v_a\"><b>a. </b> s&#259; respecta&#355;i regimul legal de vitez&#259; aplicabil pe traseul pe care v&#259; deplasa&#355;i;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; adapta&#355;i viteza în func&#355;ie de condi&#355;iile de drum, astfel încât s&#259; pute&#355;i efectua orice manevr&#259; în condi&#355;ii de siguran&#355;&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; nu dep&#259;&#351;i&#355;i viteza legal&#259; în zonele în care este instalat&#259; aparatura radar.</p>', 'da', 'da', 'nu', ''),
(147, 'Cum este indicat sa demarati, astfel incat sa reduceti consumul de caburant?', '', '<p id=\"v_a\"><b>a. </b> folositi ultima treapta de viteza atunci cand este posibil, chir si cand circulati prin localitati;\r\n</p><p id=\"v_b\"><b>b. </b> schimbati cat mai repede vitezele pana ajungeti la treapta de viteza optima;\r\n</p><p id=\"v_c\"><b>c. </b> circulati cu treptele de viteza inferioare pentru a nu forta motorul, obtinand astfel o putere mai mare.</p>', 'da', 'da', 'nu', ''),
(148, 'Se interzice circula&#355;ia pe drumurile publice a autovehiculului dac&#259;:', '', '<p id=\"v_a\"><b>a. </b> mecanismul de direc&#355;ie prezint&#259; uzuri peste limitele admise;\r\n</p><p id=\"v_b\"><b>b. </b> autovehiculul are aplicate inscrip&#355;ii, desene, semne distinctive sau reclame de natur&#259; s&#259; împiedice ori s&#259; diminueze eficacitatea dispozitivelor de iluminare &#351;i de semnalizare luminoas&#259; sau citirea num&#259;rului de înmatriculare;\r\n</p><p id=\"v_c\"><b>c. </b> autovehiculul are montate accesorii pe parbrize ori pe geamurile laterale, care restrâng sau estompeaz&#259; vizibilitatea conduc&#259;torului în timpul mersului.</p>', 'da', 'da', 'da', ''),
(149, 'Care dintre cele dou&#259; indicatoare î&#355;i confer&#259; prioritate?', 'uploads/2857.jpg', '<p id=\"v_a\"><b>a. </b> indicatorul 1;\r\n</p><p id=\"v_b\"><b>b. </b> indicatorul 2;\r\n</p><p id=\"v_c\"><b>c. </b> nici unul din indicatoare.</p>', 'da', 'da', 'nu', ''),
(150, 'În care dintre urm&#259;toarele situa&#355;ii sunte&#355;i obligat s&#259; circula&#355;i cu o vitez&#259; care s&#259; nu dep&#259;&#351;easc&#259; 30 km/h în localit&#259;&#355;i?', '', '<p id=\"v_a\"><b>a. </b> într-o zon&#259; reziden&#355;ial&#259;, semnalizat&#259; ca atare;\r\n</p><p id=\"v_b\"><b>b. </b> la trecerea prin intersec&#355;iile cu circula&#355;ie nedirijat&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> într-o zon&#259; pietonal&#259;, semnalizat&#259; ca atare.</p>', 'nu', 'da', 'nu', ''),
(151, 'În care dintre situa&#355;iile de mai jos este interzis&#259; întoarcerea?', '', '<p id=\"v_a\"><b>a. </b> în curbele cu vizibilitatea redus&#259; sub 50 m;\r\n</p><p id=\"v_b\"><b>b. </b> la întâlnirea indicatorului „Drum cu prioritate“;\r\n</p><p id=\"v_c\"><b>c. </b> la întâlnirea indicatorului „Drum îngustat“.</p>', 'da', 'nu', 'da', ''),
(152, 'Ce obliga&#355;ii ai într-o intersec&#355;ie?', '', '<p id=\"v_a\"><b>a. </b> s&#259; acorzi prioritate de trecere atunci când pietonii angaja&#355;i regulamentar în traversare se afl&#259; pe sensul t&#259;u de mers\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; circuli cât mai aproape de bordura din partea dreapt&#259; a trotuarului\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; circuli cu o vitez&#259; care s&#259; nu dep&#259;&#351;easc&#259; 30 km/h în localit&#259;&#355;i sau 50 km/h în afara acestora, dac&#259; drumul are cel mult o band&#259; pe sens, iar pietonii afla&#355;i pe trotuar în apropierea p&#259;r&#355;ii carosabile inten&#355;ioneaz&#259; s&#259; traverseze</p>', 'da', 'nu', 'da', ''),
(153, 'Se interzice oprirea voluntar&#259;:', '', '<p id=\"v_a\"><b>a. </b> la mai pu&#355;in de 100 m de calea ferat&#259; industrial&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> pe trotuar;\r\n</p><p id=\"v_c\"><b>c. </b> în zona de ac&#355;iune a indicatorului „Prioritate fa&#355;&#259; de circula&#355;ia din sens invers\".</p>', 'nu', 'nu', 'da', ''),
(154, 'Oprirea vehiculelor este interzis&#259;:', '', '<p id=\"v_a\"><b>a. </b> pe partea carosabil&#259; a drumurilor na&#355;ionale;\r\n</p><p id=\"v_b\"><b>b. </b> în zona de ac&#355;iune a indicatorului „Oprirea interzis&#259;“, pân&#259; la prima intersec&#355;ie;\r\n</p><p id=\"v_c\"><b>c. </b> în dreptul c&#259;ilor de acces care deservesc propriet&#259;&#355;ile al&#259;turate drumurilor publice.</p>', 'nu', 'da', 'nu', ''),
(155, 'Mersul înapoi este interzis:', '', '<p id=\"v_a\"><b>a. </b> la mai pu&#355;in de 100 m de trecerile la nivel cu calea ferat&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> la ie&#351;irea din cur&#355;i, garaje sau ganguri;\r\n</p><p id=\"v_c\"><b>c. </b> pe str&#259;zile unde circula&#355;ia se desf&#259;&#351;oar&#259; într-un singur sens.</p>', 'nu', 'da', 'nu', ''),
(156, 'În care dintre situa&#355;iile de mai jos este interzis&#259; oprirea vehiculelor pe drumurile publice?', '', '<p id=\"v_a\"><b>a. </b> pe partea carosabil&#259; a drumurilor na&#355;ionale;\r\n</p><p id=\"v_b\"><b>b. </b> la o distan&#355;&#259; mai mic&#259; de 25 m înainte &#351;i dup&#259; indicatorul sta&#355;iei pentru mijloacele de transport public de persoane;\r\n</p><p id=\"v_c\"><b>c. </b> la mai pu&#355;in de 50 m de cel mai apropiat col&#355; al intersec&#355;iei.</p>', 'nu', 'da', 'nu', ''),
(157, 'Oprirea este interzis&#259;:', '', '<p id=\"v_a\"><b>a. </b> la mai pu&#355;in de 25 m de col&#355;ul unei intersec&#355;ii;\r\n</p><p id=\"v_b\"><b>b. </b> în zona de ac&#355;iune a indicatorului „Sta&#355;ionarea interzis&#259;“;\r\n</p><p id=\"v_c\"><b>c. </b> în curbe &#351;i pe sectoarele de drum cu vizibilitate redus&#259; sub 50 m.</p>', 'da', 'nu', 'da', ''),
(158, 'Întoarcerea este interzis&#259;:', '', '<p id=\"v_a\"><b>a. </b> pe drumurile cu circula&#355;ie intens&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> când exist&#259; riscul ca marginea drumului s&#259; cedeze, respectiv soliditatea drumului nu permite;\r\n</p><p id=\"v_c\"><b>c. </b> pe poduri.</p>', 'nu', 'da', 'da', ''),
(159, 'Conduita ecologic&#259; în conducerea unui autovehicul înseamn&#259;:', '', '<p id=\"v_a\"><b>a. </b> s&#259; nu polua&#355;i fonic mediul înconjur&#259;tor, folosind sistemul de sonorizare la putere maxim&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; între&#355;ine&#355;i în permanen&#355;&#259; autovehiculul curat, prin sp&#259;lare &#351;i degresare zilnic&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; fi&#355;i calm &#351;i s&#259; anticipa&#355;i situa&#355;iile astfel încât s&#259; evita&#355;i accelerarea &#351;i frânarea brusc&#259;.</p>', 'nu', 'nu', 'da', ''),
(160, 'Nedeclararea, la cererea poli&#355;iei, a identit&#259;&#355;ii persoanelor c&#259;rora de&#355;in&#259;torii de vehicule le-au încredin&#355;at vehiculele proprii se sanc&#355;ioneaz&#259; cu:', '', '<p id=\"v_a\"><b>a. </b> amend&#259; contraven&#355;ional&#259; &#351;i puncte de penalizare;\r\n</p><p id=\"v_b\"><b>b. </b> amend&#259; contraven&#355;ional&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> re&#355;inerea permisului de conducere.</p>', 'nu', 'da', 'nu', ''),
(161, 'Care dintre cele dou&#259; indicatoare interzice oprirea?', 'uploads/3713.jpg', '<p id=\"v_a\"><b>a. </b> ambele indicatoare;\r\n</p><p id=\"v_b\"><b>b. </b> indicatorul 1;\r\n</p><p id=\"v_c\"><b>c. </b> niciun indicator.</p>', 'da', 'nu', 'nu', ''),
(162, 'Sta&#355;ionarea este interzis&#259;:', '', '<p id=\"v_a\"><b>a. </b> în zona de ac&#355;iune a indicatorului „Circula&#355;ie în ambele sensuri“;\r\n</p><p id=\"v_b\"><b>b. </b> pe sectoarele de drum unde este interzis&#259; dep&#259;&#351;irea prin indicator;\r\n</p><p id=\"v_c\"><b>c. </b> pe partea carosabil&#259; a drumurilor na&#355;ionale.</p>', 'nu', 'da', 'nu', ''),
(163, 'Pentru care dintre faptele de mai jos se aplic&#259; sanc&#355;iuni contraven&#355;ionale?', '', '<p id=\"v_a\"><b>a. </b> conducerea unui autovehicul sub influen&#355;a b&#259;uturilor alcoolice, având o îmbiba&#355;ie sub 0,8 g/l alcool pur în sânge;\r\n</p><p id=\"v_b\"><b>b. </b> punerea în circula&#355;ie sau conducerea pe drumurile publice a unui autovehicul neînmatriculat;\r\n</p><p id=\"v_c\"><b>c. </b> conducerea pe drumurile publice a unui autovehicul de c&#259;tre o persoan&#259; f&#259;r&#259; permis de conducere.</p>', 'da', 'nu', 'nu', ''),
(164, 'În care dintre urm&#259;toarele situa&#355;ii sunte&#355;i obligat s&#259; circula&#355;i cu o vitez&#259; care s&#259; nu dep&#259;&#351;easc&#259; 30 km/h în localit&#259;&#355;i?', '', '<p id=\"v_a\"><b>a. </b> la trecerile pentru pietoni semaforizate;\r\n</p><p id=\"v_b\"><b>b. </b> la circula&#355;ia pe poduri;\r\n</p><p id=\"v_c\"><b>c. </b> pe drumurile cu denivel&#259;ri semnalizate ca atare.</p>', 'nu', 'nu', 'da', ''),
(165, 'Întoarcerea autovehiculului este interzis&#259;:', '', '<p id=\"v_a\"><b>a. </b> pe drumurile cu sens unic;\r\n</p><p id=\"v_b\"><b>b. </b> în locurile în care soliditatea drumului nu permite;\r\n</p><p id=\"v_c\"><b>c. </b> pe marcajul pietonal.</p>', 'da', 'da', 'da', ''),
(166, 'Ave&#355;i obliga&#355;ia de a opri imediat autovehiculul la semnalele adresate de:', '', '<p id=\"v_a\"><b>a. </b> personalul autorizat de la trecerile la nivel cu calea ferat&#259; industrial&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> conduc&#259;torii unor grupuri organizate;\r\n</p><p id=\"v_c\"><b>c. </b> nev&#259;z&#259;torii, prin ridicarea bastonului alb, atunci când ace&#351;tia traverseaz&#259; strada.</p>', 'nu', 'nu', 'da', ''),
(167, 'Conduc&#259;torii auto afla&#355;i la o trecere la nivel cu o cale ferat&#259; industrial&#259; trebuie:', '', '<p id=\"v_a\"><b>a. </b> s&#259; sporeasc&#259; aten&#355;ia;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; respecte semnalele agentului de cale ferat&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; se asigure &#351;i s&#259; opreasc&#259;, dac&#259; observ&#259; c&#259; se apropie vreun tren.</p>', 'nu', 'da', 'nu', ''),
(168, 'Care dintre indicatoarele al&#259;turate te oblig&#259; s&#259; execu&#355;i virajul la stânga?', 'uploads/3721.jpg', '<p id=\"v_a\"><b>a. </b> indicatorul 1;\r\n</p><p id=\"v_b\"><b>b. </b> indicatorul 2;\r\n</p><p id=\"v_c\"><b>c. </b> niciunul.</p>', 'nu', 'nu', 'da', ''),
(169, 'Ce permite modul de control electronic ABS, în situa&#355;ia unui început de blocaj al uneia sau al mai multor ro&#355;i, în timpul frân&#259;rii autovehiculului?', '', '<p id=\"v_a\"><b>a. </b> o mai bun&#259; înc&#259;rcare a ro&#355;ilor;\r\n</p><p id=\"v_b\"><b>b. </b> st&#259;pânirea direc&#355;iei autovehiculului;\r\n</p><p id=\"v_c\"><b>c. </b> un derapaj controlat.</p>', 'nu', 'da', 'nu', ''),
(170, 'Ce semnific&#259; indicatorul?', 'uploads/3736.jpg', '<p id=\"v_a\"><b>a. </b> band&#259; rezervat&#259; circula&#355;iei autobuzelor;\r\n</p><p id=\"v_b\"><b>b. </b> drum interzis circula&#355;iei autobuzelor;\r\n</p><p id=\"v_c\"><b>c. </b> sta&#355;ie pentru mijloace de transport în comun.</p>', 'da', 'nu', 'nu', ''),
(171, 'Ave&#355;i obliga&#355;ia de a opri imediat autovehiculul la semnalele adresate de:', '', '<p id=\"v_a\"><b>a. </b> membrii patrulelor &#351;colare de circula&#355;ie, de la trecerile pentru pietoni din apropierea unit&#259;&#355;ilor de înv&#259;&#355;&#259;mânt;\r\n</p><p id=\"v_b\"><b>b. </b> personalul autorizat din cadrul Ministerului Transporturilor;\r\n</p><p id=\"v_c\"><b>c. </b> lucr&#259;torii ce î&#351;i desf&#259;&#351;oar&#259; activitatea în cadrul Administra&#355;iei Drumurilor.</p>', 'da', 'nu', 'nu', ''),
(172, 'Sunte&#355;i obligat s&#259; circula&#355;i cu o vitez&#259; care s&#259; nu dep&#259;&#351;easc&#259; 50 km/h în afara localit&#259;&#355;ilor, în urm&#259;toarele situa&#355;ii:', '', '<p id=\"v_a\"><b>a. </b> la trecerea pe lâng&#259; animale care se deplaseaz&#259; pe partea carosabil&#259; sau pe acostament;\r\n</p><p id=\"v_b\"><b>b. </b> la circula&#355;ia prin tunele;\r\n</p><p id=\"v_c\"><b>c. </b> în zona pietonal&#259;, semnalizat&#259; ca atare.</p>', 'da', 'nu', 'nu', ''),
(173, 'Panoul aditional semnific&#259;:', 'uploads/2855.jpg', '<p id=\"v_a\"><b>a. </b> num&#259;rul de kilometri pân&#259; la ie&#351;irea de pe autostrad&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> num&#259;rul nodului rutier pe autostrad&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> indicativul drumului european.</p>', 'nu', 'da', 'nu', ''),
(174, 'În care dintre situa&#355;iile de mai jos se anuleaz&#259; permisul de conducere?', '', '<p id=\"v_a\"><b>a. </b> pentru conducerea unui autovehicul sub influen&#355;a b&#259;uturilor alcoolice;\r\n</p><p id=\"v_b\"><b>b. </b> pentru infrac&#355;iunile care s-au soldat cu moartea sau v&#259;t&#259;marea grav&#259; a unei persoane, s&#259;vâr&#351;ite ca urmare a nerespect&#259;rii regulilor de circula&#355;ie;\r\n</p><p id=\"v_c\"><b>c. </b> pentru nerespectarea semnalelor poli&#355;i&#351;tilor la trecerea coloanelor oficiale.</p>', 'nu', 'da', 'nu', ''),
(175, 'Indicatorul informeaz&#259; c&#259;:', 'uploads/2854.jpg', '<p id=\"v_a\"><b>a. </b> restric&#355;iile de oprire &#351;i stationare au luat sfâr&#351;it;\r\n</p><p id=\"v_b\"><b>b. </b> au luat sfar&#351;it toate restric&#355;iile;\r\n</p><p id=\"v_c\"><b>c. </b> zona de stationare cu durata limitata a luat sfar&#351;it.</p>', 'nu', 'nu', 'da', ''),
(176, 'Nu este permis unui autovehicul s&#259; circule atunci când:', '', '<p id=\"v_a\"><b>a. </b> polueaz&#259; fonic, în timpul mersului sau sta&#355;ion&#259;rii, peste pragul admis de lege;\r\n</p><p id=\"v_b\"><b>b. </b> nu este dotat cu roat&#259; de rezerv&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> pl&#259;cu&#355;ele cu num&#259;rul de înmatriculare sunt deteriorate.</p>', 'da', 'nu', 'da', ''),
(177, 'Tramvaiele în mers se dep&#259;&#351;esc, de regul&#259;:', '', '<p id=\"v_a\"><b>a. </b> pe partea stâng&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> pe partea dreapt&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> pe ambele p&#259;r&#355;i;</p>', 'nu', 'da', 'nu', ''),
(178, 'Indicatorul al&#259;turat reprezint&#259;:', 'uploads/3688.jpg', '<p id=\"v_a\"><b>a. </b> baliz&#259; direc&#355;ional&#259; care indic&#259; ocolirea obstacolului prin stânga;\r\n</p><p id=\"v_b\"><b>b. </b> panou suplimentar, montat la 150 m fa&#355;&#259; de calea ferat&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> baliz&#259; direc&#355;ional&#259; care indic&#259; ocolirea obstacolului prin dreapta.</p>', 'nu', 'nu', 'da', ''),
(179, 'Ce categorii de persoane sunt exceptate de la obliga&#355;ia de a purta centura de siguran&#355;&#259;?', '', '<p id=\"v_a\"><b>a. </b> copiii sub 3 ani, dar transporta&#355;i în dispozitive de re&#355;inere omologate;\r\n</p><p id=\"v_b\"><b>b. </b> conduc&#259;torii autoturismelor, atunci când execut&#259; manevra de mers înapoi sau când sta&#355;ioneaz&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> tinerii care nu au împlinit 14 ani.</p>', 'da', 'da', 'nu', ''),
(180, 'Indicatorul al&#259;turat reprezint&#259;:', 'uploads/3689.jpg', '<p id=\"v_a\"><b>a. </b> panou suplimentar, montat la 100 m fa&#355;&#259; de calea ferat&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> baliz&#259; direc&#355;ional&#259; care indic&#259; ocolirea obstacolului prin dreapta;\r\n</p><p id=\"v_c\"><b>c. </b> baliz&#259; direc&#355;ional&#259; care indic&#259; ocolirea obstacolului prin stânga.</p>', 'nu', 'nu', 'da', ''),
(181, 'Ce poate cauza transmiterea unor vibra&#355;ii la volan?', '', '<p id=\"v_a\"><b>a. </b> presiunea în pneuri este superioar&#259; celei recomandate de constructor;\r\n</p><p id=\"v_b\"><b>b. </b> pneurile insuficient umflate sau neechilibrate;\r\n</p><p id=\"v_c\"><b>c. </b> producerea unei explozii la un pneu.</p>', 'nu', 'da', 'nu', ''),
(182, 'Indicatorul semnific&#259;:', 'uploads/3690.jpg', '<p id=\"v_a\"><b>a. </b> denivelare fa&#355;&#259; de banda al&#259;turat&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> parcarea se face cu o roat&#259; pe trotuar &#351;i cu una pe partea carosabil&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> parcarea este interzis&#259; atât pe partea carosabil&#259;, cât &#351;i pe trotuar.</p>', 'da', 'nu', 'nu', ''),
(183, 'Indicatorul al&#259;turat semnific&#259;:', 'uploads/3724.jpg', '<p id=\"v_a\"><b>a. </b> drum pentru pietoni &#351;i pist&#259; pentru bicicli&#351;ti;\r\n</p><p id=\"v_b\"><b>b. </b> pist&#259; obligatorie pentru pietoni &#351;i bicicli&#351;ti;\r\n</p><p id=\"v_c\"><b>c. </b> pist&#259; obligatorie pentru pietoni &#351;i bicicli&#351;ti, având c&#259;i separate.</p>', 'nu', 'nu', 'da', ''),
(184, 'Indicatorul semnific&#259;:', 'uploads/3699.jpg', '<p id=\"v_a\"><b>a. </b> reducerea unei benzi de circula&#355;ie;\r\n</p><p id=\"v_b\"><b>b. </b> organizarea traficului pe benzi de circula&#355;ie;\r\n</p><p id=\"v_c\"><b>c. </b> circula&#355;ia autovehiculelor de mare tonaj interzis&#259;.</p>', 'nu', 'da', 'nu', ''),
(185, 'Cui trebuie s&#259; acorzi prioritate în situa&#355;ia dat&#259;?', 'uploads/2876.jpg', '<p id=\"v_a\"><b>a. </b> vehiculelor care circul&#259; din partea stâng&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> vehiculelor care circul&#259; din partea dreapt&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> tuturor vehiculelor.</p>', 'nu', 'nu', 'da', ''),
(186, 'Ce trebuie s&#259; ave&#355;i în vedere la scoaterea persoanelor accidentate din autovehicul?', '', '<p id=\"v_a\"><b>a. </b> ca opera&#355;iunea s&#259; se desf&#259;&#351;oare rapid &#351;i f&#259;r&#259; s&#259; provoace alte traumatisme;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; nu se agraveze leziunile;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; nu se îndep&#259;rteze indiciile care ar putea ajuta la explicarea modului în care s-a produs accidentul.</p>', 'nu', 'da', 'nu', ''),
(187, 'Care dintre panourile adi&#355;ionale al&#259;turate indic&#259; distan&#355;a între indicator &#351;i începutul unui loc periculos?', 'uploads/3714.jpg', '<p id=\"v_a\"><b>a. </b> panoul 1;\r\n</p><p id=\"v_b\"><b>b. </b> panoul 2;\r\n</p><p id=\"v_c\"><b>c. </b> ambele panouri.</p>', 'nu', 'da', 'nu', ''),
(188, 'Care dintre cei doi conducatori nu circula corect?', 'uploads/2485.jpg', '<p id=\"v_a\"><b>a. </b> conducatorul autoturismului, deoarece nu respecta semnificatia indicatorului;\r\n</p><p id=\"v_b\"><b>b. </b> motociclistul, deoarece circula aproape de axul drumului;\r\n</p><p id=\"v_c\"><b>c. </b> ambii circula corect.</p>', 'nu', 'da', 'nu', ''),
(189, 'Nu po&#355;i circula cu autovehiculul pe drumurile publice atunci când:', '', '<p id=\"v_a\"><b>a. </b> lipse&#351;te simbolul „RO”;\r\n</p><p id=\"v_b\"><b>b. </b> autovehiculul prezint&#259; scurgeri de carburan&#355;i sau de lubrifian&#355;i;\r\n</p><p id=\"v_c\"><b>c. </b> nu ai practic&#259; de conducere de cel pu&#355;in un an.</p>', 'nu', 'da', 'nu', ''),
(190, 'Ce indic&#259; efortul mai mare depus la ac&#355;ionarea pedalei de frân&#259;?', '', '<p id=\"v_a\"><b>a. </b> griparea cilindrilor receptori &#351;i a pistona&#351;elor;\r\n</p><p id=\"v_b\"><b>b. </b> ruperea cablului frânei de sta&#355;ionare;\r\n</p><p id=\"v_c\"><b>c. </b> lipsa lichidului din instala&#355;ia de frânare.</p>', 'da', 'nu', 'nu', ''),
(191, 'Care dintre cele dou&#259; indicatoare te oblig&#259; s&#259; opre&#351;ti?', 'uploads/3691.jpg', '<p id=\"v_a\"><b>a. </b> indicatorul 1;\r\n</p><p id=\"v_b\"><b>b. </b> indicatorul 2;\r\n</p><p id=\"v_c\"><b>c. </b> ambele indicatoare.</p>', 'nu', 'nu', 'da', ''),
(192, 'Indicatorul al&#259;turat reprezint&#259;:', 'uploads/3687.jpg', '<p id=\"v_a\"><b>a. </b> panou suplimentar, montat la 150 m fa&#355;&#259; de calea ferat&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> baliz&#259; bidirec&#355;ional&#259; care indic&#259; ocolirea obstacolului prin stânga sau prin dreapta;\r\n</p><p id=\"v_c\"><b>c. </b> panouri succesive pentru curbe deosebit de periculoase.</p>', 'nu', 'da', 'nu', ''),
(193, 'Indicatorul rutier al&#259;turat semnific&#259;:', 'uploads/2493.jpg', '<p id=\"v_a\"><b>a. </b> refugiu rezervat depan&#259;rii;\r\n</p><p id=\"v_b\"><b>b. </b> refugiu pentru stationarea de urgen&#355;&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> band&#259; de parcare.</p>', 'da', 'nu', 'nu', ''),
(194, 'Indicatorul semnific&#259;:', 'uploads/3725.jpg', '<p id=\"v_a\"><b>a. </b> drum obligatoriu pentru vehiculele cu dou&#259; ro&#355;i;\r\n</p><p id=\"v_b\"><b>b. </b> accesul interzis mopedelor;\r\n</p><p id=\"v_c\"><b>c. </b> accesul interzis motocicletelor.</p>', 'nu', 'nu', 'da', ''),
(195, 'În care dintre situa&#355;ii se suspend&#259; exercitarea dreptului de a conduce autovehicule?', '', '<p id=\"v_a\"><b>a. </b> în cazul dep&#259;&#351;irii în mod repetat, cu mai mult de 20 km/h, a vitezei maxime legale;\r\n</p><p id=\"v_b\"><b>b. </b> în cazul neopririi la semnalul agentului de cale ferat&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> în cazul neprezent&#259;rii în termen de 24 de ore la unitatea de poli&#355;ie competent&#259; pe raza c&#259;reia s-a produs un accident cu avarierea vehiculului.</p>', 'nu', 'nu', 'da', ''),
(196, 'Ce rol are ambreajul?', '', '<p id=\"v_a\"><b>a. </b> comand&#259; m&#259;rirea tura&#355;iei motorului, în func&#355;ie de sarcina transportat&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> asigur&#259; viteze diferite ale ro&#355;ilor motoare la parcurgerea curbelor;\r\n</p><p id=\"v_c\"><b>c. </b> realizeaz&#259; cuplarea progresiv&#259; &#351;i decuplarea motorului de restul transmisiei.</p>', 'nu', 'nu', 'da', ''),
(197, 'Dup&#259; o pan&#259; de cauciuc, ai fost nevoit s&#259; montezi roata de rezerv&#259;. Ce trebuie s&#259; ai în vedere la continuarea drumului?', '', '<p id=\"v_a\"><b>a. </b> s&#259; nu conduci cu o vitez&#259; mai mare de 80 km/h;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; nu conduci cu roata de rezerv&#259; mai mult decât este necesar;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; nu iei persoane de ocazie în autovehicul.</p>', 'nu', 'da', 'nu', ''),
(198, 'Ce determin&#259; înc&#259;lzirea tamburilor ro&#355;ii în timpul mersului?', '', '<p id=\"v_a\"><b>a. </b> griparea cilindrilor receptori &#351;i a pistoanelor;\r\n</p><p id=\"v_b\"><b>b. </b> lipsa lichidului din instala&#355;ia de frânare;\r\n</p><p id=\"v_c\"><b>c. </b> ruperea cablului frânei de sta&#355;ionare.</p>', 'da', 'nu', 'nu', ''),
(199, 'Oprirea este interzis&#259;:', '', '<p id=\"v_a\"><b>a. </b> la mai pu&#355;in de 50 m de col&#355;ul intersec&#355;iei;\r\n</p><p id=\"v_b\"><b>b. </b> în locul în care se împiedic&#259; vizibilitatea unui semnal luminos;\r\n</p><p id=\"v_c\"><b>c. </b> în curbe cu vizibilitatea sub 100 m.</p>', 'nu', 'da', 'nu', ''),
(200, 'Blocarea unei roti in timpul mersului poate fi determinata de una dintre defectiunile:', '', '<p id=\"v_a\"><b>a. </b> ovalizarea tamburului;\r\n</p><p id=\"v_b\"><b>b. </b> griparea rulmentilor;\r\n</p><p id=\"v_c\"><b>c. </b> uzura exagerata a sabotilor.</p>', 'nu', 'da', 'nu', ''),
(201, 'Care este rolul catalizatorului?', '', '<p id=\"v_a\"><b>a. </b> de a asigura depoluarea atmosferic&#259;, prin arderea complet&#259; a gazelor de evacuare\r\n</p><p id=\"v_b\"><b>b. </b> de a facilita arderea amestecului carburant in cilindrii motorului\r\n</p><p id=\"v_c\"><b>c. </b> de a filtra carburantul înainte de pulverizarea acestuia în cilindri</p>', 'da', 'nu', 'nu', ''),
(202, 'Conduci un autovehicul ac&#355;ionat cu motor cu aprindere prin scânteie, dotat cu catalizator (dispozitiv antipoluant). Ce fel de benzine trebuie s&#259; folose&#351;ti?', '', '<p id=\"v_a\"><b>a. </b> numai benzine fara plumb\r\n</p><p id=\"v_b\"><b>b. </b> numai benzine aditive cu tetraetil de plumb\r\n</p><p id=\"v_c\"><b>c. </b> orice fel de benzine cu cifra octanic&#259; peste 90</p>', 'da', 'nu', 'nu', ''),
(203, 'Ce semnific&#259; panourile adi&#355;ionale?', 'uploads/3731.jpg', '<p id=\"v_a\"><b>a. </b> ramp&#259; pentru persoanele cu handicap locomotor;\r\n</p><p id=\"v_b\"><b>b. </b> spa&#355;iu de servicii pentru persoanele cu handicap;\r\n</p><p id=\"v_c\"><b>c. </b> parcare pentru persoanele cu handicap.</p>', 'da', 'nu', 'nu', ''),
(204, 'La dep&#259;&#351;irea c&#259;ror vehicule trebuie s&#259; p&#259;strezi o distan&#355;&#259; lateral&#259; mai mare?', '', '<p id=\"v_a\"><b>a. </b> la dep&#259;&#351;irea bicicletelor\r\n</p><p id=\"v_b\"><b>b. </b> la dep&#259;&#351;irea autobuzelor\r\n</p><p id=\"v_c\"><b>c. </b> la dep&#259;&#351;irea camioanelor</p>', 'da', 'nu', 'nu', ''),
(205, 'C&#259;ror categorii de autovehicule le este interzis s&#259; circule pe autostrad&#259;?', '', '<p id=\"v_a\"><b>a. </b> celor care, prin construc&#355;ie, nu pot dep&#259;&#351;i viteza de 50 km/or&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> motocicletelor cu &#351;i f&#259;r&#259; ata&#351;;\r\n</p><p id=\"v_c\"><b>c. </b> tractoarelor &#351;i ma&#351;inilor agricole autopropulsate.</p>', 'da', 'nu', 'da', ''),
(206, 'Este permis virajul la stânga în acest loc?', 'uploads/3728.jpg', '<p id=\"v_a\"><b>a. </b> da, deoarece te-ai încadrat corespunz&#259;tor;\r\n</p><p id=\"v_b\"><b>b. </b> da, dup&#259; acordarea priorit&#259;&#355;ii de trecere;\r\n</p><p id=\"v_c\"><b>c. </b> nu, deoarece este interzis&#259; trecerea peste marcajul dintre cele dou&#259; scuaruri.</p>', 'nu', 'nu', 'da', ''),
(207, 'Cum trebuie s&#259; fie nivelul corect al uleiului din baie?:', '', '<p id=\"v_a\"><b>a. </b> cu aproximativ 1 cm sub reperul MIN;\r\n</p><p id=\"v_b\"><b>b. </b> între reperele MIN-MAX, însemnate pe joja de ulei;\r\n</p><p id=\"v_c\"><b>c. </b> cu aproximativ 1 cm deasupra reperului MAX.</p>', 'nu', 'da', 'nu', ''),
(208, 'Cum vei proceda la întâlnirea indicatorului „Limitare de vitez&#259; 80 km/h”?', '', '<p id=\"v_a\"><b>a. </b> circuli cu o vitez&#259; de peste 80 km/h;\r\n</p><p id=\"v_b\"><b>b. </b> nu dep&#259;&#351;e&#351;ti viteza de 80 km/h;\r\n</p><p id=\"v_c\"><b>c. </b> nu dep&#259;&#351;e&#351;ti viteza maxim&#259; admis&#259; pe drumurile na&#355;ionale.</p>', 'nu', 'da', 'nu', ''),
(209, 'Cum se realizeaz&#259; controlul nivelului de ulei din baie?', '', '<p id=\"v_a\"><b>a. </b> cu ajutorul unei tije metalice numite joj&#259;, introdus&#259; în blocul motor;\r\n</p><p id=\"v_b\"><b>b. </b> cu ajutorul turometrului de la bord;\r\n</p><p id=\"v_c\"><b>c. </b> cu ajutorul manometrului de la bord.</p>', 'da', 'nu', 'nu', ''),
(210, 'Conduc&#259;torilor de autovehicule le este interzis:', '', '<p id=\"v_a\"><b>a. </b> s&#259; transporte în &#351;i pe autoturism obiecte a c&#259;ror lungime sau l&#259;&#355;ime dep&#259;&#351;e&#351;te, împreun&#259; cu înc&#259;rc&#259;tura, dimensiunile acestuia;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; aib&#259; aplicate folii sau tratamente chimice pe parbrize, lunet&#259; ori geamuri laterale, care reduc vizibilitatea;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; circule cu vehiculele avariate mai mult de 30 de zile.</p>', 'da', 'da', 'da', ''),
(211, 'Panourile al&#259;turate semnific&#259;:', 'uploads/3717.jpg', '<p id=\"v_a\"><b>a. </b> panouri suplimentare pentru intersec&#355;ia cu calea ferat&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> panouri suplimentare la nodurile rutiere de pe autostr&#259;zi;\r\n</p><p id=\"v_c\"><b>c. </b> panouri succesive pentru curbe deosebit de periculoase.</p>', 'nu', 'da', 'nu', ''),
(212, 'Cum trebuie s&#259; procedezi atunci când cobori cu autoturismul o pant&#259; lung&#259; &#351;i accentuat&#259;, având ata&#351;at&#259; o remorc&#259; f&#259;r&#259; frân&#259; proprie?', '', '<p id=\"v_a\"><b>a. </b> reduci viteza, introduci într-o treapt&#259; de vitez&#259; inferioar&#259; &#351;i te preg&#259;te&#351;ti pentru frânare;\r\n</p><p id=\"v_b\"><b>b. </b> opre&#351;ti motorul în treapta a ll-a de vitez&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> opre&#351;ti de mai multe ori pân&#259; cobori panta.</p>', 'da', 'nu', 'nu', ''),
(213, 'Ce semnific&#259; indicatorul din imagine?', 'uploads/3712.jpg', '<p id=\"v_a\"><b>a. </b> acces pe baz&#259; de tichet;\r\n</p><p id=\"v_b\"><b>b. </b> drum rezervat autovehiculelor cu ecuson;\r\n</p><p id=\"v_c\"><b>c. </b> punct de alimentare cu combustibil.</p>', 'da', 'nu', 'nu', ''),
(214, 'Cum trebuie s&#259; procedeze conduc&#259;torul auto la semnalul poli&#355;istului din imagine?', 'uploads/3701.jpg', '<p id=\"v_a\"><b>a. </b> s&#259; vireze spre dreapta;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; opreasc&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> semnalul se adreseaz&#259; numai conduc&#259;torilor care circul&#259; din sens opus.</p>', 'nu', 'da', 'nu', ''),
(215, 'La întâlnirea unui vehicul care circul&#259; noaptea, din sens opus, pe un drum neiluminat, conduc&#259;torul de autovehicul este obligat:', '', '<p id=\"v_a\"><b>a. </b> s&#259; circule cât mai aproape de marginea din dreapta a drumului;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; reduc&#259; viteza;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; foloseasc&#259; avertizorul sonor.</p>', 'nu', 'da', 'nu', ''),
(216, 'Când nu v&#259; este permis&#259; întoarcerea vehiculului?', '', '<p id=\"v_a\"><b>a. </b> când l&#259;&#355;imea drumului public este mai mic&#259; de 7 m;\r\n</p><p id=\"v_b\"><b>b. </b> la urcarea pantelor;\r\n</p><p id=\"v_c\"><b>c. </b> pe marcajul pietonal.</p>', 'nu', 'nu', 'da', ''),
(217, 'Întoarcerea este interzis&#259;:', '', '<p id=\"v_a\"><b>a. </b> în ramp&#259;, dac&#259; vizibilitatea este sub 50 m;\r\n</p><p id=\"v_b\"><b>b. </b> în dreptul unui garaj;\r\n</p><p id=\"v_c\"><b>c. </b> la trecerile pentru pietoni.</p>', 'da', 'nu', 'da', ''),
(218, 'Punctele de penalizare se aplic&#259; pentru:', '', '<p id=\"v_a\"><b>a. </b> nerespectarea semnifica&#355;iei indicatorului „Copii“;\r\n</p><p id=\"v_b\"><b>b. </b> nerespectarea semnifica&#355;iei indicatorului „Drum cu denivel&#259;ri“;\r\n</p><p id=\"v_c\"><b>c. </b> nerespectarea semnifica&#355;iei indicatorului „Oprire“, instalat la trecerea la nivel cu o cale ferat&#259;.</p>', 'nu', 'nu', 'da', ''),
(219, 'Care dintre faptele de mai jos atrag suspendarea exercit&#259;rii dreptului de conduce pe o perioad&#259; de 90 de zile?', '', '<p id=\"v_a\"><b>a. </b> conducerea sub influen&#355;a b&#259;uturilor alcoolice;\r\n</p><p id=\"v_b\"><b>b. </b> conducerea sub influen&#355;a produselor stupefiante;\r\n</p><p id=\"v_c\"><b>c. </b> conducerea unui vehicul neînmatriculat.</p>', 'da', 'nu', 'nu', ''),
(220, 'Ce manevre sunt interzise pe autostrad&#259;?', '', '<p id=\"v_a\"><b>a. </b> oprirea &#351;i efectuarea unei pene de cauciuc;\r\n</p><p id=\"v_b\"><b>b. </b> înv&#259;&#355;area conducerii unui vehicul;\r\n</p><p id=\"v_c\"><b>c. </b> încercarea prototipurilor de &#351;asiuri &#351;i de vehicule cu motor.</p>', 'nu', 'da', 'da', ''),
(221, 'În ce situa&#355;ie, pe timp de zi, conduc&#259;torii auto sunt obliga&#355;i s&#259; foloseasc&#259; luminile de întâlnire?', '', '<p id=\"v_a\"><b>a. </b> când tracteaz&#259; alte vehicule;\r\n</p><p id=\"v_b\"><b>b. </b> când circul&#259; pe drumurile jude&#355;ene;\r\n</p><p id=\"v_c\"><b>c. </b> când circul&#259; cu vitez&#259; redus&#259;.</p>', 'da', 'nu', 'nu', ''),
(222, 'Luminile de pozi&#355;ie se folosesc, pe timp de zi, atunci când autovehiculul este oprit:', '', '<p id=\"v_a\"><b>a. </b> pentru urcarea sau coborârea pasagerilor;\r\n</p><p id=\"v_b\"><b>b. </b> din cauza unor avarii;\r\n</p><p id=\"v_c\"><b>c. </b> pentru c&#259; vizibilitatea este redus&#259;.</p>', 'nu', 'nu', 'da', ''),
(223, 'Indicatorul interzice accesul:', 'uploads/863.jpg', '<p id=\"v_a\"><b>a. </b> autoturismelor, motocicletelor f&#259;r&#259; ata&#351; &#351;i vehiculelor cu trac&#355;iune animal&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> autovehiculelor &#351;i vehiculelor cu trac&#355;iune animal&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> autoturismelor, motocicletelor &#351;i vehiculelor împinse sau trase cu mâna.</p>', 'nu', 'da', 'nu', ''),
(224, 'Care dintre cele trei autovehicule va trece ultimul prin intersec&#355;ia din imagine?', 'uploads/1011.jpg', '<p id=\"v_a\"><b>a. </b> motocicleta;\r\n</p><p id=\"v_b\"><b>b. </b> autoturismul ro&#351;u;\r\n</p><p id=\"v_c\"><b>c. </b> autoturismul verde.</p>', 'nu', 'da', 'nu', ''),
(225, 'La intersec&#355;ia nedirijat&#259; dintre un drum comunal &#351;i unul local, prioritatea de trecere se acord&#259;:', '', '<p id=\"v_a\"><b>a. </b> vehiculelor care circul&#259; pe drumul comunal;\r\n</p><p id=\"v_b\"><b>b. </b> vehiculelor care circul&#259; pe drumul local;\r\n</p><p id=\"v_c\"><b>c. </b> vehiculelor care vin din dreapta.</p>', 'da', 'nu', 'nu', ''),
(226, 'Cum se sanc&#355;ioneaz&#259; dep&#259;&#351;irea pe partea dreapt&#259; a sensului de mers, f&#259;r&#259; semnalizare &#351;i încadrare corespunz&#259;toare direc&#355;iei de deplasare?', '', '<p id=\"v_a\"><b>a. </b> cu amend&#259; contraven&#355;ional&#259; &#351;i puncte de penalizare;\r\n</p><p id=\"v_b\"><b>b. </b> cu amend&#259; contraven&#355;ional&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> cu amend&#259; contraven&#355;ional&#259; &#351;i suspendarea exercit&#259;rii dreptului de a conduce autovehiculul.</p>', 'nu', 'nu', 'da', ''),
(227, 'În care dintre urm&#259;toarele situa&#355;ii sunte&#355;i obligat s&#259; circula&#355;i cu vitez&#259; redus&#259;?', '', '<p id=\"v_a\"><b>a. </b> când sunte&#355;i obosit &#351;i autovehiculul are sistemul de frânare defect;\r\n</p><p id=\"v_b\"><b>b. </b> la trecerea la nivel cu calea ferat&#259; curent&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> pe drumurile jude&#355;ene &#351;i comunale.</p>', 'nu', 'da', 'nu', ''),
(228, 'Cine nu este obligat s&#259; poarte centura de siguran&#355;&#259;?', '', '<p id=\"v_a\"><b>a. </b> pasagerii din taxiuri;\r\n</p><p id=\"v_b\"><b>b. </b> persoanele suferinde;\r\n</p><p id=\"v_c\"><b>c. </b> examinatorul, în timpul desf&#259;&#351;ur&#259;rii probelor practice ale examenului.</p>', 'nu', 'nu', 'da', ''),
(229, 'Cum trebuie s&#259; procedeze conduc&#259;torul auto atunci când circul&#259; pe un drum cu trei sau mai multe benzi pe sens?', '', '<p id=\"v_a\"><b>a. </b> s&#259; circule pe banda situat&#259; lâng&#259; acostament;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; nu circule pe liniile de tramvai;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; circule pe banda a doua, respectiv a treia, cu o vitez&#259; mai mic&#259; de 100 km/h.</p>', 'da', 'nu', 'nu', ''),
(230, 'Oprirea voluntar&#259; a vehiculelor este interzis&#259;:', '', '<p id=\"v_a\"><b>a. </b> pe drumurile cu sens unic;\r\n</p><p id=\"v_b\"><b>b. </b> în zonele reziden&#355;iale semnalizate ca atare;\r\n</p><p id=\"v_c\"><b>c. </b> la mai pu&#355;in de 25 m înainte &#351;i dup&#259; sta&#355;iile mijloacelor de transport public de persoane.</p>', 'nu', 'nu', 'da', ''),
(231, 'Se interzice întoarcerea vehiculului:', '', '<p id=\"v_a\"><b>a. </b> pe drumurile supuse restric&#355;iilor de vitez&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> pe drumurile cu o singur&#259; band&#259; pe sens;\r\n</p><p id=\"v_c\"><b>c. </b> în intersec&#355;iile în care este interzis virajul la stânga.</p>', 'nu', 'nu', 'da', ''),
(232, 'Schimbarea direc&#355;iei de mers la dreapta sau la stânga nu este permis&#259; la întâlnirea:', 'uploads/647.jpg', '<p id=\"v_a\"><b>a. </b>indicatorului 1;</p><p id=\"v_b\"><b>b. </b>indicatorului 2;</p><p id=\"v_c\"><b>c. </b>ambelor indicatoare.</p>', 'nu', 'da', 'nu', ''),
(233, 'Pute&#355;i dep&#259;&#351;i tramvaiul oprit într-o sta&#355;ie f&#259;r&#259; refugiu pentru pietoni, pe o strad&#259; cu sens unic?', '', '<p id=\"v_a\"><b>a. </b> da, dar numai pe partea stâng&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> da, dar numai dac&#259; nu exist&#259; pietoni în sta&#355;ia de tramvai;\r\n</p><p id=\"v_c\"><b>c. </b> nu, deoarece dep&#259;&#351;irea este interzis&#259;.</p>', 'nu', 'nu', 'da', ''),
(234, 'Preciza&#355;i în care situa&#355;ie vehiculele enumerate mai jos au prioritate de trecere:', '', '<p id=\"v_a\"><b>a. </b> vehiculele aflate în sensul giratoriu, fa&#355;&#259; de cele care se preg&#259;tesc s&#259; intre în sensul giratoriu;\r\n</p><p id=\"v_b\"><b>b. </b> autovehiculele din serviciile regulate de transport public, care se pun în mi&#351;care din sta&#355;ie;\r\n</p><p id=\"v_c\"><b>c. </b> vehiculele care execut&#259; manevra de întoarcere.</p>', 'da', 'nu', 'nu', ''),
(235, 'Care este limita maxim&#259; de vitez&#259; în afara localit&#259;&#355;ilor, pe drumurile expres sau pe cele na&#355;ionale europene (E)?', '', '<p id=\"v_a\"><b>a. </b> 100 km/h;\r\n</p><p id=\"v_b\"><b>b. </b> 110 km/h;\r\n</p><p id=\"v_c\"><b>c. </b> 90 km/h.</p>', 'da', 'nu', 'nu', ''),
(236, 'În care dintre situa&#355;ii este interzis&#259; sta&#355;ionarea voluntar&#259; a vehiculelor?', '', '<p id=\"v_a\"><b>a. </b> în dreptul c&#259;ilor de acces care deservesc propriet&#259;&#355;ile al&#259;turate drumurilor publice;\r\n</p><p id=\"v_b\"><b>b. </b> în zona reziden&#355;ial&#259; semnalizat&#259; corespunz&#259;tor;\r\n</p><p id=\"v_c\"><b>c. </b> pe drumurile jude&#355;ene.</p>', 'da', 'nu', 'nu', ''),
(237, 'Neacordarea priorit&#259;&#355;ii înseamn&#259;:', '', '<p id=\"v_a\"><b>a. </b> nerespectarea semnifica&#355;iei semnaliz&#259;rii rutiere de obligare sau de avertizare;\r\n</p><p id=\"v_b\"><b>b. </b> nerespectarea dispozi&#355;iilor legale privind asigurarea &#351;i semnalizarea în intersec&#355;ii;\r\n</p><p id=\"v_c\"><b>c. </b> obligarea celor care au prioritate de trecere de a-&#351;i modifica brusc direc&#355;ia sau viteza de deplasare ori s&#259; opreasc&#259;.</p>', 'nu', 'nu', 'da', ''),
(238, 'La întâlnirea c&#259;rui indicator este permis&#259; întoarcerea autovehiculului?', '', '<p id=\"v_a\"><b>a. </b> „Sens unic“;\r\n</p><p id=\"v_b\"><b>b. </b> „Înainte“;\r\n</p><p id=\"v_c\"><b>c. </b> „Sta&#355;ionarea interzis&#259;“.</p>', 'nu', 'nu', 'da', ''),
(239, 'Ce se recomand&#259; pe timp de noapte, din punct de vedere al conduitei preventive, pentru un conduc&#259;tor de autovehicul încep&#259;tor?', '', '<p id=\"v_a\"><b>a. </b> s&#259; aib&#259; bateria de acumulatori în stare bun&#259; de func&#355;ionare;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; evite, pe cât posibil, deplasarea, dac&#259; trebuie s&#259; parcurg&#259; un traseu lung, care cuprinde drumuri intens circulate;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; aib&#259; în dotare setul de triunghiuri reflectorizante, precum &#351;i becuri de rezerv&#259;, pentru a asigura func&#355;ionarea irepro&#351;abil&#259; a farurilor.</p>', 'nu', 'da', 'nu', ''),
(240, 'În ce ordine vor trece prin intersec&#355;ie cele trei autovehicule?', 'uploads/525.jpg', '<p id=\"v_a\"><b>a. </b> autobuzul, autocamionul, autoturismul;\r\n</p><p id=\"v_b\"><b>b. </b> autocamionul, autobuzul, autoturismul;\r\n</p><p id=\"v_c\"><b>c. </b> autoturismul, autobuzul, autocamionul.</p>', 'nu', 'nu', 'da', ''),
(241, 'Ce semnific&#259; semnalul poli&#355;istului aflat într-o intersec&#355;ie, cu bra&#355;ul drept întins orizontal?', '', '<p id=\"v_a\"><b>a. </b> oprire pentru vehiculele &#351;i pietonii care vin din fa&#355;a acestuia;\r\n</p><p id=\"v_b\"><b>b. </b> oprire pentru vehiculele &#351;i pietonii care vin din spatele acestuia;\r\n</p><p id=\"v_c\"><b>c. </b> oprire pentru vehiculele &#351;i pietonii care vin din fa&#355;a &#351;i din spatele acestuia.</p>', 'nu', 'da', 'nu', '');
INSERT INTO `intrebari` (`id`, `cerinta`, `imagine`, `variante`, `rasp_a`, `rasp_b`, `rasp_c`, `eticheta`) VALUES
(242, 'Este permis&#259; sta&#355;ionarea autovehiculelor pe timpul nop&#355;ii pe partea carosabil&#259; a unui drum comunal neiluminat?', '', '<p id=\"v_a\"><b>a. </b> da, dac&#259; se aprind luminile de pozi&#355;ie sau de sta&#355;ionare;\r\n</p><p id=\"v_b\"><b>b. </b> nu, întrucât în toate cazurile se interzice pe timpul nop&#355;ii sta&#355;ionarea pe partea carosabil&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> da, dac&#259; în afara luminilor de pozi&#355;ie, prezen&#355;a autovehiculului este semnalizat&#259; &#351;i cu ajutorul triunghiurilor reflectorizante.</p>', 'da', 'nu', 'nu', ''),
(243, 'Ce obliga&#355;ii v&#259; revin atunci când conduce&#355;i un autovehicul pe timpul nop&#355;ii &#351;i v&#259; apropia&#355;i de un alt autovehicul care circul&#259; în fa&#355;a dvs.?', '', '<p id=\"v_a\"><b>a. </b> s&#259; reduce&#355;i viteza &#351;i s&#259; circula&#355;i cu aten&#355;ie sporit&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; nu v&#259; angaja&#355;i în dep&#259;&#351;irea acestuia;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; folosi&#355;i luminile de întâlnire de la o distan&#355;&#259; de cel pu&#355;in 100 m.</p>', 'nu', 'nu', 'da', ''),
(244, 'Se interzice întoarcerea vehiculului:', '', '<p id=\"v_a\"><b>a. </b> pe marcajul pietonal;\r\n</p><p id=\"v_b\"><b>b. </b> la o distan&#355;&#259; mai mic&#259; de 50 m de marcajul pietonal;\r\n</p><p id=\"v_c\"><b>c. </b> în intersec&#355;ii, inclusiv în cele cu circula&#355;ie în sens giratoriu.</p>', 'da', 'nu', 'nu', ''),
(245, 'Cum proceda&#355;i dac&#259; v&#259; afla&#355;i la volanul autoturismului?', 'uploads/959.jpg', '<p id=\"v_a\"><b>a. </b> acorda&#355;i prioritate tramvaiului;\r\n</p><p id=\"v_b\"><b>b. </b> acorda&#355;i prioritate autobuzului;\r\n</p><p id=\"v_c\"><b>c. </b> acorda&#355;i prioritate ambelor vehicule, fiind din serviciul de transport public de persoane.</p>', 'nu', 'da', 'nu', ''),
(246, 'Cum se recomand&#259; s&#259; proceda&#355;i pentru a conduce ecologic un autovehicul?', '', '<p id=\"v_a\"><b>a. </b> s&#259; utiliza&#355;i frâna de motor pentru a încetini;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; scoate&#355;i autovehiculul din vitez&#259; în timpul frân&#259;rii;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; folosi&#355;i frâna de mân&#259; concomitent cu frâna de serviciu, pentru a ob&#355;ine o frânare rapid&#259; &#351;i eficient&#259;.</p>', 'da', 'nu', 'nu', ''),
(247, 'Pute&#355;i opri sau sta&#355;iona regulamentar, cu toate luminile stinse, pe un drum iluminat, dac&#259; vehiculul pe care îl conduce&#355;i este vizibil de la o distan&#355;&#259; de cel pu&#355;in:', '', '<p id=\"v_a\"><b>a. </b> 50 m;\r\n</p><p id=\"v_b\"><b>b. </b> 100 m;\r\n</p><p id=\"v_c\"><b>c. </b> 150 m.</p>', 'da', 'nu', 'nu', ''),
(248, 'În care dintre urm&#259;toarele situa&#355;ii conduc&#259;torul unui autovehicul este obligat s&#259; foloseasc&#259; luminile de întâlnire sau de drum?', '', '<p id=\"v_a\"><b>a. </b> în cazul arterelor de circula&#355;ie slab iluminate, când vizibilitatea este redus&#259;, în mers, atât în localit&#259;&#355;i, cât &#351;i în afara acestora;\r\n</p><p id=\"v_b\"><b>b. </b> în cazul sta&#355;ion&#259;rii pe timp de noapte pe partea carosabil&#259; a drumurilor na&#355;ionale sau în cazul parc&#259;rii autovehiculului în locuri special amenajate, dar neiluminate;\r\n</p><p id=\"v_c\"><b>c. </b> pe timp de zi pe drumurile na&#355;ionale, în situa&#355;iile în care traficul este supraaglomerat.</p>', 'da', 'nu', 'nu', ''),
(249, 'Balansarea, în plan vertical, a bra&#355;ului drept al unui biciclist semnific&#259;:', '', '<p id=\"v_a\"><b>a. </b> inten&#355;ia de a schimba direc&#355;ia de mers la dreapta;\r\n</p><p id=\"v_b\"><b>b. </b> inten&#355;ia de a opri;\r\n</p><p id=\"v_c\"><b>c. </b> inten&#355;ia de a se angaja în dep&#259;&#351;ire.</p>', 'nu', 'da', 'nu', ''),
(250, 'Se aplic&#259; puncte de penalizare pentru:', '', '<p id=\"v_a\"><b>a. </b> circula&#355;ia sau sta&#355;ionarea pe spa&#355;iul interzis care separ&#259; sensurile de circula&#355;ie pe autostrad&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> conducerea unui autovehicul cu permisul de conducere deteriorat;\r\n</p><p id=\"v_c\"><b>c. </b> lipsa unui &#351;terg&#259;tor de parbriz.</p>', 'da', 'nu', 'nu', ''),
(251, 'Utilizarea pe autostrad&#259; a limitatorului de vitez&#259; pentru autovehiculele dotate cu acest sistem are ca efect:', '', '<p id=\"v_a\"><b>a. </b> cre&#351;terea consumului de combustibil;\r\n</p><p id=\"v_b\"><b>b. </b> reducerea consumului de combustibil;\r\n</p><p id=\"v_c\"><b>c. </b> acest sistem nu are efecte asupra consumului de combustibil.</p>', 'nu', 'da', 'nu', ''),
(252, 'Ce trebuie s&#259; folosi&#355;i pentru imobilizarea fracturilor de la membrele superioare?', '', '<p id=\"v_a\"><b>a. </b> atele improvizate;\r\n</p><p id=\"v_b\"><b>b. </b> o e&#351;arf&#259; simpl&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> în acest caz nu se intervine.</p>', 'nu', 'da', 'nu', ''),
(253, 'Pe autostrad&#259; se interzice:', '', '<p id=\"v_a\"><b>a. </b> circula&#355;ia cu o vitez&#259; sub 80 km/h;\r\n</p><p id=\"v_b\"><b>b. </b> manevra de mers înapoi;\r\n</p><p id=\"v_c\"><b>c. </b> folosirea benzii de lâng&#259; zona median&#259; ce desparte cele dou&#259; p&#259;r&#355;i carosabile.</p>', 'nu', 'da', 'nu', ''),
(254, 'În ce situa&#355;ie este sanc&#355;ionat conduc&#259;torul unui vehicul cu amend&#259; contraven&#355;ional&#259; &#351;i suspendarea dreptului de a conduce autovehicule?', '', '<p id=\"v_a\"><b>a. </b> la dep&#259;&#351;irea cu 20 km/h a vitezei maxime admise;\r\n</p><p id=\"v_b\"><b>b. </b> la dep&#259;&#351;irea cu mai mult de 30 km/h a vitezei maxime admise;\r\n</p><p id=\"v_c\"><b>c. </b> la dep&#259;&#351;irea cu mai mult de 50 km/h a vitezei maxime admise.</p>', 'nu', 'nu', 'da', ''),
(255, 'Care autovehicule trebuie s&#259;-&#351;i refac&#259; inspec&#355;ia tehnic&#259; înainte de termen?', '', '<p id=\"v_a\"><b>a. </b> autovehiculele mai vechi de 10 ani;\r\n</p><p id=\"v_b\"><b>b. </b> autovehiculele cu sistemele de frânare &#351;i de direc&#355;ie grav avariate;\r\n</p><p id=\"v_c\"><b>c. </b> autovehiculele cu inscrip&#355;ia „&#350;COAL&#258;“.</p>', 'nu', 'da', 'nu', ''),
(256, 'Care este traseul de urmat pentru autoturismul din imagine?', 'uploads/524.jpg', '<p id=\"v_a\"><b>a. </b> traseul 1;\r\n</p><p id=\"v_b\"><b>b. </b> traseul 2;\r\n</p><p id=\"v_c\"><b>c. </b> oricare dintre cele dou&#259;, dac&#259; respect&#259; toate prevederile legii.</p>', 'nu', 'nu', 'da', ''),
(257, 'Ce obliga&#355;ii ave&#355;i, în calitate de conduc&#259;tor auto?', '', '<p id=\"v_a\"><b>a. </b> s&#259; circula&#355;i cu o vitez&#259; cel pu&#355;in egal&#259; celei prescrise de indicatorul „Vitez&#259; minim&#259; obligatorie“;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; nu accelera&#355;i în intersec&#355;ie;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; circula&#355;i cu o vitez&#259; inferioar&#259; celei înscrise în indicatorul „Vitez&#259; minim&#259; obligatorie“.</p>', 'da', 'nu', 'nu', ''),
(258, 'Autobuzul este sta&#355;ionat. Care dintre cei doi conduc&#259;tori procedeaz&#259; neregulamentar?', 'uploads/996.jpg', '<p id=\"v_a\"><b>a. </b> conduc&#259;torul autobuzului;\r\n</p><p id=\"v_b\"><b>b. </b> conduc&#259;torul autoturismului;\r\n</p><p id=\"v_c\"><b>c. </b> ambii conduc&#259;tori procedeaz&#259; regulamentar.</p>', 'da', 'nu', 'nu', ''),
(259, 'Care sunt vitezele maxime admise pentru subcategoria de autovehicule B1?', '', '<p id=\"v_a\"><b>a. </b> 90 km/h pe autostr&#259;zi, 80 km/h pe drumurile expres sau na&#355;ionale europene &#351;i 70 km/h pe celelalte categorii de drumuri;\r\n</p><p id=\"v_b\"><b>b. </b> 110 km/h pe autostr&#259;zi, 90 km/h pe drumurile expres sau na&#355;ionale europene &#351;i 80 km/h pe celelalte categorii de drumuri;\r\n</p><p id=\"v_c\"><b>c. </b> 130 km/h pe autostr&#259;zi &#351;i 90 km/h pe celelalte categorii de drumuri.</p>', 'da', 'nu', 'nu', ''),
(260, 'Ave&#355;i obliga&#355;ia de a opri imediat autovehiculul la semnalele adresate de:', '', '<p id=\"v_a\"><b>a. </b> poli&#355;i&#351;tii de frontier&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> poli&#355;i&#351;tii comunitari;\r\n</p><p id=\"v_c\"><b>c. </b> personalul autorizat din cadrul serviciului de examin&#259;ri.</p>', 'da', 'nu', 'nu', ''),
(261, 'Dac&#259; semaforul în func&#355;iune se afl&#259; instalat de cealalt&#259; parte a intersec&#355;iei, conduc&#259;torul de autovehicul:', '', '<p id=\"v_a\"><b>a. </b> nu este obligat s&#259; respecte semnifica&#355;ia semnalelor luminoase, deoarece nu i se adreseaz&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> este obligat s&#259; respecte semnifica&#355;ia semnalelor luminoase, doar dac&#259; semaforul este înso&#355;it de un semn adi&#355;ional care indic&#259; direc&#355;iile c&#259;rora li se adreseaz&#259; semnalele;\r\n</p><p id=\"v_c\"><b>c. </b> este obligat s&#259; respecte semnifica&#355;ia semnalelor luminoase ale acestuia, dac&#259; un alt semafor instalat la intrarea în intersec&#355;ie are în func&#355;iune un semnal similar.</p>', 'nu', 'nu', 'da', ''),
(262, 'Ce semnifica&#355;ie are marcajul longitudinal continuu de separare a sensurilor de circula&#355;ie?', '', '<p id=\"v_a\"><b>a. </b> orienteaz&#259; conduc&#259;torii de vehicule în condi&#355;ii de vizibilitate redus&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> interzice înc&#259;lcarea lui;\r\n</p><p id=\"v_c\"><b>c. </b> interzice dep&#259;&#351;irea pe sectorul pe care este aplicat.</p>', 'nu', 'da', 'nu', ''),
(263, 'Dep&#259;&#351;irea vehiculelor pe drumurile publice este interzis&#259;:', '', '<p id=\"v_a\"><b>a. </b> pe por&#355;iunile de drum unde oprirea este interzis&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> în intersec&#355;iile cu circula&#355;ie nedirijat&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> pe drumurile publice cu o singur&#259; band&#259; pe sens.</p>', 'nu', 'da', 'nu', ''),
(264, 'Conduita preventiv&#259; asigur&#259;:', '', '<p id=\"v_a\"><b>a. </b> men&#355;inerea st&#259;rii tehnice a autovehiculului;\r\n</p><p id=\"v_b\"><b>b. </b> siguran&#355;a &#351;i fluen&#355;a deplas&#259;rii pe drumurile publice;\r\n</p><p id=\"v_c\"><b>c. </b> prelungirea duratei de exploatare a motorului.</p>', 'nu', 'da', 'nu', ''),
(265, 'Cum trebuie s&#259; conduce&#355;i, astfel încât s&#259; reduce&#355;i poluarea?', '', '<p id=\"v_a\"><b>a. </b> porni&#355;i motorul ac&#355;ionând pedala de accelera&#355;ie la un sfert din curs&#259; &#351;i a&#351;tepta&#355;i ca acesta s&#259; se înc&#259;lzeasc&#259;, pentru a reduce ulterior consumul de carburant;\r\n</p><p id=\"v_b\"><b>b. </b> porni&#355;i motorul f&#259;r&#259; s&#259; ac&#355;iona&#355;i pedala de accelera&#355;ie &#351;i demara&#355;i imediat;\r\n</p><p id=\"v_c\"><b>c. </b> ap&#259;sa&#355;i pân&#259; la cap&#259;t pedala de accelera&#355;ie, porni&#355;i motorul &#351;i demara&#355;i cu o vitez&#259; cât mai mare.</p>', 'nu', 'da', 'nu', ''),
(266, 'Ave&#355;i obliga&#355;ia de a opri imediat autovehiculul la semnalele adresate de:', '', '<p id=\"v_a\"><b>a. </b> ofi&#355;erii sau subofi&#355;erii Jandarmeriei Române;\n</p><p id=\"v_b\"><b>b. </b> poli&#355;i&#351;tii comunitari;\n</p><p id=\"v_c\"><b>c. </b> agen&#355;ii de cale ferat&#259;, la trecerile la nivel.</p>', 'nu', 'nu', 'da', ''),
(267, 'Cum trebuie s&#259; proceda&#355;i în situa&#355;ia prezentat&#259;?', 'uploads/269.jpg', '<p id=\"v_a\"><b>a. </b> reduce&#355;i viteza &#351;i opri&#355;i la limita de vizibilitate maxim&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> reduce&#355;i viteza &#351;i opri&#355;i în dreptul ultimului panou suplimentar;\r\n</p><p id=\"v_c\"><b>c. </b> reduce&#355;i viteza &#351;i v&#259; asigura&#355;i c&#259; pute&#355;i traversa f&#259;r&#259; niciun pericol.</p>', 'nu', 'nu', 'da', ''),
(268, 'Motociclistul dep&#259;&#351;e&#351;te corect autocamionul?', 'uploads/795.jpg', '<p id=\"v_a\"><b>a. </b> da, procedeaz&#259; corect;\r\n</p><p id=\"v_b\"><b>b. </b> nu, deoarece din sens opus circul&#259; alt vehicul;\r\n</p><p id=\"v_c\"><b>c. </b> nu, deoarece indicatorul din imagine interzice acest lucru.</p>', 'da', 'nu', 'nu', ''),
(269, 'Care dintre vehicule au prioritate de trecere?', 'uploads/870.jpg', '<p id=\"v_a\"><b>a. </b> autobuzul &#351;i autoturismul verde;\r\n</p><p id=\"v_b\"><b>b. </b> autoturismul verde &#351;i autocamionul;\r\n</p><p id=\"v_c\"><b>c. </b> motocicleta &#351;i autocamionul.</p>', 'da', 'nu', 'nu', ''),
(270, 'Marcajul aplicat pe marginea din dreapta a p&#259;r&#355;ii carosabile, format dintr-o linie în zig-zag, semnific&#259;:', '', '<p id=\"v_a\"><b>a. </b> interzicerea sta&#355;ion&#259;rii;\r\n</p><p id=\"v_b\"><b>b. </b> interzicerea opririi;\r\n</p><p id=\"v_c\"><b>c. </b> alte pericole.</p>', 'da', 'nu', 'nu', ''),
(271, 'Pe str&#259;zile cu sens unic este interzis:', '', '<p id=\"v_a\"><b>a. </b> s&#259; întoarce&#355;i autovehiculul;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; vira&#355;i la stânga sau la dreapta;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; manevra&#355;i înapoi autovehiculul.</p>', 'da', 'nu', 'nu', ''),
(272, 'Bra&#355;ul drept, întins orizontal, al poli&#355;istului indic&#259; „Oprire“ pentru:', '', '<p id=\"v_a\"><b>a. </b> vehiculele &#351;i pietonii care vin din fa&#355;&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> vehiculele &#351;i pietonii care vin din spate;\r\n</p><p id=\"v_c\"><b>c. </b> vehiculele &#351;i pietonii care vin din fa&#355;&#259; &#351;i din spate.</p>', 'nu', 'da', 'nu', ''),
(273, 'Oprirea este interzis&#259;:', '', '<p id=\"v_a\"><b>a. </b> la o distan&#355;&#259; mai mic&#259; de 25 m de col&#355;ul intersec&#355;iei;\r\n</p><p id=\"v_b\"><b>b. </b> pe drumul cu sens unic;\r\n</p><p id=\"v_c\"><b>c. </b> la o distan&#355;&#259; de 5 m în spatele ultimului vagon de tramvai oprit în sta&#355;ia f&#259;r&#259; refugiu pentru pietoni.</p>', 'da', 'nu', 'nu', ''),
(274, 'Care dintre indicatoarele al&#259;turate creeaz&#259; obliga&#355;ii pentru conduc&#259;torii de vehicule?', 'uploads/35.jpg', '<p id=\"v_a\"><b>a. </b> indicatorul nr. 1;\r\n</p><p id=\"v_b\"><b>b. </b> indicatorul nr. 2;\r\n</p><p id=\"v_c\"><b>c. </b> ambele indicatoare.</p>', 'da', 'nu', 'nu', ''),
(275, 'În care dintre situa&#355;iile de mai jos se suspend&#259; exercitarea dreptului de a conduce?', '', '<p id=\"v_a\"><b>a. </b> dep&#259;&#351;irea vitezei maxime admise cu peste 20 km/h;\r\n</p><p id=\"v_b\"><b>b. </b> nerespectarea regulilor privind dep&#259;&#351;irea;\r\n</p><p id=\"v_c\"><b>c. </b> nefolosirea luminilor de cea&#355;&#259; dup&#259; l&#259;sarea serii.</p>', 'nu', 'da', 'nu', ''),
(276, 'C&#259;ile respiratorii pot fi deblocate:', '', '<p id=\"v_a\"><b>a. </b> numai în unit&#259;&#355;i medicale, folosindu-se instrumente speciale;\r\n</p><p id=\"v_b\"><b>b. </b> la locul accidentului, prin interven&#355;ii specifice;\r\n</p><p id=\"v_c\"><b>c. </b> indiferent unde, dac&#259; victima suport&#259; aceast&#259; interven&#355;ie.</p>', 'nu', 'da', 'nu', ''),
(277, 'Oprirea voluntar&#259; a vehiculelor este interzis&#259;:', '', '<p id=\"v_a\"><b>a. </b> pe platforma c&#259;ii ferate sau de tramvai, dac&#259; prin aceasta este stânjenit&#259; circula&#355;ia vehiculelor pe &#351;ine;\r\n</p><p id=\"v_b\"><b>b. </b> la mai pu&#355;in de 50 m de trecerile pentru pietoni;\r\n</p><p id=\"v_c\"><b>c. </b> la o distan&#355;&#259; mai mic&#259; de 150 m de trecerea la nivel cu calea ferat&#259;.</p>', 'da', 'nu', 'nu', ''),
(278, 'Dac&#259; efortul depus pentru ac&#355;ionarea volanului este mai mare decât în mod obi&#351;nuit, trebuie s&#259; verific&#259;m, în primul rând:', '', '<p id=\"v_a\"><b>a. </b> starea de prindere a volanului pe axul s&#259;u;\r\n</p><p id=\"v_b\"><b>b. </b> presiunea în pneurile pun&#355;ii din spate;\r\n</p><p id=\"v_c\"><b>c. </b> presiunea în pneurile pun&#355;ii din fa&#355;&#259;.</p>', 'nu', 'nu', 'da', ''),
(279, 'Cum este corect s&#259; proceda&#355;i în situa&#355;ia prezentat&#259;?', 'uploads/629.jpg', '<p id=\"v_a\"><b>a. </b> sunte&#355;i obligat s&#259; a&#351;tepta&#355;i;\r\n</p><p id=\"v_b\"><b>b. </b> nu ave&#355;i nicio obliga&#355;ie;\r\n</p><p id=\"v_c\"><b>c. </b> semnaliza&#355;i autobuzul galben c&#259; poate circula.</p>', 'nu', 'da', 'nu', ''),
(280, 'În care dintre situa&#355;ii pot fi retrase pl&#259;cu&#355;ele cu num&#259;rul de înmatriculare ale unui autovehicul?', '', '<p id=\"v_a\"><b>a. </b> atunci când pl&#259;cu&#355;ele cu num&#259;rul de înmatriculare nu sunt conforme cu standardele în vigoare;\r\n</p><p id=\"v_b\"><b>b. </b> atunci când certificatul de înmatriculare este pierdut;\r\n</p><p id=\"v_c\"><b>c. </b> atunci când conduc&#259;torul autovehiculului nu poate prezenta cartea de identitate &#351;i carnetul de conducere.</p>', 'da', 'nu', 'nu', ''),
(281, 'Pute&#355;i circula cu autovehiculul într-o zon&#259; pietonal&#259; dac&#259;:', '', '<p id=\"v_a\"><b>a. </b> presta&#355;i servicii comerciale;\r\n</p><p id=\"v_b\"><b>b. </b> ave&#355;i sistemul de iluminare în stare de func&#355;ionare;\r\n</p><p id=\"v_c\"><b>c. </b> locui&#355;i în acea zon&#259;.</p>', 'nu', 'nu', 'da', ''),
(282, 'Ce cauze determin&#259; poluarea mediului ambiant de c&#259;tre autovehicule?', '', '<p id=\"v_a\"><b>a. </b> rularea cu vitez&#259; redus&#259;, f&#259;r&#259; motiv;\r\n</p><p id=\"v_b\"><b>b. </b> arderea incomplet&#259; &#351;i defectuoas&#259; a combustibilului;\r\n</p><p id=\"v_c\"><b>c. </b> defectarea filtrului de benzin&#259; sau motorin&#259;.</p>', 'nu', 'da', 'nu', ''),
(283, 'La apropierea de o cale ferat&#259; industrial&#259;:', '', '<p id=\"v_a\"><b>a. </b> conduc&#259;torii de autovehicule vor da dovad&#259; de pruden&#355;&#259; sporit&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> conduc&#259;torii de autovehicule nu au nicio obliga&#355;ie, dac&#259; nu exist&#259; un agent de cale ferat&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> se va respecta semnifica&#355;ia semaforului electric.</p>', 'da', 'nu', 'nu', ''),
(284, 'Ce traseu trebuie s&#259; urma&#355;i pentru a vira la stânga în intersec&#355;ie?', 'uploads/132.jpg', '<p id=\"v_a\"><b>a. </b> traseul 1;\r\n</p><p id=\"v_b\"><b>b. </b> traseul 2;\r\n</p><p id=\"v_c\"><b>c. </b> traseul 3.</p>', 'nu', 'nu', 'da', ''),
(285, 'Pute&#355;i executa dep&#259;&#351;irea în aceast&#259; situa&#355;ie?', 'uploads/488.jpg', '<p id=\"v_a\"><b>a. </b> da, legea nu interzice;\r\n</p><p id=\"v_b\"><b>b. </b> nu, deoarece drumul se îngusteaz&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> nu, deoarece urmeaz&#259; un drum în lucru.</p>', 'da', 'nu', 'nu', ''),
(286, 'În ce situa&#355;ie se re&#355;ine permisul de conducere?', '', '<p id=\"v_a\"><b>a. </b> atunci când nu se respect&#259; obliga&#355;ia de reducere a vitezei;\r\n</p><p id=\"v_b\"><b>b. </b> atunci când nu se respect&#259; normele referitoare la prioritatea de trecere a pietonilor;\r\n</p><p id=\"v_c\"><b>c. </b> atunci când se încalc&#259; normele legale referitoare la sta&#355;ionare.</p>', 'nu', 'da', 'nu', ''),
(287, 'În care dintre situa&#355;iile de mai jos este interzis&#259; oprirea vehiculelor pe drumurile publice?', '', '<p id=\"v_a\"><b>a. </b> în intersec&#355;iile cu sens giratoriu;\r\n</p><p id=\"v_b\"><b>b. </b> în zona de ac&#355;iune a indicatorului „Sta&#355;ionarea interzis&#259;“;\r\n</p><p id=\"v_c\"><b>c. </b> pe arterele cu un singur sens de circula&#355;ie.</p>', 'da', 'nu', 'nu', ''),
(288, 'Indicatorul semnific&#259;:', 'uploads/17.jpg', '<p id=\"v_a\"><b>a. </b> reducerea num&#259;rului benzilor de circula&#355;ie;\r\n</p><p id=\"v_b\"><b>b. </b> îngustare temporar&#259; a drumului;\r\n</p><p id=\"v_c\"><b>c. </b> terminarea benzii de circula&#355;ie din stânga p&#259;r&#355;ii carosabile.</p>', 'nu', 'da', 'nu', ''),
(289, 'Este obligat conduc&#259;torul unui vehicul s&#259; foloseasc&#259; banda suplimentar&#259;, la urcarea în ramp&#259;, dac&#259; viteza sa este mai mic&#259; decât cea înscris&#259; pe indicator?', '', '<p id=\"v_a\"><b>a. </b> nu, deoarece banda este destinat&#259; vehiculelor care se deplaseaz&#259; cu o vitez&#259; egal&#259; sau superioar&#259; celei precizate de indicator;\r\n</p><p id=\"v_b\"><b>b. </b> da;\r\n</p><p id=\"v_c\"><b>c. </b> nu, fiind obligat în toate situa&#355;iile s&#259; circule doar pe banda de lâng&#259; marginea drumului.</p>', 'nu', 'da', 'nu', ''),
(290, 'În care dintre situa&#355;iile prezentate este interzis&#259; dep&#259;&#351;irea vehiculelor pe drumurile publice?', '', '<p id=\"v_a\"><b>a. </b> la întâlnirea indicatorului „Circula&#355;ie în ambele sensuri“;\r\n</p><p id=\"v_b\"><b>b. </b> la o distan&#355;&#259; mai mic&#259; de 50 m de sta&#355;ia de tramvai, când acesta este oprit;\r\n</p><p id=\"v_c\"><b>c. </b> la trecerile pentru pietoni prev&#259;zute cu indicatoare &#351;i marcaje.</p>', 'nu', 'nu', 'da', ''),
(291, 'Scoaterea pe partea lateral&#259; a bastonului de c&#259;tre poli&#355;istul care înso&#355;e&#351;te o coloan&#259; oficial&#259; oblig&#259; conduc&#259;torul:', 'uploads/990.jpg', '<p id=\"v_a\"><b>a. </b> s&#259; reduc&#259; viteza &#351;i s&#259; opreasc&#259; în afara drumului imediat ce este posibil;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; reduc&#259; viteza &#351;i s&#259; circule cât mai aproape de marginea drumului;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; opreasc&#259; autovehiculul cât mai aproape de marginea drumului.</p>', 'nu', 'nu', 'da', ''),
(292, 'Cum este corect s&#259; proceda&#355;i dac&#259; p&#259;r&#259;si&#355;i drumul prioritar?', 'uploads/915.jpg', '<p id=\"v_a\"><b>a. </b> ave&#355;i prioritate, deoarece v&#259; afla&#355;i pe un drum prioritar;\r\n</p><p id=\"v_b\"><b>b. </b> ceda&#355;i trecerea vehiculelor care vin din fa&#355;&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> ceda&#355;i trecerea vehiculelor care vin din dreapta.</p>', 'nu', 'nu', 'da', ''),
(293, 'Fumul de culoare neagr&#259; emis de e&#351;apament indic&#259;:', '', '<p id=\"v_a\"><b>a. </b> defectarea supapelor de evacuare;\r\n</p><p id=\"v_b\"><b>b. </b> un consum exagerat de combustibil;\r\n</p><p id=\"v_c\"><b>c. </b> un consum exagerat de ulei.</p>', 'nu', 'da', 'nu', ''),
(294, 'Semaforul de avertizare constituit dintr-un corp de iluminat de culoare galben&#259; intermitent&#259; v&#259; oblig&#259;:', '', '<p id=\"v_a\"><b>a. </b> s&#259; acorda&#355;i prioritate de trecere;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; circula&#355;i cu vitez&#259; redus&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; opri&#355;i în orice situa&#355;ie.</p>', 'nu', 'da', 'nu', ''),
(295, 'La plecarea de pe loc cu autovehiculul, v&#259; revin urm&#259;toarele obliga&#355;ii:', '', '<p id=\"v_a\"><b>a. </b> s&#259; evita&#355;i ambalarea excesiv&#259; a motorului;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; evita&#355;i bruscarea comenzilor autovehiculului;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; acorda&#355;i prioritate de trecere vehiculelor care circul&#259; pe drum, indiferent de direc&#355;ia de mers.</p>', 'nu', 'nu', 'da', ''),
(296, 'Se interzice sta&#355;ionarea voluntar&#259;:', '', '<p id=\"v_a\"><b>a. </b> pe drumurile cu mai pu&#355;in de dou&#259; benzi pe sensul de mers;\r\n</p><p id=\"v_b\"><b>b. </b> în zona de ac&#355;ionare a indicatorului „Circula&#355;ie în ambele sensuri\";\r\n</p><p id=\"v_c\"><b>c. </b> în dreptul c&#259;ilor de acces care deservesc propriet&#259;&#355;i al&#259;turate drumurilor publice.</p>', 'nu', 'nu', 'da', ''),
(297, 'Conduc&#259;torilor de vehicule le este interzis:', '', '<p id=\"v_a\"><b>a. </b> s&#259; profereze injurii;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; s&#259;vâr&#351;easc&#259; acte sau gesturi obscene;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; adreseze celorlal&#355;i participan&#355;i la trafic expresii jignitoare sau vulgare.</p>', 'da', 'da', 'da', ''),
(298, 'Cum proceda&#355;i în intersec&#355;ia care urmeaz&#259;?', 'uploads/710.jpg', '<p id=\"v_a\"><b>a. </b> traversa&#355;i f&#259;r&#259; s&#259; acorda&#355;i prioritate;\r\n</p><p id=\"v_b\"><b>b. </b> traversa&#355;i dup&#259; ce acorda&#355;i prioritate de dreapta;\r\n</p><p id=\"v_c\"><b>c. </b> traversa&#355;i dup&#259; ce acorda&#355;i prioritate de stânga.</p>', 'da', 'nu', 'nu', ''),
(299, 'Care dintre autovehicule va trece al doilea prin intersec&#355;ia prezentat&#259;?', 'uploads/969.jpg', '<p id=\"v_a\"><b>a. </b> autoturismul ro&#351;u;\r\n</p><p id=\"v_b\"><b>b. </b> autoturismul verde;\r\n</p><p id=\"v_c\"><b>c. </b> autocamionul.</p>', 'nu', 'da', 'nu', ''),
(300, 'În aceast&#259; situa&#355;ie, cum pute&#355;i sta&#355;iona regulamentar?', 'uploads/399.jpg', '<p id=\"v_a\"><b>a. </b> cu toate luminile stinse;\r\n</p><p id=\"v_b\"><b>b. </b> numai cu luminile de pozi&#355;ie;\r\n</p><p id=\"v_c\"><b>c. </b> numai cu luminile de avarie.</p>', 'da', 'nu', 'nu', ''),
(301, 'Cum se penalizeaz&#259; p&#259;trunderea într-o intersec&#355;ie dirijat&#259; prin semafoare, dac&#259; prin aceasta se produce blocarea circula&#355;iei în intersec&#355;ie?', '', '<p id=\"v_a\"><b>a. </b> cu amend&#259; contraven&#355;ional&#259; &#351;i aplicarea punctelor de penalizare;\r\n</p><p id=\"v_b\"><b>b. </b> cu suspendarea exercit&#259;rii dreptului de a conduce autovehicule pe o perioad&#259; de 30 de zile;\r\n</p><p id=\"v_c\"><b>c. </b> cu amend&#259; contraven&#355;ional&#259;.</p>', 'da', 'nu', 'nu', ''),
(302, 'Bicicli&#351;tii &#351;i conducatorii de vehicule cu trac&#355;iune animal&#259; sunt obliga&#355;i s&#259; acorde întotdeauna prioritate?', '', '<p id=\"v_a\"><b>a. </b> da, deoarece viteza de deplasare este mic&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> da, dac&#259; circul&#259; pe drumuri jude&#355;ene sau comunale;\r\n</p><p id=\"v_c\"><b>c. </b> nu, dac&#259; semnalizarea rutier&#259; nu prevede acest lucru.</p>', 'nu', 'nu', 'da', ''),
(303, 'La cererea poli&#355;istului rutier, împreun&#259; cu reprezentantul autorit&#259;&#355;ilor abilitate, conduc&#259;torul auto este obligat:', '', '<p id=\"v_a\"><b>a. </b> s&#259; prezinte documentele de c&#259;l&#259;torie;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259;-l transporte la unitatea de poli&#355;ie;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; permit&#259; controlul st&#259;rii tehnice a vehiculului, precum &#351;i al bunurilor transportate.</p>', 'nu', 'nu', 'da', ''),
(304, 'Conduc&#259;torii de vehicule trebuie s&#259; respecte regulile de circula&#355;ie, generale &#351;i particulare, în urm&#259;toarea ordine:', '', '<p id=\"v_a\"><b>a. </b> semnalele, indica&#355;iile &#351;i dispozi&#355;iile poli&#355;istului rutier; semnalele speciale de avertizare, luminoase sau sonore, ale autovehiculelor cu regim de circula&#355;ie prioritar&#259;; semnalizarea temporar&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> semnalele, indica&#355;iile &#351;i dispozi&#355;iile poli&#355;istului rutier; semnalizarea temporar&#259;; semnalele speciale de avertizare, luminoase sau sonore, ale autovehiculelor cu regim de circula&#355;ie prioritar&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> semnalele speciale de avertizare, luminoase sau sonore, ale autovehiculelor cu regim de circula&#355;ie prioritar&#259;; semnalele, indica&#355;iile &#351;i dispozi&#355;iile poli&#355;istului rutier; semnalizarea temporar&#259;.</p>', 'da', 'nu', 'nu', ''),
(305, 'Principala cauz&#259; a exploziei pneului în timpul mersului este:', 'uploads/773.jpg', '<p id=\"v_a\"><b>a. </b> umflarea insuficient&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> umflarea exagerat&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> uzura moderat&#259;.</p>', 'nu', 'da', 'nu', ''),
(306, 'Cum se execut&#259; dep&#259;&#351;irea autoturismelor &#351;i a autocamioanelor pe un drum f&#259;r&#259; marcaje, pe care circula&#355;ia se desf&#259;&#351;oar&#259; în sens unic?', '', '<p id=\"v_a\"><b>a. </b> pe partea stâng&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> pe partea dreapt&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> pe ambele p&#259;r&#355;i.</p>', 'da', 'nu', 'nu', ''),
(307, 'Poate sta&#355;iona noaptea, pe un drum public, remorca unui autoturism?', '', '<p id=\"v_a\"><b>a. </b> nu;\r\n</p><p id=\"v_b\"><b>b. </b> da, dac&#259; drumul este iluminat;\r\n</p><p id=\"v_c\"><b>c. </b> da, dac&#259; aceasta este semnalizat&#259; cu triunghiuri reflectorizante.</p>', 'da', 'nu', 'nu', ''),
(308, 'În acest caz:', 'uploads/1018.jpg', '<p id=\"v_a\"><b>a. </b> trebuie s&#259; permite&#355;i trecerea autocamionului;\r\n</p><p id=\"v_b\"><b>b. </b> ave&#355;i voie s&#259; vira&#355;i înaintea autocamionului;\r\n</p><p id=\"v_c\"><b>c. </b> trebuie s&#259; permite&#355;i numai trecerea autoturismului.</p>', 'da', 'nu', 'nu', ''),
(309, 'Panoul de sub indicator permite:', 'uploads/728a.jpg', '<p id=\"v_a\"><b>a. </b> sta&#355;ionarea mijloacelor de transport public de persoane în intervalul men&#355;ionat;\r\n</p><p id=\"v_b\"><b>b. </b> sta&#355;ionarea în timpul orelor men&#355;ionate;\r\n</p><p id=\"v_c\"><b>c. </b> sta&#355;ionarea în afara intervalului orar men&#355;ionat.</p>', 'nu', 'nu', 'da', ''),
(310, 'Poli&#355;istul rutier are dreptul de a confisca:', '', '<p id=\"v_a\"><b>a. </b> centurile de siguran&#355;&#259; neomologate;\r\n</p><p id=\"v_b\"><b>b. </b> mijloacele proprii de detectare a dispozitivelor de m&#259;surare a vitezei;\r\n</p><p id=\"v_c\"><b>c. </b> dispozitivele care perturb&#259; func&#355;ionarea mijloacelor tehnice de supraveghere a traficului.</p>', 'nu', 'nu', 'da', ''),
(311, 'În care dintre situa&#355;ii este interzis&#259; dep&#259;&#351;irea?', '', '<p id=\"v_a\"><b>a. </b> în apropierea vârfurilor de ramp&#259;, când vizibilitatea este redus&#259; sub 50 m;\r\n</p><p id=\"v_b\"><b>b. </b> în curbe, dac&#259; vizibilitatea este redus&#259; sub 50 m;\r\n</p><p id=\"v_c\"><b>c. </b> la întâlnirea indicatorului „Drum cu prioritate“.</p>', 'da', 'da', 'nu', ''),
(312, 'Parcarea este permis&#259;:', '', '<p id=\"v_a\"><b>a. </b> numai în locurile special destinate, semnalizate prin indicatoare sau marcaje;\r\n</p><p id=\"v_b\"><b>b. </b> pe orice drum public, unde sta&#355;ionarea nu este interzis&#259;, cu condi&#355;ia s&#259; nu se stânjeneasc&#259; circula&#355;ia celorlalte vehicule;\r\n</p><p id=\"v_c\"><b>c. </b> numai pe str&#259;zile cu o l&#259;&#355;ime de cel pu&#355;in 6 m.</p>', 'da', 'nu', 'nu', ''),
(313, 'Prioritatea de trecere se acord&#259; în urm&#259;toarea situa&#355;ie:', '', '<p id=\"v_a\"><b>a. </b> la întâlnirea indicatorului „Prioritate pentru circula&#355;ia din sens invers\";\r\n</p><p id=\"v_b\"><b>b. </b> la întâlnirea indicatorului „Intersec&#355;ie cu un drum f&#259;r&#259; prioritate\";\r\n</p><p id=\"v_c\"><b>c. </b> la întâlnirea indicatorului „Sfâr&#351;itul drumului cu prioritate\".</p>', 'da', 'nu', 'nu', ''),
(314, 'Viteza de deplasare trebuie redus&#259; pân&#259; la 30 km/h în localit&#259;&#355;i sau 50 km/h în afara localit&#259;&#355;ilor:', '', '<p id=\"v_a\"><b>a. </b> la trecerea pe lâng&#259; grupuri organizate, coloane militare sau cortegii, dac&#259; acestea se afl&#259; pe drumurile cu o singur&#259; band&#259; de circula&#355;ie pe sens;\r\n</p><p id=\"v_b\"><b>b. </b> la întâlnirea indicatorului „Pist&#259; obligatorie pentru bicicli&#351;ti“;\r\n</p><p id=\"v_c\"><b>c. </b> la trecerile la nivel cu calea ferat&#259; dubl&#259; sau simpl&#259; prev&#259;zut&#259; cu bariere.</p>', 'da', 'nu', 'nu', ''),
(315, 'Care dintre cele trei vehicule va trece ultimul prin intersec&#355;ie?', 'uploads/502.jpg', '<p id=\"v_a\"><b>a. </b> autobuzul;\r\n</p><p id=\"v_b\"><b>b. </b> autoturismul;\r\n</p><p id=\"v_c\"><b>c. </b> autocamioneta mov.</p>', 'nu', 'da', 'nu', ''),
(316, 'Vira&#355;i la stânga în intersec&#355;ie. Cum proceda&#355;i?', 'uploads/609.jpg', '<p id=\"v_a\"><b>a. </b> acorda&#355;i prioritate autoturismului din stânga;\r\n</p><p id=\"v_b\"><b>b. </b> ave&#355;i prioritate fa&#355;&#259; de autoturismul alb din stânga;\r\n</p><p id=\"v_c\"><b>c. </b> nu v&#259; este permis s&#259; vira&#355;i la stânga.</p>', 'nu', 'da', 'nu', ''),
(317, 'Ce trebuie s&#259; ave&#355;i în vedere atunci când inten&#355;iona&#355;i s&#259; dep&#259;&#351;i&#355;i atât bicicli&#351;ti, cât &#351;i grupuri de pietoni care se deplaseaz&#259; pe carosabil?', '', '<p id=\"v_a\"><b>a. </b> pute&#355;i dep&#259;&#351;i numai dup&#259; ce pietonii &#351;i bicicli&#351;tii v-au f&#259;cut semn în acest sens;\r\n</p><p id=\"v_b\"><b>b. </b> respecta&#355;i prevederile legale;\r\n</p><p id=\"v_c\"><b>c. </b> bicicli&#351;tii &#351;i pietonii pot fi dep&#259;&#351;i&#355;i numai dac&#259; distan&#355;a lateral&#259; este de 3 m.</p>', 'nu', 'da', 'nu', ''),
(318, 'Ce m&#259;suri vor fi luate în cazul conducerii pe drumurile publice a unui autovehicul cu defec&#355;iuni grave la mecanismul de direc&#355;ie?', '', '<p id=\"v_a\"><b>a. </b> amend&#259; contraven&#355;ional&#259;, re&#355;inerea certificatului de înmatriculare, precum &#351;i re&#355;inerea permisului de conducere în vederea suspend&#259;rii exercit&#259;rii dreptului de a conduce autovehicule;\r\n</p><p id=\"v_b\"><b>b. </b> amend&#259; contraven&#355;ional&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> amend&#259; contraven&#355;ional&#259; &#351;i re&#355;inerea certificatului de înmatriculare.</p>', 'da', 'nu', 'nu', ''),
(319, 'În situa&#355;ia în care poli&#355;istul se afl&#259; cu fa&#355;a la un autovehicul care se apropie, având bra&#355;ul drept întins orizontal, conduc&#259;torul auto trebuie:', '', '<p id=\"v_a\"><b>a. </b> s&#259; opreasc&#259;, deoarece semnalul i se adreseaz&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; continue deplasarea, deoarece semnalul nu i se adreseaz&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; schimbe direc&#355;ia de mers spre stânga.</p>', 'nu', 'da', 'nu', ''),
(320, 'De la ce distan&#355;&#259; fa&#355;&#259; de vehiculul care circul&#259; din sens opus, pe timp de noapte, conduc&#259;torul unui autovehicul este obligat s&#259; foloseasc&#259; lumina de întâlnire?', '', '<p id=\"v_a\"><b>a. </b> de la cel pu&#355;in 100 m;\r\n</p><p id=\"v_b\"><b>b. </b> de la cel pu&#355;in 150 m;\r\n</p><p id=\"v_c\"><b>c. </b> de la cel pu&#355;in 200 m.</p>', 'nu', 'nu', 'da', ''),
(321, 'Ce se în&#355;elege prin vigilen&#355;&#259;, din punct de vedere al conduitei preventive?', '', '<p id=\"v_a\"><b>a. </b> capacitatea de a nu intra în conflict cu organele de poli&#355;ie rutier&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; fi&#355;i odihnit &#351;i bine alimentat, pentru a nu fi obligat s&#259; opri&#355;i pe traseu;\r\n</p><p id=\"v_c\"><b>c. </b> men&#355;inerea neîntrerupt&#259; a aten&#355;iei pe toat&#259; durata deplas&#259;rii.</p>', 'nu', 'nu', 'da', ''),
(322, 'Sustragerea de la recoltarea probelor biologice, în vederea stabilirii alcoolemiei, constituie:', '', '<p id=\"v_a\"><b>a. </b> infrac&#355;iune, &#351;i atrage anularea permisului de conducere;\r\n</p><p id=\"v_b\"><b>b. </b> contraven&#355;ie, &#351;i se pedepse&#351;te cu închisoare;\r\n</p><p id=\"v_c\"><b>c. </b> contraven&#355;ie, &#351;i atrage re&#355;inerea permisului de conducere.</p>', 'da', 'nu', 'nu', ''),
(323, 'Semnele unei fracturi sunt:', '', '<p id=\"v_a\"><b>a. </b> durere în zona afectat&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> durere &#351;i pozi&#355;ie nefireasc&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> temperatur&#259; m&#259;rit&#259; &#351;i transpira&#355;ie abundent&#259;.</p>', 'nu', 'da', 'nu', ''),
(324, 'Pentru a conduce ecologic un autovehicul, se recomand&#259;:', '', '<p id=\"v_a\"><b>a. </b> folosirea motorului la relanti, în cazul în care sta&#355;iona&#355;i mai mult timp în acela&#351;i loc;\r\n</p><p id=\"v_b\"><b>b. </b> men&#355;inerea unei viteze constante, de preferin&#355;&#259; medie;\r\n</p><p id=\"v_c\"><b>c. </b> oprirea motorului în timp ce coborâ&#355;i o pant&#259;, pentru a economisi carburant.</p>', 'nu', 'da', 'nu', ''),
(325, 'Când este interzis&#259; schimbarea direc&#355;iei de mers la dreapta, de&#351;i este în func&#355;iune semnalul verde?', '', '<p id=\"v_a\"><b>a. </b> când semaforul nu este înso&#355;it de un semn adi&#355;ional ce con&#355;ine o s&#259;geat&#259; cu vârful c&#259;tre dreapta;\r\n</p><p id=\"v_b\"><b>b. </b> când la dreapta se afl&#259; o strad&#259; cu sens unic;\r\n</p><p id=\"v_c\"><b>c. </b> când banda pe care se afl&#259; încadrat conduc&#259;torul de vehicul este semnalizat&#259; cu indicator &#351;i marcaj direc&#355;ional care indic&#259; mersul înainte.</p>', 'nu', 'nu', 'da', ''),
(326, 'Indicatorul permite accesul:', 'uploads/544.jpg', '<p id=\"v_a\"><b>a. </b> vehiculelor f&#259;r&#259; motor;\r\n</p><p id=\"v_b\"><b>b. </b> mopedelor &#351;i motocicletelor;\r\n</p><p id=\"v_c\"><b>c. </b> autovehiculelor.</p>', 'da', 'nu', 'nu', ''),
(327, 'Pentru care dintre faptele de mai jos se aplic&#259; puncte de penalizare?', '', '<p id=\"v_a\"><b>a. </b> pentru conducerea autovehiculului de c&#259;tre o alt&#259; persoan&#259; decât proprietarul;\r\n</p><p id=\"v_b\"><b>b. </b> pentru conducerea autovehiculului f&#259;r&#259; una dintre pl&#259;cu&#355;ele cu num&#259;rul de înmatriculare;\r\n</p><p id=\"v_c\"><b>c. </b> noxele motorului ating limita legal&#259; admis&#259;.</p>', 'nu', 'da', 'nu', ''),
(328, 'Ce trebuie s&#259; face&#355;i pentru evitarea complica&#355;iilor în cazul unei fracturi?', '', '<p id=\"v_a\"><b>a. </b> s&#259; imobiliza&#355;i zona fracturat&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; steriliza&#355;i &#351;i s&#259; pansa&#355;i zona fracturat&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; aplica&#355;i un garou de cauciuc deasupra fracturii.</p>', 'da', 'nu', 'nu', ''),
(329, 'Care dintre vehicule au prioritate de trecere prin intersec&#355;ia prezentat&#259;?', 'uploads/549.jpg', '<p id=\"v_a\"><b>a. </b> autocamionul &#351;i autoturismul;\r\n</p><p id=\"v_b\"><b>b. </b> motocicleta &#351;i tramvaiul;\r\n</p><p id=\"v_c\"><b>c. </b> motocicleta &#351;i autocamionul.</p>', 'nu', 'nu', 'da', ''),
(330, 'Oprirea este interzis&#259;:', '', '<p id=\"v_a\"><b>a. </b> pe drumurile publice unde circul&#259; troleibuze &#351;i tramvaie;\r\n</p><p id=\"v_b\"><b>b. </b> la o distan&#355;&#259; mai mic&#259; de 25 m fa&#355;&#259; de trecerea pentru pietoni;\r\n</p><p id=\"v_c\"><b>c. </b> în zona de sta&#355;ionare cu durat&#259; limitat&#259;.</p>', 'nu', 'da', 'nu', ''),
(331, 'Care dintre cele trei autovehicule a parcat regulamentar?', 'uploads/309.jpg', '<p id=\"v_a\"><b>a. </b> niciunul;\r\n</p><p id=\"v_b\"><b>b. </b> autoturismul 3;\r\n</p><p id=\"v_c\"><b>c. </b> autoturismele 1 &#351;i 2.</p>', 'da', 'nu', 'nu', ''),
(332, 'În care dintre situa&#355;ii oprirea este interzis&#259;:', '', '<p id=\"v_a\"><b>a. </b> pe str&#259;zile cu sens unic;\r\n</p><p id=\"v_b\"><b>b. </b> în intersec&#355;iile cu sens giratoriu;\r\n</p><p id=\"v_c\"><b>c. </b> în dreptul indicatorului „Parcarea interzis&#259;\".</p>', 'nu', 'da', 'nu', ''),
(333, 'Ce obliga&#355;ii are conduc&#259;torul unui vehicul atunci când dore&#351;te s&#259; intre sau s&#259; ias&#259; dintr-un rând de vehicule sta&#355;ionate?', '', '<p id=\"v_a\"><b>a. </b> s&#259; se asigure c&#259; distan&#355;a dintre vehicule este suficient&#259; pentru parcarea vehiculului;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; semnalizeze &#351;i s&#259; se asigure c&#259; poate executa manevra;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; nu execute manevra în zona de ac&#355;iune a indicatorului „Sta&#355;ionarea interzis&#259;“.</p>', 'nu', 'da', 'nu', ''),
(334, 'Ce culoare pot avea l&#259;mpile cu lumin&#259; intermitent&#259; din spatele autovehiculului?', '', '<p id=\"v_a\"><b>a. </b> ro&#351;ie sau alb&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> ro&#351;ie sau galben&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> galben&#259; sau alb&#259;.</p>', 'nu', 'da', 'nu', ''),
(335, 'Conduc&#259;torul autoturismului din imagine va circula cu:', 'uploads/328.jpg', '<p id=\"v_a\"><b>a. </b> 80 km/h;\r\n</p><p id=\"v_b\"><b>b. </b> 90 km/h;\r\n</p><p id=\"v_c\"><b>c. </b> 100 km/h.</p>', 'da', 'nu', 'nu', ''),
(336, 'Întoarcerea unui autovehicul este interzis&#259;:', '', '<p id=\"v_a\"><b>a. </b> la o distan&#355;&#259; mai mic&#259; de 100 m de sta&#355;iile de autobuz, troleibuz sau tramvai;\r\n</p><p id=\"v_b\"><b>b. </b> pe drumurile pe care o band&#259; de circula&#355;ie este ocupat&#259; de linia de tramvai;\r\n</p><p id=\"v_c\"><b>c. </b> la mai pu&#355;in de 50 m de trecerea la nivel cu o cale ferat&#259; curent&#259;.</p>', 'nu', 'nu', 'da', ''),
(337, 'Ce v&#259; este interzis în aceast&#259; situa&#355;ie?', 'uploads/224.jpg', '<p id=\"v_a\"><b>a. </b> s&#259; opri&#355;i sau s&#259; sta&#355;iona&#355;i;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; dep&#259;&#351;i&#355;i alte vehicule, cu excep&#355;ia bicicletelor sau a c&#259;ru&#355;elor;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; c&#259;lca&#355;i marcajul aplicat pe carosabil.</p>', 'da', 'nu', 'nu', ''),
(338, 'Sunte&#355;i obligat s&#259; opri&#355;i la semnalele date de:', '', '<p id=\"v_a\"><b>a. </b> îndrum&#259;torii de circula&#355;ie ai Ministerului Ap&#259;r&#259;rii;\r\n</p><p id=\"v_b\"><b>b. </b> conduc&#259;torii vehiculelor de ambulan&#355;&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> inspectorii prim&#259;riei.</p>', 'da', 'nu', 'nu', ''),
(339, 'Ce obliga&#355;ii ave&#355;i în situa&#355;ia prezentat&#259; în imagine?', 'uploads/648.jpg', '<p id=\"v_a\"><b>a. </b> s&#259; nu dep&#259;&#351;i&#355;i alte vehicule;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; reduce&#355;i viteza;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; opri&#355;i la 50 m de calea ferat&#259;.</p>', 'nu', 'da', 'nu', ''),
(340, 'Care dintre cele dou&#259; indicatoare oblig&#259; la schimbarea direc&#355;iei de mers spre dreapta, dup&#259; dep&#259;&#351;irea locului unde este instalat?', 'uploads/15.jpg', '<p id=\"v_a\"><b>a. </b> indicatorul 2;\r\n</p><p id=\"v_b\"><b>b. </b> indicatorul 1;\r\n</p><p id=\"v_c\"><b>c. </b> ambele indicatoare.</p>', 'da', 'nu', 'nu', ''),
(341, 'În ce situa&#355;ii sunte&#355;i obligat s&#259; acorda&#355;i prioritate de trecere?', '', '<p id=\"v_a\"><b>a. </b> într-o intersec&#355;ie nedirijat&#259;, atunci când p&#259;trunde&#355;i pe un drum na&#355;ional, venind de pe un drum jude&#355;ean;\r\n</p><p id=\"v_b\"><b>b. </b> la întâlnirea indicatorului „Drum cu prioritate“;\r\n</p><p id=\"v_c\"><b>c. </b> la întâlnirea pietonilor afla&#355;i pe sensul de mers opus.</p>', 'da', 'nu', 'nu', ''),
(342, 'Dep&#259;&#351;irea nu este permis&#259;:', '', '<p id=\"v_a\"><b>a. </b> atunci când vehiculul ce urmeaz&#259; a fi dep&#259;&#351;it ruleaz&#259; cu viteza admis&#259; de lege;\r\n</p><p id=\"v_b\"><b>b. </b> în orice locuri &#351;i situa&#355;ii în care vizibilitatea nu permite executarea manevrei;\r\n</p><p id=\"v_c\"><b>c. </b> atunci când traficul rutier este aglomerat.</p>', 'nu', 'da', 'nu', ''),
(343, 'În cazul unei entorse a gleznei, proceda&#355;i astfel:', '', '<p id=\"v_a\"><b>a. </b> interzice&#355;i victimei s&#259; se sprijine pe glezna respectiv&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> ridica&#355;i piciorul afectat &#351;i aplica&#355;i un bandaj elastic strâns;\r\n</p><p id=\"v_c\"><b>c. </b> nu interveni&#355;i &#351;i a&#351;tepta&#355;i ca victima s&#259; fie transportat&#259; la spital.</p>', 'nu', 'da', 'nu', ''),
(344, 'Care dintre urm&#259;toarele obliga&#355;ii revin conduc&#259;torului de vehicul?', '', '<p id=\"v_a\"><b>a. </b> s&#259; reduc&#259; viteza, atunci când trece pe lâng&#259; un poli&#355;ist de circula&#355;ie aflat în serviciu pe drumul public;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; men&#355;in&#259; curate pl&#259;cu&#355;ele cu num&#259;rul de înmatriculare, pentru a fi vizibile;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; circule cu o vitez&#259; inferioar&#259; celei prescrise de indicatorul „Vitez&#259; minim&#259; obligatorie“.</p>', 'nu', 'da', 'nu', ''),
(345, 'Este corect&#259; manevra de devansare pe autostrad&#259; a unui autovehicul pe banda dreapt&#259; a acestuia?', '', '<p id=\"v_a\"><b>a. </b> da;\r\n</p><p id=\"v_b\"><b>b. </b> nu;\r\n</p><p id=\"v_c\"><b>c. </b> da, dar numai dac&#259; se folosesc semnalele de aten&#355;ionare sonore &#351;i luminoase.</p>', 'da', 'nu', 'nu', ''),
(346, 'În situa&#355;ia dat&#259;, sunte&#355;i obligat:', 'uploads/440.jpg', '<p id=\"v_a\"><b>a. </b> s&#259; vira&#355;i la dreapta pân&#259; la indicator;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; vira&#355;i la dreapta, deoarece urmeaz&#259; un sector de drum îngustat;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; ocoli&#355;i scuarul pe care este instalat indicatorul.</p>', 'da', 'nu', 'nu', ''),
(347, 'Este interzis&#259; oprirea voluntar&#259;:', '', '<p id=\"v_a\"><b>a. </b> pe trecerile pentru pietoni sau la mai pu&#355;in de 25 m, înainte &#351;i dup&#259; acestea;\r\n</p><p id=\"v_b\"><b>b. </b> la o distan&#355;&#259; mai mic&#259; de 100 m, înainte &#351;i dup&#259; trecerea la nivel cu calea ferat&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> la o distan&#355;&#259; mai mic&#259; de 50 m, înainte &#351;i dup&#259; sta&#355;iile mijloacelor de transport public.</p>', 'da', 'nu', 'nu', ''),
(348, 'Întoarcerea unui autovehicul este interzis&#259;:', '', '<p id=\"v_a\"><b>a. </b> la o distan&#355;&#259; mai mic&#259; de 50 m de sta&#355;ia de autobuz, troleibuz sau tramvai;\r\n</p><p id=\"v_b\"><b>b. </b> în ramp&#259;, unde vizibilitatea este sub 50 m;\r\n</p><p id=\"v_c\"><b>c. </b> pe str&#259;zile pe care circula&#355;ia se desf&#259;&#351;oar&#259; în ambele sensuri.</p>', 'nu', 'da', 'nu', ''),
(349, 'Cum proceda&#355;i când circula&#355;i pe un drum public &#351;i întâlni&#355;i indicatorul „Prioritate fa&#355;&#259; de circula&#355;ia din sens invers“?', '', '<p id=\"v_a\"><b>a. </b> opri&#355;i &#351;i acorda&#355;i prioritate de trecere vehiculelor care se apropie din sens opus;\r\n</p><p id=\"v_b\"><b>b. </b> v&#259; continua&#355;i deplasarea, pentru c&#259; ave&#355;i prioritate de trecere;\r\n</p><p id=\"v_c\"><b>c. </b> opri&#355;i &#351;i acorda&#355;i prioritate numai vehiculelor cu gabarit dep&#259;&#351;it, care se apropie din sens opus.</p>', 'nu', 'da', 'nu', ''),
(350, 'Este permis&#259; parcarea autoturismelor pe trotuar, dac&#259; se asigur&#259; spa&#355;iu de cel pu&#355;in 1 m pentru circula&#355;ia pietonilor ?', '', '<p id=\"v_a\"><b>a. </b> nu, întrucât trotuarul este rezervat în exclusivitate deplas&#259;rii pietonilor;\r\n</p><p id=\"v_b\"><b>b. </b> da, dar numai în acele zone unde exist&#259; reglement&#259;ri în acest sens;\r\n</p><p id=\"v_c\"><b>c. </b> da, când vehiculul trebuie scos de pe partea carosabil&#259; pentru a nu stânjeni autovehiculele aflate în mi&#351;care.</p>', 'nu', 'da', 'nu', ''),
(351, 'Imobilizarea voluntar&#259; a unui vehicul se consider&#259; oprire, dac&#259;:', '', '<p id=\"v_a\"><b>a. </b> dureaz&#259; cel mult 5 minute;\r\n</p><p id=\"v_b\"><b>b. </b> vehiculul a fost oprit pentru îmbarcarea sau debarcarea unor persoane, f&#259;r&#259; ca prin aceasta s&#259; fie perturbat&#259; circula&#355;ia;\r\n</p><p id=\"v_c\"><b>c. </b> vehiculele de pân&#259; la 3,5 tone execut&#259; opera&#355;iunea de aprovizionare cu m&#259;rfuri alimentare.</p>', 'da', 'nu', 'nu', ''),
(352, 'Ce trebuie s&#259; fac&#259; conduc&#259;torul auto în situa&#355;ia prezentat&#259;?', 'uploads/636.jpg', '<p id=\"v_a\"><b>a. </b> s&#259; reduc&#259; viteza;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; opreasc&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; nu dep&#259;&#351;easc&#259; motocicleta de poli&#355;ie.</p>', 'da', 'nu', 'nu', ''),
(353, 'Indicatorul precizeaz&#259;:', 'uploads/225.jpg', '<p id=\"v_a\"><b>a. </b> pe banda din stânga, vehiculele vor circula cu cel mult viteza indicat&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> pe banda din dreapta, se poate circula f&#259;r&#259; restric&#355;ie de vitez&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> pe banda din stânga, pot circula cei care se deplaseaz&#259; cel pu&#355;in cu viteza men&#355;ionat&#259;.</p>', 'nu', 'nu', 'da', ''),
(354, 'Ce deprindere gre&#351;it&#259; a conduc&#259;torului auto determin&#259; frecvent defectarea ambreiajului?', '', '<p id=\"v_a\"><b>a. </b> coborârea pantelor cu motorul oprit sau aducerea schimb&#259;torului de viteze în punctul mort;\r\n</p><p id=\"v_b\"><b>b. </b> rularea cu jocuri mari la pedala de ambreiaj sau la mecanismul de direc&#355;ie;\r\n</p><p id=\"v_c\"><b>c. </b> a&#351;teptarea la semafor având pedala de ambreiaj ap&#259;sat&#259;.</p>', 'nu', 'nu', 'da', ''),
(355, 'În ce situa&#355;ii este interzis&#259; dep&#259;&#351;irea unei biciclete?', '', '<p id=\"v_a\"><b>a. </b> în zona de ac&#355;iune a indicatorului „Dep&#259;&#351;irea autovehiculelor, cu excep&#355;ia motocicletelor f&#259;r&#259; ata&#351;, interzis&#259;“;\r\n</p><p id=\"v_b\"><b>b. </b> pe trecerile de pietoni semnalizate;\r\n</p><p id=\"v_c\"><b>c. </b> la mai pu&#355;in de 100 m de trecerea la nivel cu calea ferat&#259;.</p>', 'nu', 'da', 'nu', ''),
(356, 'Care dintre cele trei vehicule nu au prioritate de trecere?', 'uploads/474.jpg', '<p id=\"v_a\"><b>a. </b> autoturismul &#351;i autobuzul;\r\n</p><p id=\"v_b\"><b>b. </b> autobuzul &#351;i autocamionul;\r\n</p><p id=\"v_c\"><b>c. </b> autocamionul &#351;i autoturismul.</p>', 'nu', 'nu', 'da', ''),
(357, 'Ce vehicule pot fi dep&#259;&#351;ite, prin excep&#355;ie, pe un pod?', '', '<p id=\"v_a\"><b>a. </b> vehiculele cu trac&#355;iune animal&#259;, motociclete f&#259;r&#259; ata&#351; &#351;i biciclete;\r\n</p><p id=\"v_b\"><b>b. </b> tractoarele &#351;i alte autovehicule lente;\r\n</p><p id=\"v_c\"><b>c. </b> niciun vehicul.</p>', 'da', 'nu', 'nu', ''),
(358, 'Persoanele accidentate, cu arsuri multiple pe corp &#351;i membre, trebuie s&#259; fie:', '', '<p id=\"v_a\"><b>a. </b> dezbr&#259;cate rapid de haine, dup&#259; stingerea fl&#259;c&#259;rilor;\r\n</p><p id=\"v_b\"><b>b. </b> transportate urgent la o unitate medical&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> sp&#259;late, bandajate &#351;i apoi transportate la cea mai apropiat&#259; unitate sanitar&#259;.</p>', 'nu', 'da', 'nu', '');
INSERT INTO `intrebari` (`id`, `cerinta`, `imagine`, `variante`, `rasp_a`, `rasp_b`, `rasp_c`, `eticheta`) VALUES
(359, 'Interdic&#355;ia impus&#259; de indicatorul din imagine se adreseaz&#259;:', 'uploads/395.jpg', '<p id=\"v_a\"><b>a. </b> tuturor autovehiculelor, cu excep&#355;ia motocicletelor f&#259;r&#259; ata&#351;;\r\n</p><p id=\"v_b\"><b>b. </b> autoturismelor &#351;i motocicletelor;\r\n</p><p id=\"v_c\"><b>c. </b> tuturor vehiculelor.</p>', 'da', 'nu', 'nu', ''),
(360, 'Care este limita legal&#259; de vitez&#259; în afara localit&#259;&#355;ilor, pe celelalte categorii de drumuri, altele decât autostr&#259;zile, drumurile expres sau cele na&#355;ionale europene (E)?', '', '<p id=\"v_a\"><b>a. </b> 70 km/h;\r\n</p><p id=\"v_b\"><b>b. </b> 90 km/h;\r\n</p><p id=\"v_c\"><b>c. </b> 100 km/h.</p>', 'nu', 'da', 'nu', ''),
(361, 'Ce trebuie s&#259; face&#355;i imediat dup&#259; un accident cu un animal s&#259;lbatic?', '', '<p id=\"v_a\"><b>a. </b> opri&#355;i, aprinde&#355;i luminile de avarie &#351;i asigura&#355;i locul accidentului;\r\n</p><p id=\"v_b\"><b>b. </b> în&#351;tiin&#355;a&#355;i societ&#259;&#355;ile de protec&#355;ie a animalelor;\r\n</p><p id=\"v_c\"><b>c. </b> lua&#355;i vânatul acas&#259; &#351;i comunica&#355;i societ&#259;&#355;ii de asigurare daunele suferite de autoturism.</p>', 'da', 'nu', 'nu', ''),
(362, 'Sunte&#355;i obligat s&#259; folosi&#355;i luminile de întâlnire în timpul zilei:', '', '<p id=\"v_a\"><b>a. </b> pe toate categoriile de drumuri;\r\n</p><p id=\"v_b\"><b>b. </b> pe drumurile jude&#355;ene, comunale &#351;i locale;\r\n</p><p id=\"v_c\"><b>c. </b> pe autostr&#259;zi, drumuri expres &#351;i na&#355;ionale europene.</p>', 'nu', 'nu', 'da', ''),
(363, 'Care dintre cele trei vehicule trece primul prin intersec&#355;ie?', 'uploads/314.jpg', '<p id=\"v_a\"><b>a. </b> tramvaiul, deoarece are prioritate în mers;\r\n</p><p id=\"v_b\"><b>b. </b> autoturismul galben, deoarece î&#351;i schimb&#259; direc&#355;ia de mers;\r\n</p><p id=\"v_c\"><b>c. </b> autoturismul ro&#351;u, deoarece vine din dreapta celorlalte dou&#259; vehicule.</p>', 'da', 'nu', 'nu', ''),
(364, 'Cum procedeaz&#259; conduc&#259;torul de autovehicul care inten&#355;ioneaz&#259; s&#259; circule pe direc&#355;ia înainte printr-o intersec&#355;ie nedirijat&#259;?', '', '<p id=\"v_a\"><b>a. </b> reduce viteza &#351;i acord&#259; prioritate tuturor vehiculelor;\r\n</p><p id=\"v_b\"><b>b. </b> reduce viteza &#351;i acord&#259; prioritate vehiculelor care vin din dreapta sa;\r\n</p><p id=\"v_c\"><b>c. </b> reduce viteza &#351;i acord&#259; prioritate vehiculelor care circul&#259; din sens opus &#351;i urmeaz&#259; s&#259; schimbe direc&#355;ia de mers spre stânga.</p>', 'nu', 'da', 'nu', ''),
(365, 'Cu ce vitez&#259; ve&#355;i putea rula dac&#259; sunte&#355;i la volan?', 'uploads/227.jpg', '<p id=\"v_a\"><b>a. </b> 60 km/h;\r\n</p><p id=\"v_b\"><b>b. </b> 50 km/h;\r\n</p><p id=\"v_c\"><b>c. </b> 40 km/h.</p>', 'nu', 'da', 'nu', ''),
(366, 'Autovehiculul din fa&#355;a dvs. traverseaz&#259; intersec&#355;ia. Cum ve&#355;i proceda?', 'uploads/871.jpg', '<p id=\"v_a\"><b>a. </b> executa&#355;i aceea&#351;i manevr&#259;, dac&#259; nu sunt alte obliga&#355;ii;\r\n</p><p id=\"v_b\"><b>b. </b> opri&#355;i, deoarece trebuie s&#259; v&#259; asigura&#355;i;\r\n</p><p id=\"v_c\"><b>c. </b> ceda&#355;i trecerea autovehiculelor care vin din dreapta.</p>', 'da', 'nu', 'nu', ''),
(367, 'Unui autovehicul nu-i este permis s&#259; circule atunci când:', '', '<p id=\"v_a\"><b>a. </b> unul dintre faruri nu func&#355;ioneaz&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> parbrizul este fisurat;\r\n</p><p id=\"v_c\"><b>c. </b> noxele emanate dep&#259;&#351;esc limita legal&#259; admis&#259;.</p>', 'nu', 'nu', 'da', ''),
(368, 'Conduc&#259;torii de autovehicule pot folosi mijloace proprii de detectare a dispozitivelor de m&#259;surare a vitezei?', '', '<p id=\"v_a\"><b>a. </b> da;\r\n</p><p id=\"v_b\"><b>b. </b> nu;\r\n</p><p id=\"v_c\"><b>c. </b> acest aspect nu este stabilit legal.</p>', 'da', 'nu', 'nu', ''),
(369, 'Consumul de alcool, chiar &#351;i în cantit&#259;&#355;i mici:', '', '<p id=\"v_a\"><b>a. </b> poate determina o manier&#259; de conducere riscant&#259; sau excesiv de degajat&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> poate reduce capacitatea vizual&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> poate reduce capacitatea auditiv&#259;.</p>', 'da', 'nu', 'nu', ''),
(370, 'În ce ordine vor intra autovehiculele în intersec&#355;ie?', 'uploads/490.jpg', '<p id=\"v_a\"><b>a. </b> motocicleta, autocamionul, autoturismul;\r\n</p><p id=\"v_b\"><b>b. </b> motocicleta, autoturismul, autocamionul;\r\n</p><p id=\"v_c\"><b>c. </b> autocamionul, motocicleta, autoturismul.</p>', 'da', 'nu', 'nu', ''),
(371, 'În aceast&#259; situa&#355;ie ve&#355;i circula cu:', 'uploads/366.jpg', '<p id=\"v_a\"><b>a. </b> luminile de drum;\r\n</p><p id=\"v_b\"><b>b. </b> luminile de întâlnire;\r\n</p><p id=\"v_c\"><b>c. </b> luminile de pozi&#355;ie.</p>', 'nu', 'da', 'nu', ''),
(372, 'Termenul de valabilitate a inspec&#355;iei tehnice periodice poate fi reg&#259;sit:', '', '<p id=\"v_a\"><b>a. </b> într-un document eliberat de service;\r\n</p><p id=\"v_b\"><b>b. </b> în anexa certificatului de înmatriculare al autovehiculului;\r\n</p><p id=\"v_c\"><b>c. </b> în manualul tehnic al autovehiculului sau al remorcii.</p>', 'nu', 'da', 'nu', ''),
(373, 'Conduce&#355;i autoturismul albastru. V-a&#355;i angajat corect în dep&#259;&#351;irea motocicletei?', 'uploads/442.jpg', '<p id=\"v_a\"><b>a. </b> da, deoarece din sens opus nu circul&#259; niciun vehicul;\r\n</p><p id=\"v_b\"><b>b. </b> da, deoarece motocicleta nu trece peste axul drumului;\r\n</p><p id=\"v_c\"><b>c. </b> nu, deoarece motocicleta dep&#259;&#351;e&#351;te autocamionul.</p>', 'nu', 'nu', 'da', ''),
(374, 'În care dintre situa&#355;ii este permis&#259; dep&#259;&#351;irea?', '', '<p id=\"v_a\"><b>a. </b> în intersec&#355;iile dirijate prin semnale sonore;\r\n</p><p id=\"v_b\"><b>b. </b> în intersec&#355;iile dirijate;\r\n</p><p id=\"v_c\"><b>c. </b> în intersec&#355;iile nedirijate.</p>', 'nu', 'da', 'nu', ''),
(375, 'Aprinderea luminii „martor“ de la bord, care semnalizeaz&#259; func&#355;ionarea generatorului de curent, poate indica:', '', '<p id=\"v_a\"><b>a. </b> griparea rotorului pompei de ap&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> ruperea curelei de antrenare a pompei de ap&#259;, a ventilatorului &#351;i a alternatorului;\r\n</p><p id=\"v_c\"><b>c. </b> scurgeri de electrolit din acumulatori.</p>', 'nu', 'da', 'nu', ''),
(376, 'Consumul de carburant se m&#259;re&#351;te dac&#259;:', '', '<p id=\"v_a\"><b>a. </b> lichidul de r&#259;cire nu con&#355;ine antigel;\r\n</p><p id=\"v_b\"><b>b. </b> sistemul de direc&#355;ie este manual;\r\n</p><p id=\"v_c\"><b>c. </b> folosi&#355;i orice aparat electric alimentat de generatorul autovehiculului.</p>', 'nu', 'nu', 'da', ''),
(377, 'Care dintre autovehicule va intra primul în intersec&#355;ie?', 'uploads/569.jpg', '<p id=\"v_a\"><b>a. </b> autocamionul, deoarece autoturismul vireaz&#259; la stânga &#351;i intersecteaz&#259; direc&#355;ia acestuia de mers;\r\n</p><p id=\"v_b\"><b>b. </b> autoturismul, deoarece vine din dreapta autocamionului;\r\n</p><p id=\"v_c\"><b>c. </b> autocamionul, deoarece nu-&#351;i schimb&#259; direc&#355;ia de mers.</p>', 'nu', 'da', 'nu', ''),
(378, 'Este interzis&#259; sta&#355;ionarea unui autoturism pe un drum public cu o l&#259;&#355;ime mai mic&#259; de 6 m?', '', '<p id=\"v_a\"><b>a. </b> da;\r\n</p><p id=\"v_b\"><b>b. </b> nu;\r\n</p><p id=\"v_c\"><b>c. </b> legal, nu este prev&#259;zut.</p>', 'da', 'nu', 'nu', ''),
(379, 'O hemoragie produs&#259; la unul dintre membrele unei persoane r&#259;nite se va opri prin:', '', '<p id=\"v_a\"><b>a. </b> dezinfectarea r&#259;nii, pansarea &#351;i aplicarea atelelor;\r\n</p><p id=\"v_b\"><b>b. </b> ridicarea membrului accidentat în pozi&#355;ie vertical&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> ac&#355;iunea compresiv&#259; asupra vasului lezat sau aplicarea garoului.</p>', 'nu', 'nu', 'da', ''),
(380, 'Care este cea mai vulnerabil&#259; categorie de participan&#355;i la trafic, din rândul c&#259;reia se înregistreaz&#259; cele mai multe victime?', '', '<p id=\"v_a\"><b>a. </b> conduc&#259;torii de vehicule f&#259;r&#259; experien&#355;&#259; în conducere;\r\n</p><p id=\"v_b\"><b>b. </b> conduc&#259;torii de vehicule cu atestat profesional;\r\n</p><p id=\"v_c\"><b>c. </b> pietonii, îndeosebi copiii &#351;i persoanele în vârst&#259;.</p>', 'nu', 'nu', 'da', ''),
(381, 'Care este ordinea de trecere prin intersec&#355;ie a autovehiculelor?', 'uploads/839.jpg', '<p id=\"v_a\"><b>a. </b> autocamionul, autoturismul, motocicleta, troleibuzul;\r\n</p><p id=\"v_b\"><b>b. </b> troleibuzul, autocamionul, motocicleta, autoturismul;\r\n</p><p id=\"v_c\"><b>c. </b> autocamionul, autoturismul, troleibuzul, motocicleta.</p>', 'nu', 'nu', 'da', ''),
(382, 'Pe ploaie ve&#355;i aprinde luminile de cea&#355;&#259; din spate?', 'uploads/982.jpg', '<p id=\"v_a\"><b>a. </b> dac&#259; exist&#259; în dotare;\r\n</p><p id=\"v_b\"><b>b. </b> da;\r\n</p><p id=\"v_c\"><b>c. </b> nu.</p>', 'nu', 'nu', 'da', ''),
(383, 'Unde trebuie s&#259; a&#351;tepta&#355;i dac&#259; v&#259; afla&#355;i la o trecere de cale ferat&#259; cu bariera coborât&#259;?', '', '<p id=\"v_a\"><b>a. </b> la panoul cu dou&#259; dungi;\r\n</p><p id=\"v_b\"><b>b. </b> în fa&#355;a indicatorului în cruce;\r\n</p><p id=\"v_c\"><b>c. </b> în fa&#355;a barierei.</p>', 'nu', 'nu', 'da', ''),
(384, 'Întoarcerea autovehiculului este interzis&#259;:', '', '<p id=\"v_a\"><b>a. </b> unde marcajul axial este discontinuu;\r\n</p><p id=\"v_b\"><b>b. </b> în intersec&#355;iile unde este necesar&#259; manevrarea înainte &#351;i înapoi;\r\n</p><p id=\"v_c\"><b>c. </b> în zona de ac&#355;iune a indicatorului „Sta&#355;ionarea interzis&#259;“.</p>', 'nu', 'da', 'nu', ''),
(385, 'Imobilizarea unei fracturi f&#259;r&#259; atele se face cu:', '', '<p id=\"v_a\"><b>a. </b> o folie de material plastic;\r\n</p><p id=\"v_b\"><b>b. </b> e&#351;arfe &#351;i triunghiuri de pânz&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> carton rigid.</p>', 'nu', 'da', 'nu', ''),
(386, 'Într-o zon&#259; reziden&#355;ial&#259; pute&#355;i circula cu:', '', '<p id=\"v_a\"><b>a. </b> viteza minim&#259; obligatorie de 20 km/h;\r\n</p><p id=\"v_b\"><b>b. </b> viteza maxim&#259; de 20 km/h;\r\n</p><p id=\"v_c\"><b>c. </b> viteza maxim&#259; de 30 km/h.</p>', 'nu', 'da', 'nu', ''),
(387, 'Viteza maxim&#259; cu care pot s&#259; conduc&#259; un autoturism, pe drumurile na&#355;ionale europene (E), persoanele care au mai pu&#355;in de un an de practic&#259; de conducere, este de:', '', '<p id=\"v_a\"><b>a. </b> 100 km/h;\r\n</p><p id=\"v_b\"><b>b. </b> 80 km/h;\r\n</p><p id=\"v_c\"><b>c. </b> 90 km/h.</p>', 'nu', 'da', 'nu', ''),
(388, 'Ce obliga&#355;ii ave&#355;i dac&#259; întâlni&#355;i un vehicul cu semnalele speciale de avertizare luminoase de culoare albastr&#259; în func&#355;iune?', '', '<p id=\"v_a\"><b>a. </b> nicio obliga&#355;ie, deoarece semnalul albastru se adreseaz&#259; coloanelor în mi&#351;care;\r\n</p><p id=\"v_b\"><b>b. </b> opri&#355;i cât mai aproape de marginea drumului;\r\n</p><p id=\"v_c\"><b>c. </b> acorda&#355;i prioritate vehiculului de mai sus.</p>', 'nu', 'nu', 'da', ''),
(389, 'Limita inferioar&#259; de vitez&#259; pe care administratorul drumului public o poate stabili pe anumite sectoare, din interiorul localit&#259;&#355;ilor, nu poate fi mai mic&#259; de:', '', '<p id=\"v_a\"><b>a. </b> 10 km/h pentru tramvaie &#351;i 30 km/h pentru toate autovehiculele;\r\n</p><p id=\"v_b\"><b>b. </b> 20 km/h pentru tramvaie &#351;i 40 km/h pentru toate autovehiculele;\r\n</p><p id=\"v_c\"><b>c. </b> 30 km/h pentru tramvaie &#351;i 50 km/h pentru toate autovehiculele.</p>', 'da', 'nu', 'nu', ''),
(390, 'Care dintre autoturisme sta&#355;ioneaz&#259; regulamentar?', 'uploads/717.jpg', '<p id=\"v_a\"><b>a. </b> autoturismul ro&#351;u;\r\n</p><p id=\"v_b\"><b>b. </b> autoturismul albastru;\r\n</p><p id=\"v_c\"><b>c. </b> ambele autoturisme.</p>', 'nu', 'da', 'nu', ''),
(391, 'Care dintre autovehicule va intra ultimul în intersec&#355;ie?', 'uploads/126.jpg', '<p id=\"v_a\"><b>a. </b> autoturismul;\r\n</p><p id=\"v_b\"><b>b. </b> autobuzul;\r\n</p><p id=\"v_c\"><b>c. </b> autocamionul.</p>', 'nu', 'da', 'nu', ''),
(392, 'Autoturismul semnalizeaz&#259; inten&#355;ia de a se pune în mi&#351;care. Pute&#355;i efectua dep&#259;&#351;irea?', 'uploads/701.jpg', '<p id=\"v_a\"><b>a. </b> nu, deoarece a semnalizat corect;\r\n</p><p id=\"v_b\"><b>b. </b> nu, vehiculele care pleac&#259; de pe loc au prioritate;\r\n</p><p id=\"v_c\"><b>c. </b> da, proceda&#355;i regulamentar.</p>', 'nu', 'nu', 'da', ''),
(393, 'Care este principalul factor de risc pe care trebuie s&#259;-l ave&#355;i în vedere la conducerea autovehiculului prin tunele rutiere?', '', '<p id=\"v_a\"><b>a. </b> viteza redus&#259; de deplasare &#351;i mersul în coloan&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> dificult&#259;&#355;i în adaptarea vederii la intrarea &#351;i la ie&#351;irea din asemenea pasaje;\r\n</p><p id=\"v_c\"><b>c. </b> posibilitatea existen&#355;ei unor denivel&#259;ri ale p&#259;r&#355;ii carosabile.</p>', 'nu', 'da', 'nu', ''),
(394, 'Dac&#259; r&#259;mâne&#355;i în pan&#259; într-un tunel:', '', '<p id=\"v_a\"><b>a. </b> opri&#355;i func&#355;ionarea motorului;\r\n</p><p id=\"v_b\"><b>b. </b> motorul trebuie s&#259; func&#355;ioneze, pentru a putea folosi luminile de avarie;\r\n</p><p id=\"v_c\"><b>c. </b> închide&#355;i autovehiculul &#351;i v&#259; îndep&#259;rta&#355;i de acesta.</p>', 'da', 'nu', 'nu', ''),
(395, 'Pute&#355;i schimba pozi&#355;ia autovehiculului, dac&#259; sunte&#355;i implicat într-un accident de circula&#355;ie?', '', '<p id=\"v_a\"><b>a. </b> da, dup&#259; ce a&#355;i acordat primul ajutor victimelor;\r\n</p><p id=\"v_b\"><b>b. </b> da, dup&#259; ce a&#355;i anun&#355;at poli&#355;ia;\r\n</p><p id=\"v_c\"><b>c. </b> da, dac&#259; accidentul s-a soldat numai cu avarii.</p>', 'nu', 'nu', 'da', ''),
(396, 'Întoarcerea este interzis&#259;:', '', '<p id=\"v_a\"><b>a. </b> pe poduri &#351;i sub poduri;\r\n</p><p id=\"v_b\"><b>b. </b> pe drumurile na&#355;ionale;\r\n</p><p id=\"v_c\"><b>c. </b> la mai pu&#355;in de 50 m de intrarea în sediile institu&#355;iilor.</p>', 'da', 'nu', 'nu', ''),
(397, 'Cum proceda&#355;i corect dac&#259; circula&#355;i înainte &#351;i semaforul indic&#259; lumin&#259; galben&#259; intermitent&#259;?', 'uploads/967.jpg', '<p id=\"v_a\"><b>a. </b> opri&#355;i, deoarece la urm&#259;toarea comutare va ap&#259;rea lumina ro&#351;ie;\r\n</p><p id=\"v_b\"><b>b. </b> reduce&#355;i viteza, iar dac&#259; se impune, acorda&#355;i prioritate;\r\n</p><p id=\"v_c\"><b>c. </b> opri&#355;i întotdeauna.</p>', 'nu', 'da', 'nu', ''),
(398, 'Tramvaiul poate fi dep&#259;&#351;it pe partea stâng&#259;?', '', '<p id=\"v_a\"><b>a. </b> nu;\r\n</p><p id=\"v_b\"><b>b. </b> da, în sta&#355;iile f&#259;r&#259; refugiu, pentru protejarea c&#259;l&#259;torilor care urc&#259; sau coboar&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> da, atunci când între &#351;ina din dreapta &#351;i bordur&#259; nu exist&#259; spa&#355;iu suficient pentru dep&#259;&#351;ire.</p>', 'nu', 'nu', 'da', ''),
(399, 'Viteza de deplasare trebuie s&#259; nu dep&#259;&#351;easc&#259; 30 km/h în localit&#259;&#355;i sau 50 km/h în afara localit&#259;&#355;ilor:', '', '<p id=\"v_a\"><b>a. </b> când vizibilitatea este sub 100 m în condi&#355;ii de cea&#355;&#259;, ploi toren&#355;iale, ninsori abundente;\r\n</p><p id=\"v_b\"><b>b. </b> când circula&#355;i pe poduri sau sub poduri;\r\n</p><p id=\"v_c\"><b>c. </b> la întâlnirea indicatorului „Prioritate fa&#355;&#259; de circula&#355;ia din sens invers“.</p>', 'da', 'nu', 'nu', ''),
(400, 'Cum se define&#351;te corect autovehiculul din categoria B?', '', '<p id=\"v_a\"><b>a. </b> autovehiculul care nu dep&#259;&#351;e&#351;te 5.000 kg, al c&#259;rui num&#259;r de locuri pe scaune, în afara conduc&#259;torului, este mai mic de 8;\r\n</p><p id=\"v_b\"><b>b. </b> autovehiculul care nu dep&#259;&#351;e&#351;te 3.500 kg, al c&#259;rui num&#259;r de locuri pe scaune, în afara conduc&#259;torului, nu este mai mare de 8;\r\n</p><p id=\"v_c\"><b>c. </b> autovehiculul care nu dep&#259;&#351;e&#351;te 3.500 kg, al c&#259;rui num&#259;r de locuri pe scaune, în afara conduc&#259;torului, nu este mai mare de 5.</p>', 'nu', 'da', 'nu', ''),
(401, 'Trebuie respectat&#259; semnifica&#355;ia unui indicator instalat pe partea stâng&#259; a drumului, orientat c&#259;tre conduc&#259;torii de vehicule care se apropie?', '', '<p id=\"v_a\"><b>a. </b> nu, deoarece se adreseaz&#259; circula&#355;iei din sens invers;\r\n</p><p id=\"v_b\"><b>b. </b> da, dar numai de c&#259;tre cei care circul&#259; pe banda cea mai apropiat&#259; de axul drumului;\r\n</p><p id=\"v_c\"><b>c. </b> da, de c&#259;tre to&#355;i conduc&#259;torii de vehicule care îl întâlnesc.</p>', 'nu', 'nu', 'da', ''),
(402, 'În ce ordine vor trece autovehiculele prin intersec&#355;ia prezentat&#259;?', 'uploads/1027.jpg', '<p id=\"v_a\"><b>a. </b> 4, 3, 1, 2;\r\n</p><p id=\"v_b\"><b>b. </b> 4, 3, 2, 1;\r\n</p><p id=\"v_c\"><b>c. </b> 3, 4, 1, 2.</p>', 'nu', 'nu', 'da', ''),
(403, 'Este permis&#259; dep&#259;&#351;irea vehiculelor în intersec&#355;ii?', '', '<p id=\"v_a\"><b>a. </b> nu;\r\n</p><p id=\"v_b\"><b>b. </b> da, dac&#259; intersec&#355;ia este dirijat&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> da, dar numai a bicicletelor &#351;i a mopedelor.</p>', 'nu', 'da', 'nu', ''),
(404, 'Vi se interzice circula&#355;ia cu autoturismul pe drumurile publice când:', '', '<p id=\"v_a\"><b>a. </b> zgomotul în mers sau în sta&#355;ionare dep&#259;&#351;e&#351;te limita legal&#259; admis&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> oglinzile retrovizoare sunt de dimensiuni mai mari;\r\n</p><p id=\"v_c\"><b>c. </b> dispozitivul de avertizare sonor&#259; nu este corespunz&#259;tor.</p>', 'da', 'nu', 'nu', ''),
(405, 'Cum trebuie s&#259; proceda&#355;i atunci când autovehiculul derapeaz&#259; cu spatele c&#259;tre dreapta?', '', '<p id=\"v_a\"><b>a. </b> frâna&#355;i puternic &#351;i redresa&#355;i vehiculul prin virare la stânga;\r\n</p><p id=\"v_b\"><b>b. </b> trage&#355;i rapid de volan spre stânga, f&#259;r&#259; a frâna;\r\n</p><p id=\"v_c\"><b>c. </b> elibera&#355;i pedala de accelera&#355;ie &#351;i vira&#355;i u&#351;or spre dreapta.</p>', 'nu', 'nu', 'da', ''),
(406, 'Conduc&#259;torilor de vehicule le este interzis în timpul mersului:', '', '<p id=\"v_a\"><b>a. </b> s&#259; fumeze;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; arunce orice fel de obiecte pe drumul public;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; se angajeze în discu&#355;ii cu ceilal&#355;i ocupan&#355;i ai autovehiculului.</p>', 'nu', 'da', 'nu', ''),
(407, 'Oprirea voluntar&#259; este interzis&#259;:', '', '<p id=\"v_a\"><b>a. </b> în intersec&#355;ii;\r\n</p><p id=\"v_b\"><b>b. </b> pe drumurile cu prioritate;\r\n</p><p id=\"v_c\"><b>c. </b> pe sectoarele de drum neiluminate.</p>', 'da', 'nu', 'nu', ''),
(408, 'În ce ordine vor trece prin intersec&#355;ie autovehiculele?', 'uploads/1052.jpg', '<p id=\"v_a\"><b>a. </b> autoturismul, motocicleta, autobuzul;\r\n</p><p id=\"v_b\"><b>b. </b> motocicleta, autobuzul, autoturismul;\r\n</p><p id=\"v_c\"><b>c. </b> motocicleta, autoturismul, autobuzul.</p>', 'nu', 'nu', 'da', ''),
(409, 'Sunte&#355;i obligat s&#259; circula&#355;i cu o vitez&#259; care s&#259; nu dep&#259;&#351;easc&#259; 30 km/h în localit&#259;&#355;i, în urm&#259;toarele situa&#355;ii:', '', '<p id=\"v_a\"><b>a. </b> în zona de ac&#355;iune a indicatorului de avertizare „Copii“, în intervalul orar 07.00-22.00;\r\n</p><p id=\"v_b\"><b>b. </b> într-o zon&#259; reziden&#355;ial&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> la schimbarea benzii de circula&#355;ie.</p>', 'da', 'nu', 'nu', ''),
(410, 'Inten&#355;iona&#355;i s&#259; remorca&#355;i un autoturism cu servodirec&#355;ie r&#259;mas în pan&#259; de motor. Ce trebuie s&#259; ave&#355;i în vedere?', '', '<p id=\"v_a\"><b>a. </b> bloca&#355;i direc&#355;ia;\r\n</p><p id=\"v_b\"><b>b. </b> manevrarea volanului solicit&#259; un efort fizic mai mare, de aceea ve&#355;i efectua tractarea cu deosebit&#259; aten&#355;ie;\r\n</p><p id=\"v_c\"><b>c. </b> nu ave&#355;i voie s&#259;-l remorca&#355;i în aceast&#259; situa&#355;ie.</p>', 'nu', 'da', 'nu', ''),
(411, 'Este interzis&#259; sta&#355;ionarea pe un drum public prev&#259;zut cu marcaj longitudinal continuu aplicat între sensurile de circula&#355;ie?', 'uploads/546.jpg', '<p id=\"v_a\"><b>a. </b> nu, dac&#259; nu oblig&#259; celelalte vehicule s&#259; treac&#259; peste marcajul continuu;\r\n</p><p id=\"v_b\"><b>b. </b> nu exist&#259; prevedere legal&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> da, deoarece este interzis prin lege.</p>', 'da', 'nu', 'nu', ''),
(412, 'Cursa liber&#259; a pedalei de frân&#259;, mai mic&#259; decât cea obi&#351;nuit&#259;, indic&#259;:', '', '<p id=\"v_a\"><b>a. </b> blocarea pedalei de frân&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> un joc insuficient între sabo&#355;i &#351;i tamburi;\r\n</p><p id=\"v_c\"><b>c. </b> pierderi de lichid pe la garnituri.</p>', 'nu', 'da', 'nu', ''),
(413, 'Cum proceda&#355;i în aceast&#259; situa&#355;ie, dac&#259; semafoarele nu func&#355;ioneaz&#259;?', 'uploads/910.jpg', '<p id=\"v_a\"><b>a. </b> acorda&#355;i prioritate de stânga;\r\n</p><p id=\"v_b\"><b>b. </b> acorda&#355;i prioritate de dreapta;\r\n</p><p id=\"v_c\"><b>c. </b> ave&#355;i prioritate.</p>', 'nu', 'da', 'nu', ''),
(414, 'Cum este sanc&#355;ionat conduc&#259;torul unui vehicul care a cumulat 15 puncte de penalizare?', '', '<p id=\"v_a\"><b>a. </b> cu amend&#259; contraven&#355;ional&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> cu suspendarea dreptului de a conduce autovehicule pentru o perioad&#259; de 30 de zile;\r\n</p><p id=\"v_c\"><b>c. </b> cu amend&#259; contraven&#355;ional&#259; &#351;i suspendarea exercit&#259;rii dreptului de a conduce, pentru o perioad&#259; de 60 de zile.</p>', 'nu', 'da', 'nu', ''),
(415, 'Cum este corect s&#259; proceda&#355;i, dac&#259; v&#259; afla&#355;i la volanul autoturismului ro&#351;u?', 'uploads/942.jpg', '<p id=\"v_a\"><b>a. </b> circulând spre stânga, v&#259; pute&#355;i angaja abia ultimul;\r\n</p><p id=\"v_b\"><b>b. </b> v&#259; angaja&#355;i primul în traversare;\r\n</p><p id=\"v_c\"><b>c. </b> permite&#355;i trecerea tramvaiului.</p>', 'nu', 'da', 'nu', ''),
(416, 'Ce semnific&#259; indicatorul împreun&#259; cu panoul adi&#355;ional?', 'uploads/498.jpg', '<p id=\"v_a\"><b>a. </b> sfâr&#351;itul zonei de ac&#355;iune a indicatorului „Sta&#355;ionarea interzis&#259;“;\r\n</p><p id=\"v_b\"><b>b. </b> sfâr&#351;itul zonei de ac&#355;iune a indicatorului „Oprirea interzis&#259;“;\r\n</p><p id=\"v_c\"><b>c. </b> începutul zonei în care este interzis&#259; oprirea.</p>', 'nu', 'da', 'nu', ''),
(417, 'Cauzele ce pot determina cre&#351;terea nivelului de ulei în baia de ungere sunt:', '', '<p id=\"v_a\"><b>a. </b> circula&#355;ia frecvent&#259; pe drumuri neasfaltate;\r\n</p><p id=\"v_b\"><b>b. </b> p&#259;trunderea apei sau a combustibilului în baia de ulei;\r\n</p><p id=\"v_c\"><b>c. </b> defectarea pompei de ulei.</p>', 'nu', 'da', 'nu', ''),
(418, 'Cu ce vitez&#259; ve&#355;i circula în condi&#355;ii de cea&#355;&#259;, ploi toren&#355;iale, ninsori abundente, dac&#259; vizibilitatea este redus&#259; sub 100 m?', '', '<p id=\"v_a\"><b>a. </b> maxim 50 km/h în localit&#259;&#355;i sau 60 km/h în afara localit&#259;&#355;ilor;\r\n</p><p id=\"v_b\"><b>b. </b> reduce&#355;i viteza pân&#259; la limita evit&#259;rii oric&#259;rui pericol;\r\n</p><p id=\"v_c\"><b>c. </b> maxim 30 km/h în localit&#259;&#355;i sau 50 km/h în afara localit&#259;&#355;ilor.</p>', 'nu', 'nu', 'da', ''),
(419, 'V&#259; este permis s&#259; claxona&#355;i un biciclist înainte de a-l dep&#259;&#351;i?', 'uploads/489.jpg', '<p id=\"v_a\"><b>a. </b> da, dac&#259; distan&#355;a este de peste 25 m;\r\n</p><p id=\"v_b\"><b>b. </b> nu;\r\n</p><p id=\"v_c\"><b>c. </b> numai în reprize foarte scurte.</p>', 'da', 'nu', 'nu', ''),
(420, 'În aceast&#259; situa&#355;ie trebuie s&#259; ceda&#355;i trecerea vehiculului din dreapta?', 'uploads/589.jpg', '<p id=\"v_a\"><b>a. </b> da, deoarece se aplic&#259; regula priorit&#259;&#355;ii de dreapta;\r\n</p><p id=\"v_b\"><b>b. </b> da, deoarece v&#259; intersecta&#355;i cu un drum prioritar;\r\n</p><p id=\"v_c\"><b>c. </b> nu, deoarece semaforul este pe culoarea verde.</p>', 'nu', 'nu', 'da', ''),
(421, 'Trecerea peste marcajul discontinuu este permis&#259;:', '', '<p id=\"v_a\"><b>a. </b> în orice situa&#355;ie, acesta având un caracter orientativ;\r\n</p><p id=\"v_b\"><b>b. </b> atunci când un asemenea marcaj nu delimiteaz&#259; cele dou&#259; sensuri de circula&#355;ie;\r\n</p><p id=\"v_c\"><b>c. </b> la schimbarea benzii de circula&#355;ie, a direc&#355;iei de mers sau la dep&#259;&#351;ire.</p>', 'nu', 'nu', 'da', ''),
(422, 'Accidentatul cu hemoragie extern&#259; trebuie a&#351;ezat:', '', '<p id=\"v_a\"><b>a. </b> în a&#351;a fel încât rana care sângereaz&#259; s&#259; fie mai jos decât planul corpului;\r\n</p><p id=\"v_b\"><b>b. </b> nu are importan&#355;&#259; dac&#259; rana este situat&#259; mai sus sau mai jos decât planul corpului;\r\n</p><p id=\"v_c\"><b>c. </b> în a&#351;a fel încât rana sângerând&#259; s&#259; fie mai sus decât nivelul corpului.</p>', 'nu', 'nu', 'da', ''),
(423, 'Ce v&#259; este interzis în situa&#355;ia dat&#259;?', 'uploads/497.jpg', '<p id=\"v_a\"><b>a. </b> indicatorul se adreseaz&#259; conduc&#259;torilor mijloacelor de transport public de persoane;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; circula&#355;i cu o vitez&#259; mai mic&#259; de 60 km/h;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; circula&#355;i cu o vitez&#259; mai mare de 60 km/h.</p>', 'nu', 'nu', 'da', ''),
(424, 'Care dintre conduc&#259;torii autovehiculelor execut&#259; corect dep&#259;&#351;irea tramvaiului aflat în mers?', 'uploads/1040.jpg', '<p id=\"v_a\"><b>a. </b> ambii conduc&#259;tori;\r\n</p><p id=\"v_b\"><b>b. </b> conduc&#259;torul motocicletei;\r\n</p><p id=\"v_c\"><b>c. </b> conduc&#259;torul autoturismului.</p>', 'da', 'nu', 'nu', ''),
(425, 'În care dintre situa&#355;ii se re&#355;ine certificatul de înmatriculare sau înregistrare?', '', '<p id=\"v_a\"><b>a. </b> când inspec&#355;ia tehnic&#259; periodic&#259; a expirat;\r\n</p><p id=\"v_b\"><b>b. </b> când claxonul nu func&#355;ioneaz&#259; la parametri optimi;\r\n</p><p id=\"v_c\"><b>c. </b> când motorul emite diverse zgomote.</p>', 'da', 'nu', 'nu', ''),
(426, 'Care este momentul în care pute&#355;i începe dep&#259;&#351;irea unui tramvai oprit într-o sta&#355;ie f&#259;r&#259; refugiu?', '', '<p id=\"v_a\"><b>a. </b> când c&#259;l&#259;torii au coborât din tramvai &#351;i au p&#259;r&#259;sit partea carosabil&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> acordând prioritate pietonilor &#351;i când u&#351;ile tramvaiului au fost închise;\r\n</p><p id=\"v_c\"><b>c. </b> când autovehiculul din fa&#355;&#259; s-a pus în mi&#351;care.</p>', 'nu', 'da', 'nu', ''),
(427, 'Ce obliga&#355;ii îi revin conduc&#259;torului auto când constat&#259;, noaptea, c&#259; lumina farurilor nu se mai aprinde?', '', '<p id=\"v_a\"><b>a. </b> s&#259; foloseasc&#259; luminile de pozi&#355;ie pân&#259; ajunge într-un loc de parcare unde poate remedia defec&#355;iunea;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; asigure func&#355;ionarea cel pu&#355;in a luminii de întâlnire din stânga a autovehiculului &#351;i a unei lumini de pozi&#355;ie din spate;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; circule cu aten&#355;ie pân&#259; la primul service auto.</p>', 'nu', 'da', 'nu', ''),
(428, 'Ce indicator urmeaz&#259; s&#259; fie întâlnit în intersec&#355;ia aflat&#259; la 100 m de indicatorul 1?', 'uploads/85.jpg', '<p id=\"v_a\"><b>a. </b> indicatorul 3;\r\n</p><p id=\"v_b\"><b>b. </b> indicatorul 2;\r\n</p><p id=\"v_c\"><b>c. </b> oricare dintre cele dou&#259; indicatoare.</p>', 'da', 'nu', 'nu', ''),
(429, 'În care dintre situa&#355;ii dep&#259;&#351;irea este interzis&#259;?', '', '<p id=\"v_a\"><b>a. </b> în intersec&#355;iile cu circula&#355;ie nedirijat&#259; &#351;i la trecerile pentru pietoni semnalizate;\r\n</p><p id=\"v_b\"><b>b. </b> în intersec&#355;ii &#351;i la o distan&#355;&#259; de 50 m de acestea;\r\n</p><p id=\"v_c\"><b>c. </b> în zona de ac&#355;iune a indicatorului „Limitare de vitez&#259;“.</p>', 'da', 'nu', 'nu', ''),
(430, 'Ce interdic&#355;ie au conduc&#259;torii de autovehicule atunci când circul&#259; pe drumuri acoperite de z&#259;pad&#259;, ghea&#355;&#259; sau polei?', '', '<p id=\"v_a\"><b>a. </b> de a circula pe sectoarele respective de drum f&#259;r&#259; autoriza&#355;ie de la administra&#355;ia drumurilor;\r\n</p><p id=\"v_b\"><b>b. </b> de a circula cu un autovehicul a c&#259;rui mas&#259; total&#259; maxim&#259; este mai mare de 3,5 tone, f&#259;r&#259; a avea montate pe ro&#355;i lan&#355;uri sau alte echipamente antiderapante omologate;\r\n</p><p id=\"v_c\"><b>c. </b> de a efectua dep&#259;&#351;iri.</p>', 'nu', 'da', 'nu', ''),
(431, 'Care este traseul optim pentru a întoarce în intersec&#355;ia prezentat&#259;?', 'uploads/608.jpg', '<p id=\"v_a\"><b>a. </b> traseul 1;\r\n</p><p id=\"v_b\"><b>b. </b> traseul 2;\r\n</p><p id=\"v_c\"><b>c. </b> traseul 3.</p>', 'nu', 'da', 'nu', ''),
(432, 'Când este obligat conduc&#259;torul unui autovehicul s&#259; opreasc&#259;?', '', '<p id=\"v_a\"><b>a. </b> la semnalul conduc&#259;torului grupurilor organizate;\r\n</p><p id=\"v_b\"><b>b. </b> la semnalul patrulelor &#351;colare de circula&#355;ie la trecerile pentru pietoni din apropierea unit&#259;&#355;ilor &#351;colare;\r\n</p><p id=\"v_c\"><b>c. </b> când se apropie de o intersec&#355;ie sau când este semnalizat cu farurile de un alt participant la trafic.</p>', 'nu', 'da', 'nu', ''),
(433, 'Care dintre autovehicule sta&#355;ioneaz&#259; gre&#351;it?', 'uploads/381.jpg', '<p id=\"v_a\"><b>a. </b> autocamionul;\r\n</p><p id=\"v_b\"><b>b. </b> autoutilitara;\r\n</p><p id=\"v_c\"><b>c. </b> ambele autovehicule.</p>', 'nu', 'nu', 'da', ''),
(434, 'Pentru care dintre situa&#355;iile de mai jos se dispune re&#355;inerea permisului de conducere?', '', '<p id=\"v_a\"><b>a. </b> pentru nerespectarea normelor legale referitoare la verificarea medical&#259; periodic&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> pentru acumularea a 10 puncte de penalizare;\r\n</p><p id=\"v_c\"><b>c. </b> când permisul de conducere prezint&#259; modific&#259;ri, &#351;ters&#259;turi sau ad&#259;ugiri.</p>', 'nu', 'nu', 'da', ''),
(435, 'Care dintre vehicule va trece primul prin intersec&#355;ie?', 'uploads/418.jpg', '<p id=\"v_a\"><b>a. </b> tramvaiul 1;\r\n</p><p id=\"v_b\"><b>b. </b> tramvaiul 2;\r\n</p><p id=\"v_c\"><b>c. </b> autoturismul.</p>', 'nu', 'nu', 'da', ''),
(436, 'Este permis&#259; oprirea în dreptul sta&#355;iei de tramvai prev&#259;zut&#259; cu refugiu pentru pietoni?', '', '<p id=\"v_a\"><b>a. </b> da, când tramvaiul nu se afl&#259; în sta&#355;ie;\r\n</p><p id=\"v_b\"><b>b. </b> da, deoarece în astfel de sta&#355;ii este permis&#259; dep&#259;&#351;irea;\r\n</p><p id=\"v_c\"><b>c. </b> nu.</p>', 'nu', 'nu', 'da', ''),
(437, 'Factorii principali care ajut&#259; un conduc&#259;tor auto s&#259; piloteze preventiv sunt:', '', '<p id=\"v_a\"><b>a. </b> atitudinea pozitiv&#259; în trafic, calmul, cuno&#351;tin&#355;ele dobândite &#351;i polite&#355;ea;\r\n</p><p id=\"v_b\"><b>b. </b> iscusin&#355;a &#351;i priceperea de a-&#351;i impune calit&#259;&#355;ile fizice dobândite;\r\n</p><p id=\"v_c\"><b>c. </b> mijloacele de semnalizare rutier&#259; &#351;i agen&#355;ii de poli&#355;ie rutier&#259;.</p>', 'da', 'nu', 'nu', ''),
(438, 'Camionul este sta&#355;ionat. Cine încalc&#259; legea dac&#259; încerca&#355;i s&#259;-l dep&#259;&#351;i&#355;i ?', 'uploads/709.jpg', '<p id=\"v_a\"><b>a. </b> conduc&#259;torul autocamionului;\r\n</p><p id=\"v_b\"><b>b. </b> dumneavoastr&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> atât conduc&#259;torul autocamionului, cât &#351;i dumneavoastr&#259;.</p>', 'da', 'nu', 'nu', ''),
(439, 'Ce obliga&#355;ii are conduc&#259;torul auto atunci când un poli&#355;ist îi solicit&#259; documentele?', '', '<p id=\"v_a\"><b>a. </b> s&#259; prezinte certificatul de înmatriculare &#351;i cartea de identitate a vehiculului;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; prezinte documentele personale;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; înmâneze documentele prev&#259;zute de lege.</p>', 'nu', 'nu', 'da', ''),
(440, 'Conduc&#259;torul unui vehicul este obligat s&#259; acorde prioritate de trecere într-o intersec&#355;ie nedirijat&#259; dac&#259;:', '', '<p id=\"v_a\"><b>a. </b> p&#259;trunde pe un drum jude&#355;ean, venind de pe un drum na&#355;ional;\r\n</p><p id=\"v_b\"><b>b. </b> p&#259;trunde pe un drum na&#355;ional, venind de pe un drum jude&#355;ean, comunal sau local;\r\n</p><p id=\"v_c\"><b>c. </b> p&#259;trunde pe un drum comunal sau local, venind de pe un drum jude&#355;ean.</p>', 'nu', 'da', 'nu', ''),
(441, 'Cum se recunoa&#351;te o fractur&#259; deschis&#259; la membre?', '', '<p id=\"v_a\"><b>a. </b> zona fracturat&#259; este tumefiat&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> pielea este str&#259;puns&#259; în zona fracturii;\r\n</p><p id=\"v_c\"><b>c. </b> accidentatul nu se poate mi&#351;ca.</p>', 'nu', 'da', 'nu', ''),
(442, 'Cum se sanc&#355;ioneaz&#259; nerespectarea semnalelor poli&#355;i&#351;tilor la trecerea coloanelor oficiale sau intercalarea într-o astfel de coloan&#259;?', '', '<p id=\"v_a\"><b>a. </b> cu amend&#259; penal&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> cu amend&#259; contraven&#355;ional&#259; &#351;i suspendarea exercit&#259;rii dreptului de a conduce autovehicule;\r\n</p><p id=\"v_c\"><b>c. </b> cu amend&#259; contraven&#355;ional&#259; &#351;i puncte de penalizare.</p>', 'nu', 'da', 'nu', ''),
(443, 'Pute&#355;i folosi o vitez&#259; mai mare decât cea legal&#259; pentru dep&#259;&#351;irea unui autovehicul?', '', '<p id=\"v_a\"><b>a. </b> da, dac&#259; circula&#355;i pe autostrad&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> da, dac&#259; v&#259; asigura&#355;i c&#259; din sens opus nu circul&#259; niciun alt vehicul;\r\n</p><p id=\"v_c\"><b>c. </b> nu.</p>', 'nu', 'nu', 'da', ''),
(444, 'În care dintre urm&#259;toarele situa&#355;ii sunte&#355;i obligat s&#259; circula&#355;i cu o vitez&#259; care s&#259; nu dep&#259;&#351;easc&#259; 30 km/h în localit&#259;&#355;i?', '', '<p id=\"v_a\"><b>a. </b> la trecerile la nivel cu linia de tramvai;\r\n</p><p id=\"v_b\"><b>b. </b> la trecerile la nivel cu calea ferat&#259; industrial&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> când partea carosabil&#259; este acoperit&#259; cu mâzg&#259;.</p>', 'nu', 'nu', 'da', ''),
(445, 'Certificatul de înmatriculare se re&#355;ine atunci când:', '', '<p id=\"v_a\"><b>a. </b> vehiculul nu are efectuat&#259; inspec&#355;ia tehnic&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> sunt aplicate ab&#355;ibilduri pe parbriz;\r\n</p><p id=\"v_c\"><b>c. </b> exist&#259; defec&#355;iuni la sistemul de climatizare.</p>', 'da', 'nu', 'nu', ''),
(446, 'Cum este corect s&#259; proceda&#355;i dac&#259; semafoarele func&#355;ioneaz&#259; cu lumina galben&#259; intermitent&#259;?', 'uploads/1061.jpg', '<p id=\"v_a\"><b>a. </b> reduce&#355;i viteza &#351;i traversa&#355;i intersec&#355;ia;\r\n</p><p id=\"v_b\"><b>b. </b> reduce&#355;i viteza &#351;i ceda&#355;i trecerea;\r\n</p><p id=\"v_c\"><b>c. </b> opri&#355;i &#351;i ceda&#355;i trecerea.</p>', 'da', 'nu', 'nu', ''),
(447, 'Autoturismul alb din dreapta dvs. a oprit regulamentar?', 'uploads/51.jpg', '<p id=\"v_a\"><b>a. </b> da, deoarece semnul adi&#355;ional indic&#259; sfâr&#351;itul zonei unde oprirea este interzis&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> nu, deoarece se afl&#259; în zona de ac&#355;iune a indicatorului „Oprirea interzis&#259;“;\r\n</p><p id=\"v_c\"><b>c. </b> nu, deoarece indicatorul „Oprirea interzis&#259;“ nu permite oprirea la o distan&#355;&#259; mai mic&#259; de 100 m de acesta.</p>', 'nu', 'da', 'nu', ''),
(448, 'Ce categorii de vehicule au obliga&#355;ia de a folosi în timpul zilei luminile de întâlnire?', '', '<p id=\"v_a\"><b>a. </b> autovehiculele care tracteaz&#259; alte vehicule sau care transport&#259; produse periculoase;\r\n</p><p id=\"v_b\"><b>b. </b> autovehiculele ce se deplaseaz&#259; cu vitez&#259; mare;\r\n</p><p id=\"v_c\"><b>c. </b> autovehiculele pentru transport public de persoane.</p>', 'da', 'nu', 'nu', ''),
(449, 'Pentru care dintre faptele de mai jos se aplic&#259; m&#259;sura re&#355;inerii permisului de conducere?', '', '<p id=\"v_a\"><b>a. </b> pentru nerespectarea semnalelor, indica&#355;iilor &#351;i dispozi&#355;iilor poli&#355;istului rutier aflat în exercitarea atribu&#355;iilor de serviciu;\r\n</p><p id=\"v_b\"><b>b. </b> pentru nerespectarea dispozi&#355;iilor legale referitoare la oprire &#351;i sta&#355;ionare;\r\n</p><p id=\"v_c\"><b>c. </b> pentru înc&#259;lcarea normelor legale referitoare la semnalizarea vehiculelor.</p>', 'da', 'nu', 'nu', ''),
(450, 'Inspec&#355;ia tehnic&#259; periodic&#259; a autovehiculelor &#351;i a remorcilor trebuie efectuat&#259;:', '', '<p id=\"v_a\"><b>a. </b> anual, la orice service auto;\r\n</p><p id=\"v_b\"><b>b. </b> periodic, conform legii;\r\n</p><p id=\"v_c\"><b>c. </b> la cel mult doi ani, la o sta&#355;ie de inspec&#355;ie tehnic&#259; autorizat&#259;.</p>', 'nu', 'da', 'nu', ''),
(451, 'Cum proceda&#355;i în cazul unui accidentat care are puls, dar nu respir&#259;?', '', '<p id=\"v_a\"><b>a. </b> îi face&#355;i o resuscitare cardio-respiratorie;\r\n</p><p id=\"v_b\"><b>b. </b> îi da&#355;i câteva palme peste fa&#355;&#259;, pân&#259; î&#351;i revine;\r\n</p><p id=\"v_c\"><b>c. </b> începe&#355;i respira&#355;ia artificial&#259;.</p>', 'nu', 'nu', 'da', ''),
(452, 'La intersec&#355;ia nedirijat&#259; dintre un drum na&#355;ional &#351;i unul jude&#355;ean, comunal sau local, prioritatea de trecere se acord&#259;:', '', '<p id=\"v_a\"><b>a. </b> vehiculelor care circul&#259; pe drumul jude&#355;ean, comunal sau local;\r\n</p><p id=\"v_b\"><b>b. </b> vehiculelor care vin din dreapta;\r\n</p><p id=\"v_c\"><b>c. </b> vehiculelor care circul&#259; pe drumul na&#355;ional.</p>', 'nu', 'nu', 'da', ''),
(453, 'În ce situa&#355;ie se interzice circula&#355;ia autovehiculelor pe drumurile publice?', '', '<p id=\"v_a\"><b>a. </b> când pl&#259;cu&#355;ele cu num&#259;rul de înmatriculare sunt neconforme cu standardul;\r\n</p><p id=\"v_b\"><b>b. </b> când parbrizul prezint&#259; fisuri;\r\n</p><p id=\"v_c\"><b>c. </b> când acestea au montate dispozitive de detectare a prezen&#355;ei aparatelor radar.</p>', 'da', 'nu', 'nu', ''),
(454, 'Dac&#259; circula&#355;i într-o intersec&#355;ie cu sens giratoriu, acorda&#355;i prioritate:', '', '<p id=\"v_a\"><b>a. </b> niciunui vehicul;\r\n</p><p id=\"v_b\"><b>b. </b> vehiculelor care urmeaz&#259; s&#259; intre în intersec&#355;ie;\r\n</p><p id=\"v_c\"><b>c. </b> autobuzelor &#351;i troleibuzelor.</p>', 'da', 'nu', 'nu', ''),
(455, 'Cum trebuie s&#259; procedeze conduc&#259;torul unui autovehicul dac&#259; în timpul deplas&#259;rii apar probleme tehnice care fac imposibil&#259; continuarea c&#259;l&#259;toriei?', '', '<p id=\"v_a\"><b>a. </b> dac&#259; nu poate înl&#259;tura defec&#355;iunile, s&#259; se deplaseze la cea mai apropiat&#259; unitate de depanare;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; scoat&#259; autovehiculul în afara p&#259;r&#355;ii carosabile;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; asigure autovehiculul contra efrac&#355;iei &#351;i s&#259; plece dup&#259; ajutor.</p>', 'nu', 'da', 'nu', ''),
(456, 'Pe drumul public cu cel mult dou&#259; benzi pe sens &#351;i cu o a treia band&#259; pe care este amplasat&#259; linia tramvaiului lâng&#259; axa drumului, conduc&#259;torii de vehicule:', '', '<p id=\"v_a\"><b>a. </b> pot folosi banda pe care este amplasat&#259; linia tramvaiului, f&#259;r&#259; s&#259; incomodeze circula&#355;ia acestuia;\r\n</p><p id=\"v_b\"><b>b. </b> nu pot folosi banda pe care este amplasat&#259; linia tramvaiului;\r\n</p><p id=\"v_c\"><b>c. </b> legal, nu este prev&#259;zut.</p>', 'da', 'nu', 'nu', ''),
(457, 'V&#259; este permis s&#259; dep&#259;&#351;i&#355;i bicicli&#351;tii?', 'uploads/367.jpg', '<p id=\"v_a\"><b>a. </b> da, dac&#259; prin claxonare bicicli&#351;tii sunt determina&#355;i s&#259; se angajeze cât mai spre dreapta;\r\n</p><p id=\"v_b\"><b>b. </b> nu, pentru c&#259; din cauza circula&#355;iei din sens opus nu se poate p&#259;stra o distan&#355;&#259; lateral&#259; corespunz&#259;toare;\r\n</p><p id=\"v_c\"><b>c. </b> bicicli&#351;tii pot fi dep&#259;&#351;i&#355;i f&#259;r&#259; restric&#355;ii.</p>', 'nu', 'da', 'nu', ''),
(458, 'Cum ve&#355;i proceda corect atunci când circula&#355;i pe timpul nop&#355;ii, în condi&#355;ii atmosferice ce împiedic&#259; mult vizibilitatea?', '', '<p id=\"v_a\"><b>a. </b> opri&#355;i &#351;i a&#351;tepta&#355;i modificarea condi&#355;iilor atmosferice;\r\n</p><p id=\"v_b\"><b>b. </b> folosi&#355;i luminile de drum &#351;i circula&#355;i cât mai aproape de marginea din dreapta a drumului;\r\n</p><p id=\"v_c\"><b>c. </b> folosi&#355;i luminile de întâlnire &#351;i semnaliza&#355;i prin schimbarea luminii de întâlnire cu cea de drum, la trecerea prin intersec&#355;iile cu circula&#355;ie nedirijat&#259;.</p>', 'nu', 'nu', 'da', ''),
(459, 'Cum poate fi men&#355;inut&#259; luminozitatea farurilor în condi&#355;ii meteorologice nefavorabile?', '', '<p id=\"v_a\"><b>a. </b> prin reglarea periodic&#259; a fasciculului luminos;\r\n</p><p id=\"v_b\"><b>b. </b> p&#259;strând curate permanent farurile;\r\n</p><p id=\"v_c\"><b>c. </b> folosind numai lumina de drum.</p>', 'nu', 'da', 'nu', ''),
(460, 'Bra&#355;ul stâng, întins orizontal, al poli&#355;istului semnific&#259; oprire pentru:', '', '<p id=\"v_a\"><b>a. </b> vehiculele care vin din fa&#355;&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> vehiculele care vin din spate;\r\n</p><p id=\"v_c\"><b>c. </b> vehiculele care vin din fa&#355;&#259; &#351;i din spate.</p>', 'nu', 'nu', 'da', ''),
(461, 'Este permis&#259; dep&#259;&#351;irea când din sens opus se apropie un alt autovehicul?', '', '<p id=\"v_a\"><b>a. </b> da, dac&#259; cel care se apropie din sens opus a redus viteza de deplasare pentru a facilita dep&#259;&#351;irea;\r\n</p><p id=\"v_b\"><b>b. </b> nu, dac&#259; cel care se apropie din sens opus este obligat s&#259; efectueze manevre de evitare a coliziunii;\r\n</p><p id=\"v_c\"><b>c. </b> da, dac&#259; spa&#355;iul lateral este suficient.</p>', 'nu', 'da', 'nu', ''),
(462, 'V&#259; pute&#355;i apropia de vehiculul din fa&#355;&#259;?', 'uploads/799.jpg', '<p id=\"v_a\"><b>a. </b> da;\r\n</p><p id=\"v_b\"><b>b. </b> nu, pentru c&#259; astfel bloca&#355;i intersec&#355;ia;\r\n</p><p id=\"v_c\"><b>c. </b> legea nu prevede obliga&#355;ii în aceast&#259; situa&#355;ie.</p>', 'nu', 'da', 'nu', ''),
(463, 'Este permis&#259; remorcarea unui autovehicul pe autostrad&#259;?', '', '<p id=\"v_a\"><b>a. </b> nu, legea interzice în mod expres acest lucru;\r\n</p><p id=\"v_b\"><b>b. </b> da, dac&#259; ansamblul astfel format poate dep&#259;&#351;i viteza de 50 km/h;\r\n</p><p id=\"v_c\"><b>c. </b> da, dac&#259; autovehiculul remorcat este un autoturism.</p>', 'nu', 'da', 'nu', ''),
(464, 'Cum procedeaz&#259; conduc&#259;torul de autovehicule noaptea, când este orbit de lumina farurilor unui autovehicul care circul&#259; din sens opus?', '', '<p id=\"v_a\"><b>a. </b> pune sau men&#355;ine în func&#355;iune faza de drum, pentru a observa mai bine drumul pe direc&#355;ia de deplasare;\r\n</p><p id=\"v_b\"><b>b. </b> aprinde alternativ faza de drum &#351;i de întâlnire, pentru a-l aten&#355;iona pe cel&#259;lalt conduc&#259;tor c&#259; circul&#259; incorect;\r\n</p><p id=\"v_c\"><b>c. </b> men&#355;ine în func&#355;iune luminile de întâlnire, reduce viteza de deplasare &#351;i opre&#351;te dac&#259; este cazul.</p>', 'nu', 'nu', 'da', ''),
(465, 'Dac&#259; un drum public este neiluminat, este permis&#259; sta&#355;ionarea vehiculelor pe partea carosabil&#259;?', '', '<p id=\"v_a\"><b>a. </b> da, dac&#259; sunt aprinse luminile de pozi&#355;ie sau de sta&#355;ionare;\r\n</p><p id=\"v_b\"><b>b. </b> da, dar numai în interiorul localit&#259;&#355;ilor;\r\n</p><p id=\"v_c\"><b>c. </b> nu.</p>', 'da', 'nu', 'nu', ''),
(466, 'Pentru a circula pe un drum cu polei, se recomand&#259; s&#259; echipa&#355;i autoturismul cu:', 'uploads/548.jpg', '<p id=\"v_a\"><b>a. </b> lan&#355;uri pentru z&#259;pad&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> pneuri speciale sau lan&#355;uri antiderapante;\r\n</p><p id=\"v_c\"><b>c. </b> lopat&#259; &#351;i nisip.</p>', 'nu', 'da', 'nu', ''),
(467, 'Temperatura lichidului de r&#259;cire care asigur&#259; performan&#355;a maxim&#259; &#351;i uzura minim&#259; a motorului este cuprins&#259; în intervalul:', '', '<p id=\"v_a\"><b>a. </b> 100-120 grade C;\r\n</p><p id=\"v_b\"><b>b. </b> 60-70 grade C;\r\n</p><p id=\"v_c\"><b>c. </b> 90-95 grade C.</p>', 'nu', 'nu', 'da', ''),
(468, 'Care dintre autoturisme s-a încadrat corect pentru a schimba direc&#355;ia de mers?', 'uploads/828.jpg', '<p id=\"v_a\"><b>a. </b> autoturismul ro&#351;u;\r\n</p><p id=\"v_b\"><b>b. </b> autoturismul albastru;\r\n</p><p id=\"v_c\"><b>c. </b> ambele autoturisme.</p>', 'nu', 'da', 'nu', ''),
(469, 'C&#259;rui autoturism trebuie s&#259;-i acorda&#355;i prioritate?', 'uploads/740.jpg', '<p id=\"v_a\"><b>a. </b> niciunui autoturism;\r\n</p><p id=\"v_b\"><b>b. </b> autoturismului ro&#351;u;\r\n</p><p id=\"v_c\"><b>c. </b> autoturismului verde.</p>', 'nu', 'nu', 'da', ''),
(470, 'Este regulamentar s&#259; dep&#259;&#351;i&#355;i autobuzul oprit în sta&#355;ie?', 'uploads/954.jpg', '<p id=\"v_a\"><b>a. </b> da, dac&#259; v&#259; asigura&#355;i &#351;i semnaliza&#355;i;\r\n</p><p id=\"v_b\"><b>b. </b> da, numai dac&#259; autobuzul are refugiu pentru pietoni;\r\n</p><p id=\"v_c\"><b>c. </b> nu, pân&#259; la plecarea autobuzului din sta&#355;ie.</p>', 'da', 'nu', 'nu', ''),
(471, 'Viteza va fi redus&#259; pân&#259; la limita legal&#259; în urm&#259;toarele situa&#355;ii:', '', '<p id=\"v_a\"><b>a. </b> în perimetrul pie&#355;elor aglomerate;\r\n</p><p id=\"v_b\"><b>b. </b> la întâlnirea indicatorului „Oprire“;\r\n</p><p id=\"v_c\"><b>c. </b> când partea carosabil&#259; este acoperit&#259; cu polei, ghea&#355;&#259;, z&#259;pad&#259; b&#259;t&#259;torit&#259;, mâzg&#259; sau piatr&#259; cubic&#259; umed&#259;.</p>', 'nu', 'nu', 'da', ''),
(472, 'Indicatorul v&#259; avertizeaz&#259; c&#259; urmeaz&#259;:', 'uploads/413.jpg', '<p id=\"v_a\"><b>a. </b> o coborâre periculoas&#259;, care începe la o distan&#355;&#259; de 200 m de la indicator;\r\n</p><p id=\"v_b\"><b>b. </b> o coborâre periculoas&#259;, pe o distan&#355;&#259; de 200 m;\r\n</p><p id=\"v_c\"><b>c. </b> o succesiune de curbe periculoase, la o distan&#355;&#259; de 200 m de la indicator.</p>', 'da', 'nu', 'nu', ''),
(473, 'Circula&#355;ia pe banda rezervat&#259; transportului public de persoane, semnalizat&#259; ca atare, este permis&#259;:', '', '<p id=\"v_a\"><b>a. </b> vehiculelor care efectueaz&#259; transport public de persoane &#351;i autovehiculelor cu regim de circula&#355;ie prioritar, atunci când se deplaseaz&#259; în ac&#355;iuni de interven&#355;ie sau în misiuni care au caracter de urgen&#355;&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> numai vehiculelor care efectueaz&#259; transport public de persoane;\r\n</p><p id=\"v_c\"><b>c. </b> tuturor vehiculelor, dac&#259; nu incomodeaz&#259; circula&#355;ia vehiculelor prioritare.</p>', 'da', 'nu', 'nu', ''),
(474, 'Ce se întâmpl&#259; atunci când, pe ro&#355;ile unei osii, eficien&#355;a frân&#259;rii este mult diferit&#259;?', '', '<p id=\"v_a\"><b>a. </b> se reduce viteza de deplasare;\r\n</p><p id=\"v_b\"><b>b. </b> vehiculul î&#351;i men&#355;ine &#355;inuta de drum, în ciuda acestui fapt;\r\n</p><p id=\"v_c\"><b>c. </b> vehiculul poate derapa lateral.</p>', 'nu', 'nu', 'da', ''),
(475, 'Cum ve&#355;i proceda în intersec&#355;ia care urmeaz&#259;?', 'uploads/162.jpg', '<p id=\"v_a\"><b>a. </b> acorda&#355;i prioritate numai vehiculelor care vin din stânga;\r\n</p><p id=\"v_b\"><b>b. </b> acorda&#355;i prioritate numai vehiculelor care vin din dreapta;\r\n</p><p id=\"v_c\"><b>c. </b> acorda&#355;i prioritate celor în drept.</p>', 'nu', 'nu', 'da', ''),
(476, 'În ce ordine vor circula autovehiculele prin intersec&#355;ia prezentat&#259;?', 'uploads/1053.jpg', '<p id=\"v_a\"><b>a. </b> troleibuzul, tramvaiul, autoturismul;\r\n</p><p id=\"v_b\"><b>b. </b> tramvaiul, autoturismul, troleibuzul;\r\n</p><p id=\"v_c\"><b>c. </b> tramvaiul, troleibuzul, autoturismul.</p>', 'nu', 'da', 'nu', ''),
(477, 'La intersec&#355;iile f&#259;r&#259; marcaje, conduc&#259;torii auto care vor s&#259; circule pe direc&#355;ia înainte vor ocupa:', '', '<p id=\"v_a\"><b>a. </b> rândul din stânga;\r\n</p><p id=\"v_b\"><b>b. </b> rândul din dreapta;\r\n</p><p id=\"v_c\"><b>c. </b> oricare dintre rânduri.</p>', 'nu', 'nu', 'da', ''),
(478, 'Dac&#259; într-o intersec&#355;ie conduc&#259;torul de vehicul întâlne&#351;te semnalul verde al semaforului, precum &#351;i indicatorul „Cedeaz&#259; trecerea“, el este obligat:', '', '<p id=\"v_a\"><b>a. </b> s&#259; respecte semnifica&#355;ia semnalului verde;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; respecte semnifica&#355;ia indicatorului;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; respecte semnifica&#355;ia semnalului verde, acordând prioritate de trecere vehiculelor care circul&#259; pe drumul prioritar.</p>', 'da', 'nu', 'nu', ''),
(479, 'Când se folosesc luminile de avarie?', '', '<p id=\"v_a\"><b>a. </b> când se circul&#259; în coloan&#259;, cu vitez&#259; redus&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> când vehiculul se deplaseaz&#259; foarte lent, din motive obiective;\r\n</p><p id=\"v_c\"><b>c. </b> când se circul&#259; pe un drum alunecos.</p>', 'nu', 'da', 'nu', '');
INSERT INTO `intrebari` (`id`, `cerinta`, `imagine`, `variante`, `rasp_a`, `rasp_b`, `rasp_c`, `eticheta`) VALUES
(480, 'În situa&#355;ia dat&#259;, ave&#355;i prioritate fa&#355;&#259; de vehiculele care circul&#259; din sens opus?', 'uploads/606.jpg', '<p id=\"v_a\"><b>a. </b> nu;\r\n</p><p id=\"v_b\"><b>b. </b> da;\r\n</p><p id=\"v_c\"><b>c. </b> indicatorul se refer&#259; la faptul c&#259; circula&#355;ia se desf&#259;&#351;oar&#259; în ambele sensuri.</p>', 'da', 'nu', 'nu', ''),
(481, 'Cum trebuie s&#259; procedeze conduc&#259;torul unui autovehicul la trecerea la nivel cu calea ferat&#259;, atunci când barierele sunt ridicate?', '', '<p id=\"v_a\"><b>a. </b> s&#259; reduc&#259; viteza, f&#259;r&#259; a dep&#259;&#351;i linia continu&#259; pentru oprire;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; opreasc&#259; autovehiculul în locul unde vizibilitatea este maxim&#259;, f&#259;r&#259; a dep&#259;&#351;i indicatorul de prioritate în cruce;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; reduc&#259; viteza autovehiculului &#351;i s&#259; traverseze cu aten&#355;ie, asigurându-se c&#259; din partea stâng&#259; sau din partea dreapt&#259; nu se apropie un vehicul feroviar.</p>', 'nu', 'nu', 'da', ''),
(482, 'Pute&#355;i dep&#259;&#351;i în aceast&#259; situa&#355;ie?', 'uploads/918.jpg', '<p id=\"v_a\"><b>a. </b> nu, deoarece v&#259; afla&#355;i în zona unei treceri la nivel cu calea ferat&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> nu, deoarece v&#259; afla&#355;i la mai pu&#355;in de 150 m de calea ferat&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> da, este regulamentar.</p>', 'nu', 'nu', 'da', ''),
(483, 'Ce m&#259;suri ia conduc&#259;torul de autovehicul, angajat într-un accident cu avarii u&#351;oare?', '', '<p id=\"v_a\"><b>a. </b> a&#351;teapt&#259; sosirea poli&#355;istului din zon&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> scoate autovehiculul în afara p&#259;r&#355;ii carosabile &#351;i anun&#355;&#259; poli&#355;ia în termen de 24 de ore;\r\n</p><p id=\"v_c\"><b>c. </b> anun&#355;&#259; poli&#355;ia, f&#259;r&#259; a schimba pozi&#355;ia autovehiculului.</p>', 'nu', 'da', 'nu', ''),
(484, 'Un vehicul va fi imobilizat dac&#259;:', '', '<p id=\"v_a\"><b>a. </b> num&#259;rul de înmatriculare sau de înregistrare este fals;\r\n</p><p id=\"v_b\"><b>b. </b> starea tehnic&#259; a pneurilor este necorespunz&#259;toare;\r\n</p><p id=\"v_c\"><b>c. </b> conduc&#259;torul auto refuz&#259; s&#259; poarte centura de siguran&#355;&#259;.</p>', 'da', 'nu', 'nu', ''),
(485, 'Indicatorul interzice:', 'uploads/965.jpg', '<p id=\"v_a\"><b>a. </b> oprirea din dreptul lui pân&#259; la prima intersec&#355;ie;\r\n</p><p id=\"v_b\"><b>b. </b> sta&#355;ionarea din dreptul lui pân&#259; la prima intersec&#355;ie;\r\n</p><p id=\"v_c\"><b>c. </b> oprirea pe o distan&#355;&#259; de 50 m de la locul în care este instalat.</p>', 'nu', 'da', 'nu', ''),
(486, 'Autoturismul din imagine a parcat regulamentar?', 'uploads/694.jpg', '<p id=\"v_a\"><b>a. </b> da, deoarece este parcat în întregime pe trotuar;\r\n</p><p id=\"v_b\"><b>b. </b> nu, deoarece se afl&#259; în afara zonei în care parcarea este permis&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> da, deoarece respect&#259; atât semnifica&#355;ia indicatorului, cât &#351;i a semnului adi&#355;ional.</p>', 'nu', 'da', 'nu', ''),
(487, 'Ce obliga&#355;ii are conduc&#259;torul unui autovehicul sta&#355;ionat pe drumul public, dac&#259; se îndep&#259;rteaz&#259; de acesta?', '', '<p id=\"v_a\"><b>a. </b> s&#259; ac&#355;ioneze frâna de ajutor, s&#259; opreasc&#259; func&#355;ionarea motorului &#351;i s&#259; cupleze o treapt&#259; inferioar&#259; de vitez&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259;-i semnalizeze prezen&#355;a pe timp de noapte cu o surs&#259; de lumin&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; nu opreasc&#259; prea aproape de urm&#259;torul autovehicul.</p>', 'da', 'nu', 'nu', ''),
(488, 'Care este pozi&#355;ia corect&#259; a conduc&#259;torului auto în timpul conducerii?', '', '<p id=\"v_a\"><b>a. </b> s&#259; fie cât mai concentrat, s&#259; &#355;in&#259; volanul strâns &#351;i privirea îndreptat&#259; numai înainte &#351;i s&#259; stea cât mai aproape de parbriz;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; stea cât mai confortabil pe scaun, cu capul rezemat de tetier&#259; &#351;i cu ambele mâini pe volan, în zona orelor 10:00-14:00;\r\n</p><p id=\"v_c\"><b>c. </b> cea mai corect&#259; pozi&#355;ie în timpul conducerii auto este aceea care previne apari&#355;ia somnului.</p>', 'nu', 'da', 'nu', ''),
(489, 'Un autovehicul avariat nu mai poate circula dac&#259;:', '', '<p id=\"v_a\"><b>a. </b> are elemente de caroserie distruse;\r\n</p><p id=\"v_b\"><b>b. </b> au trecut mai mult de 30 de zile de la data producerii avariei;\r\n</p><p id=\"v_c\"><b>c. </b> au trecut mai mult de 3 zile de la constatare.</p>', 'nu', 'da', 'nu', ''),
(490, 'Când semaforul este înso&#355;it de lumina intermitent&#259; de culoare verde, sub forma unei s&#259;ge&#355;i pe fond negru c&#259;tre dreapta, ave&#355;i urm&#259;toarea obliga&#355;ie:', '', '<p id=\"v_a\"><b>a. </b> niciuna, indicatorul fiind de informare;\r\n</p><p id=\"v_b\"><b>b. </b> niciuna, întrucât indicatorul nu vi se adreseaz&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> permite trecerea numai în direc&#355;ia indicat&#259;, oricare ar fi în acel moment semnalul în func&#355;iune al semaforului.</p>', 'nu', 'nu', 'da', ''),
(491, 'Când poli&#355;istul se afl&#259; cu spatele la autovehiculul care se apropie &#351;i cu bra&#355;ul stâng întins orizontal, conduc&#259;torul acestuia trebuie:', '', '<p id=\"v_a\"><b>a. </b> s&#259; continue deplasarea, deoarece semnalul de oprire nu i se adreseaz&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; schimbe direc&#355;ia de mers spre stânga;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; opreasc&#259;, deoarece semnalul i se adreseaz&#259;.</p>', 'da', 'nu', 'nu', ''),
(492, 'La ie&#351;irea din zonele reziden&#355;iale sau pietonale, sunte&#355;i obligat s&#259; acorda&#355;i prioritate:', '', '<p id=\"v_a\"><b>a. </b> autovehiculelor;\r\n</p><p id=\"v_b\"><b>b. </b> pietonilor de pe sensul opus de circula&#355;ie;\r\n</p><p id=\"v_c\"><b>c. </b> tuturor vehiculelor cu care v&#259; intersecta&#355;i.</p>', 'nu', 'nu', 'da', ''),
(493, 'Conduc&#259;torul de vehicul are obliga&#355;ia de a ceda trecerea la schimbarea direc&#355;iei de mers spre stânga, în timp ce semnalul verde este în func&#355;iune?', '', '<p id=\"v_a\"><b>a. </b> nu, deoarece semnalul verde îi confer&#259; prioritate de trecere;\r\n</p><p id=\"v_b\"><b>b. </b> nu, cu excep&#355;ia cazului în care, din sens opus, se apropie un mijloc de transport public;\r\n</p><p id=\"v_c\"><b>c. </b> da, atât vehiculelor care circul&#259; din sens opus, cât &#351;i pietonilor care traverseaz&#259; strada pe care acesta urmeaz&#259; s&#259; intre, pe sensul s&#259;u de mers.</p>', 'nu', 'nu', 'da', ''),
(494, 'Cum trebuie s&#259; proceda&#355;i în situa&#355;ia dat&#259;, dac&#259; semnalele luminoase &#351;i sonore nu func&#355;ioneaz&#259;?', 'uploads/396.jpg', '<p id=\"v_a\"><b>a. </b> opri&#355;i f&#259;r&#259; a dep&#259;&#351;i calea ferat&#259;, v&#259; asigura&#355;i &#351;i v&#259; continua&#355;i deplasarea, dac&#259; nu se apropie trenul;\r\n</p><p id=\"v_b\"><b>b. </b> reduce&#355;i viteza &#351;i v&#259; continua&#355;i deplasarea, dup&#259; ce v&#259; asigura&#355;i c&#259; nu se apropie trenul;\r\n</p><p id=\"v_c\"><b>c. </b> opri&#355;i numai dac&#259; se apropie trenul.</p>', 'da', 'nu', 'nu', ''),
(495, 'Uzura prematur&#259; a pneurilor din fa&#355;&#259; se datoreaz&#259;, în primul rând:', '', '<p id=\"v_a\"><b>a. </b> deregl&#259;rii geometriei direc&#355;iei;\r\n</p><p id=\"v_b\"><b>b. </b> circula&#355;iei frecvente cu vitez&#259; redus&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> fenomenul este specific autovehiculelor care au trac&#355;iunea pe puntea din spate.</p>', 'da', 'nu', 'nu', ''),
(496, 'Pot fi dep&#259;&#351;ite autovehiculele pe partea dreapt&#259;?', '', '<p id=\"v_a\"><b>a. </b> nu, în nicio situa&#355;ie;\r\n</p><p id=\"v_b\"><b>b. </b> da, dac&#259; spa&#355;iul lateral dintre vehicule permite acest lucru;\r\n</p><p id=\"v_c\"><b>c. </b> da, în cazul vehiculelor care urmeaz&#259; s&#259; vireze la stânga, iar spa&#355;iul r&#259;mas permite dep&#259;&#351;irea prin dreapta.</p>', 'nu', 'nu', 'da', ''),
(497, 'Cum se pedepse&#351;te conducerea pe drumurile publice a unui autovehicul din categoria C, de c&#259;tre o persoan&#259; al c&#259;rei permis este valabil pentru categoria B?', '', '<p id=\"v_a\"><b>a. </b> penal;\r\n</p><p id=\"v_b\"><b>b. </b> contraven&#355;ional;\r\n</p><p id=\"v_c\"><b>c. </b> amend&#259; contraven&#355;ional&#259; &#351;i re&#355;inerea permisului de conducere.</p>', 'da', 'nu', 'nu', ''),
(498, 'La un accidentat cu corpi str&#259;ini în interiorul pl&#259;gilor, procedura corect&#259; este:', '', '<p id=\"v_a\"><b>a. </b> extragerea corpilor str&#259;ini din pl&#259;gi;\r\n</p><p id=\"v_b\"><b>b. </b> sp&#259;larea corpilor str&#259;ini cu ap&#259; curat&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> corpii str&#259;ini nu se extrag din pl&#259;gi, întrucât se pot produce complica&#355;ii.</p>', 'nu', 'nu', 'da', ''),
(499, 'Cum proceda&#355;i dac&#259; v-a&#355;i încadrat pe banda din stânga, de&#351;i inten&#355;iona&#355;i s&#259; vira&#355;i la dreapta?', 'uploads/365.jpg', '<p id=\"v_a\"><b>a. </b> semnaliza&#355;i, claxona&#355;i &#351;i vira&#355;i la dreapta;\r\n</p><p id=\"v_b\"><b>b. </b> vira&#355;i la stânga;\r\n</p><p id=\"v_c\"><b>c. </b> întrucât din sens opus nu circul&#259; alte vehicule, vira&#355;i accelerat la dreapta.</p>', 'nu', 'da', 'nu', ''),
(500, 'Printre primele cauze ale accidentelor rutiere figureaz&#259; &#351;i neaten&#355;ia în conducere. Cum poate fi evitat&#259; aceasta?', '', '<p id=\"v_a\"><b>a. </b> prin alternarea st&#259;rii de vigilen&#355;&#259; cu cea de relaxare la volan, îndeosebi pe sectoarele de drum în aliniament;\r\n</p><p id=\"v_b\"><b>b. </b> prin concentrarea permanent&#259; doar asupra activit&#259;&#355;ilor pe care trebuie s&#259; le efectua&#355;i pentru conducerea autovehiculului în condi&#355;ii de siguran&#355;&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> prin punerea în func&#355;iune a unor surse de muzic&#259; cu intensitate sonor&#259; mare, pentru stimularea vigilen&#355;ei în conducere.</p>', 'nu', 'da', 'nu', ''),
(501, 'Care dintre cele trei vehicule va trece ultimul prin intersec&#355;ie?', 'uploads/703.jpg', '<p id=\"v_a\"><b>a. </b> motocicleta;\r\n</p><p id=\"v_b\"><b>b. </b> autoturismul;\r\n</p><p id=\"v_c\"><b>c. </b> tramvaiul.</p>', 'nu', 'da', 'nu', ''),
(502, 'Pute&#355;i dep&#259;&#351;i tramvaiul oprit în sta&#355;ie?', 'uploads/347.jpg', '<p id=\"v_a\"><b>a. </b> nu, deoarece tramvaiul se dep&#259;&#351;e&#351;te prin partea stângã;\r\n</p><p id=\"v_b\"><b>b. </b> nu, deoarece în sta&#355;ie nu exist&#259; refugiu pentru pietoni;\r\n</p><p id=\"v_c\"><b>c. </b> da, deoarece între &#351;ina de tramvai &#351;i trotuar exist&#259; spa&#355;iu suficient pentru dep&#259;&#351;ire.</p>', 'nu', 'da', 'nu', ''),
(503, 'Care sunt sanc&#355;iunile contraven&#355;ionale?', '', '<p id=\"v_a\"><b>a. </b> amenda penal&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> amenda, &#351;i dup&#259; caz, aten&#355;ionarea;\r\n</p><p id=\"v_c\"><b>c. </b> amend&#259;, puncte de penalizare, precum &#351;i suspendarea exercit&#259;rii dreptului de a conduce autovehicule.</p>', 'nu', 'nu', 'da', ''),
(504, 'Care este semnifica&#355;ia indicatorului?', 'uploads/363.jpg', '<p id=\"v_a\"><b>a. </b> „Limitare de vitez&#259;“;\r\n</p><p id=\"v_b\"><b>b. </b> „Vitez&#259; minim&#259; obligatorie“;\r\n</p><p id=\"v_c\"><b>c. </b> „Vitez&#259; maxim&#259; obligatorie“.</p>', 'nu', 'da', 'nu', ''),
(505, 'Cum proceda&#355;i dac&#259;, într-o intersec&#355;ie, poli&#355;istul este orientat cu spatele c&#259;tre dvs. &#351;i cu bra&#355;ul stâng întins orizontal?', '', '<p id=\"v_a\"><b>a. </b> vira&#355;i la stânga;\r\n</p><p id=\"v_b\"><b>b. </b> continua&#355;i drumul;\r\n</p><p id=\"v_c\"><b>c. </b> reduce&#355;i viteza autovehiculului &#351;i opri&#355;i în spatele acestuia.</p>', 'nu', 'da', 'nu', ''),
(506, 'Conduc&#259;torul auto este obligat:', '', '<p id=\"v_a\"><b>a. </b> s&#259; nu conduc&#259; dup&#259; ce a consumat medicamente împotriva bolilor cronice;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; se prezinte la termen, la unitatea de poli&#355;ie care l-a citat, pentru solu&#355;ionarea oric&#259;rei probleme legate de calitatea sa de conduc&#259;tor;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; anun&#355;e imediat cea mai apropiat&#259; unitate de poli&#355;ie, dac&#259; s-a îmboln&#259;vit.</p>', 'nu', 'da', 'nu', ''),
(507, 'Pute&#355;i l&#259;sa noaptea, pe un drum public, remorca autoturismului pe care îl conduce&#355;i?', '', '<p id=\"v_a\"><b>a. </b> nu, întrucât în acest caz sta&#355;ionarea este interzis&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> da, deoarece remorca nu face parte din categoria autovehiculelor;\r\n</p><p id=\"v_c\"><b>c. </b> da, dac&#259; luminile de pozi&#355;ie sunt aprinse.</p>', 'da', 'nu', 'nu', ''),
(508, 'Cum proceda&#355;i când urmeaz&#259; s&#259; traversa&#355;i o trecere la nivel cu calea ferat&#259; simpl&#259;, f&#259;r&#259; bariere?', '', '<p id=\"v_a\"><b>a. </b> opri&#355;i, f&#259;r&#259; a dep&#259;&#351;i indicatorul în cruce, v&#259; angaja&#355;i &#351;i traversa&#355;i numai dup&#259; ce v-a&#355;i asigurat c&#259; nu exist&#259; niciun pericol;\r\n</p><p id=\"v_b\"><b>b. </b> reduce&#355;i viteza, v&#259; asigura&#355;i în stânga &#351;i în dreapta, dup&#259; care traversa&#355;i cu aten&#355;ie;\r\n</p><p id=\"v_c\"><b>c. </b> opri&#355;i sau, dac&#259; nu e cazul, doar reduce&#355;i viteza, v&#259; asigura&#355;i, traversa&#355;i calea ferat&#259;, dup&#259; care v&#259; continua&#355;i drumul.</p>', 'da', 'nu', 'nu', ''),
(509, 'Ce trebuie s&#259; ave&#355;i în vedere înaintea transport&#259;rii unei victime la spital?', '', '<p id=\"v_a\"><b>a. </b> ca func&#355;ia respiratorie &#351;i func&#355;ia circulatorie s&#259; fie asigurate;\r\n</p><p id=\"v_b\"><b>b. </b> ca punctul sanitar c&#259;tre care v&#259; îndrepta&#355;i s&#259; nu fie la o distan&#355;&#259; mai mare de 50 km;\r\n</p><p id=\"v_c\"><b>c. </b> ca autovehiculul cu care va fi transportat&#259; victima s&#259; asigure confortul necesar.</p>', 'da', 'nu', 'nu', ''),
(510, 'Întoarcerea unui autovehicul pe un drum presupune, înainte de efectuarea manevrei:', '', '<p id=\"v_a\"><b>a. </b> semnalizarea &#351;i asigurarea din fa&#355;&#259;, din spate &#351;i din lateral;\r\n</p><p id=\"v_b\"><b>b. </b> oprirea &#351;i semnalizarea inten&#355;iei de a întoarce;\r\n</p><p id=\"v_c\"><b>c. </b> asigurarea &#351;i încadrarea pe banda de lâng&#259; axul drumului.</p>', 'da', 'nu', 'nu', ''),
(511, 'Schimbarea direc&#355;iei de mers spre stânga, într-o intersec&#355;ie neprev&#259;zut&#259; cu marcaje de ghidare, se face:', '', '<p id=\"v_a\"><b>a. </b> prin ocolirea centrului imaginar al intersec&#355;iei;\r\n</p><p id=\"v_b\"><b>b. </b> prin stânga centrului imaginar al intersec&#355;iei, f&#259;r&#259; a intersecta traiectoria celor care se deplaseaz&#259; din sens opus &#351;i care vireaz&#259; la stânga;\r\n</p><p id=\"v_c\"><b>c. </b> la libera în&#355;elegere cu cei care se deplaseaz&#259; din sens opus.</p>', 'nu', 'da', 'nu', ''),
(512, 'În aceast&#259; situa&#355;ie constata&#355;i c&#259; p&#259;r&#259;si&#355;i autostrada în mod eronat. Cum proceda&#355;i?', 'uploads/780.jpg', '<p id=\"v_a\"><b>a. </b> reintra&#355;i imediat pe autostrad&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> v&#259; continua&#355;i drumul;\r\n</p><p id=\"v_c\"><b>c. </b> opri&#355;i, v&#259; asigura&#355;i &#351;i reintra&#355;i pe autostrad&#259;.</p>', 'nu', 'da', 'nu', ''),
(513, 'Substan&#355;ele antiseptice se folosesc pentru:', '', '<p id=\"v_a\"><b>a. </b> dezinfectarea r&#259;nilor;\r\n</p><p id=\"v_b\"><b>b. </b> calmarea durerilor;\r\n</p><p id=\"v_c\"><b>c. </b> oprirea hemoragiei.</p>', 'da', 'nu', 'nu', ''),
(514, 'Folosirea incorect&#259;, pe timp de noapte, a luminilor de drum la întâlnirea cu alt autovehicul care circul&#259; din sens opus, se sanc&#355;ioneaz&#259; cu:', '', '<p id=\"v_a\"><b>a. </b> suspendarea exercit&#259;rii dreptului de a conduce autovehicule, dac&#259; aceast&#259; contraven&#355;ie a fost s&#259;vâr&#351;it&#259; în mod repetat;\r\n</p><p id=\"v_b\"><b>b. </b> anularea permisului de conducere;\r\n</p><p id=\"v_c\"><b>c. </b> amend&#259; &#351;i puncte de penalizare.</p>', 'nu', 'nu', 'da', ''),
(515, 'În ce ordine vor trece autovehiculele prin intersec&#355;ie?', 'uploads/332.jpg', '<p id=\"v_a\"><b>a. </b> motocicleta, tramvaiul 1, tramvaiul 2, autocamionul;\r\n</p><p id=\"v_b\"><b>b. </b> motocicleta, autocamionul, tramvaiul 2, tramvaiul 1;\r\n</p><p id=\"v_c\"><b>c. </b> tramvaiul 1, tramvaiul 2, motocicleta, autocamionul.</p>', 'nu', 'da', 'nu', ''),
(516, 'Care dintre tipurile de semnalizare de mai jos vor fi respectate cu prioritate?', '', '<p id=\"v_a\"><b>a. </b> semnalele luminoase;\r\n</p><p id=\"v_b\"><b>b. </b> semnalizarea temporar&#259;, care modific&#259; regimul normal de desf&#259;&#351;urare a circula&#355;iei;\r\n</p><p id=\"v_c\"><b>c. </b> semnalele &#351;i indica&#355;iile poli&#355;istului.</p>', 'nu', 'nu', 'da', ''),
(517, 'Unde v&#259; este permis&#259; sta&#355;ionarea pe partea stâng&#259; în direc&#355;ia de mers?', '', '<p id=\"v_a\"><b>a. </b> pe str&#259;zile cu sens unic;\r\n</p><p id=\"v_b\"><b>b. </b> în nicio situa&#355;ie;\r\n</p><p id=\"v_c\"><b>c. </b> pe &#351;oselele cu band&#259; lateral&#259; consolidat&#259;.</p>', 'da', 'nu', 'nu', ''),
(518, 'Ce m&#259;suri se iau în cazul accident&#259;rii foarte grave a unei persoane, într-un accident de circula&#355;ie?', '', '<p id=\"v_a\"><b>a. </b> transportul imediat la cea mai apropiat&#259; unitate sanitar&#259;, cu orice mijloc de transport posibil;\r\n</p><p id=\"v_b\"><b>b. </b> transportul imediat pân&#259; la prima localitate &#351;i anun&#355;area ambulan&#355;ei;\r\n</p><p id=\"v_c\"><b>c. </b> acordarea primului ajutor, pân&#259; la sosirea ambulan&#355;ei.</p>', 'nu', 'nu', 'da', ''),
(519, 'Cum proceda&#355;i corect în aceast&#259; situa&#355;ie?', 'uploads/610.jpg', '<p id=\"v_a\"><b>a. </b> v&#259; angaja&#355;i în traversare înaintea autocamionului;\r\n</p><p id=\"v_b\"><b>b. </b> permite&#355;i trecerea autocamionului;\r\n</p><p id=\"v_c\"><b>c. </b> acorda&#355;i prioritate de dreapta.</p>', 'nu', 'da', 'nu', ''),
(520, 'Ce obliga&#355;ii ave&#355;i la semnalul membrilor patrulelor &#351;colare de circula&#355;ie, la trecerile pentru pietoni din apropierea unit&#259;&#355;ilor de înv&#259;&#355;&#259;mânt?', '', '<p id=\"v_a\"><b>a. </b> s&#259; reduce&#355;i viteza &#351;i s&#259; circula&#355;i cu aten&#355;ie;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; trece&#355;i pe banda de lâng&#259; axul drumului;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; opri&#355;i.</p>', 'nu', 'nu', 'da', ''),
(521, 'Care dintre indicatoare confer&#259; prioritatea de trecere?', 'uploads/8.jpg', '<p id=\"v_a\"><b>a. </b> indicatorul 1;\r\n</p><p id=\"v_b\"><b>b. </b> indicatorul 2;\r\n</p><p id=\"v_c\"><b>c. </b> indicatorul 3.</p>', 'da', 'nu', 'nu', ''),
(522, 'Preciza&#355;i când sunt scuti&#355;i instructorii auto de purtarea centurii de siguran&#355;&#259;:', '', '<p id=\"v_a\"><b>a. </b> în timpul orelor de preg&#259;tire a cursan&#355;ilor;\r\n</p><p id=\"v_b\"><b>b. </b> în permanen&#355;&#259;, când se afl&#259; în autovehiculele cu inscrip&#355;ia „&#350;COALA“;\r\n</p><p id=\"v_c\"><b>c. </b> legal, nu este prev&#259;zut.</p>', 'da', 'nu', 'nu', ''),
(523, 'Care este viteza cu care trebuie s&#259; v&#259; deplasa&#355;i pe un drum public umed sau acoperit cu mâzg&#259; sau z&#259;pad&#259;?', '', '<p id=\"v_a\"><b>a. </b> viteza prev&#259;zut&#259; de Regulament pentru categoria de drum pe care circula&#355;i în acel moment;\r\n</p><p id=\"v_b\"><b>b. </b> viteza care s&#259; v&#259; confere siguran&#355;a c&#259; pute&#355;i opri &#351;i evita orice eveniment rutier, f&#259;r&#259; a intra în derapaj;\r\n</p><p id=\"v_c\"><b>c. </b> în aceast&#259; situa&#355;ie nu trebuie s&#259; circula&#355;i cu mai mult de 20 km/h.</p>', 'nu', 'da', 'nu', ''),
(524, 'Sfâr&#351;itul zonei în care oprirea a fost interzis&#259; este anun&#355;at&#259; de:', 'uploads/149.jpg', '<p id=\"v_a\"><b>a. </b> ambele indicatoare;\r\n</p><p id=\"v_b\"><b>b. </b> indicatorul 1;\r\n</p><p id=\"v_c\"><b>c. </b> indicatorul 2, înso&#355;it de panoul adi&#355;ional.</p>', 'nu', 'nu', 'da', ''),
(525, 'La circula&#355;ia pe autostr&#259;zi, conduc&#259;torului auto îi este interzis:', '', '<p id=\"v_a\"><b>a. </b> s&#259; sta&#355;ioneze pe banda de urgen&#355;&#259;, cu excep&#355;ia cazurilor justificate;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; opreasc&#259; pentru a remedia o defec&#355;iune;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; dep&#259;&#351;easc&#259; alte autovehicule.</p>', 'da', 'nu', 'nu', ''),
(526, 'În cazul unei fracturi de clavicul&#259; (um&#259;rul se afl&#259; într-o pozi&#355;ie atârnând&#259;, u&#351;or înainte &#351;i în jos), se procedeaz&#259; astfel:', '', '<p id=\"v_a\"><b>a. </b> se a&#351;az&#259; bra&#355;ul într-o e&#351;arf&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> se aduce um&#259;rul în pozi&#355;ie normal&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> se las&#259; bra&#355;ul liber.</p>', 'da', 'nu', 'nu', ''),
(527, 'Este permis&#259; oprirea în situa&#355;ia dat&#259;?', 'uploads/500.jpg', '<p id=\"v_a\"><b>a. </b> da, deoarece sta&#355;ia de tramvai este prev&#259;zut&#259; cu refugiu pentru pietoni;\r\n</p><p id=\"v_b\"><b>b. </b> nu;\r\n</p><p id=\"v_c\"><b>c. </b> da, dac&#259; în sta&#355;ie nu se afl&#259; niciun tramvai.</p>', 'nu', 'da', 'nu', ''),
(528, 'Ce obliga&#355;ii v&#259; revin la trecerile pentru pietoni?', '', '<p id=\"v_a\"><b>a. </b> s&#259; circula&#355;i cu vitez&#259; redus&#259;, pentru a putea ocoli pietonii angaja&#355;i în traversare;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; reduce&#355;i viteza, dac&#259; sunt pietoni angaja&#355;i în traversare, pentru evitarea unui posibil accident;\r\n</p><p id=\"v_c\"><b>c. </b> dac&#259; sunt pietoni angaja&#355;i în traversare pe sensul dvs. de mers, s&#259; le acorda&#355;i prioritate.</p>', 'nu', 'nu', 'da', ''),
(529, 'Ce m&#259;suri de precau&#355;ie trebuie s&#259; lua&#355;i la utilizarea solu&#355;iei antigel?', '', '<p id=\"v_a\"><b>a. </b> nicio m&#259;sur&#259;, întrucât lichidul respectiv nu este nociv;\r\n</p><p id=\"v_b\"><b>b. </b> se va evita contactul cu aceasta, deoarece este toxic&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> nu se va combina cu apa, deoarece degaj&#259; mult&#259; c&#259;ldur&#259;.</p>', 'nu', 'da', 'nu', ''),
(530, 'Care sunt organele mobile ale mecanismului motor?', '', '<p id=\"v_a\"><b>a. </b> blocul motor, cilindrii, chiulasa &#351;i carterul;\r\n</p><p id=\"v_b\"><b>b. </b> pistonul, segmen&#355;ii, bol&#355;ul, biela, arborele cotit &#351;i volantul;\r\n</p><p id=\"v_c\"><b>c. </b> ro&#355;ile directoare &#351;i cele tractoare.</p>', 'nu', 'da', 'nu', ''),
(531, 'Ce semnifica&#355;ie are lumina de culoare ro&#351;ie a semaforului?', '', '<p id=\"v_a\"><b>a. </b> oblig&#259; atât conduc&#259;torii de vehicule cât &#351;i pietonii s&#259; se opreasc&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> îi oblig&#259; la oprire doar pe conduc&#259;torii auto care circul&#259; pe direc&#355;ia înainte, permi&#355;ând în unele situa&#355;ii virajul la dreapta, pietonilor fiindu-le interzis&#259; traversarea;\r\n</p><p id=\"v_c\"><b>c. </b> nu permite intrarea în intersec&#355;ie a autovehiculelor, ci doar traversarea str&#259;zii de c&#259;tre pietoni.</p>', 'da', 'nu', 'nu', ''),
(532, 'Conduc&#259;torul auto care circul&#259; pe banda de lâng&#259; acostament, în acela&#351;i sens cu autovehiculele din serviciile regulate de transport public de persoane, este obligat:', '', '<p id=\"v_a\"><b>a. </b> s&#259; nu dep&#259;&#351;easc&#259; un asemenea vehicul, când se afl&#259; oprit în sta&#355;ie;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; permit&#259; conduc&#259;torilor acestor vehicule s&#259; efectueze manevrele necesare repunerii în mi&#351;care din sta&#355;ie, dac&#259; este prev&#259;zut&#259; cu alveol&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; permit&#259; traversarea c&#259;l&#259;torilor pentru a se urca într-un asemenea vehicul.</p>', 'nu', 'da', 'nu', ''),
(533, 'Ce semnific&#259; panoul adi&#355;ional?', 'uploads/22.jpg', '<p id=\"v_a\"><b>a. </b> începutul zonei de ac&#355;iune a indicatorului „Sta&#355;ionarea interzis&#259;“;\r\n</p><p id=\"v_b\"><b>b. </b> începutul zonei de ac&#355;iune a indicatorului „Oprirea interzis&#259;“;\r\n</p><p id=\"v_c\"><b>c. </b> sta&#355;ionarea este interzis&#259; pân&#259; la indicator.</p>', 'nu', 'da', 'nu', ''),
(534, 'Când se aplic&#259; regulile conduitei preventive?', '', '<p id=\"v_a\"><b>a. </b> când conduce&#355;i efectiv autovehiculul pe drumurile publice;\r\n</p><p id=\"v_b\"><b>b. </b> când autovehiculul este oprit pe partea carosabil&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> atât în timpul conducerii efective, cât &#351;i atunci când autovehiculul este oprit.</p>', 'nu', 'nu', 'da', ''),
(535, 'La intersec&#355;ia nedirijat&#259; dintre un drum jude&#355;ean &#351;i unul comunal sau local, prioritatea de trecere se acord&#259;:', '', '<p id=\"v_a\"><b>a. </b> vehiculelor care circul&#259; pe drumul jude&#355;ean;\r\n</p><p id=\"v_b\"><b>b. </b> vehiculelor care circul&#259; pe drumul comunal sau local;\r\n</p><p id=\"v_c\"><b>c. </b> vehiculelor care vin din dreapta.</p>', 'da', 'nu', 'nu', ''),
(536, 'Sunte&#355;i obligat s&#259; nu dep&#259;&#351;i&#355;i viteza de 50 km/h în afara localit&#259;&#355;ilor, în urm&#259;toarele situa&#355;ii:', '', '<p id=\"v_a\"><b>a. </b> când partea carosabil&#259; este acoperit&#259; cu mâzg&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> la coborârea drumurilor în pant&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> la întâlnirea indicatorului „Prioritate pentru circula&#355;ia din sens invers“.</p>', 'da', 'nu', 'nu', ''),
(537, 'Care este rolul marcajelor rezonatoare?', '', '<p id=\"v_a\"><b>a. </b> dubleaz&#259; marcajele conven&#355;ionale în zonele periculoase;\r\n</p><p id=\"v_b\"><b>b. </b> avertizeaz&#259; conduc&#259;torii de autovehicule c&#259; dep&#259;&#351;esc zona de carosabil marcat&#259; &#351;i destinat&#259; sensului de deplasare;\r\n</p><p id=\"v_c\"><b>c. </b> avertizeaz&#259; conduc&#259;torii de autovehicule c&#259; vor p&#259;trunde într-o zon&#259; supravegheat&#259; cu aparatur&#259; radar.</p>', 'nu', 'da', 'nu', ''),
(538, 'Ce semnific&#259; indicatorul?', 'uploads/40.jpg', '<p id=\"v_a\"><b>a. </b> circula&#355;ia se desf&#259;&#351;oar&#259; pe mai multe benzi;\r\n</p><p id=\"v_b\"><b>b. </b> presemnalizarea traseului de urmat în cazul unei restric&#355;ii de circula&#355;ie;\r\n</p><p id=\"v_c\"><b>c. </b> selectarea circula&#355;iei pe direc&#355;ii de mers în apropierea unei intersec&#355;ii.</p>', 'nu', 'nu', 'da', ''),
(539, 'Care sunt factorii care fac ca motorul Diesel al unui autoturism s&#259; emit&#259; fum excesiv?', '', '<p id=\"v_a\"><b>a. </b> lipsa combustibilului;\r\n</p><p id=\"v_b\"><b>b. </b> bu&#351;onul rezervorului închis la maximum;\r\n</p><p id=\"v_c\"><b>c. </b> defec&#355;iuni la instala&#355;ia de injec&#355;ie.</p>', 'nu', 'nu', 'da', ''),
(540, 'În care dintre situa&#355;ii nu este permis&#259; m&#259;rirea vitezei de deplasare a autovehiculului?', '', '<p id=\"v_a\"><b>a. </b> când un alt vehicul s-a angajat în dep&#259;&#351;ire;\r\n</p><p id=\"v_b\"><b>b. </b> în zona de ac&#355;iune a indicatorului „Vitez&#259; recomandat&#259;“;\r\n</p><p id=\"v_c\"><b>c. </b> când acesta circul&#259; pe prima band&#259;, iar pe cea de lâng&#259; axul drumului se deplaseaz&#259; alte autovehicule, cu viteze superioare.</p>', 'da', 'nu', 'nu', ''),
(541, 'La imobilizarea cu atele, în cazul fracturilor, se va &#355;ine cont ca:', '', '<p id=\"v_a\"><b>a. </b> atela s&#259; nu fie rigid&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> nu sunt necesare criterii pentru imobilizarea fracturilor cu atele;\r\n</p><p id=\"v_c\"><b>c. </b> atela s&#259; fie suficient de lung&#259;.</p>', 'nu', 'nu', 'da', ''),
(542, 'Victima care a pierdut mult sânge trebuie s&#259; fie a&#351;ezat&#259;:', '', '<p id=\"v_a\"><b>a. </b> cu fa&#355;a în sus;\r\n</p><p id=\"v_b\"><b>b. </b> cu fa&#355;a în jos;\r\n</p><p id=\"v_c\"><b>c. </b> cu picioarele situate mai sus decât nivelul corpului.</p>', 'nu', 'nu', 'da', ''),
(543, 'În situa&#355;ia dat&#259; pute&#355;i continua dep&#259;&#351;irea?', 'uploads/620.jpg', '<p id=\"v_a\"><b>a. </b> da, dac&#259; conduc&#259;torul autocamionului reduce viteza;\r\n</p><p id=\"v_b\"><b>b. </b> nu, pentru c&#259; sunt trei benzi de circula&#355;ie;\r\n</p><p id=\"v_c\"><b>c. </b> da.</p>', 'nu', 'nu', 'da', ''),
(544, 'Care este viteza maxim&#259; prev&#259;zut&#259; de lege pentru circula&#355;ia în zonele reziden&#355;iale?', '', '<p id=\"v_a\"><b>a. </b> 5 km/h;\r\n</p><p id=\"v_b\"><b>b. </b> 10 km/h;\r\n</p><p id=\"v_c\"><b>c. </b> 20 km/h.</p>', 'nu', 'nu', 'da', ''),
(545, 'Mersul înapoi este interzis:', '', '<p id=\"v_a\"><b>a. </b> când vizibilitatea în spate nu este asigurat&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> nu exist&#259; prevedere legal&#259; în acest sens;\r\n</p><p id=\"v_c\"><b>c. </b> la mai pu&#355;in de 50 m de sta&#355;iile de autobuz &#351;i tramvai.</p>', 'da', 'nu', 'nu', ''),
(546, 'Care dintre autovehicule trebuie s&#259; acorde prioritate pietonilor?', 'uploads/774.jpg', '<p id=\"v_a\"><b>a. </b> autoturismul pe care îl conduce&#355;i;\r\n</p><p id=\"v_b\"><b>b. </b> autocamionul galben;\r\n</p><p id=\"v_c\"><b>c. </b> ambele autovehicule.</p>', 'da', 'nu', 'nu', ''),
(547, 'Care este traseul recomandat pentru a vira la stânga?', 'uploads/471.jpg', '<p id=\"v_a\"><b>a. </b> traseul 1;\r\n</p><p id=\"v_b\"><b>b. </b> traseul 2;\r\n</p><p id=\"v_c\"><b>c. </b> virajul la stânga este interzis.</p>', 'nu', 'da', 'nu', ''),
(548, 'Este permis&#259; p&#259;trunderea pe racordurile dintre cele dou&#259; p&#259;r&#355;i carosabile ale unei autostr&#259;zi?', '', '<p id=\"v_a\"><b>a. </b> nu;\r\n</p><p id=\"v_b\"><b>b. </b> da, pentru oprire în caz de urgen&#355;&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> da, pentru întoarcere, în zona intersec&#355;iilor.</p>', 'da', 'nu', 'nu', ''),
(549, 'Ce semnific&#259; indicatorul?', 'uploads/248.jpg', '<p id=\"v_a\"><b>a. </b> interzice circula&#355;ia în ambele sensuri a autovehiculelor;\r\n</p><p id=\"v_b\"><b>b. </b> interzice circula&#355;ia în ambele sensuri a motocicletelor &#351;i motoretelor;\r\n</p><p id=\"v_c\"><b>c. </b> interzice circula&#355;ia în ambele sensuri a tuturor vehiculelor.</p>', 'nu', 'nu', 'da', ''),
(550, 'Imobilizarea membrului inferior fracturat al unei victime într-un accident de circula&#355;ie se face:', '', '<p id=\"v_a\"><b>a. </b> prin bandajarea strâns&#259; a locului fracturii;\r\n</p><p id=\"v_b\"><b>b. </b> prin folosirea atelelor;\r\n</p><p id=\"v_c\"><b>c. </b> prin folosirea pansamentului compresiv.</p>', 'nu', 'da', 'nu', ''),
(551, 'Ce mijloace de semnalizare pot fi folosite pentru evitarea unui pericol imediat, în zona de ac&#355;iune a indicatorului „Claxonarea interzis&#259;”?', '', '<p id=\"v_a\"><b>a. </b> niciun mijloc de semnalizare;\r\n</p><p id=\"v_b\"><b>b. </b> atât mijloacele de avertizare sonor&#259;, cât &#351;i cele luminoase;\r\n</p><p id=\"v_c\"><b>c. </b> numai mijloacele de semnalizare luminoas&#259;.</p>', 'nu', 'da', 'nu', ''),
(552, 'Trecerea cu autovehiculul la semnalul luminos de culoare ro&#351;ie se sanc&#355;ioneaz&#259; astfel:', '', '<p id=\"v_a\"><b>a. </b> cu amend&#259; penal&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> cu amend&#259; contraven&#355;ional&#259; &#351;i puncte de penalizare;\r\n</p><p id=\"v_c\"><b>c. </b> cu amend&#259; contraven&#355;ional&#259; &#351;i suspendarea exercit&#259;rii dreptului de a conduce autovehiculul.</p>', 'nu', 'nu', 'da', ''),
(553, 'Imobilizarea unui bra&#355; fracturat se face:', '', '<p id=\"v_a\"><b>a. </b> în dreptul osului fracturat;\r\n</p><p id=\"v_b\"><b>b. </b> cuprinzând osul fracturat &#351;i articula&#355;ia superioar&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> cuprinzând osul fracturat &#351;i cele dou&#259; articula&#355;ii, respectiv cea superioar&#259; &#351;i inferioar&#259;.</p>', 'nu', 'nu', 'da', ''),
(554, 'În func&#355;ie de starea accidentatului, m&#259;surile de prim ajutor se vor lua în urm&#259;toarea ordine:', '', '<p id=\"v_a\"><b>a. </b> oprirea hemoragiei, pansarea r&#259;nilor, imobilizarea fracturilor, respira&#355;ia artificial&#259;, masajul cardiac;\r\n</p><p id=\"v_b\"><b>b. </b> respira&#355;ia artificial&#259;, masajul cardiac, oprirea hemoragiei, pansarea r&#259;nilor, imobilizarea fracturilor;\r\n</p><p id=\"v_c\"><b>c. </b> imobilizarea fracturilor, pansarea r&#259;nilor, respira&#355;ia artificial&#259;, masajul cardiac, oprirea hemoragiei.</p>', 'nu', 'da', 'nu', ''),
(555, 'Unde se întâlne&#351;te acest indicator?', 'uploads/362.jpg', '<p id=\"v_a\"><b>a. </b> în zonele de deal &#351;i de munte;\r\n</p><p id=\"v_b\"><b>b. </b> la începutul sectoarelor de drum acoperite de z&#259;pad&#259;, ghea&#355;&#259; sau polei;\r\n</p><p id=\"v_c\"><b>c. </b> pe toate drumurile lunecoase.</p>', 'nu', 'da', 'nu', ''),
(556, 'Cu ce vitez&#259; maxim&#259; pot circula în afara localit&#259;&#355;ilor autovehiculele din categoriile A &#351;i B?', '', '<p id=\"v_a\"><b>a. </b> 130 km/h pe autostr&#259;zi, 100 km/h pe drumurile expres sau na&#355;ionale europene &#351;i 90 km/h pe celelalte categorii de drumuri;\r\n</p><p id=\"v_b\"><b>b. </b> 110 km/h pe autostr&#259;zi, 90 km/h pe drumurile expres sau na&#355;ionale europene &#351;i 80 km/h pe celelalte categorii de drumuri;\r\n</p><p id=\"v_c\"><b>c. </b> 130 km/h pe autostr&#259;zi &#351;i 80 km/h pe celelalte categorii de drumuri.</p>', 'da', 'nu', 'nu', ''),
(557, 'Când circula&#355;i pe un drum na&#355;ional european v&#259; este interzis&#259;:', '', '<p id=\"v_a\"><b>a. </b> folosirea mijloacelor de averizare sonor&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> remorcarea altui autovehicul;\r\n</p><p id=\"v_c\"><b>c. </b> sta&#355;ionarea voluntar&#259; pe partea carosabil&#259;.</p>', 'nu', 'nu', 'da', ''),
(558, 'Ce le este interzis conduc&#259;torilor de autovehicule &#351;i de tramvaie?', '', '<p id=\"v_a\"><b>a. </b> s&#259; foloseasc&#259; telefoanele mobile în timpul mersului, f&#259;r&#259; s&#259; aib&#259; sistem de tip „mâini libere“;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; foloseasc&#259; luminile de avarie în localit&#259;&#355;i;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; se angajeze în discu&#355;ii cu al&#355;i pasageri.</p>', 'da', 'nu', 'nu', ''),
(559, 'Semnalul luminos intermitent de culoare alb&#259;, având caden&#355;a lent&#259;, func&#355;ioneaz&#259;:', '', '<p id=\"v_a\"><b>a. </b> pe timpul cât semnalele luminoase de culoare ro&#351;ie de la trecerea la nivel cu calea ferat&#259; sunt stinse;\r\n</p><p id=\"v_b\"><b>b. </b> pe timpul cât semnalele luminoase ale semaforului defect sunt stinse;\r\n</p><p id=\"v_c\"><b>c. </b> pe timpul cât barierele ori semibarierele sunt în curs de coborâre sau de ridicare.</p>', 'da', 'nu', 'nu', ''),
(560, 'Ce înseamn&#259; s&#259; conduce&#355;i preventiv pe timp nefavorabil?', '', '<p id=\"v_a\"><b>a. </b> s&#259; respecta&#355;i cu stricte&#355;e limitele de vitez&#259; pentru sectorul de drum pe care circula&#355;i;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; reduce&#355;i viteza pân&#259; la limita la care s&#259; v&#259; asigura&#355;i o bun&#259; vizibilitate &#351;i un control deplin asupra comenzilor;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; rula&#355;i cât mai aproape de axul drumului.</p>', 'nu', 'da', 'nu', ''),
(561, 'Autoturismul echipat cu o remorc&#259; poate circula pe autostrad&#259;?', '', '<p id=\"v_a\"><b>a. </b> nu;\r\n</p><p id=\"v_b\"><b>b. </b> da, dac&#259; ansamblul respectiv poate dep&#259;&#351;i viteza de 50 km/h;\r\n</p><p id=\"v_c\"><b>c. </b> da, dac&#259; a&#355;i ob&#355;inut autoriza&#355;ie de la poli&#355;ia rutier&#259;.</p>', 'nu', 'da', 'nu', ''),
(562, 'De ce trebuie reparat necondi&#355;ionat un e&#351;apament defect?', '', '<p id=\"v_a\"><b>a. </b> pentru c&#259; produce un zgomot prea mare;\r\n</p><p id=\"v_b\"><b>b. </b> nu este o urgen&#355;&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> pentru c&#259; destabilizeaz&#259; autovehiculul.</p>', 'da', 'nu', 'nu', ''),
(563, 'Ce reprezint&#259; simbolurile EURO I, EURO II, EURO III &#351;i EURO IV?', '', '<p id=\"v_a\"><b>a. </b> caracteristicile tehnice de provenien&#355;&#259; a unor vehicule din Uniunea European&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> standardele de poluare din Uniunea European&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> încadrarea autovehiculelor în diferite norme de consum.</p>', 'nu', 'da', 'nu', ''),
(564, 'Preciza&#355;i dac&#259; presiunea din pneuri are efect asupra consumului de carburant:', 'uploads/776.jpg', '<p id=\"v_a\"><b>a. </b> da;\r\n</p><p id=\"v_b\"><b>b. </b> nu;\r\n</p><p id=\"v_c\"><b>c. </b> numai în cazul vehiculelor de mare tonaj.</p>', 'da', 'nu', 'nu', ''),
(565, 'În care dintre urm&#259;toarele situa&#355;ii se anuleaz&#259; permisul de conducere?', '', '<p id=\"v_a\"><b>a. </b> în urma unei condamn&#259;ri, printr-o hot&#259;râre definitiv&#259;, pentru o infrac&#355;iune la regimul circula&#355;iei;\r\n</p><p id=\"v_b\"><b>b. </b> pentru înc&#259;lcarea dispozi&#355;iilor legale referitoare la folosirea luminilor de întâlnire pe timp de noapte, în afara localit&#259;&#355;ilor;\r\n</p><p id=\"v_c\"><b>c. </b> pentru înc&#259;lcarea dispozi&#355;iilor legale referitoare la obliga&#355;ia de a semnaliza schimbarea direc&#355;iei de mers.</p>', 'da', 'nu', 'nu', ''),
(566, 'Preciza&#355;i care vehicule sunt exceptate de la obliga&#355;ia acord&#259;rii priorit&#259;&#355;ii de trecere:', '', '<p id=\"v_a\"><b>a. </b> cele care se pun în mi&#351;care de pe loc fa&#355;&#259; de cele care circul&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> autovehiculele din serviciile regulate de transport public, care se pun în mi&#351;care din sta&#355;ia prev&#259;zut&#259; cu alveol&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> cele care ies din cur&#355;i, ganguri sau garaje.</p>', 'nu', 'da', 'nu', ''),
(567, 'Într-un pansament, pe plag&#259;, se aplic&#259;:', '', '<p id=\"v_a\"><b>a. </b> o compres&#259; de vat&#259; steril&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> o compres&#259; din tifon steril;\r\n</p><p id=\"v_c\"><b>c. </b> o compres&#259; oarecare.</p>', 'nu', 'da', 'nu', ''),
(568, 'Cum se semnalizeaz&#259;, noaptea, inten&#355;ia de a dep&#259;&#351;i alt vehicul, într-o localitate, pe un drum neiluminat?', '', '<p id=\"v_a\"><b>a. </b> cu faza de drum a luminii farurilor;\r\n</p><p id=\"v_b\"><b>b. </b> cu luminile indicatoare de direc&#355;ie;\r\n</p><p id=\"v_c\"><b>c. </b> prin folosirea combinat&#259; a fazei scurte &#351;i a semnalului sonor.</p>', 'nu', 'da', 'nu', ''),
(569, 'Cum se imobilizeaz&#259; fractura labei piciorului?', '', '<p id=\"v_a\"><b>a. </b> se suspend&#259; piciorul într-o ching&#259; rigid&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> cu atele sau cu un bandaj gros, de la degete pân&#259; la genunchi;\r\n</p><p id=\"v_c\"><b>c. </b> cu o fa&#351;&#259; de la glezn&#259; pân&#259; la &#351;old.</p>', 'nu', 'da', 'nu', ''),
(570, 'Ce m&#259;suri se recomand&#259; atunci când urmeaz&#259; s&#259; str&#259;bate&#355;i o por&#355;iune de drum public acoperit&#259; cu piatr&#259; cubic&#259;?', '', '<p id=\"v_a\"><b>a. </b> s&#259; folosi&#355;i numai frâna de ajutor pentru a evita derapajul;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; reduce&#355;i din timp viteza, evitând bruscarea comenzilor;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; circula&#355;i cât mai aproape de axul drumului, pentru a evita lovirea acostamentului.</p>', 'nu', 'da', 'nu', ''),
(571, 'Dac&#259; vre&#355;i s&#259; p&#259;r&#259;si&#355;i autostrada:', 'uploads/693.jpg', '<p id=\"v_a\"><b>a. </b> m&#259;ri&#355;i viteza;\r\n</p><p id=\"v_b\"><b>b. </b> circula&#355;i cu aceea&#351;i vitez&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> reduce&#355;i viteza.</p>', 'nu', 'nu', 'da', ''),
(572, 'Cum pute&#355;i folosi în condi&#355;ii optime capacitatea de frânare a motorului, atunci când coborâ&#355;i o pant&#259; lung&#259; &#351;i înclinat&#259;?', '', '<p id=\"v_a\"><b>a. </b> schimba&#355;i într-o treapt&#259; superioar&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> schimba&#355;i la timp într-o treapt&#259; inferioar&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> dup&#259; schimbarea vitezei, aduce&#355;i imediat maneta în „punctul mort“.</p>', 'nu', 'da', 'nu', ''),
(573, 'Într-o intersec&#355;ie unde circula&#355;ia se desf&#259;&#351;oar&#259; în sens giratoriu, conduc&#259;torilor auto le este interzis:', '', '<p id=\"v_a\"><b>a. </b> s&#259; reduc&#259; viteza, întrucât se creeaz&#259; pericolul de blocare;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; opreasc&#259; autovehiculele, în afara situa&#355;iilor impuse de trafic;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; întoarc&#259;, prin ocolirea sensului giratoriu.</p>', 'nu', 'da', 'nu', ''),
(574, 'Exist&#259; vreo excep&#355;ie de la regula dep&#259;&#351;irii pe partea stâng&#259;?', '', '<p id=\"v_a\"><b>a. </b> nu;\r\n</p><p id=\"v_b\"><b>b. </b> da, în cazul autovehiculului care inten&#355;ioneaz&#259; s&#259; vireze spre stânga, &#351;i în cazul tramvaiului;\r\n</p><p id=\"v_c\"><b>c. </b> da, numai în cazul în care dep&#259;&#351;irea se face pe o strad&#259; semnalizat&#259; cu indicatorul „Drum f&#259;r&#259; ie&#351;ire“.</p>', 'nu', 'da', 'nu', ''),
(575, 'Care este ordinea de trecere prin intersec&#355;ia prezentat&#259;?', 'uploads/444.jpg', '<p id=\"v_a\"><b>a. </b> motocicleta, autoturismul, autocamionul;\r\n</p><p id=\"v_b\"><b>b. </b> autocamionul, autoturismul, motocicleta;\r\n</p><p id=\"v_c\"><b>c. </b> autoturismul, motocicleta, autocamionul.</p>', 'nu', 'nu', 'da', ''),
(576, 'Cum asigura&#355;i un autoturism cu schimb&#259;tor de vitez&#259;, pentru ca acesta s&#259; nu se deplaseze din pant&#259;?', '', '<p id=\"v_a\"><b>a. </b> ac&#355;iona&#355;i frâna de serviciu;\r\n</p><p id=\"v_b\"><b>b. </b> aduce&#355;i maneta în „punctul mort“;\r\n</p><p id=\"v_c\"><b>c. </b> introduce&#355;i maneta într-o treapt&#259; inferioar&#259; &#351;i ac&#355;iona&#355;i frâna de ajutor sau sta&#355;ionare.</p>', 'nu', 'nu', 'da', ''),
(577, 'Ce comportament este corect în situa&#355;ia prezentat&#259;, dac&#259; v&#259; afla&#355;i la volanul autoturismului verde?', 'uploads/1048.jpg', '<p id=\"v_a\"><b>a. </b> intra&#355;i cu aten&#355;ie în intersec&#355;ie &#351;i vira&#355;i numai la stânga;\r\n</p><p id=\"v_b\"><b>b. </b> opri&#355;i &#351;i nu intra&#355;i în intersec&#355;ie;\r\n</p><p id=\"v_c\"><b>c. </b> ave&#355;i voie s&#259; vira&#355;i la dreapta.</p>', 'nu', 'da', 'nu', ''),
(578, 'În situa&#355;ia dat&#259;, ve&#355;i circula:', 'uploads/664.jpg', '<p id=\"v_a\"><b>a. </b> f&#259;r&#259; lumini;\r\n</p><p id=\"v_b\"><b>b. </b> numai cu luminile de pozi&#355;ie;\r\n</p><p id=\"v_c\"><b>c. </b> cu luminile de întâlnire.</p>', 'nu', 'nu', 'da', ''),
(579, 'Bandajele se folosesc:', '', '<p id=\"v_a\"><b>a. </b> pentru sus&#355;inerea p&#259;r&#355;ilor lezate;\r\n</p><p id=\"v_b\"><b>b. </b> pentru acoperirea &#351;i pentru sus&#355;inerea r&#259;nilor, precum &#351;i pentru oprirea hemoragiilor;\r\n</p><p id=\"v_c\"><b>c. </b> pentru imobilizarea fracturilor.</p>', 'nu', 'da', 'nu', ''),
(580, 'Ce trebuie s&#259; ave&#355;i în vedere la urcarea într-un autoturism?', '', '<p id=\"v_a\"><b>a. </b> s&#259; urca&#355;i numai pe partea dinspre trotuar;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; deschide&#355;i u&#351;a numai dac&#259; circula&#355;ia permite acest lucru;\r\n</p><p id=\"v_c\"><b>c. </b> urca&#355;i f&#259;r&#259; grij&#259;, deoarece vehiculele care circul&#259; din spate sunt obligate s&#259; a&#351;tepte.</p>', 'nu', 'da', 'nu', ''),
(581, 'Ce semnifica&#355;ie are un astfel de marcaj rutier?', 'uploads/661.jpg', '<p id=\"v_a\"><b>a. </b> deviere temporar&#259; a circula&#355;iei;\r\n</p><p id=\"v_b\"><b>b. </b> marcaj pe o band&#259; de accelerare;\r\n</p><p id=\"v_c\"><b>c. </b> marcaj pentru spa&#355;ii interzise.</p>', 'nu', 'da', 'nu', ''),
(582, 'Ave&#355;i posibilitatea de a circula pe drumurile publice cu autovehiculul avariat:', '', '<p id=\"v_a\"><b>a. </b> cel mult 10 zile de la data constat&#259;rii evenimentului;\r\n</p><p id=\"v_b\"><b>b. </b> pân&#259; la expirarea asigur&#259;rii obligatorii de r&#259;spundere civil&#259; auto;\r\n</p><p id=\"v_c\"><b>c. </b> cel mult 30 zile de la data producerii avariei.</p>', 'nu', 'nu', 'da', ''),
(583, 'Este permis&#259; folosirea luminilor de cea&#355;&#259; noaptea, dac&#259; cerul este senin?', 'uploads/730.jpg', '<p id=\"v_a\"><b>a. </b> Regulamentul nu precizeaz&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> da;\r\n</p><p id=\"v_c\"><b>c. </b> nu.</p>', 'nu', 'nu', 'da', ''),
(584, 'Sunte&#355;i corespunz&#259;tor încadrat pentru a folosi prima ie&#351;ire de pe autostrad&#259;?', 'uploads/566.jpg', '<p id=\"v_a\"><b>a. </b> da, deoarece circula&#355;i pe banda din dreapta;\r\n</p><p id=\"v_b\"><b>b. </b> nu, deoarece ve&#355;i c&#259;lca banda continu&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> nu, pentru c&#259; circula&#355;i pe banda de urgen&#355;&#259;.</p>', 'nu', 'nu', 'da', ''),
(585, 'Conduc&#259;torul autocamionului poate p&#259;trunde în intersec&#355;ie?', 'uploads/1002.jpg', '<p id=\"v_a\"><b>a. </b> da;\r\n</p><p id=\"v_b\"><b>b. </b> nu, în intersec&#355;ie vor p&#259;trunde tramvaiul &#351;i autoturismul;\r\n</p><p id=\"v_c\"><b>c. </b> nu, indicatorul „Cedeaz&#259; trecerea“ îl oblig&#259; s&#259; acorde prioritate celorlalte vehicule.</p>', 'da', 'nu', 'nu', ''),
(586, 'Criteriul de alegere a benzinei pentru motoarele cu aprindere prin scânteie îl constituie:', '', '<p id=\"v_a\"><b>a. </b> culoarea &#351;i consisten&#355;a;\r\n</p><p id=\"v_b\"><b>b. </b> cifra octanic&#259; (CO);\r\n</p><p id=\"v_c\"><b>c. </b> cifra catanic&#259; (CC).</p>', 'nu', 'da', 'nu', ''),
(587, 'Dep&#259;&#351;irea vehiculelor pe drumurile publice este interzis&#259;:', '', '<p id=\"v_a\"><b>a. </b> pe sectorul de drum unde s-a format o coloan&#259; de autovehicule în a&#351;teptare, dac&#259; prin aceasta se intr&#259; pe sensul opus de circula&#355;ie;\r\n</p><p id=\"v_b\"><b>b. </b> în intersec&#355;iile dirijate doar cu indicatoare rutiere;\r\n</p><p id=\"v_c\"><b>c. </b> atunci când cel dep&#259;&#351;it este remorcat &#351;i are puse în func&#355;iune luminile de avarie.</p>', 'da', 'nu', 'nu', ''),
(588, 'Indicatorul interzice accesul:', 'uploads/308.jpg', '<p id=\"v_a\"><b>a. </b> motocicletelor;\r\n</p><p id=\"v_b\"><b>b. </b> mopedelor;\r\n</p><p id=\"v_c\"><b>c. </b> bicicletelor.</p>', 'nu', 'da', 'nu', ''),
(589, 'În ce ordine vor trece vehiculele prin intersec&#355;ia prezentat&#259;?', 'uploads/166.jpg', '<p id=\"v_a\"><b>a. </b> tramvaiul 1, tramvaiul 2, autoturismul;\r\n</p><p id=\"v_b\"><b>b. </b> tramvaiul 2, tramvaiul 1, autoturismul;\r\n</p><p id=\"v_c\"><b>c. </b> tramvaiul 1, autoturismul, tramvaiul 2.</p>', 'da', 'nu', 'nu', ''),
(590, 'Ce consecin&#355;e poate avea transportul bagajelor pe acoperi&#351;ul autoturismului?', '', '<p id=\"v_a\"><b>a. </b> nu se pot executa viraje;\r\n</p><p id=\"v_b\"><b>b. </b> cre&#351;te instabilitatea vehiculului la vânt lateral;\r\n</p><p id=\"v_c\"><b>c. </b> se reduce distan&#355;a de frânare.</p>', 'nu', 'da', 'nu', ''),
(591, 'Ce autovehicule pot p&#259;trunde în intersec&#355;ie?', 'uploads/487.jpg', '<p id=\"v_a\"><b>a. </b> troleibuzul &#351;i motocicleta;\r\n</p><p id=\"v_b\"><b>b. </b> motocicleta &#351;i autocamionul;\r\n</p><p id=\"v_c\"><b>c. </b> troleibuzul.</p>', 'nu', 'da', 'nu', ''),
(592, 'Semnalul de culoare galben&#259; al semaforului:', '', '<p id=\"v_a\"><b>a. </b> permite intrarea în intersec&#355;ie, dac&#259; urmeaz&#259; dup&#259; lumina verde;\r\n</p><p id=\"v_b\"><b>b. </b> permite schimbarea direc&#355;iei de mers c&#259;tre dreapta;\r\n</p><p id=\"v_c\"><b>c. </b> interzice intrarea în intersec&#355;ie, dac&#259; vehiculul poate fi oprit în condi&#355;ii de siguran&#355;&#259;.</p>', 'nu', 'nu', 'da', ''),
(593, 'Inten&#355;iona&#355;i s&#259; consulta&#355;i o hart&#259; rutier&#259;. Cum este recomandat s&#259; proceda&#355;i?', 'uploads/927.jpg', '<p id=\"v_a\"><b>a. </b> opri&#355;i, cu luminile de întâlnire aprinse;\r\n</p><p id=\"v_b\"><b>b. </b> opri&#355;i autovehiculul în afara carosabilului;\r\n</p><p id=\"v_c\"><b>c. </b> nu ave&#355;i voie s&#259; consulta&#355;i harta pe timp de noapte.</p>', 'nu', 'da', 'nu', ''),
(594, 'Pute&#355;i dep&#259;&#351;i mai multe vehicule în situa&#355;ia de fa&#355;&#259;?', 'uploads/860.jpg', '<p id=\"v_a\"><b>a. </b> da;\r\n</p><p id=\"v_b\"><b>b. </b> nu;\r\n</p><p id=\"v_c\"><b>c. </b> numai dac&#259; sunt peste dou&#259; benzi de circula&#355;ie.</p>', 'da', 'nu', 'nu', ''),
(595, 'Ce obliga&#355;ie are conduc&#259;torul auto când a accidentat un pieton care a traversat strada neregulamentar?', '', '<p id=\"v_a\"><b>a. </b> transport&#259; victima la cea mai apropiat&#259; unitate sanitar&#259;, dac&#259; nu este posibil&#259; transportarea cu alte mijloace, &#351;i se reîntoarce imediat la locul accidentului;\r\n</p><p id=\"v_b\"><b>b. </b> r&#259;mâne la locul accidentului pân&#259; la sosirea organelor de anchet&#259;, re&#355;inând numele, prenumele &#351;i adresele martorilor;\r\n</p><p id=\"v_c\"><b>c. </b> se prezint&#259; la cel mai apropiat post de poli&#355;ie.</p>', 'da', 'nu', 'nu', ''),
(596, 'Dac&#259;, din motive obiective, v&#259; deplasa&#355;i cu vitez&#259; redus&#259;, trebuie:', '', '<p id=\"v_a\"><b>a. </b> s&#259; circula&#355;i numai pe banda de lâng&#259; acostament sau bordur&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; circula&#355;i numai pe banda de lâng&#259; axul drumului, semnalizând cu luminile de avarie;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; emite&#355;i semnale luminoase &#351;i sonore, pentru a preveni orice posibil eveniment.</p>', 'da', 'nu', 'nu', '');
INSERT INTO `intrebari` (`id`, `cerinta`, `imagine`, `variante`, `rasp_a`, `rasp_b`, `rasp_c`, `eticheta`) VALUES
(597, 'Spre ce parte a drumului public are tendin&#355;a de a derapa autovehiculul, în cazul efectu&#259;rii unui viraj la stânga cu o vitez&#259; mare?', '', '<p id=\"v_a\"><b>a. </b> spre dreapta, c&#259;tre exteriorul curbei;\r\n</p><p id=\"v_b\"><b>b. </b> spre interiorul curbei, c&#259;tre sensul opus de mers;\r\n</p><p id=\"v_c\"><b>c. </b> autovehiculul nu poate derapa în nicio situa&#355;ie.</p>', 'da', 'nu', 'nu', ''),
(598, 'Pe ce band&#259; de circula&#355;ie v&#259; încadra&#355;i pentru a vira la stânga, într-o intersec&#355;ie semnalizat&#259; cu astfel de marcaje?', 'uploads/414.jpg', '<p id=\"v_a\"><b>a. </b> pe banda de circula&#355;ie nr.1;\r\n</p><p id=\"v_b\"><b>b. </b> pe benzile de circula&#355;ie nr. 2 &#351;i 3;\r\n</p><p id=\"v_c\"><b>c. </b> pe banda de circula&#355;ie nr. 4.</p>', 'nu', 'nu', 'da', ''),
(599, 'Indicatorul semnific&#259;:', 'uploads/618.jpg', '<p id=\"v_a\"><b>a. </b> „Vitez&#259; minim&#259; obligatorie“;\r\n</p><p id=\"v_b\"><b>b. </b> „Sfâr&#351;itul limit&#259;rii de vitez&#259;“;\r\n</p><p id=\"v_c\"><b>c. </b> „Sfâr&#351;itul vitezei minime obligatorii“.</p>', 'nu', 'nu', 'da', ''),
(600, 'Pe timpul deplas&#259;rii într-un autoturism pe un drum public este interzis:', '', '<p id=\"v_a\"><b>a. </b> s&#259; fie transporta&#355;i copii de pân&#259; la 12 ani pe scaunul din spate;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; se fumeze în timpul mersului;\r\n</p><p id=\"v_c\"><b>c. </b> ocupan&#355;ii locurilor din fa&#355;&#259; s&#259; &#355;in&#259; în bra&#355;e animale.</p>', 'nu', 'nu', 'da', ''),
(601, 'Cum proceda&#355;i în situa&#355;ia în care, dup&#259; resuscitarea cardio-respiratorie, accidentatul î&#351;i recap&#259;t&#259; pulsul?', '', '<p id=\"v_a\"><b>a. </b> înceta&#355;i compresiile exterioare ale pieptului;\r\n</p><p id=\"v_b\"><b>b. </b> continua&#355;i compresiile exterioare ale pieptului;\r\n</p><p id=\"v_c\"><b>c. </b> aplica&#355;i un bandaj toracic.</p>', 'da', 'nu', 'nu', ''),
(602, 'Care dintre autovehicule pot traversa intersec&#355;ia din imagine?', 'uploads/416.jpg', '<p id=\"v_a\"><b>a. </b> niciunul;\r\n</p><p id=\"v_b\"><b>b. </b> autocamionul &#351;i autobuzul;\r\n</p><p id=\"v_c\"><b>c. </b> autoturismul galben &#351;i autoturismul ro&#351;u.</p>', 'da', 'nu', 'nu', ''),
(603, 'Ave&#355;i obliga&#355;ia de a opri imediat autovehiculul la semnalele adresate de:', '', '<p id=\"v_a\"><b>a. </b> îndrum&#259;torii de circula&#355;ie ai Ministerului Ap&#259;r&#259;rii;\r\n</p><p id=\"v_b\"><b>b. </b> un conduc&#259;tor auto r&#259;mas în pan&#259;, pentru a-l remorca;\r\n</p><p id=\"v_c\"><b>c. </b> poli&#355;i&#351;tii comunitari.</p>', 'da', 'nu', 'nu', ''),
(604, 'În prima intersec&#355;ie:', 'uploads/291.jpg', '<p id=\"v_a\"><b>a. </b> ve&#355;i ceda trecerea vehiculelor care circul&#259; din dreapta;\r\n</p><p id=\"v_b\"><b>b. </b> ave&#355;i prioritate de trecere;\r\n</p><p id=\"v_c\"><b>c. </b> ve&#355;i ceda trecerea vehiculelor care circul&#259; din stânga.</p>', 'nu', 'da', 'nu', ''),
(605, 'Distrugerea, degradarea sau aducerea în stare de neîntrebuin&#355;are, în mod inten&#355;ionat, a indicatoarelor, semafoarelor, amenaj&#259;rilor rutiere se pedepse&#351;te astfel:', '', '<p id=\"v_a\"><b>a. </b> cu amend&#259; contraven&#355;ional&#259; &#351;i puncte de penalizare;\r\n</p><p id=\"v_b\"><b>b. </b> penal;\r\n</p><p id=\"v_c\"><b>c. </b> cu suspendarea exercit&#259;rii dreptului de a conduce autovehicule.</p>', 'nu', 'da', 'nu', ''),
(606, 'Cum trebuie s&#259; proceda&#355;i atunci când un autobuz &#351;colar opre&#351;te cu luminile de avarie aprinse?', '', '<p id=\"v_a\"><b>a. </b> luminile intermitente de avertizare ale autobuzelor &#351;colare v&#259; oblig&#259; s&#259; opri&#355;i;\r\n</p><p id=\"v_b\"><b>b. </b> circula&#355;i cu aten&#355;ie sporit&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> nu ave&#355;i nicio obliga&#355;ie.</p>', 'nu', 'da', 'nu', ''),
(607, 'În cazul unui accidentat care nu respir&#259;, pulsul este verificat:', '', '<p id=\"v_a\"><b>a. </b> sub mandibul&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> lâng&#259; ureche;\r\n</p><p id=\"v_c\"><b>c. </b> pe partea lateral&#259; a epiglotei (M&#259;rul lui Adam), în scobitura dintre trahee &#351;i mu&#351;chii gâtului.</p>', 'nu', 'nu', 'da', ''),
(608, 'Ce autovehicule pot opri în rândul doi, lâng&#259; alte vehicule sta&#355;ionate pe partea dreapt&#259; a carosabilului?', '', '<p id=\"v_a\"><b>a. </b> to&#355;i cei care au ceva urgent de rezolvat;\r\n</p><p id=\"v_b\"><b>b. </b> taximetrele, la urcarea sau coborârea pasagerilor, atunci când circula&#355;ia permite acest lucru;\r\n</p><p id=\"v_c\"><b>c. </b> orice autoturism.</p>', 'nu', 'da', 'nu', ''),
(609, 'Conduc&#259;torul unui autovehicul poate executa o dep&#259;&#351;ire la întâlnirea indicatorului „Limitare de vitez&#259;“?', '', '<p id=\"v_a\"><b>a. </b> da, dac&#259; în timpul manevrei viteza sa nu este mai mare decât cea înscris&#259; pe indicator;\r\n</p><p id=\"v_b\"><b>b. </b> nu, pân&#259; la întâlnirea indicatorului care semnalizeaz&#259; terminarea zonei de limitare a vitezei;\r\n</p><p id=\"v_c\"><b>c. </b> da, dac&#259; dep&#259;&#351;irea se execut&#259; în afara unei localit&#259;&#355;i.</p>', 'da', 'nu', 'nu', ''),
(610, 'La întâlnirea c&#259;rui indicator ave&#355;i prioritate de trecere?', '', '<p id=\"v_a\"><b>a. </b> „Circula&#355;ie în ambele sensuri\";\r\n</p><p id=\"v_b\"><b>b. </b> „Prioritate fa&#355;&#259; de circula&#355;ia din sens invers\";\r\n</p><p id=\"v_c\"><b>c. </b> „Prioritate pentru circula&#355;ia din sens invers\".</p>', 'nu', 'da', 'nu', ''),
(611, 'Tensiunea nervoas&#259; &#351;i oboseala conduc&#259;torului auto pot fi reduse dac&#259;:', '', '<p id=\"v_a\"><b>a. </b> rula&#355;i cu o vitez&#259; ponderat&#259;, în condi&#355;ii de siguran&#355;&#259;, men&#355;inând un ritm de mers constant;\r\n</p><p id=\"v_b\"><b>b. </b> circula&#355;i cu aceea&#351;i vitez&#259; atât în localit&#259;&#355;i, cât &#351;i în afara acestora;\r\n</p><p id=\"v_c\"><b>c. </b> folosi&#355;i medicamente calmante sau energizante care nu sunt interzise conduc&#259;torilor auto.</p>', 'da', 'nu', 'nu', ''),
(612, 'Prezen&#355;a calaminei pe suprafa&#355;a bujiilor &#351;i fumul alb&#259;strui al e&#351;apamentului indic&#259;:', '', '<p id=\"v_a\"><b>a. </b> mecanismul motor (grup piston-cilindru) este uzat;\r\n</p><p id=\"v_b\"><b>b. </b> rularea frecvent&#259; cu o vitez&#259; inferioar&#259; limitei legale;\r\n</p><p id=\"v_c\"><b>c. </b> folosirea unor bujii necorespunz&#259;toare.</p>', 'da', 'nu', 'nu', ''),
(613, 'Care dintre cele trei autoturisme este parcat regulamentar?', 'uploads/953.jpg', '<p id=\"v_a\"><b>a. </b> autoturismul albastru;\r\n</p><p id=\"v_b\"><b>b. </b> autoturismul ro&#351;u &#351;i autoturismul albastru, deoarece nu dep&#259;&#351;esc zona marcat&#259; de indicator;\r\n</p><p id=\"v_c\"><b>c. </b> autoturismul verde &#351;i autoturismul ro&#351;u, deoarece respect&#259; semnifica&#355;ia indicatorului.</p>', 'nu', 'nu', 'da', ''),
(614, 'Ce obliga&#355;ii ave&#355;i la semnalele agen&#355;ilor c&#259;ilor ferate, plasa&#355;i la trecerea la nivel cu o cale ferat&#259;?', '', '<p id=\"v_a\"><b>a. </b> nicio obliga&#355;ie, deoarece agen&#355;ii c&#259;ilor ferate dirijeaz&#259; garniturile de tren;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; v&#259; conforma&#355;i semnifica&#355;iei semnalelor acestora;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; reduce&#355;i viteza, s&#259; v&#259; asigura&#355;i, iar, dac&#259; trenul nu este în mi&#351;care, pute&#355;i s&#259; continua&#355;i traversarea c&#259;ii ferate cu aten&#355;ie.</p>', 'nu', 'da', 'nu', ''),
(615, 'Cum trebuie s&#259; procedeze conduc&#259;torul unui autovehicul dac&#259; i-a fost prescris un tratament medicamentos?', '', '<p id=\"v_a\"><b>a. </b> s&#259; se asigure c&#259; administrarea acestuia nu-i va afecta capacitatea de a conduce;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; nu î&#351;i administreze medicamentele înainte de a se urca la volan;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; se informeze care este doza minim&#259; care poate fi administrat&#259; pe perioada cât se afl&#259; la volan.</p>', 'da', 'nu', 'nu', ''),
(616, 'Conduc&#259;torul autoturismului ro&#351;u s-a angajat corect în dep&#259;&#351;irea autocamionului?', 'uploads/441.jpg', '<p id=\"v_a\"><b>a. </b> nu, deoarece nu s-a asigurat &#351;i nu a semnalizat inten&#355;ia de a dep&#259;&#351;i;\r\n</p><p id=\"v_b\"><b>b. </b> da, deoarece din sens opus nu circul&#259; niciun vehicul;\r\n</p><p id=\"v_c\"><b>c. </b> nu, deoarece încalc&#259; semnifica&#355;ia indicatorului.</p>', 'da', 'nu', 'nu', ''),
(617, 'Cum ve&#355;i parca regulamentar într-o pia&#355;&#259; cu sens giratoriu?', '', '<p id=\"v_a\"><b>a. </b> paralel cu bordura trotuarului &#351;i cât mai aproape de aceasta;\r\n</p><p id=\"v_b\"><b>b. </b> par&#355;ial pe trotuar;\r\n</p><p id=\"v_c\"><b>c. </b> în acest loc nu este permis&#259; parcarea.</p>', 'nu', 'nu', 'da', ''),
(618, 'Când gura unui r&#259;nit r&#259;mâne încle&#351;tat&#259;, respira&#355;ia artificial&#259; se poate face:', '', '<p id=\"v_a\"><b>a. </b> prin gura închis&#259; a victimei;\r\n</p><p id=\"v_b\"><b>b. </b> prin n&#259;rile victimei;\r\n</p><p id=\"v_c\"><b>c. </b> în acest caz nu se mai face respira&#355;ie artificial&#259;.</p>', 'nu', 'da', 'nu', ''),
(619, 'Viteza maxim&#259; cu care pot s&#259; conduc&#259; un autoturism, pe autostrad&#259;, persoanele care au mai pu&#355;in de un an de practic&#259; de conducere, este de:', '', '<p id=\"v_a\"><b>a. </b> 130 km/h;\r\n</p><p id=\"v_b\"><b>b. </b> 100 km/h;\r\n</p><p id=\"v_c\"><b>c. </b> 110 km/h.</p>', 'nu', 'nu', 'da', ''),
(620, 'Poate fi re&#355;inut de c&#259;tre poli&#355;istul rutier certificatul de înmatriculare al unui vehicul?', '', '<p id=\"v_a\"><b>a. </b> nu, deoarece legea prevede o asemenea m&#259;sur&#259; numai pentru permisul de conducere;\r\n</p><p id=\"v_b\"><b>b. </b> da, dac&#259; autovehiculul sau remorca prezint&#259; defec&#355;iuni tehnice care pun în pericol siguran&#355;a circula&#355;iei;\r\n</p><p id=\"v_c\"><b>c. </b> da, dar numai pentru o perioad&#259; de 15 zile.</p>', 'nu', 'da', 'nu', ''),
(621, 'Ce obliga&#355;ii ave&#355;i atunci când urmeaz&#259; s&#259; p&#259;r&#259;si&#355;i autostrada?', '', '<p id=\"v_a\"><b>a. </b> s&#259; reduce&#355;i viteza începând cu 300 m înainte de ie&#351;irea de pe autostrad&#259;, în dreptul panoului care marcheaz&#259; aceast&#259; distan&#355;&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; circula&#355;i pe prima band&#259; cu o vitez&#259; de maximum 60 km/or&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; semnaliza&#355;i din timp &#351;i s&#259; v&#259; angaja&#355;i pe banda de ie&#351;ire (decelerare).</p>', 'nu', 'nu', 'da', ''),
(622, 'Pentru a folosi telefonul mobil, atunci când nu ave&#355;i un sistem de tip „mâini libere“:', 'uploads/383.jpg', '<p id=\"v_a\"><b>a. </b> opri&#355;i pe banda de sta&#355;ionare;\r\n</p><p id=\"v_b\"><b>b. </b> opri&#355;i în parcarea din zona complexului de servicii;\r\n</p><p id=\"v_c\"><b>c. </b> telefonul poate fi folosit f&#259;r&#259; restric&#355;ii.</p>', 'nu', 'da', 'nu', ''),
(623, 'Ce se în&#355;elege prin conduit&#259; preventiv&#259;?', '', '<p id=\"v_a\"><b>a. </b> un concept valabil pentru conduc&#259;torii auto încep&#259;tori;\r\n</p><p id=\"v_b\"><b>b. </b> comportamentul rutier care asigur&#259; prevenirea accidentelor, prin anticiparea &#351;i evitarea ac&#355;iunilor incorecte ale partenerilor de drum, precum &#351;i adaptarea vitezei de deplasare la condi&#355;iile meteo-rutiere specifice;\r\n</p><p id=\"v_c\"><b>c. </b> calitatea unor conduc&#259;tori auto de a circula prudent, cu vitez&#259; redus&#259;, pentru a nu crea nepl&#259;ceri în trafic celorlal&#355;i utilizatori ai drumului public.</p>', 'nu', 'da', 'nu', ''),
(624, 'În care dintre situa&#355;ii conduc&#259;torul de autovehicule este obligat, pe timp de noapte, s&#259; schimbe lumina de drum cu cea de întâlnire?', '', '<p id=\"v_a\"><b>a. </b> când drumul este neiluminat;\r\n</p><p id=\"v_b\"><b>b. </b> când circul&#259; în localitate, pe un drum cu mai multe benzi pe sens;\r\n</p><p id=\"v_c\"><b>c. </b> în cazul în care din sens opus se apropie un autovehicul, de la o distan&#355;&#259; de cel pu&#355;in 200 m.</p>', 'nu', 'nu', 'da', ''),
(625, 'Ce se în&#355;elege prin distan&#355;a de cel pu&#355;in 50 m de la care conduc&#259;torul de autovehicul este obligat s&#259; semnalizeze inten&#355;ia de a dep&#259;&#351;i în localitate?', '', '<p id=\"v_a\"><b>a. </b> distan&#355;a dintre autovehiculul care dep&#259;&#351;e&#351;te &#351;i cel care urmeaz&#259; s&#259; fie dep&#259;&#351;it;\r\n</p><p id=\"v_b\"><b>b. </b> distan&#355;a dintre locul în care s-a semnalizat inten&#355;ia de a dep&#259;&#351;i &#351;i locul în care se schimb&#259; pozi&#355;ia de mers a autovehiculelor pentru începerea dep&#259;&#351;irii;\r\n</p><p id=\"v_c\"><b>c. </b> distan&#355;a dintre autovehiculul angajat în dep&#259;&#351;ire &#351;i locul unde efectueaz&#259; dep&#259;&#351;irea.</p>', 'nu', 'da', 'nu', ''),
(626, 'Este interzis ca autovehiculele s&#259; fie echipate cu:', '', '<p id=\"v_a\"><b>a. </b> dispozitive antiradar;\r\n</p><p id=\"v_b\"><b>b. </b> sisteme de naviga&#355;ie;\r\n</p><p id=\"v_c\"><b>c. </b> sisteme sonor antifurt.</p>', 'da', 'nu', 'nu', ''),
(627, 'Schimbarea uleiului &#351;i a elementului filtrant se face:', '', '<p id=\"v_a\"><b>a. </b> conform periodicit&#259;&#355;ii stabilite de constructor;\r\n</p><p id=\"v_b\"><b>b. </b> din 5 în 5 ani;\r\n</p><p id=\"v_c\"><b>c. </b> din 3 în 3 ani.</p>', 'da', 'nu', 'nu', ''),
(628, 'Care autoturism î&#351;i continu&#259; deplasarea prin intersec&#355;ie?', 'uploads/501.jpg', '<p id=\"v_a\"><b>a. </b> autoturismul alb;\r\n</p><p id=\"v_b\"><b>b. </b> autoturismul ro&#351;u;\r\n</p><p id=\"v_c\"><b>c. </b> autoturismul albastru.</p>', 'nu', 'nu', 'da', ''),
(629, 'Cum ve&#355;i proceda la semnalul poli&#355;istului, atunci când conduce&#355;i autoturismul din imagine?', 'uploads/578.jpg', '<p id=\"v_a\"><b>a. </b> opri&#355;i, deoarece semnalul vi se adreseaz&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> v&#259; continua&#355;i deplasarea, deoarece semnalul nu vi se adreseaz&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> schimba&#355;i direc&#355;ia de mers spre stânga.</p>', 'nu', 'da', 'nu', ''),
(630, 'Conduce&#355;i autovehiculul pe drumurile publice &#351;i refuza&#355;i recoltarea unor probe biologice în vederea stabilirii alcoolemiei. Ve&#355;i fi pedepsit penal?', '', '<p id=\"v_a\"><b>a. </b> da;\r\n</p><p id=\"v_b\"><b>b. </b> numai dac&#259; sunte&#355;i implicat într-un accident de circula&#355;ie;\r\n</p><p id=\"v_c\"><b>c. </b> nu.</p>', 'da', 'nu', 'nu', ''),
(631, 'Luminile de avarie se folosesc în urm&#259;toarea situa&#355;ie:', '', '<p id=\"v_a\"><b>a. </b> când sunte&#355;i dezorientat în privin&#355;a traseului de urmat;\r\n</p><p id=\"v_b\"><b>b. </b> când vehiculul este imobilizat involuntar pe partea carosabil&#259; sau în timp ce sunte&#355;i remorcat;\r\n</p><p id=\"v_c\"><b>c. </b> când luminile de drum s-au defectat.</p>', 'nu', 'da', 'nu', ''),
(632, 'Ce semnific&#259; semnalul ro&#351;u al semaforului, care func&#355;ioneaz&#259; concomitent cu cel galben?', '', '<p id=\"v_a\"><b>a. </b> semaforul este în regim de avarie;\r\n</p><p id=\"v_b\"><b>b. </b> trecerea unei coloane de vehicule;\r\n</p><p id=\"v_c\"><b>c. </b> oprire.</p>', 'nu', 'nu', 'da', ''),
(633, 'În cazul persoanelor accidentate, care prezint&#259; fracturi de coloan&#259;, este recomandabil s&#259; se foloseascã:', '', '<p id=\"v_a\"><b>a. </b> orice mijloc de transport, dac&#259; se deplaseaz&#259; în direc&#355;ia unei unit&#259;&#355;i sanitare;\r\n</p><p id=\"v_b\"><b>b. </b> mijloacele de transport cu platform&#259;, respectiv furgonete, camioane, dac&#259; ambulan&#355;a nu se poate deplasa la locul accidentului;\r\n</p><p id=\"v_c\"><b>c. </b> autoturismele, întrucât asigur&#259; confort &#351;i rapiditate în deplasare.</p>', 'nu', 'da', 'nu', ''),
(634, 'Când ninge abundent ve&#355;i folosi:', 'uploads/250.jpg', '<p id=\"v_a\"><b>a. </b> luminile de pozi&#355;ie;\r\n</p><p id=\"v_b\"><b>b. </b> claxonul;\r\n</p><p id=\"v_c\"><b>c. </b> luminile de întâlnire ale farurilor.</p>', 'nu', 'nu', 'da', ''),
(635, 'În cazul pl&#259;gilor înso&#355;ite de hemoragii arteriale, persoanelor r&#259;nite trebuie s&#259; li se aplice un garou de cauciuc:', '', '<p id=\"v_a\"><b>a. </b> deasupra pl&#259;gii;\r\n</p><p id=\"v_b\"><b>b. </b> sub plag&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> peste plag&#259;.</p>', 'da', 'nu', 'nu', ''),
(636, 'De ce este necesar s&#259; m&#259;ri&#355;i distan&#355;a fa&#355;&#259; de autovehiculul care circul&#259; în fa&#355;a dvs., atunci când circula&#355;i pe un carosabil umed?', '', '<p id=\"v_a\"><b>a. </b> deoarece aderen&#355;a anvelopelor pe partea carosabil&#259; scade, iar spa&#355;iul de frânare cre&#351;te;\r\n</p><p id=\"v_b\"><b>b. </b> deoarece nu pute&#355;i sesiza prompt aprinderea l&#259;mpilor de stop ale autovehiculului din fa&#355;&#259;, atunci când acesta frâneaz&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> deoarece ro&#355;ile autovehiculului din fa&#355;&#259; arunc&#259; spre dvs. criblur&#259;.</p>', 'da', 'nu', 'nu', ''),
(637, 'Indicatorul interzice:', 'uploads/160.jpg', '<p id=\"v_a\"><b>a. </b> dep&#259;&#351;irea;\r\n</p><p id=\"v_b\"><b>b. </b> deplasarea pe direc&#355;ia înainte dup&#259; prima intersec&#355;ie;\r\n</p><p id=\"v_c\"><b>c. </b> schimbarea direc&#355;iei de mers în prima intersec&#355;ie.</p>', 'nu', 'nu', 'da', ''),
(638, 'Semnalul galben intermitent al semaforului:', '', '<p id=\"v_a\"><b>a. </b> anun&#355;&#259; apari&#355;ia semnalului ro&#351;u;\r\n</p><p id=\"v_b\"><b>b. </b> oblig&#259; conduc&#259;torii vehiculelor s&#259; reduc&#259; viteza &#351;i s&#259; circule respectând regulile de circula&#355;ie aplicabile în acea intersec&#355;ie;\r\n</p><p id=\"v_c\"><b>c. </b> interzice intrarea în intersec&#355;ie.</p>', 'nu', 'da', 'nu', ''),
(639, 'Fumul de culoare albastr&#259; emis de e&#351;apament indic&#259;:', '', '<p id=\"v_a\"><b>a. </b> un consum exagerat de ulei;\r\n</p><p id=\"v_b\"><b>b. </b> înfundarea par&#355;ial&#259; a tobei de e&#351;apament;\r\n</p><p id=\"v_c\"><b>c. </b> folosirea unor benzine colorate.</p>', 'da', 'nu', 'nu', ''),
(640, 'Ce obliga&#355;ii v&#259; revin dac&#259;, într-o intersec&#355;ie, vira&#355;i la stânga pe semnalul verde al semaforului?', '', '<p id=\"v_a\"><b>a. </b> nicio obliga&#355;ie;\r\n</p><p id=\"v_b\"><b>b. </b> acorda&#355;i prioritate de trecere mijloacelor de transport public de persoane;\r\n</p><p id=\"v_c\"><b>c. </b> acorda&#355;i prioritate de trecere atât vehiculelor care circul&#259; din sens opus, cât &#351;i pietonilor afla&#355;i în traversare pe sensul dumneavoastr&#259; de mers.</p>', 'nu', 'nu', 'da', ''),
(641, 'Semnifica&#355;ia indicatoarelor de interzicere sau restric&#355;ie, în lipsa altor preciz&#259;ri, înceteaz&#259;:', '', '<p id=\"v_a\"><b>a. </b> la 100 m de locul unde este instalat indicatorul;\r\n</p><p id=\"v_b\"><b>b. </b> la 200 m de locul unde este instalat indicatorul;\r\n</p><p id=\"v_c\"><b>c. </b> în prima intersec&#355;ie.</p>', 'nu', 'nu', 'da', ''),
(642, 'Care dintre autoturisme sta&#355;ioneaz&#259; gre&#351;it?', 'uploads/270.jpg', '<p id=\"v_a\"><b>a. </b> autoturismul alb;\r\n</p><p id=\"v_b\"><b>b. </b> autoturismul ro&#351;u;\r\n</p><p id=\"v_c\"><b>c. </b> ambele autoturisme.</p>', 'nu', 'nu', 'da', ''),
(643, 'Care este limita inferioar&#259; de vitez&#259; ce poate fi stabilit&#259; în interiorul localit&#259;&#355;ilor?', '', '<p id=\"v_a\"><b>a. </b> nu mai pu&#355;in de 30 km/h;\r\n</p><p id=\"v_b\"><b>b. </b> nu mai pu&#355;in de 40 km/h;\r\n</p><p id=\"v_c\"><b>c. </b> nu exist&#259; prevedere legal&#259;.</p>', 'da', 'nu', 'nu', ''),
(644, 'Cum ve&#355;i proceda corect în intersec&#355;ia dat&#259;?', 'uploads/966.jpg', '<p id=\"v_a\"><b>a. </b> respecta&#355;i indica&#355;iile semnalului luminos, dac&#259; toate semafoarele au aceea&#351;i culoare, în acela&#351;i timp;\r\n</p><p id=\"v_b\"><b>b. </b> respecta&#355;i regulile generale de circula&#355;ie, dac&#259; semafoarele nu au aceea&#351;i culoare;\r\n</p><p id=\"v_c\"><b>c. </b> respecta&#355;i semnifica&#355;ia semnalului luminos pentru virajul la stânga.</p>', 'nu', 'nu', 'da', ''),
(645, 'Prin intersec&#355;ia prezentat&#259; pot trece:', 'uploads/568.jpg', '<p id=\"v_a\"><b>a. </b> tramvaiul 1 &#351;i autoturismul;\r\n</p><p id=\"v_b\"><b>b. </b> bicicleta;\r\n</p><p id=\"v_c\"><b>c. </b> tramvaiul 1 &#351;i tramvaiul 2.</p>', 'nu', 'da', 'nu', ''),
(646, 'În ce ordine vor trece prin intersec&#355;ie autoturismele la culoarea galben&#259; intermitent&#259; a semaforului?', 'uploads/882.jpg', '<p id=\"v_a\"><b>a. </b> ro&#351;u, verde, albastru;\r\n</p><p id=\"v_b\"><b>b. </b> ro&#351;u, albastru, verde;\r\n</p><p id=\"v_c\"><b>c. </b> verde, albastru, ro&#351;u.</p>', 'nu', 'nu', 'da', ''),
(647, 'Ce trebuie s&#259; verifica&#355;i la un autoturism cu rulot&#259;, înainte de a porni la drum?', '', '<p id=\"v_a\"><b>a. </b> dac&#259; sistemul de alimentare cu ap&#259; al rulotei func&#355;ioneaz&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> dac&#259; vizibilitatea în spate, prin oglinzile retrovizoare, este asigurat&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> dac&#259; persoanele transportate în rulot&#259; poart&#259; centura de siguran&#355;&#259;.</p>', 'nu', 'da', 'nu', ''),
(648, 'Pe o por&#355;iune de drum acoperit&#259; cu polei, sesiza&#355;i impactul inevitabil cu un câine. Cum ve&#355;i proceda pentru evitarea unui accident?', '', '<p id=\"v_a\"><b>a. </b> considera&#355;i c&#259; o asemenea situa&#355;ie nu vi se poate întâmpla;\r\n</p><p id=\"v_b\"><b>b. </b> elibera&#355;i accelera&#355;ia, men&#355;ine&#355;i ferm volanul pe direc&#355;ia de deplasare, fiind preg&#259;tit pentru a stabiliza autovehiculul dup&#259; impact, prin manevrarea lin&#259; a volanului;\r\n</p><p id=\"v_c\"><b>c. </b> încerca&#355;i, pe cât posibil, s&#259; evita&#355;i impactul frontal, prin manevrarea brusc&#259; a volanului în direc&#355;ia de deplasare opus&#259; câinelui.</p>', 'nu', 'da', 'nu', ''),
(649, 'Fenomenul de acvaplanare genereaz&#259; urm&#259;toarele pericole:', '', '<p id=\"v_a\"><b>a. </b> stropirea cu ap&#259; a parbrizului;\r\n</p><p id=\"v_b\"><b>b. </b> stropirea cu ap&#259; a cl&#259;dirilor &#351;i a pietonilor;\r\n</p><p id=\"v_c\"><b>c. </b> pierderea controlului asupra direc&#355;iei.</p>', 'nu', 'nu', 'da', ''),
(650, 'La întâlnirea c&#259;rui indicator conduc&#259;torul auto nu are prioritate de trecere?', '', '<p id=\"v_a\"><b>a. </b> „Prioritate pentru circula&#355;ia din sens invers”;\r\n</p><p id=\"v_b\"><b>b. </b> „Prioritate fa&#355;&#259; de circula&#355;ia din sens invers”;\r\n</p><p id=\"v_c\"><b>c. </b> „Drum cu prioritate”.</p>', 'da', 'nu', 'nu', ''),
(651, 'Conduc&#259;torul de vehicul care are mai pu&#355;in de un an de practic&#259; de conducere, va circula cu o vitez&#259; maxim&#259; admis&#259; în afara localit&#259;&#355;ilor, mai mic&#259; cu:', '', '<p id=\"v_a\"><b>a. </b> 10 km/h;\r\n</p><p id=\"v_b\"><b>b. </b> 20 km/h;\r\n</p><p id=\"v_c\"><b>c. </b> 25 km/h.</p>', 'nu', 'da', 'nu', ''),
(652, 'În ce ordine vor trece prin intersec&#355;ie cele patru autovehicule?', 'uploads/429.jpg', '<p id=\"v_a\"><b>a. </b> autoturismul, autocamionul, motocicleta, autobuzul;\r\n</p><p id=\"v_b\"><b>b. </b> autobuzul, autocamionul, motocicleta, autoturismul;\r\n</p><p id=\"v_c\"><b>c. </b> autoturismul, autobuzul, motocicleta, autocamionul.</p>', 'nu', 'nu', 'da', ''),
(653, 'Ce obliga&#355;ii ave&#355;i la întâlnirea indicatorului?', 'uploads/439.jpg', '<p id=\"v_a\"><b>a. </b> opri&#355;i autovehiculul, f&#259;r&#259; a dep&#259;&#351;i indicatorul, v&#259; asigura&#355;i, apoi traversa&#355;i;\r\n</p><p id=\"v_b\"><b>b. </b> traversa&#355;i calea ferat&#259; cu vitez&#259; redus&#259;, dup&#259; ce v-a&#355;i asigurat c&#259; nu se apropie trenul;\r\n</p><p id=\"v_c\"><b>c. </b> nicio obliga&#355;ie; indicatorul nu vi se adreseaz&#259;.</p>', 'da', 'nu', 'nu', ''),
(654, 'V&#259; apropia&#355;i de un drum forestier, ce intr&#259; în &#351;osea din partea dreapt&#259; &#351;i de pe care iese un alt autovehicul. Cine trebuie s&#259; a&#351;tepte?', '', '<p id=\"v_a\"><b>a. </b> dvs., pentru c&#259; acorda&#355;i prioritate de dreapta;\r\n</p><p id=\"v_b\"><b>b. </b> autovehiculul care iese de pe drumul forestier;\r\n</p><p id=\"v_c\"><b>c. </b> autovehiculul care circul&#259; cu vitez&#259; mai mare.</p>', 'nu', 'da', 'nu', ''),
(655, 'În ce ordine vor trece prin intersec&#355;ie autovehiculele din imagine?', 'uploads/638.jpg', '<p id=\"v_a\"><b>a. </b> autoturismul, autocamionul, motocicleta;\r\n</p><p id=\"v_b\"><b>b. </b> autocamionul, motocicleta, autoturismul;\r\n</p><p id=\"v_c\"><b>c. </b> autocamionul, autoturismul, motocicleta.</p>', 'da', 'nu', 'nu', ''),
(656, 'Indicatorul semnific&#259; o vitez&#259;:', 'uploads/398.jpg', '<p id=\"v_a\"><b>a. </b> maxim&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> informativ&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> minim&#259; obligatorie.</p>', 'nu', 'nu', 'da', ''),
(657, 'Când m&#259;sura&#355;i presiunea din pneuri?', '', '<p id=\"v_a\"><b>a. </b> înaintea plec&#259;rii în curs&#259;, când pneurile sunt reci;\r\n</p><p id=\"v_b\"><b>b. </b> dup&#259; parcurgerea a cel pu&#355;in 400-500 km, când pneurile sunt calde;\r\n</p><p id=\"v_c\"><b>c. </b> doar atunci când observ&#259;m c&#259; autovehiculul este înclinat într-o parte.</p>', 'da', 'nu', 'nu', ''),
(658, 'La ce v&#259; pute&#355;i a&#351;tepta dac&#259; circula&#355;i în spatele unui camion cu remorc&#259;, ce inten&#355;ioneaz&#259; s&#259; vireze la dreapta pe o strad&#259; îngust&#259;?', '', '<p id=\"v_a\"><b>a. </b> înainte de a vira, camionul se opre&#351;te;\r\n</p><p id=\"v_b\"><b>b. </b> înainte de a vira, camionul se angajeaz&#259; ini&#355;ial spre stânga, din cauza gabaritului;\r\n</p><p id=\"v_c\"><b>c. </b> camionul se comport&#259; ca orice autoturism.</p>', 'nu', 'da', 'nu', ''),
(659, 'Este corect&#259; dep&#259;&#351;irea autocamionului din imagine?', 'uploads/473.jpg', '<p id=\"v_a\"><b>a. </b> da, deoarece din sens opus nu se apropie alt vehicul;\r\n</p><p id=\"v_b\"><b>b. </b> nu, pentru c&#259; dep&#259;&#351;irea s-ar face într-o curb&#259; cu vizibilitate redus&#259; sub 50 m;\r\n</p><p id=\"v_c\"><b>c. </b> nu, pentru c&#259; linia continu&#259; interzice acest lucru.</p>', 'nu', 'nu', 'da', ''),
(660, 'Care dintre indicatoare anun&#355;&#259; sfâr&#351;itul drumului cu prioritate?', 'uploads/41.jpg', '<p id=\"v_a\"><b>a. </b> niciunul;\r\n</p><p id=\"v_b\"><b>b. </b> ambele;\r\n</p><p id=\"v_c\"><b>c. </b> indicatorul 1.</p>', 'nu', 'nu', 'da', ''),
(661, 'Semnifica&#355;ia indicatorului &#351;i a panoului adi&#355;ional este:', 'uploads/729.jpg', '<p id=\"v_a\"><b>a. </b> oprirea &#351;i sta&#355;ionarea sunt interzise;\r\n</p><p id=\"v_b\"><b>b. </b> zon&#259; de oprire permis&#259; pentru cel mult 30 de minute;\r\n</p><p id=\"v_c\"><b>c. </b> zon&#259; de sta&#355;ionare permis&#259; pentru cel mult 30 de minute.</p>', 'nu', 'nu', 'da', ''),
(662, 'Ce trebuie s&#259; con&#355;in&#259; biletul care se prinde lâng&#259; garoul aplicat unei persoane r&#259;nite?', '', '<p id=\"v_a\"><b>a. </b> datele de stare civil&#259; ale persoanei;\r\n</p><p id=\"v_b\"><b>b. </b> pozi&#355;ia în care a fost g&#259;sit&#259; victima;\r\n</p><p id=\"v_c\"><b>c. </b> ora &#351;i minutul aplic&#259;rii garoului.</p>', 'nu', 'nu', 'da', ''),
(663, 'În prima intersec&#355;ie ve&#355;i acorda prioritate:', 'uploads/513.jpg', '<p id=\"v_a\"><b>a. </b> de dreapta, dac&#259; nu exist&#259; alte reglement&#259;ri în intersec&#355;ie;\r\n</p><p id=\"v_b\"><b>b. </b> de stânga;\r\n</p><p id=\"v_c\"><b>c. </b> tuturor autovehiculelor care circul&#259; din stânga &#351;i din dreapta.</p>', 'da', 'nu', 'nu', ''),
(664, 'În ce situa&#355;ie v&#259; este permis s&#259; opri&#355;i vehiculul sau s&#259; sta&#355;iona&#355;i pe partea stâng&#259; a sensului de mers?', '', '<p id=\"v_a\"><b>a. </b> pe drumurile cu sens unic, dac&#259; r&#259;mâne liber&#259; cel pu&#355;in o band&#259; de circula&#355;ie;\r\n</p><p id=\"v_b\"><b>b. </b> pe drumurile secundare;\r\n</p><p id=\"v_c\"><b>c. </b> pe drumurile cu cel pu&#355;in dou&#259; benzi pe sens.</p>', 'da', 'nu', 'nu', ''),
(665, 'Care autoturisme s-au încadrat corect pentru a traversa intersec&#355;ia prezentat&#259;?', 'uploads/251.jpg', '<p id=\"v_a\"><b>a. </b> toate;\r\n</p><p id=\"v_b\"><b>b. </b> autoturismul verde &#351;i cel ro&#351;u;\r\n</p><p id=\"v_c\"><b>c. </b> autoturismul albastru &#351;i cel galben.</p>', 'da', 'nu', 'nu', ''),
(666, 'Conduc&#259;torului auto i se interzice:', '', '<p id=\"v_a\"><b>a. </b> s&#259; arunce din vehicul obiecte, substan&#355;e sau alte bunuri;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; deschid&#259; u&#351;ile, dac&#259; vehiculul este oprit sau sta&#355;ionat pe partea carosabil&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; foloseasc&#259; instala&#355;ia de sonorizare cu care este echipat autoturismul.</p>', 'da', 'nu', 'nu', ''),
(667, 'La întâlnirea acestui indicator, ave&#355;i obliga&#355;ia:', 'uploads/893.jpg', '<p id=\"v_a\"><b>a. </b> s&#259; spori&#355;i aten&#355;ia, deoarece la o distan&#355;&#259; de 50 m urmeaz&#259; o intersec&#355;ie cu un drum prioritar;\r\n</p><p id=\"v_b\"><b>b. </b> nu ave&#355;i nicio obliga&#355;ie, întrucât circula&#355;i pe un drum prioritar;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; acorda&#355;i prioritate de dreapta la indicatorul urm&#259;tor.</p>', 'da', 'nu', 'nu', ''),
(668, 'Conduc&#259;torul unui autovehicul este obligat:', '', '<p id=\"v_a\"><b>a. </b> s&#259; nu permit&#259; pasagerilor folosirea telefonului mobil în timpul mersului;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; aib&#259; asupra sa actul de identitate, permisul de conducere, certificatul de înmatriculare sau înregistrare &#351;i, dup&#259; caz, atestatul profesional, precum &#351;i celelalte documente prev&#259;zute de legisla&#355;ia în vigoare;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; verifice, la fiecare deplasare, presiunea din pneuri.</p>', 'nu', 'da', 'nu', ''),
(669, 'Ce obliga&#355;ii ave&#355;i dac&#259; luminile ro&#351;ii se aprind &#351;i se sting alternativ?', 'uploads/1034.jpg', '<p id=\"v_a\"><b>a. </b> reduce&#355;i viteza &#351;i, dup&#259; ce v&#259; asigura&#355;i, v&#259; continua&#355;i deplasarea;\r\n</p><p id=\"v_b\"><b>b. </b> reduce&#355;i viteza &#351;i opri&#355;i numai în cazul în care se apropie un tren;\r\n</p><p id=\"v_c\"><b>c. </b> opri&#355;i f&#259;r&#259; a dep&#259;&#351;i indicatorul.</p>', 'nu', 'nu', 'da', ''),
(670, 'Semnifica&#355;ia unui indicator rutier, instalat deasupra unei benzi de circula&#355;ie, este valabil&#259;:', '', '<p id=\"v_a\"><b>a. </b> numai pentru circula&#355;ia rutier&#259; care se desf&#259;&#351;oar&#259; în tunele;\r\n</p><p id=\"v_b\"><b>b. </b> numai pentru banda deasupra c&#259;reia este instalat;\r\n</p><p id=\"v_c\"><b>c. </b> pentru toat&#259; l&#259;&#355;imea p&#259;r&#355;ii carosabile.</p>', 'nu', 'da', 'nu', ''),
(671, 'Care este valoarea alcoolemiei în sânge peste care se aplic&#259; pedeapsa penal&#259; pentru un conduc&#259;tor aflat la volan?', '', '<p id=\"v_a\"><b>a. </b> 0,5 g/l;\r\n</p><p id=\"v_b\"><b>b. </b> 0,8 g/l;\r\n</p><p id=\"v_c\"><b>c. </b> 1 g/l.</p>', 'nu', 'da', 'nu', ''),
(672, 'Cum trebuie scoase dintr-un autovehicul persoanele r&#259;nite?', '', '<p id=\"v_a\"><b>a. </b> prin ridicare;\r\n</p><p id=\"v_b\"><b>b. </b> prin tragere;\r\n</p><p id=\"v_c\"><b>c. </b> prin împingere.</p>', 'da', 'nu', 'nu', ''),
(673, 'În situa&#355;ia dat&#259;, v&#259; este interzis:', 'uploads/912.jpg', '<p id=\"v_a\"><b>a. </b> s&#259; continua&#355;i deplasarea;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; opri&#355;i;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; ocoli&#355;i un obstacol prin stânga.</p>', 'nu', 'da', 'nu', ''),
(674, 'Autoturismul a oprit regulamentar?', 'uploads/210.jpg', '<p id=\"v_a\"><b>a. </b> da, deoarece drumul nu este intens circulat;\r\n</p><p id=\"v_b\"><b>b. </b> da, pentru c&#259; spa&#355;iul r&#259;mas este suficient pentru dep&#259;&#351;ire;\r\n</p><p id=\"v_c\"><b>c. </b> nu, deoarece pe aceast&#259; categorie de drum sunt interzise oprirea &#351;i sta&#355;ionarea.</p>', 'nu', 'nu', 'da', ''),
(675, 'Ce semnifica&#355;ie are marcajul rutier din imagine?', 'uploads/379.jpg', '<p id=\"v_a\"><b>a. </b> band&#259; de circula&#355;ie destinat&#259; opririi voluntare;\r\n</p><p id=\"v_b\"><b>b. </b> spa&#355;iu pentru oprirea în caz de urgen&#355;&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> marcaj transversal de oprire.</p>', 'nu', 'nu', 'da', ''),
(676, 'Indicatorul din imagine v&#259; avertizeaz&#259; c&#259; urmeaz&#259;:', 'uploads/326.jpg', '<p id=\"v_a\"><b>a. </b> o succesiune de curbe pe o distan&#355;&#259; de 1,5 km, prima curb&#259; fiind la stânga;\r\n</p><p id=\"v_b\"><b>b. </b> o succesiune de curbe în ramp&#259; la o distan&#355;&#259; de 1,5 km fa&#355;&#259; de indicator, prima curb&#259; fiind la dreapta;\r\n</p><p id=\"v_c\"><b>c. </b> o curb&#259; dubl&#259;, prima la dreapta, la o distan&#355;&#259; de 1,5 km fa&#355;&#259; de indicator.</p>', 'da', 'nu', 'nu', ''),
(677, 'Certificatul de înmatriculare se re&#355;ine:', '', '<p id=\"v_a\"><b>a. </b> dac&#259; sistemul de frânare este defect;\r\n</p><p id=\"v_b\"><b>b. </b> dac&#259; pneurile nu sunt umflate la presiunea minim&#259; admis&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> dac&#259; sistemul de închidere centralizat&#259; a u&#351;ilor este defect.</p>', 'da', 'nu', 'nu', ''),
(678, 'Cum trebuie s&#259; procedeze corect conduc&#259;torul de vehicul, pentru a trece de pe banda întâi a unui drum public pe banda a treia?', '', '<p id=\"v_a\"><b>a. </b> reduce viteza de mers &#351;i trece de pe o band&#259; pe alta, asigurându-se continuu în oglinda retrovizoare;\r\n</p><p id=\"v_b\"><b>b. </b> dac&#259; trecerea de pe o band&#259; pe alta nu s-a f&#259;cut cu cel pu&#355;in 200 m înainte de intersec&#355;ie, manevra nu mai este posibil&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> se asigur&#259; din spate &#351;i semnalizeaz&#259; inten&#355;ia de a trece succesiv de pe banda întâi pe a doua, apoi pe a treia.</p>', 'nu', 'nu', 'da', ''),
(679, 'Care dintre urm&#259;toarele tipuri de carosabil este cel mai periculos în condi&#355;ii de ploaie?', '', '<p id=\"v_a\"><b>a. </b> carosabilul cu suprafa&#355;a asfaltat&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> carosabilul cu suprafa&#355;a betonat&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> carosabilul cu piatr&#259; cubic&#259;.</p>', 'nu', 'nu', 'da', ''),
(680, 'Sunte&#355;i obligat s&#259; reduce&#355;i viteza pân&#259; la limita legal&#259; la trecerile pentru pietoni nesemaforizate, dar semnalizate prin indicatoare &#351;i marcaje, dac&#259;:', '', '<p id=\"v_a\"><b>a. </b> drumul are cel mult o band&#259; pe sens, iar pietonii afla&#355;i pe trotuar inten&#355;ioneaz&#259; s&#259; se angajeze în traversare;\r\n</p><p id=\"v_b\"><b>b. </b> drumul are cel pu&#355;in dou&#259; benzi pe sens;\r\n</p><p id=\"v_c\"><b>c. </b> trecerile sunt localizate în dreptul sta&#355;iilor de tramvai.</p>', 'da', 'nu', 'nu', ''),
(681, 'Ce rând trebuie s&#259; ocupa&#355;i &#351;i de la ce distan&#355;&#259;, pentru a putea executa virajul la stânga într-o intersec&#355;ie nedirijat&#259;?', '', '<p id=\"v_a\"><b>a. </b> rândul de lâng&#259; axa drumului, de la o distan&#355;&#259; de cel pu&#355;in 50 m;\r\n</p><p id=\"v_b\"><b>b. </b> rândul din mijloc, de la o distan&#355;&#259; de cel pu&#355;in 100 m;\r\n</p><p id=\"v_c\"><b>c. </b> rândul de lâng&#259; bordur&#259;, de la o distan&#355;&#259; de cel pu&#355;in 50 m.</p>', 'da', 'nu', 'nu', ''),
(682, 'Cui trebuie s&#259; acorda&#355;i prioritate dac&#259; v&#259; afla&#355;i în autoturismul albastru?', 'uploads/977.jpg', '<p id=\"v_a\"><b>a. </b> autobuzului, fiind vehicul de transport public de persoane;\r\n</p><p id=\"v_b\"><b>b. </b> autoturismului ro&#351;u, deoarece nu-&#351;i schimb&#259; direc&#355;ia de mers;\r\n</p><p id=\"v_c\"><b>c. </b> ambelor vehicule.</p>', 'nu', 'nu', 'da', ''),
(683, 'La schimbarea direc&#355;iei de mers spre stânga, la semnalul verde al semaforului, conduc&#259;torul de autovehicul este obligat s&#259; acorde prioritate:', '', '<p id=\"v_a\"><b>a. </b> numai vehiculelor care vin din sens opus;\r\n</p><p id=\"v_b\"><b>b. </b> vehiculelor care circul&#259; din sens opus &#351;i pietonilor care traverseaz&#259; regulamentar strada pe care acesta urmeaz&#259; s&#259; intre;\r\n</p><p id=\"v_c\"><b>c. </b> numai pietonilor care sunt angaja&#355;i în traversarea str&#259;zii.</p>', 'nu', 'da', 'nu', ''),
(684, 'Cum ve&#355;i proceda la întâlnirea indicatorului al&#259;turat?', 'uploads/268.jpg', '<p id=\"v_a\"><b>a. </b> circula&#355;i cu aten&#355;ie, deoarece urmeaz&#259; o intersec&#355;ie cu cedarea trecerii la 300 m;\r\n</p><p id=\"v_b\"><b>b. </b> reduce&#355;i viteza, v&#259; asigura&#355;i &#351;i ceda&#355;i trecerea tuturor vehiculelor care circul&#259; pe o distan&#355;&#259; de 300 m;\r\n</p><p id=\"v_c\"><b>c. </b> circula&#355;i cu aten&#355;ie, pentru a putea opri în condi&#355;ii de siguran&#355;&#259;, în maximum 300 m.</p>', 'da', 'nu', 'nu', ''),
(685, 'Care este rolul uleiului pentru motor?', '', '<p id=\"v_a\"><b>a. </b> asigur&#259; demontarea mai u&#351;oar&#259; a pieselor motorului;\r\n</p><p id=\"v_b\"><b>b. </b> reduce uzura motorului, prin crearea unui film rezistent de lubrifiant între suprafe&#355;ele pieselor aflate în mi&#351;care;\r\n</p><p id=\"v_c\"><b>c. </b> protejeaz&#259; motorul fa&#355;&#259; de p&#259;trunderea amestecului carburant în baia de ulei.</p>', 'nu', 'da', 'nu', ''),
(686, 'Care este semnalizarea specific&#259; mersului înapoi?', '', '<p id=\"v_a\"><b>a. </b> claxonatul intermitent, pentru a-i aten&#355;iona pe participan&#355;ii la trafic din spatele vehiculului;\r\n</p><p id=\"v_b\"><b>b. </b> semnalizarea cu luminile de mers înapoi din dotarea autovehiculului;\r\n</p><p id=\"v_c\"><b>c. </b> folosirea luminilor de avarie.</p>', 'nu', 'da', 'nu', ''),
(687, 'Ce regul&#259; de prioritate se aplic&#259; într-o intersec&#355;ie nedirijat&#259; din afara localit&#259;&#355;ii?', '', '<p id=\"v_a\"><b>a. </b> prioritatea vehiculului cu regim superior de vitez&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> prioritatea drumului principal;\r\n</p><p id=\"v_c\"><b>c. </b> prioritatea de dreapta.</p>', 'nu', 'nu', 'da', ''),
(688, 'Este interzis&#259; sta&#355;ionarea unui autoturism în zona marcajului longitudinal continuu?', '', '<p id=\"v_a\"><b>a. </b> nu;\r\n</p><p id=\"v_b\"><b>b. </b> da, în cazul în care conduc&#259;torii celorlalte vehicule ar fi determina&#355;i s&#259; treac&#259; peste marcajul respectiv;\r\n</p><p id=\"v_c\"><b>c. </b> legal, nu este prev&#259;zut.</p>', 'nu', 'da', 'nu', ''),
(689, 'Cum ve&#355;i proceda pentru a traversa intersec&#355;ia dac&#259; întâlni&#355;i indicatorul „Cedeaz&#259; trecerea“?', '', '<p id=\"v_a\"><b>a. </b> reduce&#355;i viteza numai dac&#259; se apropie vreun autovehicul;\r\n</p><p id=\"v_b\"><b>b. </b> reduce&#355;i viteza, v&#259; asigura&#355;i, iar dac&#259; pe drumul transversal circul&#259; în acel moment alte vehicule, opri&#355;i pentru a le acorda prioritate de trecere;\r\n</p><p id=\"v_c\"><b>c. </b> reduce&#355;i viteza &#351;i v&#259; asigura&#355;i, pentru a fi în m&#259;sur&#259; s&#259; acorda&#355;i prioritate de trecere vehiculelor care circul&#259; din partea dreapt&#259;.</p>', 'nu', 'da', 'nu', ''),
(690, 'Sunte&#355;i obligat s&#259; opri&#355;i autovehiculul pe care îl conduce&#355;i:', '', '<p id=\"v_a\"><b>a. </b> la semnalul poli&#355;istului care dirijeaz&#259; circula&#355;ia;\r\n</p><p id=\"v_b\"><b>b. </b> la semnalul poli&#355;istului, dar numai dac&#259; acesta corespunde semnalului de culoare ro&#351;ie al semaforului;\r\n</p><p id=\"v_c\"><b>c. </b> la semnalul pietonilor care traverseaz&#259; strada pe la col&#355;ul intersec&#355;iei f&#259;r&#259; marcaje.</p>', 'da', 'nu', 'nu', ''),
(691, 'Pe autostrad&#259; se interzice:', '', '<p id=\"v_a\"><b>a. </b> circula&#355;ia autovehiculelor în coloan&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> înv&#259;&#355;area conducerii unui vehicul, încercarea prototipurilor de &#351;asiuri &#351;i de vehicule cu motor;\r\n</p><p id=\"v_c\"><b>c. </b> executarea de lucr&#259;ri pe partea carosabil&#259; pe timp de noapte.</p>', 'nu', 'da', 'nu', ''),
(692, 'Interzicerea trecerii la nivel cu calea ferat&#259; curent&#259;, prev&#259;zut&#259; cu instala&#355;ii de semnalizare automat&#259; f&#259;r&#259; bariere, se realizeaz&#259; prin:', '', '<p id=\"v_a\"><b>a. </b> dispozitivele cu lumini intermitent-alternative ro&#351;ii &#351;i stingerea semnaliz&#259;rii reprezentat&#259; de lumina alb&#259; intermitent&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> semnalele luminoase intermitente de culoare alb&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> dispozitivele optice cu lumini alternative ro&#351;ii &#351;i albe.</p>', 'da', 'nu', 'nu', ''),
(693, 'Cum trebuie s&#259; procedeze conduc&#259;torul de autovehicul înainte de a se angaja într-o dep&#259;&#351;ire?', '', '<p id=\"v_a\"><b>a. </b> semnalizeaz&#259; &#351;i se apropie cât mai mult de axul drumului, pentru a-&#351;i asigura vizibilitatea asupra circula&#355;iei din sens opus;\r\n</p><p id=\"v_b\"><b>b. </b> avertizeaz&#259; prin semnale acustice sau optice pe conduc&#259;torul vehiculului ce urmeaz&#259; a fi dep&#259;&#351;it;\r\n</p><p id=\"v_c\"><b>c. </b> se asigur&#259; c&#259; manevra se poate efectua în condi&#355;ii de siguran&#355;&#259;, dup&#259; care semnalizeaz&#259; inten&#355;ia de a schimba direc&#355;ia de mers.</p>', 'nu', 'nu', 'da', ''),
(694, 'C&#259;ile respiratorii pot fi astupate prin:', '', '<p id=\"v_a\"><b>a. </b> a&#351;ezarea pe spate a victimei;\r\n</p><p id=\"v_b\"><b>b. </b> a&#351;ezarea pe o parte a victimei;\r\n</p><p id=\"v_c\"><b>c. </b> intrarea unor corpi str&#259;ini pe c&#259;ile respiratorii.</p>', 'nu', 'nu', 'da', ''),
(695, 'Cum ve&#355;i proceda noaptea, când vizibilitatea este redus&#259; din cauza ce&#355;ii, din punct de vedere al circula&#355;iei preventive?', '', '<p id=\"v_a\"><b>a. </b> circula&#355;i cât mai aproape de axul drumului;\r\n</p><p id=\"v_b\"><b>b. </b> circula&#355;i cât mai aproape de marginea din dreapta a drumului public;\r\n</p><p id=\"v_c\"><b>c. </b> p&#259;stra&#355;i o distan&#355;&#259; egal&#259; între axa drumului &#351;i marginea din dreapta a p&#259;r&#355;ii carosabile, rulând cu o vitez&#259; care s&#259; asigure oprirea într-un spa&#355;iu mai mic decât cel de vizibilitate.</p>', 'nu', 'nu', 'da', ''),
(696, 'Care dintre cele trei vehicule va trece al doilea prin intersec&#355;ie?', 'uploads/253.jpg', '<p id=\"v_a\"><b>a. </b> autoturismul verde;\r\n</p><p id=\"v_b\"><b>b. </b> autoturismul ro&#351;u;\r\n</p><p id=\"v_c\"><b>c. </b> autocamionul.</p>', 'da', 'nu', 'nu', ''),
(697, 'În care dintre urm&#259;toarele situa&#355;ii conduc&#259;torilor de vehicule le este interzis s&#259; execute virajul spre stânga la lumina de culoare verde a semaforului electric?', '', '<p id=\"v_a\"><b>a. </b> când întâlnesc indicatorul „Înainte sau la stânga“;\r\n</p><p id=\"v_b\"><b>b. </b> când se afl&#259; pe o strad&#259; cu sens unic;\r\n</p><p id=\"v_c\"><b>c. </b> când întâlnesc indicatorul „Înainte sau la dreapta“.</p>', 'nu', 'nu', 'da', ''),
(698, 'Pentru a fi conduse pe drumurile publice, autovehiculele trebuie s&#259; fie dotate cu:', '', '<p id=\"v_a\"><b>a. </b> sting&#259;tor de incendiu;\r\n</p><p id=\"v_b\"><b>b. </b> trus&#259; medical&#259; de prim ajutor;\r\n</p><p id=\"v_c\"><b>c. </b> dou&#259; triunghiuri reflectorizante.</p>', 'da', 'da', 'da', ''),
(699, 'Câte corpuri de iluminat are semaforul care se adreseaz&#259; pietonilor &#351;i bicicli&#351;tilor?', '', '<p id=\"v_a\"><b>a. </b> dou&#259; corpuri;\r\n</p><p id=\"v_b\"><b>b. </b> trei corpuri;\r\n</p><p id=\"v_c\"><b>c. </b> patru corpuri.</p>', 'da', 'nu', 'nu', ''),
(700, 'Încredin&#355;area cu &#351;tiin&#355;&#259; a unui autovehicul, pentru a fi condus pe drumul public, unei persoane care nu posed&#259; permis de conducere se pedepse&#351;te:', '', '<p id=\"v_a\"><b>a. </b> penal;\r\n</p><p id=\"v_b\"><b>b. </b> contraven&#355;ional;\r\n</p><p id=\"v_c\"><b>c. </b> cu amend&#259; contraven&#355;ional&#259; &#351;i anularea permisului de conducere.</p>', 'da', 'nu', 'nu', ''),
(701, 'Cea mai frecvent&#259; defec&#355;iune a instala&#355;iei de r&#259;cire a motorului este:', '', '<p id=\"v_a\"><b>a. </b> sl&#259;birea sau ruperea curelei de antrenare a ventilatorului &#351;i a pompei de ap&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> înfundarea prizei de aer care alimenteaz&#259; ventilatorul;\r\n</p><p id=\"v_c\"><b>c. </b> patinarea ambreiajului.</p>', 'da', 'nu', 'nu', ''),
(702, 'Pute&#355;i efectua dep&#259;&#351;irea camionului?', 'uploads/649.jpg', '<p id=\"v_a\"><b>a. </b> nu, deoarece nu ave&#355;i vizibilitate suficient&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> da, deoarece interzicerea dep&#259;&#351;irii se refer&#259; numai la autovehiculele destinate transportului de marf&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> nu, deoarece indicatorul interzice dep&#259;&#351;irea autovehiculelor.</p>', 'nu', 'da', 'nu', ''),
(703, 'Ce semnific&#259; aceast&#259; pozi&#355;ie a poli&#355;istului rutier?', 'uploads/809.jpg', '<p id=\"v_a\"><b>a. </b> nu ave&#355;i nicio obliga&#355;ie;\r\n</p><p id=\"v_b\"><b>b. </b> oprire;\r\n</p><p id=\"v_c\"><b>c. </b> drum blocat.</p>', 'nu', 'da', 'nu', ''),
(704, 'Este interzis&#259; sta&#355;ionarea în dreptul marcajului longitudinal continuu, aplicat între sensurile de circula&#355;ie?', '', '<p id=\"v_a\"><b>a. </b> nu;\r\n</p><p id=\"v_b\"><b>b. </b> da, atunci când conduc&#259;torii celorlalte vehicule ar fi obliga&#355;i astfel s&#259; treac&#259; peste marcajul respectiv;\r\n</p><p id=\"v_c\"><b>c. </b> legal, nu este prev&#259;zut.</p>', 'nu', 'da', 'nu', ''),
(705, 'Semnalul verde al semaforului permite deplasarea:', '', '<p id=\"v_a\"><b>a. </b> pe oricare direc&#355;ie, dac&#259; printr-un mijloc de semnalizare nu se dispune altfel;\r\n</p><p id=\"v_b\"><b>b. </b> numai pe direc&#355;ia înainte;\r\n</p><p id=\"v_c\"><b>c. </b> pe oricare direc&#355;ie, dac&#259; vehiculul se afl&#259; pe banda din mijloc.</p>', 'da', 'nu', 'nu', ''),
(706, 'Semnifica&#355;ia indicatoarelor „Oprire“ &#351;i „Cedeaz&#259; trecerea“, instalate în intersec&#355;iile semaforizate, trebuie respectat&#259;:', '', '<p id=\"v_a\"><b>a. </b> atunci când traficul este redus;\r\n</p><p id=\"v_b\"><b>b. </b> atunci când semafoarele nu func&#355;ioneaz&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> atunci când indicatoarele sunt instalate împreun&#259; cu semaforul pe acela&#351;i suport.</p>', 'nu', 'da', 'nu', ''),
(707, 'Ce obliga&#355;ie are conduc&#259;torul unui autovehicul imobilizat într-un pasaj subteran?', '', '<p id=\"v_a\"><b>a. </b> s&#259; opreasc&#259; motorul &#351;i s&#259; ac&#355;ioneze luminile de avarie;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; tracteze autovehiculul pân&#259; la ie&#351;irea din pasajul subteran;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; anun&#355;e imediat pompierii în leg&#259;tur&#259; cu locul în care se afl&#259;.</p>', 'da', 'nu', 'nu', ''),
(708, 'Cum trebuie a&#351;ezat&#259; într-un mijloc de transport o persoan&#259; r&#259;nit&#259;, care prezint&#259; leziuni ale coloanei vertebrale?', '', '<p id=\"v_a\"><b>a. </b> în pozi&#355;ie &#351;ezând&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> orizontal, cu fa&#355;a în sus;\r\n</p><p id=\"v_c\"><b>c. </b> persoana nu trebuie mi&#351;cat&#259; pân&#259; la sosirea ambulan&#355;ei.</p>', 'nu', 'nu', 'da', ''),
(709, 'Care dintre indicatoare oblig&#259; la schimbarea direc&#355;iei de mers pe prima strad&#259; la dreapta?', 'uploads/938.jpg', '<p id=\"v_a\"><b>a. </b> indicatorul 1;\r\n</p><p id=\"v_b\"><b>b. </b> indicatorul 2;\r\n</p><p id=\"v_c\"><b>c. </b> ambele indicatoare.</p>', 'nu', 'da', 'nu', ''),
(710, 'Când se poate face radierea din eviden&#355;&#259; a unui vehicul, la cererea proprietarului?', '', '<p id=\"v_a\"><b>a. </b> la schimbarea oric&#259;ror date referitoare la identificarea vehiculului respectiv;\r\n</p><p id=\"v_b\"><b>b. </b> la schimbarea domiciliului ori a sediului unit&#259;&#355;ii de&#355;in&#259;toare;\r\n</p><p id=\"v_c\"><b>c. </b> când proprietarul face dovada dezmembr&#259;rii, cas&#259;rii sau pred&#259;rii vehiculului la unit&#259;&#355;i specializate, în vederea dezmembr&#259;rii.</p>', 'nu', 'nu', 'da', ''),
(711, 'Care dintre autovehicule este parcat regulamentar?', 'uploads/458.jpg', '<p id=\"v_a\"><b>a. </b> autoturismul &#351;i autocamionul;\r\n</p><p id=\"v_b\"><b>b. </b> toate cele trei autovehicule;\r\n</p><p id=\"v_c\"><b>c. </b> autobuzul.</p>', 'da', 'nu', 'nu', ''),
(712, 'Bra&#355;ele întinse orizontal, ale poli&#355;istului care dirijeaz&#259; circula&#355;ia, semnific&#259;:', '', '<p id=\"v_a\"><b>a. </b> oprire pentru to&#355;i participan&#355;ii la trafic care, indiferent de sensul lor de mers, circul&#259; din direc&#355;ia sau direc&#355;iile intersectate de bra&#355;ele întinse ale acestuia;\r\n</p><p id=\"v_b\"><b>b. </b> oprire pentru to&#355;i participan&#355;ii la trafic care circul&#259; numai din spatele acestuia;\r\n</p><p id=\"v_c\"><b>c. </b> oprire pentru to&#355;i participan&#355;ii la trafic care circul&#259; numai din fa&#355;a acestuia.</p>', 'da', 'nu', 'nu', ''),
(713, 'Ce informa&#355;ie ofer&#259; indicatorul?', 'uploads/267.jpg', '<p id=\"v_a\"><b>a. </b> indic&#259; un traseu de drum deschis traficului interna&#355;ional;\r\n</p><p id=\"v_b\"><b>b. </b> interzice accesul autoturismelor în ora&#351; &#351;i indic&#259; traseul de urmat pentru ocolirea acestuia;\r\n</p><p id=\"v_c\"><b>c. </b> indic&#259; traseul de ocolire a localit&#259;&#355;ii.</p>', 'nu', 'nu', 'da', '');
INSERT INTO `intrebari` (`id`, `cerinta`, `imagine`, `variante`, `rasp_a`, `rasp_b`, `rasp_c`, `eticheta`) VALUES
(714, 'Care este ordinea de trecere a autovehiculelor, dac&#259; ajung simultan în intersec&#355;ie?', 'uploads/231.jpg', '<p id=\"v_a\"><b>a. </b> autocamionul, tramvaiul, autoturismul;\r\n</p><p id=\"v_b\"><b>b. </b> autoturismul, autocamionul, tramvaiul;\r\n</p><p id=\"v_c\"><b>c. </b> tramvaiul, autoturismul, autocamionul.</p>', 'da', 'nu', 'nu', ''),
(715, 'Mersul înapoi este interzis:', '', '<p id=\"v_a\"><b>a. </b> pe o distan&#355;&#259; mai mare de 50 m;\r\n</p><p id=\"v_b\"><b>b. </b> legal, nu este prev&#259;zut&#259; o anumit&#259; distan&#355;&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> pe o distan&#355;&#259; mai mare de 100 m.</p>', 'da', 'nu', 'nu', ''),
(716, 'Când nu este permis s&#259; m&#259;ri&#355;i viteza de deplasare a autovehiculului?', '', '<p id=\"v_a\"><b>a. </b> când circula&#355;i pe prima band&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> când un alt vehicul s-a angajat în dep&#259;&#351;irea dvs.;\r\n</p><p id=\"v_c\"><b>c. </b> în intersec&#355;ii.</p>', 'nu', 'da', 'nu', ''),
(717, 'Exercitarea dreptului de a conduce se suspend&#259; pe o perioad&#259; de 30 de zile pentru:', '', '<p id=\"v_a\"><b>a. </b> nerespectarea regulilor referitoare la remorcare;\r\n</p><p id=\"v_b\"><b>b. </b> utilizarea excesiv&#259; a claxonului;\r\n</p><p id=\"v_c\"><b>c. </b> dep&#259;&#351;irea unei coloane de vehicule oprite la culoarea ro&#351;ie a semaforului.</p>', 'nu', 'nu', 'da', ''),
(718, 'Ce semnifica&#355;ie are marcajul rutier al&#259;turat?', 'uploads/760.jpg', '<p id=\"v_a\"><b>a. </b> marcaj pentru benzi cu circula&#355;ie reversibil&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> marcaj pentru drumuri publice aglomerate;\r\n</p><p id=\"v_c\"><b>c. </b> marcaj cu elemente rezonatoare</p>', 'da', 'nu', 'nu', ''),
(719, 'Oprirea hemoragiei se face prin legarea strâns&#259; a bra&#355;ului:', '', '<p id=\"v_a\"><b>a. </b> în dreptul r&#259;nii;\r\n</p><p id=\"v_b\"><b>b. </b> deasupra r&#259;nii;\r\n</p><p id=\"v_c\"><b>c. </b> sub ran&#259;.</p>', 'nu', 'da', 'nu', ''),
(720, 'Poli&#355;istul nu permite trecerea:', 'uploads/755.jpg', '<p id=\"v_a\"><b>a. </b> tramvaiului;\r\n</p><p id=\"v_b\"><b>b. </b> camionului;\r\n</p><p id=\"v_c\"><b>c. </b> autoturismului verde.</p>', 'da', 'nu', 'nu', ''),
(721, 'Care sunt criteriile de apreciere a eficacit&#259;&#355;ii frânelor?', '', '<p id=\"v_a\"><b>a. </b> spa&#355;iul de frânare;\r\n</p><p id=\"v_b\"><b>b. </b> forma pedalei;\r\n</p><p id=\"v_c\"><b>c. </b> m&#259;rimea urmei de frânare imprimat&#259; pe carosabil.</p>', 'da', 'nu', 'nu', ''),
(722, 'Viteza de deplasare trebuie s&#259; nu dep&#259;&#351;easc&#259; 30 km/h în localit&#259;&#355;i sau 50 km/h în afara localit&#259;&#355;ilor:', '', '<p id=\"v_a\"><b>a. </b> la întâlnirea indicatorului „Drum cu prioritate“;\r\n</p><p id=\"v_b\"><b>b. </b> în curbe deosebit de periculoase semnalizate ca atare sau în care vizibilitatea este mai mic&#259; de 50 m;\r\n</p><p id=\"v_c\"><b>c. </b> atunci când se circul&#259; în aceea&#351;i direc&#355;ie cu autobuzele oprite în sta&#355;ie, pe drumurile cu o singur&#259; band&#259; pe sens.</p>', 'nu', 'da', 'nu', ''),
(723, 'Este permis&#259; dep&#259;&#351;irea când din sens opus se apropie, la mic&#259; distan&#355;&#259;, alt vehicul?', '', '<p id=\"v_a\"><b>a. </b> da, dac&#259; prin dep&#259;&#351;ire se trece peste axa drumului;\r\n</p><p id=\"v_b\"><b>b. </b> da, dac&#259; drumul are o l&#259;&#355;ime suficient&#259;, iar prin dep&#259;&#351;ire nu se trece peste axa drumului;\r\n</p><p id=\"v_c\"><b>c. </b> da, dac&#259; nu exist&#259; interdic&#355;ia de a dep&#259;&#351;i.</p>', 'nu', 'da', 'nu', ''),
(724, 'Cum proceda&#355;i în prima intersec&#355;ie?', 'uploads/456.jpg', '<p id=\"v_a\"><b>a. </b> acorda&#355;i prioritate de trecere tuturor vehiculelor care circul&#259; în intersec&#355;ie;\r\n</p><p id=\"v_b\"><b>b. </b> acorda&#355;i prioritate de trecere numai vehiculelor care circul&#259; din stânga;\r\n</p><p id=\"v_c\"><b>c. </b> aplica&#355;i regula priorit&#259;&#355;ii de dreapta.</p>', 'nu', 'nu', 'da', ''),
(725, 'Ce obliga&#355;ii ave&#355;i înainte de efectuarea unei dep&#259;&#351;iri?', '', '<p id=\"v_a\"><b>a. </b> s&#259; semnaliza&#355;i inten&#355;ia &#351;i s&#259; efectua&#355;i dep&#259;&#351;irea;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; v&#259; asigura&#355;i din fa&#355;&#259; &#351;i s&#259; reduce&#355;i corespunz&#259;tor viteza;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; v&#259; asigura&#355;i &#351;i s&#259; semnaliza&#355;i inten&#355;ia de dep&#259;&#351;ire.</p>', 'nu', 'nu', 'da', ''),
(726, 'Când este indicat s&#259; verifica&#355;i presiunea din anvelope?', '', '<p id=\"v_a\"><b>a. </b> întotdeauna la 300 km;\r\n</p><p id=\"v_b\"><b>b. </b> de regul&#259;, înainte de a porni la drum;\r\n</p><p id=\"v_c\"><b>c. </b> numai pe timp de iarn&#259;, când anvelopele sunt reci.</p>', 'nu', 'da', 'nu', ''),
(727, 'Ce semnific&#259; indicatorul?', 'uploads/934.jpg', '<p id=\"v_a\"><b>a. </b> presemnalizeaz&#259; traseul de urmat în vederea efectu&#259;rii virajului la dreapta;\r\n</p><p id=\"v_b\"><b>b. </b> semnalizeaz&#259; o succesiune de curbe, dintre care prima la dreapta;\r\n</p><p id=\"v_c\"><b>c. </b> o curb&#259; deosebit de periculoas&#259;, spre dreapta.</p>', 'nu', 'nu', 'da', ''),
(728, 'Indicatorul semnific&#259;:', 'uploads/605.jpg', '<p id=\"v_a\"><b>a. </b> o trecere la nivel cu calea ferat&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> o trecere la nivel cu calea ferat&#259; industrial&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> apropierea de un depou de tramvaie.</p>', 'nu', 'da', 'nu', ''),
(729, 'În aceast&#259; situa&#355;ie, cui trebuie s&#259; acorda&#355;i prioritate ?', 'uploads/673.jpg', '<p id=\"v_a\"><b>a. </b> vehiculelor care circul&#259; pe drumul transversal;\r\n</p><p id=\"v_b\"><b>b. </b> vehiculelor care circul&#259; din partea stâng&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> niciunui vehicul.</p>', 'da', 'nu', 'nu', ''),
(730, 'Bra&#355;ul stâng, întins orizontal, al unui biciclist semnific&#259;:', '', '<p id=\"v_a\"><b>a. </b> inten&#355;ia de a schimba direc&#355;ia de mers la stânga sau de a dep&#259;&#351;i;\r\n</p><p id=\"v_b\"><b>b. </b> inten&#355;ia de a opri;\r\n</p><p id=\"v_c\"><b>c. </b> faptul c&#259; nu trebuie s&#259; fie dep&#259;&#351;it.</p>', 'da', 'nu', 'nu', ''),
(731, 'Inten&#355;iona&#355;i s&#259; transporta&#355;i o înc&#259;rc&#259;tur&#259; cu autoturismul personal. Ce trebuie s&#259; ave&#355;i în vedere?', '', '<p id=\"v_a\"><b>a. </b> în fa&#355;&#259;, înc&#259;rc&#259;tura poate dep&#259;&#351;i cu un metru limitele vehiculului;\r\n</p><p id=\"v_b\"><b>b. </b> înc&#259;rc&#259;tura s&#259; nu fie voluminoas&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; nu fie dep&#259;&#351;ite lungimea, l&#259;&#355;imea &#351;i greutatea admise.</p>', 'nu', 'nu', 'da', ''),
(732, 'Indicatorul v&#259; avertizeaz&#259; c&#259;:', 'uploads/131.jpg', '<p id=\"v_a\"><b>a. </b> drumul se intersecteaz&#259; cu o trecere la nivel cu linii de tramvai;\r\n</p><p id=\"v_b\"><b>b. </b> urmeaz&#259; o sta&#355;ie de tramvai prev&#259;zut&#259; cu refugiu pentru pietoni;\r\n</p><p id=\"v_c\"><b>c. </b> urmeaz&#259; o trecere la nivel cu o cale ferat&#259; f&#259;r&#259; bariere sau semibariere, cu semnalizare automat&#259;.</p>', 'da', 'nu', 'nu', ''),
(733, 'Pe un drum cu prioritate, conduc&#259;torul de autovehicul întâlne&#351;te în intersec&#355;ie marcajul de trecere pentru pietoni. Este obligat s&#259; acorde prioritate?', '', '<p id=\"v_a\"><b>a. </b> da, pietonilor angaja&#355;i în traversarea drumului pe trecerea semnalizat&#259; &#351;i afla&#355;i pe sensul lui de mers;\r\n</p><p id=\"v_b\"><b>b. </b> nu, deoarece circul&#259; pe un drum prioritar;\r\n</p><p id=\"v_c\"><b>c. </b> da, atât pietonilor, cât &#351;i vehiculelor care se apropie din sens opus &#351;i care schimb&#259; direc&#355;ia de mers spre stânga.</p>', 'da', 'nu', 'nu', ''),
(734, 'Ce semnific&#259; indicatorul din imagine?', 'uploads/62.jpg', '<p id=\"v_a\"><b>a. </b> zon&#259; în care viteza recomandat&#259; este de 30 km/h;\r\n</p><p id=\"v_b\"><b>b. </b> zon&#259; în care este interzis&#259; circula&#355;ia sub 30 km/h;\r\n</p><p id=\"v_c\"><b>c. </b> zon&#259; în care le este interzis autovehiculelor destinate transportului de m&#259;rfuri s&#259; circule cu peste 30 km/h.</p>', 'da', 'nu', 'nu', ''),
(735, 'Ce este bine s&#259; ave&#355;i în vedere pentru evitarea unui pericol, în situa&#355;ia din imagine?', 'uploads/866.jpg', '<p id=\"v_a\"><b>a. </b> copiii circul&#259; corect pe trecerea de pietoni;\r\n</p><p id=\"v_b\"><b>b. </b> copiii se pot întoarce alergând pe trecerea de pietoni, f&#259;r&#259; motiv sesizabil;\r\n</p><p id=\"v_c\"><b>c. </b> copiii urm&#259;resc cu aten&#355;ie circula&#355;ia &#351;i se comport&#259; ca atare.</p>', 'nu', 'da', 'nu', ''),
(736, 'În acest sens giratoriu, sunte&#355;i încadrat corespunz&#259;tor pentru:', 'uploads/330.jpg', '<p id=\"v_a\"><b>a. </b> a vira la dreapta;\r\n</p><p id=\"v_b\"><b>b. </b> a vira la stânga sau pentru a circula înainte;\r\n</p><p id=\"v_c\"><b>c. </b> a circula înainte.</p>', 'nu', 'da', 'nu', ''),
(737, 'Cum trebuie s&#259; semnaliza&#355;i inten&#355;ia de a dep&#259;&#351;i alte vehicule, atunci când conduce&#355;i autovehiculul noaptea, pe un drum neiluminat dintr-o localitate?', '', '<p id=\"v_a\"><b>a. </b> cu luminile farurilor;\r\n</p><p id=\"v_b\"><b>b. </b> prin folosirea luminii de drum sau a claxonului;\r\n</p><p id=\"v_c\"><b>c. </b> cu luminile indicatoare de direc&#355;ie.</p>', 'nu', 'nu', 'da', ''),
(738, 'Viteza maxim&#259; autorizat&#259; pe autostr&#259;zi pentru autovehiculele din categoriile A &#351;i B este de:', 'uploads/662.jpg', '<p id=\"v_a\"><b>a. </b> 90 km/h;\r\n</p><p id=\"v_b\"><b>b. </b> 110 km/h;\r\n</p><p id=\"v_c\"><b>c. </b> 130 km/h.</p>', 'nu', 'nu', 'da', ''),
(739, 'Ce obliga&#355;ii ave&#355;i atunci când intra&#355;i pe autostrad&#259; folosind banda de accelerare?', '', '<p id=\"v_a\"><b>a. </b> s&#259; ceda&#355;i trecerea autovehiculelor care circul&#259; pe prima band&#259; a autostr&#259;zii &#351;i s&#259; nu stânjeni&#355;i în niciun fel circula&#355;ia acestora;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; semnaliza&#355;i stânga, pentru a-i preveni pe ceilal&#355;i conduc&#259;tori s&#259; v&#259; acorde prioritate;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; opri&#355;i &#351;i s&#259; v&#259; asigura&#355;i din fa&#355;&#259; &#351;i din spate.</p>', 'da', 'nu', 'nu', ''),
(740, 'Preciza&#355;i m&#259;sura care se ia atunci când titularul permisului de conducere a fost declarat inapt pentru a conduce autovehicule, de c&#259;tre o institu&#355;ie medical&#259; autorizat&#259;:', '', '<p id=\"v_a\"><b>a. </b> i se retrage permisul de conducere;\r\n</p><p id=\"v_b\"><b>b. </b> i se aplic&#259; sanc&#355;iunile contraven&#355;ionale complementare;\r\n</p><p id=\"v_c\"><b>c. </b> va fi reexaminat de c&#259;tre autoritatea competent&#259;.</p>', 'da', 'nu', 'nu', ''),
(741, 'În ce ordine trec prin intersec&#355;ia prezentat&#259; cele trei autovehicule?', 'uploads/857.jpg', '<p id=\"v_a\"><b>a. </b> albastru, ro&#351;u, verde;\r\n</p><p id=\"v_b\"><b>b. </b> albastru, verde, ro&#351;u;\r\n</p><p id=\"v_c\"><b>c. </b> ro&#351;u, albastru, verde.</p>', 'nu', 'da', 'nu', ''),
(742, 'Semnalul verde al semaforului v&#259; permite:', 'uploads/821.jpg', '<p id=\"v_a\"><b>a. </b> s&#259; circula&#355;i pe direc&#355;ia înainte sau la stânga;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; circula&#355;i în orice direc&#355;ie;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; circula&#355;i pe direc&#355;ia înainte sau la dreapta.</p>', 'nu', 'nu', 'da', ''),
(743, 'În ce situa&#355;ii se interzice circula&#355;ia autoturismelor pe drumurile publice?', '', '<p id=\"v_a\"><b>a. </b> când nu au montate faruri de cea&#355;&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> când motorul emite noxe peste limita legal&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> când nu sunt echipate cu cârlig pentru remorcare.</p>', 'nu', 'da', 'nu', ''),
(744, 'Lumina galben&#259; intermitent&#259; instalat&#259; în zona trecerilor pentru pietoni v&#259; oblig&#259; la:', '', '<p id=\"v_a\"><b>a. </b> reducerea vitezei, având grij&#259; s&#259; feri&#355;i pietonii care traverseaz&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> reducerea vitezei &#351;i folosirea luminii farurilor, pentru avertizarea pietonilor care inten&#355;ioneaz&#259; s&#259; se angajeze în traversare;\r\n</p><p id=\"v_c\"><b>c. </b> reducerea vitezei &#351;i respectarea semnaliz&#259;rii &#351;i a regulilor de circula&#355;ie aplicabile în acea zon&#259;.</p>', 'nu', 'nu', 'da', ''),
(745, 'Circula&#355;i printr-o localitate, în spatele unui vehicul cu num&#259;r de înmatriculare din alt&#259; localitate sau din str&#259;in&#259;tate. La ce v&#259; pute&#355;i a&#351;tepta?', '', '<p id=\"v_a\"><b>a. </b> vehiculul care circul&#259; în fa&#355;&#259; merge corect;\r\n</p><p id=\"v_b\"><b>b. </b> înainte de a vira, conduc&#259;torul vehiculului respectiv va semnaliza din timp schimbarea direc&#355;iei de mers;\r\n</p><p id=\"v_c\"><b>c. </b> conduc&#259;torul care circul&#259; în fa&#355;&#259; poate s&#259; frâneze &#351;i s&#259; opreasc&#259; în mod nea&#351;teptat, pentru a cere informa&#355;ii privind drumul.</p>', 'nu', 'nu', 'da', ''),
(746, 'Proprietarul unui autovehicul este obligat:', '', '<p id=\"v_a\"><b>a. </b> s&#259; declare autorit&#259;&#355;ilor schimbarea domiciliului în cel mult 24 de ore;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; declare organului fiscal valoarea poli&#355;ei de asigurare auto;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; declare autorit&#259;&#355;ii emitente pierderea, furtul sau distrugerea certificatului de înmatriculare, în cel mult 48 de ore de la constatare.</p>', 'nu', 'nu', 'da', ''),
(747, 'Ce obliga&#355;ii are conduc&#259;torul unui autovehicul, dac&#259; pe drumul pe care circul&#259; observ&#259; evenimente de natur&#259; a pune în pericol siguran&#355;a circula&#355;iei?', '', '<p id=\"v_a\"><b>a. </b> s&#259; semnalizeze participan&#355;ii la trafic care circul&#259; din sens opus, schimbând alternativ lumina de drum cu lumina de întâlnire;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; emit&#259; semnale acustice ori de câte ori se întâlne&#351;te cu un alt vehicul circulând din sens opus;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; sesizeze cea mai apropiat&#259; unitate de poli&#355;ie în leg&#259;tur&#259; cu obstacolele sau situa&#355;iile care pun în pericol siguran&#355;a circula&#355;iei.</p>', 'nu', 'nu', 'da', ''),
(748, 'Dac&#259; vira&#355;i la stânga:', 'uploads/1055.jpg', '<p id=\"v_a\"><b>a. </b> ave&#355;i prioritate de trecere;\r\n</p><p id=\"v_b\"><b>b. </b> p&#259;r&#259;si&#355;i drumul cu prioritate;\r\n</p><p id=\"v_c\"><b>c. </b> ceda&#355;i trecerea celor care vin din dreapta.</p>', 'da', 'nu', 'nu', ''),
(749, 'Care este regula de trecere la întâlnirea acestui indicator?', 'uploads/189.jpg', '<p id=\"v_a\"><b>a. </b> regula priorit&#259;&#355;ii de stânga;\r\n</p><p id=\"v_b\"><b>b. </b> regula primului ajuns în intersec&#355;ie;\r\n</p><p id=\"v_c\"><b>c. </b> regula priorit&#259;&#355;ii de dreapta.</p>', 'nu', 'nu', 'da', ''),
(750, 'Ce tip de coliziuni au cea mai mare for&#355;&#259; de distrugere?', '', '<p id=\"v_a\"><b>a. </b> coliziunile laterale;\r\n</p><p id=\"v_b\"><b>b. </b> coliziunile frontale;\r\n</p><p id=\"v_c\"><b>c. </b> coliziunile din spate.</p>', 'nu', 'da', 'nu', ''),
(751, 'În care dintre urm&#259;toarele situa&#355;ii v&#259; este interzis s&#259; m&#259;ri&#355;i viteza de deplasare?', '', '<p id=\"v_a\"><b>a. </b> când urmeaz&#259; s&#259; fi&#355;i dep&#259;&#351;it;\r\n</p><p id=\"v_b\"><b>b. </b> la sesizarea aparaturii de detectare a sistemelor radar;\r\n</p><p id=\"v_c\"><b>c. </b> pe timp de noapte.</p>', 'da', 'nu', 'nu', ''),
(752, 'În timpul deplas&#259;rii, conduc&#259;torului de autovehicul îi este interzis:', '', '<p id=\"v_a\"><b>a. </b> s&#259; fumeze;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; aib&#259; preocup&#259;ri care i-ar putea distrage, într-o manier&#259; periculoas&#259;, aten&#355;ia;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; ac&#355;ioneze asupra comenzilor instala&#355;iei de sonorizare.</p>', 'nu', 'da', 'nu', ''),
(753, 'Cum procedeaz&#259; conduc&#259;torul auto când iese cu autovehiculul dintr-o curte sau dintr-un garaj pe un drum public?', '', '<p id=\"v_a\"><b>a. </b> acord&#259; prioritate pietonilor &#351;i vehiculelor aflate în mers;\r\n</p><p id=\"v_b\"><b>b. </b> solicit&#259; s&#259; fie pilotat de o alt&#259; persoan&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> folose&#351;te avertizorul sonor ziua &#351;i pe cel luminos, noaptea.</p>', 'da', 'nu', 'nu', ''),
(754, 'Care este denumirea indicatorului?', 'uploads/226.jpg', '<p id=\"v_a\"><b>a. </b> sens giratoriu;\r\n</p><p id=\"v_b\"><b>b. </b> punct de colectare a materialelor reciclabile;\r\n</p><p id=\"v_c\"><b>c. </b> presemnalizarea unei intersec&#355;ii cu sens giratoriu.</p>', 'nu', 'nu', 'da', ''),
(755, 'Ave&#355;i posibilitatea de a trece pe sensul opus de mers, la întâlnirea unui astfel de marcaj rutier?', 'uploads/344.jpg', '<p id=\"v_a\"><b>a. </b> legal, nu este stabilit acest aspect;\r\n</p><p id=\"v_b\"><b>b. </b> da;\r\n</p><p id=\"v_c\"><b>c. </b> nu.</p>', 'nu', 'da', 'nu', ''),
(756, 'Cum proceda&#355;i în situa&#355;ia dat&#259;?', 'uploads/923.jpg', '<p id=\"v_a\"><b>a. </b> reduce&#355;i viteza &#351;i la nevoie opri&#355;i;\r\n</p><p id=\"v_b\"><b>b. </b> dep&#259;&#351;i&#355;i f&#259;r&#259; restric&#355;ii autobuzul;\r\n</p><p id=\"v_c\"><b>c. </b> opri&#355;i la 5 m în spatele autobuzului.</p>', 'da', 'nu', 'nu', ''),
(757, 'Ce obliga&#355;ii revin conduc&#259;torilor la întâlnirea indicatorului?', 'uploads/692.jpg', '<p id=\"v_a\"><b>a. </b> s&#259; acorde prioritate vehiculelor care circul&#259; în intersec&#355;ie;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; ocoleasc&#259; centrul intersec&#355;iei &#351;i s&#259; circule cât mai aproape de marginea str&#259;zii;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; acorde prioritate de dreapta.</p>', 'da', 'nu', 'nu', ''),
(758, 'La semnalul intermitent de culoare galben&#259;:', '', '<p id=\"v_a\"><b>a. </b> opri&#355;i &#351;i a&#351;tepta&#355;i culoarea verde;\r\n</p><p id=\"v_b\"><b>b. </b> reduce&#355;i viteza &#351;i respecta&#355;i regulile de circula&#355;ie;\r\n</p><p id=\"v_c\"><b>c. </b> nu ave&#355;i nicio obliga&#355;ie.</p>', 'nu', 'da', 'nu', ''),
(759, 'Ce rol au tetierele, din punct de vedere al conduitei preventive?', '', '<p id=\"v_a\"><b>a. </b> în cazul tampon&#259;rii din spate a autovehiculului, previn posibila fracturare a coloanei cervicale;\r\n</p><p id=\"v_b\"><b>b. </b> nu au decât rol estetic;\r\n</p><p id=\"v_c\"><b>c. </b> contribuie la fixarea telefoanelor mobile, permi&#355;ând efectuarea convorbirilor f&#259;r&#259; a mai duce aparatul la ureche.</p>', 'da', 'nu', 'nu', ''),
(760, 'Conduc&#259;torul unui autoturism poate transporta obiecte al c&#259;ror gabarit dep&#259;&#351;e&#351;te, împreun&#259; cu înc&#259;rc&#259;tura, dimensiunile acestuia?', '', '<p id=\"v_a\"><b>a. </b> da, dac&#259; extremitatea obiectelor este semnalizat&#259; cu un steag ro&#351;u;\r\n</p><p id=\"v_b\"><b>b. </b> nu;\r\n</p><p id=\"v_c\"><b>c. </b> da, dac&#259; obiectele dep&#259;&#351;esc l&#259;&#355;imea autovehiculului.</p>', 'nu', 'da', 'nu', ''),
(761, 'Ce reprezint&#259; distan&#355;a de 50 m necesar&#259; pentru semnalizarea schimb&#259;rii direc&#355;iei de mers în localit&#259;&#355;i?', '', '<p id=\"v_a\"><b>a. </b> distan&#355;a fa&#355;&#259; de vehiculul care se deplaseaz&#259; în spatele dvs.;\r\n</p><p id=\"v_b\"><b>b. </b> distan&#355;a fa&#355;&#259; de locul în care se termin&#259; executarea manevrei de dep&#259;&#351;ire;\r\n</p><p id=\"v_c\"><b>c. </b> distan&#355;a dintre locul în care se semnalizeaz&#259; inten&#355;ia de a dep&#259;&#351;i &#351;i locul în care se începe manevra.</p>', 'nu', 'nu', 'da', ''),
(762, 'Preciza&#355;i dac&#259; posesorul unui permis de conducere categoria B are permisiunea de a conduce un autoturism cu remorc&#259;:', '', '<p id=\"v_a\"><b>a. </b> da, cu condi&#355;ia ca acesta s&#259; aib&#259; permis de cel pu&#355;in 3 ani;\r\n</p><p id=\"v_b\"><b>b. </b> da, dac&#259; masa remorcii nu dep&#259;&#351;e&#351;te 750 kg;\r\n</p><p id=\"v_c\"><b>c. </b> nu, deoarece pentru o astfel de remorc&#259; este necesar un permis de conducere categoria BE.</p>', 'nu', 'da', 'nu', ''),
(763, 'Care este ordinea de trecere prin intersec&#355;ie a celor trei vehicule?', 'uploads/947.jpg', '<p id=\"v_a\"><b>a. </b> autoturismul ro&#351;u, autoturismul albastru, autobuzul;\r\n</p><p id=\"v_b\"><b>b. </b> autoturismul albastru, autoturismul ro&#351;u, autobuzul;\r\n</p><p id=\"v_c\"><b>c. </b> autobuzul, autoturismul albastru, autoturismul ro&#351;u.</p>', 'da', 'nu', 'nu', ''),
(764, 'Utilizarea frânei de serviciu este semnalizat&#259; cu:', '', '<p id=\"v_a\"><b>a. </b> luminile de culoare portocalie;\r\n</p><p id=\"v_b\"><b>b. </b> luminile de avarie din fa&#355;&#259; &#351;i din spate;\r\n</p><p id=\"v_c\"><b>c. </b> luminile ro&#351;ii din spate.</p>', 'nu', 'nu', 'da', ''),
(765, 'Care este limita maxim&#259; de vitez&#259; în localit&#259;&#355;i?', '', '<p id=\"v_a\"><b>a. </b> 60 km/h;\r\n</p><p id=\"v_b\"><b>b. </b> 50 km/h;\r\n</p><p id=\"v_c\"><b>c. </b> 40 km/h.</p>', 'nu', 'da', 'nu', ''),
(766, 'Este permis&#259; de&#355;inerea, montarea sau folosirea la autovehicule a sistemelor care perturb&#259; buna func&#355;ionare a dispozitivelor de supraveghere a traficului?', '', '<p id=\"v_a\"><b>a. </b> da;\r\n</p><p id=\"v_b\"><b>b. </b> nu;\r\n</p><p id=\"v_c\"><b>c. </b> acest aspect nu este stabilit legal.</p>', 'nu', 'da', 'nu', ''),
(767, 'Se consider&#259; dep&#259;&#351;ire faptul c&#259; vehiculele de pe o band&#259; circul&#259; mai repede decât vehiculele de pe celelalte benzi?', '', '<p id=\"v_a\"><b>a. </b> nu;\r\n</p><p id=\"v_b\"><b>b. </b> nu, dac&#259; se circul&#259; pe banda din stânga;\r\n</p><p id=\"v_c\"><b>c. </b> da, dac&#259; se circul&#259; pe banda din dreapta.</p>', 'da', 'nu', 'nu', ''),
(768, 'La semnalul poli&#355;istului trebuie s&#259; opreasc&#259;:', 'uploads/699.jpg', '<p id=\"v_a\"><b>a. </b> autoturismul &#351;i autocamionul;\r\n</p><p id=\"v_b\"><b>b. </b> autocamionul &#351;i bicicleta;\r\n</p><p id=\"v_c\"><b>c. </b> bicicleta &#351;i autoturismul.</p>', 'nu', 'da', 'nu', ''),
(769, 'Ce în&#355;elege&#355;i prin conduit&#259; preventiv&#259; în condi&#355;ii atmosferice nefavorabile?', '', '<p id=\"v_a\"><b>a. </b> rula&#355;i cu o vitez&#259; mai mic&#259; decât viteza maxim&#259; legal&#259;, admis&#259; pe tronsonul de drum respectiv;\r\n</p><p id=\"v_b\"><b>b. </b> conduce&#355;i cu aten&#355;ie sporit&#259; &#351;i cu o vitez&#259; care s&#259; v&#259; permit&#259; oprirea în siguran&#355;&#259; &#351;i evitarea oric&#259;rui pericol;\r\n</p><p id=\"v_c\"><b>c. </b> folosi&#355;i atât luminile de drum cât &#351;i cele de avarie, pentru a fi mai u&#351;or de observat.</p>', 'nu', 'da', 'nu', ''),
(770, 'Care autovehicul va trece primul prin intersec&#355;ie?', 'uploads/948.jpg', '<p id=\"v_a\"><b>a. </b> autoturismul;\r\n</p><p id=\"v_b\"><b>b. </b> autocamionul;\r\n</p><p id=\"v_c\"><b>c. </b> autobuzul.</p>', 'nu', 'da', 'nu', ''),
(771, 'A&#355;i selectat corect banda de circula&#355;ie pentru a vira la stânga în intersec&#355;ia care urmeaz&#259;?', 'uploads/229.jpg', '<p id=\"v_a\"><b>a. </b> da;\r\n</p><p id=\"v_b\"><b>b. </b> nu;\r\n</p><p id=\"v_c\"><b>c. </b> sunte&#355;i obligat s&#259; circula&#355;i numai înainte.</p>', 'da', 'nu', 'nu', ''),
(772, 'Cum trebuie s&#259; proceda&#355;i în situa&#355;ia dat&#259;?', 'uploads/380.jpg', '<p id=\"v_a\"><b>a. </b> v&#259; continua&#355;i deplasarea, deoarece cele dou&#259; lumini ro&#351;ii alternante indic&#259; permisiunea de a traversa calea ferat&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> opri&#355;i;\r\n</p><p id=\"v_c\"><b>c. </b> v&#259; continua&#355;i deplasarea dac&#259; semibarierele nu sunt coborâte înc&#259;.</p>', 'nu', 'da', 'nu', ''),
(773, 'Circula&#355;i într-o coloan&#259; &#351;i respecta&#355;i distan&#355;a fa&#355;&#259; de vehiculul din fa&#355;&#259;. Ce trebuie s&#259; face&#355;i în plus?', '', '<p id=\"v_a\"><b>a. </b> s&#259; aprinde&#355;i lanterna de cea&#355;&#259; din spate;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; observa&#355;i prin geamul lateral al vehiculului marginea din dreapta a p&#259;r&#355;ii carosabile;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; r&#259;mâne&#355;i la limita propriei benzi &#351;i s&#259; v&#259; asigura&#355;i câmpul de vizibilitate.</p>', 'nu', 'nu', 'da', ''),
(774, 'Cum proceda&#355;i în situa&#355;ia dat&#259;?', 'uploads/512.jpg', '<p id=\"v_a\"><b>a. </b> acorda&#355;i prioritate autoturismului din stânga;\r\n</p><p id=\"v_b\"><b>b. </b> v&#259; continua&#355;i drumul;\r\n</p><p id=\"v_c\"><b>c. </b> acorda&#355;i prioritate autoturismului din dreapta.</p>', 'nu', 'da', 'nu', ''),
(775, 'La semnalul nev&#259;z&#259;torilor care traverseaz&#259; strada sunte&#355;i obligat:', '', '<p id=\"v_a\"><b>a. </b> s&#259; reduce&#355;i viteza autovehiculului &#351;i s&#259; circula&#355;i cu aten&#355;ie;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; opri&#355;i autovehiculul;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; reduce&#355;i viteza autovehiculului &#351;i s&#259; claxona&#355;i.</p>', 'nu', 'da', 'nu', ''),
(776, 'Imobilizarea unui vehicul de c&#259;tre poli&#355;istul rutier se dispune:', '', '<p id=\"v_a\"><b>a. </b> pentru lipsa triunghiurilor reflectorizante;\r\n</p><p id=\"v_b\"><b>b. </b> pentru lipsa trusei medicale de prim ajutor;\r\n</p><p id=\"v_c\"><b>c. </b> pentru refuzul de legitimare.</p>', 'nu', 'nu', 'da', ''),
(777, 'Cum trebuie s&#259; procedeze conduc&#259;torul autoturismului verde în situa&#355;ia dat&#259;?', 'uploads/1044.jpg', '<p id=\"v_a\"><b>a. </b> dep&#259;&#351;e&#351;te prin partea dreapt&#259; autobuzul, dup&#259; ce a trecut autoturismul ro&#351;u;\r\n</p><p id=\"v_b\"><b>b. </b> trece printre autobuz &#351;i autoturismul ro&#351;u, deoarece se deplaseaz&#259; înainte;\r\n</p><p id=\"v_c\"><b>c. </b> semnalizeaz&#259; autobuzul s&#259; elibereze banda.</p>', 'da', 'nu', 'nu', ''),
(778, 'Ce obliga&#355;ii ave&#355;i dac&#259; întâlni&#355;i un vehicul care semnalizeaz&#259; cu luminile speciale de avertizare de culoare albastr&#259; &#351;i semnalele sonore?', '', '<p id=\"v_a\"><b>a. </b> nicio obliga&#355;ie, deoarece semnalul albastru se adreseaz&#259; coloanelor în mi&#351;care;\r\n</p><p id=\"v_b\"><b>b. </b> opri&#355;i cât mai aproape de marginea drumului;\r\n</p><p id=\"v_c\"><b>c. </b> reduce&#355;i viteza, circula&#355;i cât mai aproape de bordur&#259; sau acostament &#351;i acorda&#355;i prioritate vehiculului respectiv.</p>', 'nu', 'nu', 'da', ''),
(779, 'Care sunt sanc&#355;iunile ce se aplic&#259; în cazul dep&#259;&#351;irii unei coloane de vehicule în a&#351;teptare, dac&#259; prin aceasta se intr&#259; pe sensul opus de circula&#355;ie?', '', '<p id=\"v_a\"><b>a. </b> suspendarea certificatului de înmatriculare;\r\n</p><p id=\"v_b\"><b>b. </b> amend&#259; contraven&#355;ional&#259; &#351;i suspendarea exercit&#259;rii dreptului de a conduce;\r\n</p><p id=\"v_c\"><b>c. </b> amend&#259; contraven&#355;ional&#259;.</p>', 'nu', 'da', 'nu', ''),
(780, 'Care dintre participan&#355;ii la traficul rutier au obliga&#355;ia de a respecta regulile de circula&#355;ie pe drumurile publice din interiorul localit&#259;&#355;ilor?', '', '<p id=\"v_a\"><b>a. </b> to&#355;i participan&#355;ii la traficul rutier, indiferent de situa&#355;ie;\r\n</p><p id=\"v_b\"><b>b. </b> conduc&#259;torii auto, atât cei amatori, cât &#351;i cei profesioni&#351;ti, atât ziua, cât &#351;i noaptea;\r\n</p><p id=\"v_c\"><b>c. </b> conduc&#259;torii autovehiculelor destinate transportului în comun, având în vedere destina&#355;ia autovehiculelor pe care le conduc.</p>', 'da', 'nu', 'nu', ''),
(781, 'Semnalul ro&#351;u al semaforului:', '', '<p id=\"v_a\"><b>a. </b> oblig&#259; conduc&#259;torii vehiculelor care circul&#259; pe direc&#355;ia înainte s&#259; opreasc&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> oblig&#259; conduc&#259;torii vehiculelor, indiferent de direc&#355;ia de deplasare, s&#259; opreasc&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> permite trecerea autovehiculelor care schimb&#259; direc&#355;ia de mers spre stânga.</p>', 'nu', 'da', 'nu', ''),
(782, 'Ce îi este interzis conduc&#259;torului de autovehicule pe timpul deplas&#259;rii pe un drum public?', '', '<p id=\"v_a\"><b>a. </b> s&#259; transporte în autovehicul mai multe persoane decât num&#259;rul de locuri înscrise în certificatul de înmatriculare sau înregistrare;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; permit&#259; controlul st&#259;rii tehnice a autovehiculului, precum &#351;i al bunurilor transportate;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; deschid&#259; u&#351;ile autovehiculului atunci când este parcat.</p>', 'da', 'nu', 'nu', ''),
(783, 'Cum ve&#355;i proceda în situa&#355;ia dat&#259;?', 'uploads/607.jpg', '<p id=\"v_a\"><b>a. </b> reduce&#355;i viteza &#351;i traversa&#355;i cu aten&#355;ie;\r\n</p><p id=\"v_b\"><b>b. </b> opri&#355;i &#351;i a&#351;tepta&#355;i apari&#355;ia luminii de culoare verde;\r\n</p><p id=\"v_c\"><b>c. </b> acorda&#355;i prioritate pietonilor, dup&#259; care v&#259; continua&#355;i deplasarea.</p>', 'nu', 'da', 'nu', ''),
(784, 'La semnalul poli&#355;istului având bra&#355;ul stâng întins orizontal, orientat cu fa&#355;a c&#259;tre autovehiculul care se apropie, conduc&#259;torul auto trebuie:', '', '<p id=\"v_a\"><b>a. </b> s&#259; reduc&#259; viteza;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; opreasc&#259; imediat;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; continue deplasarea.</p>', 'nu', 'da', 'nu', ''),
(785, 'Care dintre autovehiculele din imagine au prioritate de trecere?', 'uploads/1007.jpg', '<p id=\"v_a\"><b>a. </b> autobuzul &#351;i motocicleta;\r\n</p><p id=\"v_b\"><b>b. </b> motocicleta &#351;i autocamionul;\r\n</p><p id=\"v_c\"><b>c. </b> autobuzul &#351;i autoturismul.</p>', 'nu', 'da', 'nu', ''),
(786, 'Cum trebuie s&#259; proceda&#355;i corect la trecerea de pe o band&#259; pe alta?', 'uploads/534.jpg', '<p id=\"v_a\"><b>a. </b> v&#259; asigura&#355;i din spate;\r\n</p><p id=\"v_b\"><b>b. </b> v&#259; asigura&#355;i atât din fa&#355;&#259;, cât &#351;i din spate;\r\n</p><p id=\"v_c\"><b>c. </b> v&#259; asigura&#355;i, semnaliza&#355;i inten&#355;ia (manevra) de a schimba direc&#355;ia &#351;i respecta&#355;i semnifica&#355;ia marcajului rutier.</p>', 'nu', 'nu', 'da', ''),
(787, 'Ce obliga&#355;ii ave&#355;i la semnalul galben intermitent al semaforului electric?', '', '<p id=\"v_a\"><b>a. </b> opri&#355;i, pentru c&#259; nu pute&#355;i intra în intersec&#355;ie;\r\n</p><p id=\"v_b\"><b>b. </b> reduce&#355;i viteza &#351;i v&#259; continua&#355;i deplasarea cu aten&#355;ie, ocolind pietonii;\r\n</p><p id=\"v_c\"><b>c. </b> reduce&#355;i viteza &#351;i circula&#355;i respectând regulile aplicabile în acea intersec&#355;ie.</p>', 'nu', 'nu', 'da', ''),
(788, 'În cazul unui accident cu victime &#351;i cu deformarea accentuat&#259; a caroseriei, proceda&#355;i la:', '', '<p id=\"v_a\"><b>a. </b> victimele con&#351;tiente sunt sf&#259;tuite s&#259; nu se mi&#351;te, iar degajarea acestora trebuie s&#259; se realizeze cât mai repede &#351;i numai de c&#259;tre echipaje specializate;\r\n</p><p id=\"v_b\"><b>b. </b> degajarea victimelor trebuie s&#259; se realizeze cât mai repede, prin orice mijloace;\r\n</p><p id=\"v_c\"><b>c. </b> se administreaz&#259; calmante victimelor care sunt con&#351;tiente.</p>', 'da', 'nu', 'nu', ''),
(789, 'La ce trebuie s&#259; v&#259; a&#351;tepta&#355;i atunci când, în fa&#355;a dvs., a oprit în sta&#355;ie un autobuz destinat transportului de c&#259;l&#259;tori?', '', '<p id=\"v_a\"><b>a. </b> urmeaz&#259; o intersec&#355;ie, întrucât majoritatea sta&#355;iilor mijloacelor de transport în comun se amenajeaz&#259; în astfel de locuri;\r\n</p><p id=\"v_b\"><b>b. </b> este posibil ca unii c&#259;l&#259;tori s&#259; coboare &#351;i s&#259; traverseze prin fa&#355;a autobuzului, f&#259;r&#259; a se asigura în mod corespunz&#259;tor;\r\n</p><p id=\"v_c\"><b>c. </b> la plecarea din sta&#355;ie, autobuzul poate s&#259;-&#351;i schimbe direc&#355;ia de mers spre stânga, f&#259;r&#259; s&#259; se asigure.</p>', 'nu', 'da', 'nu', ''),
(790, 'Cum ve&#355;i proceda în intersec&#355;ie, dac&#259; circula&#355;i pe drumul prioritar?', 'uploads/907.jpg', '<p id=\"v_a\"><b>a. </b> ceda&#355;i trecerea vehiculelor care vin din dreapta;\r\n</p><p id=\"v_b\"><b>b. </b> ceda&#355;i trecerea tututor vehiculelor;\r\n</p><p id=\"v_c\"><b>c. </b> nu ceda&#355;i trecerea niciunui vehicul.</p>', 'nu', 'nu', 'da', ''),
(791, 'Care dintre vehicule trebuie s&#259; opreasc&#259; la semnalul poli&#355;istului?', 'uploads/819.jpg', '<p id=\"v_a\"><b>a. </b> toate vehiculele;\r\n</p><p id=\"v_b\"><b>b. </b> tramvaiele &#351;i autoturismul;\r\n</p><p id=\"v_c\"><b>c. </b> bicicleta.</p>', 'nu', 'da', 'nu', ''),
(792, 'În situa&#355;ia dat&#259;, sunte&#355;i avertizat c&#259;:', 'uploads/468.jpg', '<p id=\"v_a\"><b>a. </b> v&#259; apropia&#355;i de o trecere la nivel cu calea ferat&#259; prev&#259;zut&#259; cu bariere;\r\n</p><p id=\"v_b\"><b>b. </b> v&#259; apropia&#355;i de o trecere la nivel cu o cale ferat&#259; industrial&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> v&#259; apropia&#355;i de un chei sau de un mal abrupt.</p>', 'da', 'nu', 'nu', ''),
(793, 'Într-o intersec&#355;ie întâlni&#355;i semnalul de culoare verde al semaforului, precum &#351;i indicatorul „Oprire“. Cum ve&#355;i proceda?', '', '<p id=\"v_a\"><b>a. </b> respecta&#355;i semnifica&#355;ia indicatorului rutier;\r\n</p><p id=\"v_b\"><b>b. </b> respecta&#355;i semnifica&#355;ia culorii semaforului;\r\n</p><p id=\"v_c\"><b>c. </b> circula&#355;i cu aten&#355;ie &#351;i acorda&#355;i prioritate de trecere vehiculelor de pe drumul transversal.</p>', 'nu', 'da', 'nu', ''),
(794, 'Se interzice dep&#259;&#351;irea:', '', '<p id=\"v_a\"><b>a. </b> atunci când se încalc&#259; marcajul continuu longitudinal;\r\n</p><p id=\"v_b\"><b>b. </b> în zonele cu trafic intens;\r\n</p><p id=\"v_c\"><b>c. </b> când se încalc&#259; marcajul discontinuu longitudinal.</p>', 'da', 'nu', 'nu', ''),
(795, 'Cum ve&#355;i proceda în situa&#355;ia dat&#259;?', 'uploads/702.jpg', '<p id=\"v_a\"><b>a. </b> ve&#355;i vira ultimul;\r\n</p><p id=\"v_b\"><b>b. </b> camionul va trece primul;\r\n</p><p id=\"v_c\"><b>c. </b> ave&#355;i prioritate.</p>', 'nu', 'nu', 'da', ''),
(796, 'Intra&#355;i cu autovehiculul de pe o strad&#259; bine iluminat&#259; pe una neiluminat&#259;. Ce trebuie s&#259; ave&#355;i în vedere?', '', '<p id=\"v_a\"><b>a. </b> viteza trebuie adaptat&#259; noilor condi&#355;ii de iluminare;\r\n</p><p id=\"v_b\"><b>b. </b> nu ave&#355;i obliga&#355;ii legale;\r\n</p><p id=\"v_c\"><b>c. </b> vederea se adapteaz&#259; instantaneu.</p>', 'da', 'nu', 'nu', ''),
(797, 'Un vehicul are luminile de avarie aprinse. Ce trebuie s&#259; ave&#355;i în vedere?', '', '<p id=\"v_a\"><b>a. </b> vehiculul va vira la dreapta sau la stânga;\r\n</p><p id=\"v_b\"><b>b. </b> vehiculul este parcat;\r\n</p><p id=\"v_c\"><b>c. </b> vehiculul a r&#259;mas în pan&#259; sau este remorcat.</p>', 'nu', 'nu', 'da', ''),
(798, 'Ce ve&#355;i respecta în intersec&#355;ia prezentat&#259;?', 'uploads/329.jpg', '<p id=\"v_a\"><b>a. </b> semnifica&#355;ia luminii semaforului;\r\n</p><p id=\"v_b\"><b>b. </b> regula priorit&#259;&#355;ii de dreapta;\r\n</p><p id=\"v_c\"><b>c. </b> semnifica&#355;ia indicatoarelor.</p>', 'da', 'nu', 'nu', ''),
(799, 'Bastonul alb, ridicat de un pieton care traverseaz&#259; strada printr-un loc nesemnalizat &#351;i nemarcat, v&#259; oblig&#259;:', '', '<p id=\"v_a\"><b>a. </b> nu ave&#355;i nicio obliga&#355;ie, întrucât locul nu este marcat sau semnalizat;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; opri&#355;i imediat autovehiculul, pentru a permite trecerea pietonului;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; reduce&#355;i viteza &#351;i s&#259; circula&#355;i cu aten&#355;ie.</p>', 'nu', 'da', 'nu', ''),
(800, 'Cum ve&#355;i proceda corect în aceast&#259; intersec&#355;ie, dac&#259; semaforul este instalat deasupra intersec&#355;iei?', 'uploads/827.jpg', '<p id=\"v_a\"><b>a. </b> respecta&#355;i semnifica&#355;ia semaforului, indiferent de direc&#355;ia de mers, dac&#259; acesta este dublat de un semafor înso&#355;itor înainte de intersec&#355;ie;\r\n</p><p id=\"v_b\"><b>b. </b> nu ave&#355;i obliga&#355;ia de a respecta indica&#355;ia semnalelor luminoase plasate deasupra intersec&#355;iei;\r\n</p><p id=\"v_c\"><b>c. </b> respecta&#355;i indica&#355;iile semaforului, dac&#259; vizibilitatea v&#259; permite acest lucru.</p>', 'da', 'nu', 'nu', ''),
(801, 'Dac&#259; la trecerea la nivel cu calea ferat&#259; constata&#355;i c&#259; un vehicul este imobilizat pe calea ferat&#259;, cum ve&#355;i proceda?', '', '<p id=\"v_a\"><b>a. </b> v&#259; continua&#355;i drumul, deoarece degajarea c&#259;ii ferate se va face de c&#259;tre personalul calificat;\r\n</p><p id=\"v_b\"><b>b. </b> opri&#355;i &#351;i anun&#355;a&#355;i telefonic poli&#355;ia despre acest incident;\r\n</p><p id=\"v_c\"><b>c. </b> acorda&#355;i sprijin pentru scoaterea vehiculului de pe calea ferat&#259; sau, dac&#259; nu este posibil, semnala&#355;i prezen&#355;a acestuia.</p>', 'nu', 'nu', 'da', ''),
(802, 'Cum proceda&#355;i în cazul în care ave&#355;i o defec&#355;iune la sistemul de rulare pe autostrad&#259;?', '', '<p id=\"v_a\"><b>a. </b> p&#259;r&#259;si&#355;i carosabilul în timp ce ruleaz&#259; autovehiculul, opri&#355;i pe banda de urgen&#355;&#259; &#351;i îl semnaliza&#355;i;\r\n</p><p id=\"v_b\"><b>b. </b> aprinde&#355;i luminile de pozi&#355;ie;\r\n</p><p id=\"v_c\"><b>c. </b> aprinde&#355;i proiectoarele.</p>', 'da', 'nu', 'nu', ''),
(803, 'Un autoturism este prev&#259;zut cu centuri de siguran&#355;&#259; pentru toate locurile. Ce persoane sunt obligate s&#259; le foloseasc&#259;?', '', '<p id=\"v_a\"><b>a. </b> persoanele de pe scaunele din spate;\r\n</p><p id=\"v_b\"><b>b. </b> toate persoanele;\r\n</p><p id=\"v_c\"><b>c. </b> persoanele de pe scaunele din fa&#355;&#259;.</p>', 'nu', 'da', 'nu', ''),
(804, 'Ce semnifica&#355;ie are un astfel de marcaj rutier?', 'uploads/499.jpg', '<p id=\"v_a\"><b>a. </b> marcaj pe o band&#259; de decelerare;\r\n</p><p id=\"v_b\"><b>b. </b> deviere temporar&#259; a circula&#355;iei;\r\n</p><p id=\"v_c\"><b>c. </b> marcaj pentru spa&#355;ii interzise.</p>', 'da', 'nu', 'nu', ''),
(805, 'Indicatorul v&#259; avertizeaz&#259; c&#259; urmeaz&#259;:', 'uploads/815.jpg', '<p id=\"v_a\"><b>a. </b> o urcare cu înclinare mare, pe o lungime de 2000 m;\r\n</p><p id=\"v_b\"><b>b. </b> o coborâre periculoas&#259;, cu o diferen&#355;&#259; de nivel de 10 m pân&#259; la 100 m pe orizontal&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> limitare de vitez&#259; la 10 km/h.</p>', 'da', 'nu', 'nu', ''),
(806, 'Este permis&#259; oprirea voluntar&#259; a unui vehicul pe trotuar?', '', '<p id=\"v_a\"><b>a. </b> da, dac&#259; se asigur&#259; un spa&#355;iu de cel pu&#355;in 1 m l&#259;&#355;ime pentru circula&#355;ia pietonilor, iar semnalizarea rutier&#259; permite aceast&#259; manevr&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> legal, nu este prev&#259;zut;\r\n</p><p id=\"v_c\"><b>c. </b> nu.</p>', 'da', 'nu', 'nu', ''),
(807, 'Gravitatea unei hemoragii se apreciaz&#259; în func&#355;ie de:', '', '<p id=\"v_a\"><b>a. </b> cantitatea sângelui pierdut;\r\n</p><p id=\"v_b\"><b>b. </b> culoarea sângelui scurs;\r\n</p><p id=\"v_c\"><b>c. </b> fluiditatea sângelui scurs.</p>', 'da', 'nu', 'nu', ''),
(808, 'Este permis&#259; deschiderea portierelor autoturismului pe timpul opririi sau sta&#355;ion&#259;rii pe un drum public?', '', '<p id=\"v_a\"><b>a. </b> da, numai dup&#259; ce conduc&#259;torul sau pasagerul s-a asigurat c&#259; aceasta nu prezint&#259; pericol pentru trafic;\r\n</p><p id=\"v_b\"><b>b. </b> da, numai pe drumurile cu trafic redus;\r\n</p><p id=\"v_c\"><b>c. </b> da, dar numai pe partea dreapt&#259;.</p>', 'da', 'nu', 'nu', ''),
(809, 'Ce tip de hemoragie este mai periculoas&#259;?', '', '<p id=\"v_a\"><b>a. </b> cea care determin&#259; o sângerare lent&#259; &#351;i continu&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> cea care determin&#259; o pierdere rapid&#259; a sângelui;\r\n</p><p id=\"v_c\"><b>c. </b> orice mic&#259; sângerare vizibil&#259;.</p>', 'nu', 'da', 'nu', ''),
(810, 'Ce obliga&#355;ii v&#259; revin la executarea manevrei de trecere a vehiculului de pe o band&#259; pe alta?', '', '<p id=\"v_a\"><b>a. </b> s&#259; semnaliza&#355;i din timp &#351;i s&#259; v&#259; asigura&#355;i c&#259; pute&#355;i efectua manevra f&#259;r&#259; a perturba circula&#355;ia sau f&#259;r&#259; a pune în pericol siguran&#355;a celorlal&#355;i participan&#355;i la trafic;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; v&#259; asigura&#355;i din spate;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; v&#259; asigura&#355;i din fa&#355;&#259; &#351;i din spate.</p>', 'da', 'nu', 'nu', ''),
(811, 'Ce obliga&#355;ii ave&#355;i dac&#259; v&#259; afla&#355;i la volanul autoturismului albastru?', 'uploads/1063.jpg', '<p id=\"v_a\"><b>a. </b> v&#259; continua&#355;i deplasarea pe direc&#355;ia înainte, f&#259;r&#259; nicio obliga&#355;ie;\r\n</p><p id=\"v_b\"><b>b. </b> acorda&#355;i prioritate autoturismului ro&#351;u &#351;i autoturismului verde;\r\n</p><p id=\"v_c\"><b>c. </b> acorda&#355;i prioritate numai autoturismului verde.</p>', 'nu', 'da', 'nu', ''),
(812, 'În cazul în care, într-o intersec&#355;ie, lâng&#259; semaforul în stare de func&#355;ionare este instalat indicatorul „Oprire“, sunte&#355;i obligat s&#259; respecta&#355;i:', '', '<p id=\"v_a\"><b>a. </b> semnifica&#355;ia indicatorului;\r\n</p><p id=\"v_b\"><b>b. </b> regula priorit&#259;&#355;ii de dreapta;\r\n</p><p id=\"v_c\"><b>c. </b> semnifica&#355;ia luminii semaforului.</p>', 'nu', 'nu', 'da', ''),
(813, 'La întâlnirea unui vehicul cu un ansamblu de vehicule, pe un drum public îngust, unde trecerea din sensuri opuse este imposibil&#259; sau periculoas&#259;, va manevra cu spatele:', '', '<p id=\"v_a\"><b>a. </b> ansamblul de vehicule;\r\n</p><p id=\"v_b\"><b>b. </b> vehiculul;\r\n</p><p id=\"v_c\"><b>c. </b> la libera în&#355;elegere.</p>', 'nu', 'da', 'nu', ''),
(814, 'Cum ve&#355;i proceda în aceast&#259; situa&#355;ie?', 'uploads/230.jpg', '<p id=\"v_a\"><b>a. </b> urma&#355;i vehiculul care execut&#259; dep&#259;&#351;irea;\r\n</p><p id=\"v_b\"><b>b. </b> r&#259;mâne&#355;i în spatele camionului;\r\n</p><p id=\"v_c\"><b>c. </b> indicatorul se adreseaz&#259; vehiculelor care circul&#259; din sens opus.</p>', 'nu', 'da', 'nu', ''),
(815, 'Cum proceda&#355;i în situa&#355;ia dat&#259;?', 'uploads/443.jpg', '<p id=\"v_a\"><b>a. </b> circula&#355;i cu vitez&#259; redus&#259; &#351;i, la nevoie, opri&#355;i pentru a permite autobuzului s&#259; reintre în trafic;\r\n</p><p id=\"v_b\"><b>b. </b> dep&#259;&#351;i&#355;i autobuzul;\r\n</p><p id=\"v_c\"><b>c. </b> sunte&#355;i obligat s&#259; opri&#355;i.</p>', 'da', 'nu', 'nu', ''),
(816, 'În cazul în care exist&#259; mai multe persoane accidentate este important s&#259; se stabileasc&#259; priorit&#259;&#355;ile?', '', '<p id=\"v_a\"><b>a. </b> da, primul ajutor se acord&#259; cu prioritate persoanei celei mai grav accidentate;\r\n</p><p id=\"v_b\"><b>b. </b> nu, nu are importan&#355;&#259; ordinea în care se acord&#259; primul ajutor;\r\n</p><p id=\"v_c\"><b>c. </b> se începe acordarea primului ajutor de la victima cea mai apropiat&#259; de salvator.</p>', 'da', 'nu', 'nu', ''),
(817, 'Cum proceda&#355;i în intersec&#355;ia care urmeaz&#259;?', 'uploads/868.jpg', '<p id=\"v_a\"><b>a. </b> opri&#355;i &#351;i acorda&#355;i prioritate pietonilor;\r\n</p><p id=\"v_b\"><b>b. </b> opri&#355;i la marcajul transversal de oprire &#351;i v&#259; asigura&#355;i, dup&#259; care vira&#355;i la dreapta;\r\n</p><p id=\"v_c\"><b>c. </b> reduce&#355;i viteza &#351;i vira&#355;i la dreapta.</p>', 'nu', 'da', 'nu', ''),
(818, 'Ce obliga&#355;ii ave&#355;i dac&#259;, pe drumul pe care circula&#355;i, observa&#355;i evenimente de natur&#259; a pune în pericol siguran&#355;a circula&#355;iei?', '', '<p id=\"v_a\"><b>a. </b> s&#259; semnaliza&#355;i participan&#355;ii la trafic care circul&#259; din sens opus;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; emite&#355;i semnale acustice;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; sesiza&#355;i cea mai apropiat&#259; unitate de poli&#355;ie în leg&#259;tur&#259; cu obstacolele care pun în pericol siguran&#355;a circula&#355;iei.</p>', 'nu', 'nu', 'da', ''),
(819, 'Exist&#259; situa&#355;ii când conduc&#259;torul de autovehicul acord&#259; prioritate de trecere bicicletei sau unui vehicul cu trac&#355;iune animal&#259;?', '', '<p id=\"v_a\"><b>a. </b> nu, legisla&#355;ia rutier&#259; nu prevede asemenea situa&#355;ii;\r\n</p><p id=\"v_b\"><b>b. </b> nu, conduc&#259;torii de autovehicule au întotdeauna prioritate;\r\n</p><p id=\"v_c\"><b>c. </b> da.</p>', 'nu', 'nu', 'da', ''),
(820, 'Indicatorul din imagine semnific&#259;:', 'uploads/627.jpg', '<p id=\"v_a\"><b>a. </b> „Circula&#355;ie în ambele sensuri“;\r\n</p><p id=\"v_b\"><b>b. </b> „Circula&#355;ia interzis&#259; în ambele sensuri“;\r\n</p><p id=\"v_c\"><b>c. </b> „Prioritate pentru circula&#355;ia din sens invers“.</p>', 'da', 'nu', 'nu', ''),
(821, 'Indicatorul v&#259; avertizeaz&#259; c&#259; urmeaz&#259;:', 'uploads/509.jpg', '<p id=\"v_a\"><b>a. </b> un drum cu denivel&#259;ri;\r\n</p><p id=\"v_b\"><b>b. </b> un segment de drum pe care se execut&#259; lucr&#259;ri de amenajare;\r\n</p><p id=\"v_c\"><b>c. </b> o denivelare pentru limitarea vitezei.</p>', 'nu', 'nu', 'da', ''),
(822, 'Indicatorul semnific&#259;:', 'uploads/754.jpg', '<p id=\"v_a\"><b>a. </b> sfâr&#351;itul interzicerii de a dep&#259;&#351;i;\r\n</p><p id=\"v_b\"><b>b. </b> sfâr&#351;itul tuturor restric&#355;iilor;\r\n</p><p id=\"v_c\"><b>c. </b> interzicerea dep&#259;&#351;irii autovehiculelor, cu excep&#355;ia motocicletelor f&#259;r&#259; ata&#351;.</p>', 'da', 'nu', 'nu', ''),
(823, 'Care este semnifica&#355;ia indicatorului?', 'uploads/428.jpg', '<p id=\"v_a\"><b>a. </b> oblig&#259; la ocolirea prin dreapta a locului unde este instalat;\r\n</p><p id=\"v_b\"><b>b. </b> oblig&#259; la schimbarea direc&#355;iei de mers c&#259;tre dreapta;\r\n</p><p id=\"v_c\"><b>c. </b> oblig&#259; la folosirea benzii de lâng&#259; trotuar.</p>', 'da', 'nu', 'nu', ''),
(824, 'Care este ordinea de trecere prin intersec&#355;ie a autoturismelor?', 'uploads/908.jpg', '<p id=\"v_a\"><b>a. </b> ro&#351;u, verde, albastru;\r\n</p><p id=\"v_b\"><b>b. </b> verde, albastru, ro&#351;u;\r\n</p><p id=\"v_c\"><b>c. </b> albastru, verde, ro&#351;u.</p>', 'nu', 'da', 'nu', ''),
(825, 'Ce obliga&#355;ii îi revin conduc&#259;torului de vehicul în momentul apropierii de o intersec&#355;ie, dac&#259; semnalul verde se schimb&#259; cu cel galben?', '', '<p id=\"v_a\"><b>a. </b> s&#259; gr&#259;beasc&#259; traversarea;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; traverseze intersec&#355;ia cu vitez&#259; redus&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; opreasc&#259; autovehiculul în fa&#355;a marcajului de oprire sau a marcajului pentru trecerea pietonilor ori în dreptul semaforului.</p>', 'nu', 'nu', 'da', ''),
(826, 'Atunci când victimei unui accident de circula&#355;ie i s-au oprit b&#259;t&#259;ile inimii (stop cardiac), masajul cardiac se execut&#259;:', '', '<p id=\"v_a\"><b>a. </b> prin ap&#259;sarea ritmic&#259; cu podul palmelor suprapuse a toracelui victimei, în dreptul inimii;\r\n</p><p id=\"v_b\"><b>b. </b> prin lovirea cu palmele a obrajilor victimei;\r\n</p><p id=\"v_c\"><b>c. </b> prin încruci&#351;area ritmic&#259; a bra&#355;elor victimei simultan cu respira&#355;ia artificial&#259;.</p>', 'da', 'nu', 'nu', ''),
(827, 'Indicatorul din imagine v&#259; avertizeaz&#259; c&#259;:', 'uploads/924.jpg', '<p id=\"v_a\"><b>a. </b> ave&#355;i prioritate de trecere fa&#355;&#259; de circula&#355;ia din sens invers;\r\n</p><p id=\"v_b\"><b>b. </b> trebuie s&#259; acorda&#355;i prioritate de trecere autovehiculelor care circul&#259; din sens invers;\r\n</p><p id=\"v_c\"><b>c. </b> intra&#355;i pe un sector de drum pe care circula&#355;ia se desf&#259;&#351;oar&#259; în ambele sensuri.</p>', 'nu', 'nu', 'da', ''),
(828, 'Ce obliga&#355;ii are conduc&#259;torul de vehicul care inten&#355;ioneaz&#259; s&#259; intre pe prima strad&#259; la dreapta, într-o intersec&#355;ie cu sens giratoriu?', '', '<p id=\"v_a\"><b>a. </b> ocole&#351;te sensul giratoriu, apoi schimb&#259; direc&#355;ia de mers spre dreapta;\r\n</p><p id=\"v_b\"><b>b. </b> semnalizeaz&#259; inten&#355;ia de a executa manevra, se asigur&#259;, se angajeaz&#259; cât mai aproape de marginea din dreapta, apoi intr&#259; pe strada respectiv&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> opre&#351;te autovehiculul, se asigur&#259; &#351;i execut&#259; schimbarea direc&#355;iei de mers spre dreapta.</p>', 'nu', 'da', 'nu', ''),
(829, 'Este permis&#259; sta&#355;ionarea în dreptul c&#259;ilor de acces care deservesc propriet&#259;&#355;ile al&#259;turate drumurilor publice?', '', '<p id=\"v_a\"><b>a. </b> legal, nu este prev&#259;zut;\r\n</p><p id=\"v_b\"><b>b. </b> da, dar numai a autovehiculelor având o mas&#259; total&#259; maxim&#259; autorizat&#259; de pân&#259; la 1,5 t;\r\n</p><p id=\"v_c\"><b>c. </b> nu, deoarece sta&#355;ionarea în astfel de locuri este interzis&#259;.</p>', 'nu', 'nu', 'da', '');
INSERT INTO `intrebari` (`id`, `cerinta`, `imagine`, `variante`, `rasp_a`, `rasp_b`, `rasp_c`, `eticheta`) VALUES
(830, 'Pentru a acorda primul ajutor în caz de fracturi, se fac urm&#259;toarele manevre:', '', '<p id=\"v_a\"><b>a. </b> se împing fragmentele osoase care au perforat pielea;\r\n</p><p id=\"v_b\"><b>b. </b> se fixeaz&#259; focarul de fractur&#259;, pentru a preveni mi&#351;carea fragmentelor osoase în timpul imobiliz&#259;rii membrului, în atele care s&#259; fie destul de lungi pentru a prinde articula&#355;ia de deasupra &#351;i cea de dedesubtul focarului de fractur&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> se imobilizeaz&#259; fractura cu fe&#351;e sterile.</p>', 'nu', 'da', 'nu', ''),
(831, 'Ce substan&#355;e au efect asem&#259;n&#259;tor cu cel al alcoolului &#351;i pot reduce capacitatea de concentrare a unui conduc&#259;tor auto?', '', '<p id=\"v_a\"><b>a. </b> condimentele foarte iu&#355;i;\r\n</p><p id=\"v_b\"><b>b. </b> medicamentele sau substan&#355;ele cu efect psihoactiv;\r\n</p><p id=\"v_c\"><b>c. </b> cofeina.</p>', 'nu', 'da', 'nu', ''),
(832, 'În ce ordine vor trece autovehiculele prin intersec&#355;ia prezentat&#259;?', 'uploads/960.jpg', '<p id=\"v_a\"><b>a. </b> autoturismul, autocamionul, motocicleta;\r\n</p><p id=\"v_b\"><b>b. </b> autocamionul, autoturismul, motocicleta;\r\n</p><p id=\"v_c\"><b>c. </b> autoturismul, motocicleta, autocamionul.</p>', 'nu', 'nu', 'da', ''),
(833, 'Ce obliga&#355;ie ave&#355;i dac&#259; întâlni&#355;i într-o intersec&#355;ie indicatorul „Înainte sau la stânga“ &#351;i semaforul se afl&#259; pe culoarea verde?', '', '<p id=\"v_a\"><b>a. </b> circula&#355;i înainte sau la stânga;\r\n</p><p id=\"v_b\"><b>b. </b> circula&#355;i numai înainte;\r\n</p><p id=\"v_c\"><b>c. </b> circula&#355;i în orice direc&#355;ie, deoarece semnalul verde v&#259; permite acest lucru.</p>', 'da', 'nu', 'nu', ''),
(834, 'Conduc&#259;torul auto prev&#259;z&#259;tor are în vedere:', '', '<p id=\"v_a\"><b>a. </b> s&#259; fie odihnit, iar autovehiculul s&#259; corespund&#259; din punct de vedere tehnic pentru circula&#355;ia pe drumurile publice;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; aib&#259; asigurat&#259; presiunea normal&#259; la roata de rezerv&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; observe înaintea altora locurile în care este amplasat aparatul RADAR.</p>', 'da', 'nu', 'nu', ''),
(835, 'Cum trebuie s&#259; proceda&#355;i în aceast&#259; situa&#355;ie?', 'uploads/547.jpg', '<p id=\"v_a\"><b>a. </b> reduce&#355;i viteza &#351;i v&#259; continua&#355;i deplasarea;\r\n</p><p id=\"v_b\"><b>b. </b> opri&#355;i &#351;i a&#351;tepta&#355;i urm&#259;torul semnal al poli&#355;istului;\r\n</p><p id=\"v_c\"><b>c. </b> circula&#355;i obligatoriu pe direc&#355;ia indicat&#259; de bra&#355;ul poli&#355;istului.</p>', 'nu', 'da', 'nu', ''),
(836, 'Ce semnific&#259; indicatorul?', 'uploads/14.jpg', '<p id=\"v_a\"><b>a. </b> drum aglomerat;\r\n</p><p id=\"v_b\"><b>b. </b> interval obligatoriu între autovehicule;\r\n</p><p id=\"v_c\"><b>c. </b> distan&#355;&#259; obligatorie de oprire.</p>', 'nu', 'da', 'nu', ''),
(837, 'Pe ce drumuri sunt permise oprirea &#351;i sta&#355;ionarea vehiculelor &#351;i pe partea stâng&#259;, în sensul de mers?', '', '<p id=\"v_a\"><b>a. </b> pe drumurile cu trafic redus;\r\n</p><p id=\"v_b\"><b>b. </b> pe drumurile cu sens unic, dac&#259; r&#259;mâne liber&#259; cel pu&#355;in o band&#259; de circula&#355;ie;\r\n</p><p id=\"v_c\"><b>c. </b> pe orice drum.</p>', 'nu', 'da', 'nu', ''),
(838, 'Cum ve&#355;i proceda dac&#259; inten&#355;iona&#355;i s&#259; schimba&#355;i direc&#355;ia de mers spre dreapta?', '', '<p id=\"v_a\"><b>a. </b> semnaliza&#355;i schimbarea direc&#355;iei de mers; pietonii v&#259; vor acorda prioritate;\r\n</p><p id=\"v_b\"><b>b. </b> semnaliza&#355;i schimbarea direc&#355;iei de mers; acorda&#355;i prioritate vehiculelor care circul&#259; din partea stâng&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> semnaliza&#355;i schimbarea direc&#355;iei de mers &#351;i acorda&#355;i prioritate de trecere pietonilor.</p>', 'nu', 'nu', 'da', ''),
(839, 'Ce tendin&#355;&#259; prezint&#259; un autoturism cu trac&#355;iune pe spate, dac&#259; accelera&#355;i prea puternic în curb&#259;?', '', '<p id=\"v_a\"><b>a. </b> autoturismul urmeaz&#259;, f&#259;r&#259; deviere, cursa volanului;\r\n</p><p id=\"v_b\"><b>b. </b> autoturismul tinde s&#259; derapeze cu spatele spre exteriorul curbei;\r\n</p><p id=\"v_c\"><b>c. </b> ro&#355;ile din fa&#355;&#259; se învârtesc în gol.</p>', 'nu', 'da', 'nu', ''),
(840, 'Cum trebuie s&#259; proceda&#355;i când ajunge&#355;i în spatele unui tramvai oprit într-o sta&#355;ie f&#259;r&#259; refugiu pentru pietoni?', '', '<p id=\"v_a\"><b>a. </b> s&#259; opri&#355;i în dreptul ultimei u&#351;i;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; opri&#355;i în spatele acestuia &#351;i s&#259; v&#259; relua&#355;i deplasarea numai dup&#259; ce u&#351;ile tramvaiului au fost închise;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; opri&#355;i la o distan&#355;&#259; de 25 m de tramvai.</p>', 'nu', 'da', 'nu', ''),
(841, 'Ce obliga&#355;ii are conduc&#259;torul de autovehicule când circul&#259; pe un drum public?', '', '<p id=\"v_a\"><b>a. </b> s&#259; circule numai dac&#259; verificarea medical&#259; lunar&#259; este efectuat&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; circule numai pe sectoarele de drum pe care îi este permis accesul &#351;i s&#259; respecte normele referitoare la masele totale maxime autorizate admise de autoritatea competent&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; se informeze din timp, la administratorii de drum, în leg&#259;tur&#259; cu eventualele limite maxime &#351;i minime de vitez&#259;.</p>', 'nu', 'da', 'nu', ''),
(842, 'Camionul execut&#259; corect dep&#259;&#351;irea autoturismului?', 'uploads/748.jpg', '<p id=\"v_a\"><b>a. </b> da, deoarece conduc&#259;torul autoturismului a semnalizat inten&#355;ia de a vira la stânga, iar spa&#355;iul r&#259;mas liber permite trecerea camionului prin partea dreapt&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> nu, deoarece în intersec&#355;ii dep&#259;&#351;irea este interzis&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> nu, deoarece dep&#259;&#351;irea se execut&#259; numai pe partea stâng&#259;.</p>', 'da', 'nu', 'nu', ''),
(843, 'Cum se acord&#259; primul ajutor în cazul pl&#259;gilor t&#259;iate?', '', '<p id=\"v_a\"><b>a. </b> acoperi&#355;i rana &#351;i opri&#355;i sângerarea, apoi transporta&#355;i r&#259;nitul la medic sau la spital;\r\n</p><p id=\"v_b\"><b>b. </b> opri&#355;i sângerarea prin coaserea pl&#259;gii;\r\n</p><p id=\"v_c\"><b>c. </b> transporta&#355;i r&#259;nitul la medic, f&#259;r&#259; s&#259; face&#355;i nimic altceva.</p>', 'da', 'nu', 'nu', ''),
(844, 'Ce se în&#355;elege prin conducere ecologic&#259; a unui autovehicul?', '', '<p id=\"v_a\"><b>a. </b> obliga&#355;ia de a folosi în permanen&#355;&#259; carburant biodegradabil;\r\n</p><p id=\"v_b\"><b>b. </b> deplas&#259;ri urbane cu bicicleta, pe jos sau cu alte mijloace care nu polueaz&#259; atmosfera;\r\n</p><p id=\"v_c\"><b>c. </b> un ansamblu de m&#259;suri comportamentale, de control sau de verificare a vehiculului, prin care se realizeaz&#259; o important&#259; economie de energie &#351;i protec&#355;ia mediului.</p>', 'nu', 'nu', 'da', ''),
(845, 'Ce obliga&#355;ii are conduc&#259;torul unui autovehicul care circul&#259; în zona de frontier&#259;?', '', '<p id=\"v_a\"><b>a. </b> s&#259; opreasc&#259; imediat autovehiculul la semnalele regulamentare ale poli&#355;i&#351;tilor de frontier&#259; afla&#355;i în zona de competen&#355;&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; reduc&#259; viteza sub limita legal&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; circule cu faza de întâlnire în func&#355;iune, deoarece se afl&#259; în zona de frontier&#259;.</p>', 'da', 'nu', 'nu', ''),
(846, 'Ce semnifica&#355;ie are indicatorul din imagine?', 'uploads/759.jpg', '<p id=\"v_a\"><b>a. </b> urmeaz&#259; un sector de drum îngustat temporar;\r\n</p><p id=\"v_b\"><b>b. </b> este interzis&#259; schimbarea direc&#355;iei de mers la dreapta în prima intersec&#355;ie;\r\n</p><p id=\"v_c\"><b>c. </b> urmeaz&#259; o intersec&#355;ie cu un drum f&#259;r&#259; prioritate.</p>', 'nu', 'nu', 'da', ''),
(847, 'La întâlnirea unui vehicul greu cu unul u&#351;or, pe un drum public îngust, pentru a face posibil&#259; trecerea va manevra cu spatele:', '', '<p id=\"v_a\"><b>a. </b> vehiculul u&#351;or;\r\n</p><p id=\"v_b\"><b>b. </b> vehiculul greu;\r\n</p><p id=\"v_c\"><b>c. </b> la libera în&#355;elegere.</p>', 'da', 'nu', 'nu', ''),
(848, 'Ce obliga&#355;ii ave&#355;i în situa&#355;ia prezentat&#259;?', 'uploads/974.jpg', '<p id=\"v_a\"><b>a. </b> s&#259; circula&#355;i cu vitez&#259; redus&#259; &#351;i s&#259; traversa&#355;i calea ferat&#259;, dac&#259; bariera sau semibariera este ridicat&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; opri&#355;i în dreptul indicatorului;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; m&#259;ri&#355;i viteza, pentru a nu fi surprins de închiderea barierelor.</p>', 'da', 'nu', 'nu', ''),
(849, 'Ce semnifica&#355;ie are marcajul longitudinal aplicat sub forma unei linii continue pe axul drumurilor publice?', '', '<p id=\"v_a\"><b>a. </b> ajut&#259; conduc&#259;torii auto s&#259; se orienteze pe timp de noapte;\r\n</p><p id=\"v_b\"><b>b. </b> nu permite înc&#259;lcarea lui de c&#259;tre conduc&#259;torii de vehicule;\r\n</p><p id=\"v_c\"><b>c. </b> permite dep&#259;&#351;irea vehiculelor.</p>', 'nu', 'da', 'nu', ''),
(850, 'Indicatorul nu permite schimbarea direc&#355;iei de mers spre stânga:', 'uploads/469.jpg', '<p id=\"v_a\"><b>a. </b> în intersec&#355;ia înaintea c&#259;reia este instalat;\r\n</p><p id=\"v_b\"><b>b. </b> în toate intersec&#355;iile, pân&#259; la apari&#355;ia indicatorului „Sfâr&#351;itul tuturor restric&#355;iilor“;\r\n</p><p id=\"v_c\"><b>c. </b> la 200 m de la locul în care este instalat acesta.</p>', 'da', 'nu', 'nu', ''),
(851, 'A&#355;i accidentat un pieton care traversa strada neregulamentar. Ce obliga&#355;ii legale v&#259; revin?', '', '<p id=\"v_a\"><b>a. </b> anun&#355;a&#355;i imediat poli&#355;ia, nu modifica&#355;i sau &#351;terge&#355;i urmele accidentului &#351;i r&#259;mâne&#355;i pe loc;\r\n</p><p id=\"v_b\"><b>b. </b> v&#259; prezenta&#355;i de urgen&#355;&#259; la cea mai apropiat&#259; unitate de poli&#355;ie;\r\n</p><p id=\"v_c\"><b>c. </b> deoarece vinov&#259;&#355;ia nu v&#259; apar&#355;ine, pute&#355;i continua drumul dac&#259; accidentul nu este grav.</p>', 'da', 'nu', 'nu', ''),
(852, 'În r&#259;nile cu hemoragie se va avea în vedere, în faza ini&#355;ial&#259;:', '', '<p id=\"v_a\"><b>a. </b> cur&#259;&#355;area &#351;i pansarea r&#259;nii;\r\n</p><p id=\"v_b\"><b>b. </b> pansarea r&#259;nii;\r\n</p><p id=\"v_c\"><b>c. </b> oprirea hemoragiei.</p>', 'nu', 'nu', 'da', ''),
(853, 'La semnalul poli&#355;istului situat într-o intersec&#355;ie, cu bra&#355;ul ridicat vertical, ave&#355;i obliga&#355;ia:', '', '<p id=\"v_a\"><b>a. </b> s&#259; circula&#355;i cu pruden&#355;&#259; maxim&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; reduce&#355;i viteza;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; opri&#355;i, indiferent din ce direc&#355;ie circula&#355;i.</p>', 'nu', 'nu', 'da', ''),
(854, 'Care dintre autoturismele din imagine circul&#259; corect în intersec&#355;ie?', 'uploads/674.jpg', '<p id=\"v_a\"><b>a. </b> autoturismul mov &#351;i cel ro&#351;u;\r\n</p><p id=\"v_b\"><b>b. </b> autoturismul albastru &#351;i cel ro&#351;u;\r\n</p><p id=\"v_c\"><b>c. </b> toate cele patru autoturisme.</p>', 'nu', 'nu', 'da', ''),
(855, 'Din punct de vedere al conduitei preventive, la ce v&#259; pute&#355;i a&#351;tepta în aceast&#259; situa&#355;ie?', 'uploads/946.jpg', '<p id=\"v_a\"><b>a. </b> biciclistul va opri;\r\n</p><p id=\"v_b\"><b>b. </b> biciclistul poate schimba direc&#355;ia de mers spre stânga f&#259;r&#259; s&#259; se asigure;\r\n</p><p id=\"v_c\"><b>c. </b> biciclistul se va comporta corect.</p>', 'nu', 'da', 'nu', ''),
(856, 'Pân&#259; la transportarea victimei la spital, pozi&#355;ia de a&#351;teptare poate fi:', '', '<p id=\"v_a\"><b>a. </b> o pozi&#355;ie cât mai comod&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> o pozi&#355;ie care s&#259; permit&#259; o bun&#259; respira&#355;ie;\r\n</p><p id=\"v_c\"><b>c. </b> o pozi&#355;ie comod&#259; pentru transport.</p>', 'nu', 'da', 'nu', ''),
(857, 'Care este deosebirea dintre oprire &#351;i sta&#355;ionare?', '', '<p id=\"v_a\"><b>a. </b> niciuna;\r\n</p><p id=\"v_b\"><b>b. </b> deosebirea const&#259; în durata de imobilizare a vehiculului;\r\n</p><p id=\"v_c\"><b>c. </b> deosebirea const&#259; în modul de a&#351;ezare &#351;i asigurare a autovehiculului pe drum.</p>', 'nu', 'da', 'nu', ''),
(858, 'Ce obliga&#355;ii v&#259; revin la semnalul agentului de cale ferat&#259; de la trecerile la nivel, executat cu un dispozitiv cu lumin&#259; ro&#351;ie ori cu bastonul fluorescent-reflectorizant?', '', '<p id=\"v_a\"><b>a. </b> s&#259; reduce&#355;i viteza doar pân&#259; traversa&#355;i calea ferat&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; opri&#355;i autovehiculul;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; reduce&#355;i viteza &#351;i s&#259; opri&#355;i numai dac&#259; circul&#259; un vehicul feroviar.</p>', 'nu', 'da', 'nu', ''),
(859, 'V&#259; este permis s&#259; executa&#355;i dep&#259;&#351;irea în situa&#355;ia prezentat&#259;?', 'uploads/854.jpg', '<p id=\"v_a\"><b>a. </b> nu, deoarece efectua&#355;i manevra pe un pod;\r\n</p><p id=\"v_b\"><b>b. </b> da, întrucât axul drumului nu este marcat cu o linie continu&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> nu, deoarece podul nu are parape&#355;i din beton.</p>', 'da', 'nu', 'nu', ''),
(860, 'Cum trebuie s&#259; ave&#355;i reglat sistemul de lumini pentru circula&#355;ia pe timp de noapte?', '', '<p id=\"v_a\"><b>a. </b> astfel încât s&#259; vede&#355;i &#351;i s&#259; fi&#355;i v&#259;zut, f&#259;r&#259; a-i stânjeni pe ceilal&#355;i participan&#355;i la trafic;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; vede&#355;i cât mai bine în fa&#355;&#259;, indiferent dac&#259; sunt deranja&#355;i ceilal&#355;i participan&#355;i la trafic cu care v&#259; întersecta&#355;i;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; asigura&#355;i, cel pu&#355;in, func&#355;ionarea farului din partea dreapt&#259;.</p>', 'da', 'nu', 'nu', ''),
(861, 'Pe o autostrad&#259; cu trei benzi, s-a blocat circula&#355;ia. Pe unde vor circula vehiculele de interven&#355;ie?', '', '<p id=\"v_a\"><b>a. </b> pe banda de urgen&#355;&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> între banda din stânga &#351;i banda din mijloc;\r\n</p><p id=\"v_c\"><b>c. </b> între banda din dreapta &#351;i banda din mijloc.</p>', 'da', 'nu', 'nu', ''),
(862, 'Ce indic&#259; semnalul poli&#355;istului?', 'uploads/663.jpg', '<p id=\"v_a\"><b>a. </b> oprire;\r\n</p><p id=\"v_b\"><b>b. </b> m&#259;ri&#355;i viteza;\r\n</p><p id=\"v_c\"><b>c. </b> reduce&#355;i viteza.</p>', 'nu', 'nu', 'da', ''),
(863, 'Este permis&#259; dep&#259;&#351;irea în situa&#355;ia dat&#259;?', 'uploads/311.jpg', '<p id=\"v_a\"><b>a. </b> da, deoarece axul drumului este marcat cu linie continu&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> da, deoarece linia discontinu&#259; v&#259; este cea mai apropiat&#259; de direc&#355;ia de mers;\r\n</p><p id=\"v_c\"><b>c. </b> nu, deoarece autovehiculul din fa&#355;&#259; nu v&#259; permite dep&#259;&#351;irea.</p>', 'nu', 'da', 'nu', ''),
(864, 'Cum trebuie s&#259; proceda&#355;i la întâlnirea indicatorului?', 'uploads/737.jpg', '<p id=\"v_a\"><b>a. </b> ocoli&#355;i centrul intersec&#355;iei prin stânga;\r\n</p><p id=\"v_b\"><b>b. </b> circula&#355;i cu o vitez&#259; maxim&#259; de 40 km/h;\r\n</p><p id=\"v_c\"><b>c. </b> acorda&#355;i prioritate vehiculelor care circul&#259; în interiorul intersec&#355;iei.</p>', 'nu', 'nu', 'da', ''),
(865, 'Trebuie s&#259; acorda&#355;i prioritate autoturismului gri dac&#259; acesta vireaz&#259; la stânga?', 'uploads/331.jpg', '<p id=\"v_a\"><b>a. </b> în func&#355;ie de viteza celor dou&#259; vehicule;\r\n</p><p id=\"v_b\"><b>b. </b> da;\r\n</p><p id=\"v_c\"><b>c. </b> nu.</p>', 'nu', 'nu', 'da', ''),
(866, 'Pentru a vira la stânga:', 'uploads/470.jpg', '<p id=\"v_a\"><b>a. </b> autoturismele vor folosi traiectoriile care ocolesc prin stânga centrul imaginar al intersec&#355;iei;\r\n</p><p id=\"v_b\"><b>b. </b> autoturismul ro&#351;u va ceda trecerea;\r\n</p><p id=\"v_c\"><b>c. </b> autoturismul ro&#351;u va trece prin spatele celui albastru.</p>', 'da', 'nu', 'nu', ''),
(867, 'Utilizarea sistemului de climatizare are ca efect:', '', '<p id=\"v_a\"><b>a. </b> cre&#351;terea consumului de combustibil;\r\n</p><p id=\"v_b\"><b>b. </b> reducerea consumului de combustibil;\r\n</p><p id=\"v_c\"><b>c. </b> sc&#259;derea puternic&#259; a gradului de confort.</p>', 'da', 'nu', 'nu', ''),
(868, 'Care este limita maxim&#259; de vitez&#259; pe autostr&#259;zi?', '', '<p id=\"v_a\"><b>a. </b> 110 km/h;\r\n</p><p id=\"v_b\"><b>b. </b> 100 km/h;\r\n</p><p id=\"v_c\"><b>c. </b> 130 km/h.</p>', 'nu', 'nu', 'da', ''),
(869, 'Pentru a conduce ecologic un autovehicul, se recomand&#259;:', '', '<p id=\"v_a\"><b>a. </b> utilizarea climatiz&#259;rii numai în cazul traseelor scurte;\r\n</p><p id=\"v_b\"><b>b. </b> utilizarea climatiz&#259;rii numai pe timp de noapte;\r\n</p><p id=\"v_c\"><b>c. </b> utilizarea climatiz&#259;rii numai în situa&#355;iile absolut necesare.</p>', 'nu', 'nu', 'da', ''),
(870, 'Când sunte&#355;i obligat s&#259; p&#259;stra&#355;i o distan&#355;&#259; corespunz&#259;toare fa&#355;&#259; de cel care ruleaz&#259; în fa&#355;a dumneavoastr&#259;?', '', '<p id=\"v_a\"><b>a. </b> numai atunci când nu ave&#355;i experien&#355;&#259; în conducere;\r\n</p><p id=\"v_b\"><b>b. </b> numai dac&#259; autovehiculul pe care îl conduce&#355;i nu are frânele eficiente;\r\n</p><p id=\"v_c\"><b>c. </b> în orice situa&#355;ie.</p>', 'nu', 'nu', 'da', ''),
(871, 'La semnalul dat de personalul autorizat din zona lucr&#259;rilor pe drumurile publice, prin rotirea vioaie a bra&#355;ului, ave&#355;i obliga&#355;ia:', '', '<p id=\"v_a\"><b>a. </b> s&#259; m&#259;ri&#355;i viteza de deplasare a autovehiculului;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; ocoli&#355;i obstacolul creat în carosabil, circulând în sens giratoriu;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; ocoli&#355;i obstacolul indicat de acesta.</p>', 'da', 'nu', 'nu', ''),
(872, 'Este permis&#259; sta&#355;ionarea vehiculelor în zona de ac&#355;iune a indicatorului „Drum îngustat“?', '', '<p id=\"v_a\"><b>a. </b> da;\r\n</p><p id=\"v_b\"><b>b. </b> nu;\r\n</p><p id=\"v_c\"><b>c. </b> legea nu prevede nimic cu privire la acest aspect.</p>', 'nu', 'da', 'nu', ''),
(873, 'Copiii cu vârste de pân&#259; la 12 ani pot fi transporta&#355;i:', '', '<p id=\"v_a\"><b>a. </b> pe scaunul din fa&#355;&#259; al autovehiculului, dac&#259; sunt &#355;inu&#355;i în bra&#355;e de persoane majore;\r\n</p><p id=\"v_b\"><b>b. </b> pe scaunul din fa&#355;&#259; al autovehiculului, având fixat&#259; centura de siguran&#355;&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> pe bancheta din spate a autovehiculului, având fixat&#259; centura de siguran&#355;&#259;, adaptat&#259; greut&#259;&#355;ii &#351;i dimensiunilor lor.</p>', 'nu', 'nu', 'da', ''),
(874, 'Într-o intersec&#355;ie, prezen&#355;a unei ambulan&#355;e ce se deplaseaz&#259; la o interven&#355;ie oblig&#259; pe ceilal&#355;i conduc&#259;tori:', '', '<p id=\"v_a\"><b>a. </b> s&#259; reduc&#259; viteza &#351;i s&#259;-&#351;i continue drumul cu aten&#355;ie;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; respecte semnifica&#355;ia culorii semaforului;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; acorde prioritate de trecere ambulan&#355;ei.</p>', 'nu', 'nu', 'da', ''),
(875, 'Dezinfec&#355;ia pl&#259;gilor ce necesit&#259; a fi pansate se face cu:', '', '<p id=\"v_a\"><b>a. </b> ap&#259; oxigenat&#259; sau iod;\r\n</p><p id=\"v_b\"><b>b. </b> ser fiziologic;\r\n</p><p id=\"v_c\"><b>c. </b> ap&#259; potabil&#259;.</p>', 'da', 'nu', 'nu', ''),
(876, 'Ce obliga&#355;ii ave&#355;i la trecerea pentru pietoni, pe sensul dvs. de mers?', '', '<p id=\"v_a\"><b>a. </b> s&#259; acorda&#355;i prioritate de trecere &#351;i pietonilor care circul&#259; pe cel&#259;lalt sens de mers;\n</p><p id=\"v_b\"><b>b. </b> s&#259; reduce&#355;i viteza &#351;i s&#259; v&#259; strecura&#355;i printre pietoni;\n</p><p id=\"v_c\"><b>c. </b> s&#259; acorda&#355;i prioritate de trecere pietonilor afla&#355;i în traversare.</p>', 'nu', 'nu', 'da', ''),
(877, 'Cu ce vitez&#259; se poate circula în apropierea unei intersec&#355;ii?', '', '<p id=\"v_a\"><b>a. </b> limita maxim&#259; de vitez&#259; va fi respectat&#259; doar în cazul intersec&#355;iilor dirijate;\n</p><p id=\"v_b\"><b>b. </b> cu o vitez&#259; care s&#259; permit&#259; oprirea, pentru a acorda prioritate de trecere participan&#355;ilor la trafic care au acest drept;\n</p><p id=\"v_c\"><b>c. </b> limita maxim&#259; de vitez&#259; în localit&#259;&#355;i va fi de 50 km/h, iar în afara localit&#259;&#355;ilor de 130 km/h.</p>', 'nu', 'da', 'nu', ''),
(878, 'În aceast&#259; situa&#355;ie, inten&#355;iona&#355;i s&#259; vira&#355;i pe prima strad&#259; la dreapta. Ce obliga&#355;ii ave&#355;i?', 'uploads/346.jpg', '<p id=\"v_a\"><b>a. </b> opri&#355;i, v&#259; asigura&#355;i, apoi schimba&#355;i direc&#355;ia de mers spre dreapta;\r\n</p><p id=\"v_b\"><b>b. </b> ocoli&#355;i sensul giratoriu, apoi schimba&#355;i direc&#355;ia de mers spre dreapta;\r\n</p><p id=\"v_c\"><b>c. </b> semnaliza&#355;i inten&#355;ia de a executa manevra, v&#259; asigura&#355;i, v&#259; angaja&#355;i cât mai aproape de marginea din dreapta, apoi vira&#355;i la dreapta.</p>', 'nu', 'nu', 'da', ''),
(879, 'Este permis&#259; trecerea peste marcajul longitudinal format dintr-o linie continu&#259; &#351;i alta discontinu&#259;?', '', '<p id=\"v_a\"><b>a. </b> da, dac&#259; marcajul nu este într-o curb&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> da, dac&#259; linia discontinu&#259; este cea mai apropiat&#259; de vehicul în direc&#355;ia de mers;\r\n</p><p id=\"v_c\"><b>c. </b> nu, chiar în cazul în care linia discontinu&#259; este cea mai apropiat&#259; de vehicul.</p>', 'nu', 'da', 'nu', ''),
(880, 'Cum proceda&#355;i în situa&#355;ia dat&#259;:', 'uploads/545.jpg', '<p id=\"v_a\"><b>a. </b> acorda&#355;i prioritate de trecere autocamionului;\r\n</p><p id=\"v_b\"><b>b. </b> reduce&#355;i viteza &#351;i s&#259; semnaliza&#355;i luminos;\r\n</p><p id=\"v_c\"><b>c. </b> v&#259; continua&#355;i drumul.</p>', 'nu', 'nu', 'da', ''),
(881, 'La frânare, vehiculul derapeaz&#259;. Cum reac&#355;iona&#355;i corect?', '', '<p id=\"v_a\"><b>a. </b> frâna&#355;i la maximum;\r\n</p><p id=\"v_b\"><b>b. </b> nu ac&#355;iona&#355;i frâna &#351;i roti&#355;i volanul în sens invers derap&#259;rii;\r\n</p><p id=\"v_c\"><b>c. </b> opri&#355;i motorul.</p>', 'nu', 'da', 'nu', ''),
(882, 'Care este regula priorit&#259;&#355;ii de trecere la intersec&#355;ia a dou&#259; drumuri de aceea&#351;i categorie, cu circula&#355;ie nedirijat&#259;?', '', '<p id=\"v_a\"><b>a. </b> prioritatea de dreapta;\r\n</p><p id=\"v_b\"><b>b. </b> prioritatea primului sosit;\r\n</p><p id=\"v_c\"><b>c. </b> prioritatea de drum principal.</p>', 'da', 'nu', 'nu', ''),
(883, 'Ce obliga&#355;ii are conduc&#259;torul auto care inten&#355;ioneaz&#259; s&#259; repun&#259; în mi&#351;care autovehiculul aflat între alte vehicule sta&#355;ionate?', '', '<p id=\"v_a\"><b>a. </b> semnalizeaz&#259; plecarea de pe loc, pentru a fi observat de cei care circul&#259; în acela&#351;i sens;\r\n</p><p id=\"v_b\"><b>b. </b> semnalizeaz&#259; din timp plecarea de pe loc, se asigur&#259; c&#259; poate efectua manevra f&#259;r&#259; a perturba circula&#355;ia sau f&#259;r&#259; a pune în pericol siguran&#355;a celorlal&#355;i participan&#355;i la trafic &#351;i acord&#259; prioritate vehiculelor care circul&#259; pe drumul public, indiferent de direc&#355;ia de deplasare;\r\n</p><p id=\"v_c\"><b>c. </b> manevreaz&#259; cu grij&#259; autovehiculul, pentru a nu lovi vehiculele din fa&#355;a sau din spatele s&#259;u, dup&#259; care se repune în mi&#351;care.</p>', 'nu', 'da', 'nu', ''),
(884, 'În fa&#355;a dvs. s-a produs un accident rutier care este cercetat de poli&#355;ie, iar circula&#355;ia se desf&#259;&#351;oar&#259; anevoios, pe un singur sens. Cum ve&#355;i proceda?', '', '<p id=\"v_a\"><b>a. </b> v&#259; continua&#355;i drumul cu aten&#355;ie, respectând indica&#355;iile poli&#355;istului rutier;\r\n</p><p id=\"v_b\"><b>b. </b> v&#259; continua&#355;i drumul cu vitez&#259; sporit&#259;, pentru a nu bloca traficul rutier;\r\n</p><p id=\"v_c\"><b>c. </b> reduce&#355;i viteza &#351;i opri&#355;i în locul accidentului, pentru a vedea ce s-a întâmplat.</p>', 'da', 'nu', 'nu', ''),
(885, 'Cum trebuie s&#259; proceda&#355;i corect, dac&#259; v&#259; afla&#355;i la volanul autoturismului?', 'uploads/998.jpg', '<p id=\"v_a\"><b>a. </b> permite&#355;i trecerea camionului;\r\n</p><p id=\"v_b\"><b>b. </b> permite&#355;i trecerea tramvaiului;\r\n</p><p id=\"v_c\"><b>c. </b> vira&#355;i înaintea tramvaiului.</p>', 'nu', 'da', 'nu', ''),
(886, 'Ce obliga&#355;ii ave&#355;i la întâlnirea indicatorului „Bicicli&#351;ti“?', '', '<p id=\"v_a\"><b>a. </b> s&#259; reduce&#355;i viteza la maximum 30 km/h;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; circula&#355;i cu aten&#355;ie &#351;i, dac&#259; este cazul, acorda&#355;i prioritate de trecere bicicli&#351;tilor care circul&#259; pe pista special destinat&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> nu ave&#355;i nicio obliga&#355;ie, bicicli&#351;tii v&#259; vor acorda prioritate.</p>', 'nu', 'da', 'nu', ''),
(887, 'La întâlnirea indicatorului de avertizare în cruce, amplasat la trecerile la nivel cu calea ferat&#259; f&#259;r&#259; bariere, conduc&#259;torii de vehicule vor opri:', '', '<p id=\"v_a\"><b>a. </b> cât mai aproape de calea ferat&#259;, dar nu mai pu&#355;in de 5 m de aceasta;\r\n</p><p id=\"v_b\"><b>b. </b> în locul cu vizibilitate maxim&#259;, f&#259;r&#259; a dep&#259;&#351;i indicatorul;\r\n</p><p id=\"v_c\"><b>c. </b> la cel mult 5 m de calea ferat&#259;.</p>', 'nu', 'da', 'nu', ''),
(888, 'La întâlnirea indicatorului ave&#355;i obliga&#355;ia:', 'uploads/635.jpg', '<p id=\"v_a\"><b>a. </b> de a circula cu aten&#355;ie, deoarece urmeaz&#259; o curb&#259; la stânga;\r\n</p><p id=\"v_b\"><b>b. </b> de a schimba obligatoriu direc&#355;ia de mers spre stânga;\r\n</p><p id=\"v_c\"><b>c. </b> de a reduce viteza, deoarece urmeaz&#259; o intersec&#355;ie periculoas&#259;.</p>', 'da', 'nu', 'nu', ''),
(889, 'Este permis&#259; întoarcerea pe autostrad&#259;?', 'uploads/415.jpg', '<p id=\"v_a\"><b>a. </b> nu;\r\n</p><p id=\"v_b\"><b>b. </b> da;\r\n</p><p id=\"v_c\"><b>c. </b> da, dac&#259; sensul de mers este blocat.</p>', 'da', 'nu', 'nu', ''),
(890, 'Este permis&#259; oprirea voluntar&#259; a autovehiculului în tuneluri?', '', '<p id=\"v_a\"><b>a. </b> da;\r\n</p><p id=\"v_b\"><b>b. </b> nu;\r\n</p><p id=\"v_c\"><b>c. </b> numai dac&#259; lungimea tunelului dep&#259;&#351;e&#351;te 1000 m.</p>', 'nu', 'da', 'nu', ''),
(891, 'Cum proceda&#355;i la semnalul poli&#355;istului cu bra&#355;ul ridicat vertical?', '', '<p id=\"v_a\"><b>a. </b> opri&#355;i, indiferent din ce direc&#355;ie v&#259; apropia&#355;i, &#351;i a&#351;tepta&#355;i urm&#259;torul s&#259;u semnal;\r\n</p><p id=\"v_b\"><b>b. </b> reduce&#355;i viteza &#351;i circula&#355;i în direc&#355;ia permis&#259; de pozi&#355;ia bra&#355;ului;\r\n</p><p id=\"v_c\"><b>c. </b> reduce&#355;i viteza &#351;i îl ocoli&#355;i.</p>', 'da', 'nu', 'nu', ''),
(892, 'În ce locuri circula&#355;ia cu vitez&#259; duce adeseori la accidente?', '', '<p id=\"v_a\"><b>a. </b> în intersec&#355;ii;\r\n</p><p id=\"v_b\"><b>b. </b> în parc&#259;ri;\r\n</p><p id=\"v_c\"><b>c. </b> în poligoanele auto.</p>', 'da', 'nu', 'nu', ''),
(893, 'O func&#355;ie a bandajului este:', '', '<p id=\"v_a\"><b>a. </b> de a opri hemoragia;\r\n</p><p id=\"v_b\"><b>b. </b> de a preveni murd&#259;rirea hainelor;\r\n</p><p id=\"v_c\"><b>c. </b> de a acoperi aspectul nepl&#259;cut al r&#259;nii.</p>', 'da', 'nu', 'nu', ''),
(894, 'Ce trebuie s&#259; fac&#259; un conduc&#259;tor de vehicul pentru ca dep&#259;&#351;irea s&#259; fie considerat&#259; regulamentar&#259;?', '', '<p id=\"v_a\"><b>a. </b> s&#259; se asigure c&#259; manevra poate fi executat&#259; în condi&#355;ii de siguran&#355;&#259;, dup&#259; care s&#259; semnalizeze inten&#355;ia de schimbare a pozi&#355;iei de mers &#351;i s&#259; efectueze dep&#259;&#351;irea;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; semnalizeze din timp inten&#355;ia de schimbare a direc&#355;iei de mers;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; m&#259;reasc&#259; viteza de deplasare, astfel încât s&#259; scurteze timpul de realizare a dep&#259;&#351;irii.</p>', 'da', 'nu', 'nu', ''),
(895, 'În cazul unui accidentat cu hemoragie masiv&#259;, ac&#355;iona&#355;i astfel:', '', '<p id=\"v_a\"><b>a. </b> a&#351;eza&#355;i persoana pe abdomen &#351;i îi da&#355;i s&#259; bea cât mai multe lichide;\r\n</p><p id=\"v_b\"><b>b. </b> a&#351;eza&#355;i persoana pe un scaun în pozi&#355;ia &#351;ezând &#351;i vorbi&#355;i cu ea;\r\n</p><p id=\"v_c\"><b>c. </b> a&#351;eza&#355;i persoana pe spate, cu picioarele ridicate, imobiliza&#355;i eventualele fracturi, nu îi da&#355;i s&#259; bea lichide &#351;i o proteja&#355;i contra frigului.</p>', 'nu', 'nu', 'da', ''),
(896, 'Cum proceda&#355;i în aceast&#259; situa&#355;ie?', 'uploads/378.jpg', '<p id=\"v_a\"><b>a. </b> se recomand&#259; s&#259; m&#259;ri&#355;i aten&#355;ia;\r\n</p><p id=\"v_b\"><b>b. </b> opri&#355;i;\r\n</p><p id=\"v_c\"><b>c. </b> accelera&#355;i, pentru a nu fi surprins de c&#259;derea pietrelor.</p>', 'da', 'nu', 'nu', ''),
(897, 'Ce se în&#355;elege prin distan&#355;a de 100 m pentru semnalizarea schimb&#259;rii direc&#355;iei de mers în afara localit&#259;&#355;ii?', '', '<p id=\"v_a\"><b>a. </b> distan&#355;a fa&#355;&#259; de vehiculul care circul&#259; din spate;\r\n</p><p id=\"v_b\"><b>b. </b> distan&#355;a parcurs&#259; din momentul semnaliz&#259;rii inten&#355;iei de a efectua manevra &#351;i locul unde se produce schimbarea direc&#355;iei de mers;\r\n</p><p id=\"v_c\"><b>c. </b> distan&#355;a fa&#355;&#259; de locul termin&#259;rii manevrei de dep&#259;&#351;ire.</p>', 'nu', 'da', 'nu', ''),
(898, 'Ce semnific&#259; indicatoarele de semnalizare din imagine?', 'uploads/599.jpg', '<p id=\"v_a\"><b>a. </b> „Ocolire prin stânga“;\r\n</p><p id=\"v_b\"><b>b. </b> „Panouri succesive pentru curbe deosebit de periculoase, la stânga“;\r\n</p><p id=\"v_c\"><b>c. </b> „Curb&#259; la stânga“.</p>', 'nu', 'da', 'nu', ''),
(899, 'Care autovehicule pot fi echipate cu lumini speciale de avertizare de culoare albastr&#259;?', '', '<p id=\"v_a\"><b>a. </b> legea nu prevede;\r\n</p><p id=\"v_b\"><b>b. </b> orice autovehicul pentru care se ob&#355;ine aprobarea poli&#355;iei rutiere;\r\n</p><p id=\"v_c\"><b>c. </b> vehiculele apar&#355;inând poli&#355;iei, jandarmeriei &#351;i poli&#355;iei de frontier&#259;.</p>', 'nu', 'nu', 'da', ''),
(900, 'La ce distan&#355;&#259; sunt instalate cele trei panouri suplimentare pentru avertizarea apropierii de o trecere la nivel cu calea ferat&#259;?', '', '<p id=\"v_a\"><b>a. </b> la 300, 200 &#351;i, respectiv, 100 m;\r\n</p><p id=\"v_b\"><b>b. </b> la 90, 60 &#351;i, respectiv, 30 m;\r\n</p><p id=\"v_c\"><b>c. </b> la 150, 100 &#351;i, respectiv, 50 m.</p>', 'nu', 'nu', 'da', ''),
(901, 'Dac&#259; circula&#355;i pe un drum na&#355;ional european (E), v&#259; este interzis:', '', '<p id=\"v_a\"><b>a. </b> s&#259; folosi&#355;i pe timpul zilei luminile de întâlnire;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; opri&#355;i pe partea carosabil&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; dep&#259;&#351;i&#355;i.</p>', 'nu', 'da', 'nu', ''),
(902, 'Dac&#259; autovehiculul pe care îl conduce&#355;i r&#259;mâne imobilizat pe calea ferat&#259;, ave&#355;i urm&#259;toarea obliga&#355;ie:', '', '<p id=\"v_a\"><b>a. </b> s&#259; scoate&#355;i imediat pasagerii din vehicul &#351;i s&#259; elibera&#355;i platforma c&#259;ii ferate sau, când nu este posibil, s&#259; semnaliza&#355;i prezen&#355;a vehiculului cu orice mijloc adecvat;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; anun&#355;a&#355;i poli&#355;ia;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; porni&#355;i autovehiculul prin orice mijloace.</p>', 'da', 'nu', 'nu', ''),
(903, 'Ce autovehicul î&#351;i poate continua deplasarea în imaginea prezentat&#259;?', 'uploads/271.jpg', '<p id=\"v_a\"><b>a. </b> autocamionul;\r\n</p><p id=\"v_b\"><b>b. </b> autoturismul verde;\r\n</p><p id=\"v_c\"><b>c. </b> autoturismul ro&#351;u.</p>', 'nu', 'nu', 'da', ''),
(904, 'Autoturismul poate p&#259;trunde în intersec&#355;ia prezentat&#259;?', 'uploads/228.jpg', '<p id=\"v_a\"><b>a. </b> da;\r\n</p><p id=\"v_b\"><b>b. </b> numai în caz de urgen&#355;&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> nu, în intersec&#355;ie poate p&#259;trunde numai bicicleta.</p>', 'nu', 'nu', 'da', ''),
(905, 'A&#355;i executat corect oprirea în aceast&#259; situa&#355;ie?', 'uploads/522.jpg', '<p id=\"v_a\"><b>a. </b> da, pentru c&#259; autobuzul nu se afl&#259; în acest moment în sta&#355;ie;\r\n</p><p id=\"v_b\"><b>b. </b> nu, pentru c&#259; acest spa&#355;iu este rezervat doar opririi autobuzului;\r\n</p><p id=\"v_c\"><b>c. </b> da, dac&#259; semnaliza&#355;i acest lucru cu lumina galben&#259; intermitent&#259;.</p>', 'nu', 'da', 'nu', ''),
(906, 'Ce semnific&#259; indicatorul din imagine?', 'uploads/862.jpg', '<p id=\"v_a\"><b>a. </b> „Accesul interzis copiilor neînso&#355;i&#355;i“;\r\n</p><p id=\"v_b\"><b>b. </b> „Copii“;\r\n</p><p id=\"v_c\"><b>c. </b> „Trecere obligatorie pentru copii“.</p>', 'nu', 'da', 'nu', ''),
(907, 'Cum proceda&#355;i în aceast&#259; situa&#355;ie, dac&#259; circula&#355;ia este blocat&#259;?', 'uploads/884.jpg', '<p id=\"v_a\"><b>a. </b> nu p&#259;trunde&#355;i în intersec&#355;ie pân&#259; la deblocarea acesteia;\r\n</p><p id=\"v_b\"><b>b. </b> nu ave&#355;i obliga&#355;ia de a ceda trecerea;\r\n</p><p id=\"v_c\"><b>c. </b> ve&#355;i dep&#259;&#351;i coloana din fa&#355;&#259; prin stânga.</p>', 'da', 'nu', 'nu', ''),
(908, 'Cum trebuie s&#259; proceda&#355;i dac&#259;, circulând cu viteza maxim&#259; admis&#259; de lege, observa&#355;i c&#259; un alt autovehicul încearc&#259; s&#259; v&#259; dep&#259;&#351;easc&#259;?', '', '<p id=\"v_a\"><b>a. </b> s&#259; nu m&#259;ri&#355;i viteza &#351;i s&#259; circula&#355;i pe partea din dreapta a drumului, permi&#355;ând efectuarea dep&#259;&#351;irii;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; semnaliza&#355;i cu lumina intermitent&#259; c&#259; a&#355;i în&#355;eles inten&#355;ia celuilalt autovehicul de a v&#259; dep&#259;&#351;i;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; folosi&#355;i semnalele sonore &#351;i luminoase, pentru a-l aten&#355;iona pe conduc&#259;torul celuilalt autovehicul c&#259; dep&#259;&#351;e&#351;te limita vitezei.</p>', 'da', 'nu', 'nu', ''),
(909, 'Unde opri&#355;i în situa&#355;ia prezentat&#259;?', 'uploads/897.jpg', '<p id=\"v_a\"><b>a. </b> în locul cu vizibilitate maxim&#259;, f&#259;r&#259; a dep&#259;&#351;i indicatorul;\r\n</p><p id=\"v_b\"><b>b. </b> la cel pu&#355;in 5 m;\r\n</p><p id=\"v_c\"><b>c. </b> la cel pu&#355;in 10 m.</p>', 'da', 'nu', 'nu', ''),
(910, 'Semnalul poli&#355;istului indic&#259;:', 'uploads/940.jpg', '<p id=\"v_a\"><b>a. </b> circula&#355;ia pe direc&#355;ia înainte nu este posibil&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> oprire;\r\n</p><p id=\"v_c\"><b>c. </b> circula&#355;ia prin intersec&#355;ie se va face cu vitez&#259; redus&#259;.</p>', 'nu', 'da', 'nu', ''),
(911, 'Când este permis&#259; trecerea la nivel cu o cale ferat&#259; f&#259;r&#259; bariere, prev&#259;zut&#259; cu semnalizare luminoas&#259;, dac&#259; tocmai a trecut un tren?', '', '<p id=\"v_a\"><b>a. </b> dup&#259; ce s-a stins lumina ro&#351;ie intermitent&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> dup&#259; ce a trecut ultimul vagon;\r\n</p><p id=\"v_c\"><b>c. </b> atunci când pleac&#259; vehiculele aflate de cealalt&#259; parte a c&#259;ii ferate.</p>', 'da', 'nu', 'nu', ''),
(912, 'Permisul de conducere se restituie, dup&#259; expirarea perioadei de suspendare &#351;i promovarea testului de cunoa&#351;tere a regulilor de circula&#355;ie, dac&#259; a fost re&#355;inut pentru:', '', '<p id=\"v_a\"><b>a. </b> neacordarea priorit&#259;&#355;ii de trecere vehiculelor care au acest drept, dac&#259; prin aceasta s-a produs un accident de circula&#355;ie soldat cu pagube materiale;\r\n</p><p id=\"v_b\"><b>b. </b> nefolosirea în timpul mersului a centurii de siguran&#355;&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> utilizarea excesiv&#259; a &#351;terg&#259;toarelor de parbriz.</p>', 'da', 'nu', 'nu', ''),
(913, 'Inten&#355;iona&#355;i s&#259; vira&#355;i la dreapta. Cum proceda&#355;i?', 'uploads/855.jpg', '<p id=\"v_a\"><b>a. </b> trece&#355;i prin fa&#355;a pietonilor;\r\n</p><p id=\"v_b\"><b>b. </b> acorda&#355;i prioritate pietonilor;\r\n</p><p id=\"v_c\"><b>c. </b> acorda&#355;i prioritate numai bicicli&#351;tilor.</p>', 'nu', 'da', 'nu', ''),
(914, 'În aceast&#259; situa&#355;ie opri&#355;i:', 'uploads/941.jpg', '<p id=\"v_a\"><b>a. </b> la linia transversal&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> în dreptul semaforului;\r\n</p><p id=\"v_c\"><b>c. </b> pe trecerea pentru pietoni.</p>', 'da', 'nu', 'nu', ''),
(915, 'Cum proceda&#355;i în cazul în care observa&#355;i în timp util o persoan&#259; în vârst&#259; care traverseaz&#259; neregulamentar drumul public, în fa&#355;a dvs.?', '', '<p id=\"v_a\"><b>a. </b> semnaliza&#355;i cu claxonul &#351;i cu luminile, pentru a împiedica persoana respectiv&#259; s&#259; traverseze;\r\n</p><p id=\"v_b\"><b>b. </b> reduce&#355;i din timp viteza &#351;i lua&#355;i toate m&#259;surile de evitare a lovirii persoanei care traverseaz&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> legal nu ave&#355;i nicio obliga&#355;ie, iar în caz de accident, pietonul imprudent va r&#259;spunde, inclusiv pentru eventualele avarii produse.</p>', 'nu', 'da', 'nu', ''),
(916, 'Care dintre vehiculele din imagine va intra primul în intersec&#355;ie?', 'uploads/590.jpg', '<p id=\"v_a\"><b>a. </b> autoturismul;\r\n</p><p id=\"v_b\"><b>b. </b> motocicleta;\r\n</p><p id=\"v_c\"><b>c. </b> autocamionul.</p>', 'da', 'nu', 'nu', ''),
(917, 'Cum este bine s&#259; proceda&#355;i atunci când, circulând în spatele unui biciclist, constata&#355;i c&#259; acesta dore&#351;te s&#259; schimbe direc&#355;ia de mers?', '', '<p id=\"v_a\"><b>a. </b> semnaliza&#355;i sonor inten&#355;ia de a-l dep&#259;&#351;i;\r\n</p><p id=\"v_b\"><b>b. </b> îi asigura&#355;i condi&#355;ii pentru realizarea manevrei;\r\n</p><p id=\"v_c\"><b>c. </b> nu ave&#355;i nicio obliga&#355;ie, întrucât acesta este un vehicul foarte lent.</p>', 'nu', 'da', 'nu', ''),
(918, 'V&#259; este permis&#259; trecerea peste marcajul longitudinal din imagine?', 'uploads/146.jpg', '<p id=\"v_a\"><b>a. </b> da, deoarece linia discontinu&#259; este cea mai apropiat&#259; de vehiculul pe care îl conduce&#355;i;\r\n</p><p id=\"v_b\"><b>b. </b> nu, deoarece este un marcaj dublu;\r\n</p><p id=\"v_c\"><b>c. </b> nu, deoarece în acest caz dep&#259;&#351;irea este interzis&#259;.</p>', 'da', 'nu', 'nu', ''),
(919, 'Semnalul agen&#355;ilor c&#259;ilor ferate, executat cu un fanion ro&#351;u, afla&#355;i la trecerea la nivel cu o cale ferat&#259; industrial&#259; din ora&#351;e, v&#259; oblig&#259;:', '', '<p id=\"v_a\"><b>a. </b> s&#259; reduce&#355;i viteza, s&#259; v&#259; asigura&#355;i, iar dac&#259; trenul este la mai mult de 50 m de pasaj, s&#259; trece&#355;i cu aten&#355;ie;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; opri&#355;i;\r\n</p><p id=\"v_c\"><b>c. </b> nu ave&#355;i nicio obliga&#355;ie, deoarece agentul dirijeaz&#259; manevrele garniturii de tren.</p>', 'nu', 'da', 'nu', ''),
(920, 'Ce pericole pot ap&#259;rea prin nefolosirea centurii de siguran&#355;&#259;?', '', '<p id=\"v_a\"><b>a. </b> chiar &#351;i la o vitez&#259; de coliziune de aproximativ 20 km/h, exist&#259; riscul unei accident&#259;ri;\r\n</p><p id=\"v_b\"><b>b. </b> pericolele pot ap&#259;rea la o vitez&#259; de peste 60 km/h;\r\n</p><p id=\"v_c\"><b>c. </b> pericolul de accidentare este exclus.</p>', 'da', 'nu', 'nu', ''),
(921, 'Care dintre faptele de mai jos, comise într-un interval de un an de la restituirea permisului de conducere, atrag majorarea perioadei suspend&#259;rii dreptului de a conduce?', '', '<p id=\"v_a\"><b>a. </b> înc&#259;lcarea dispozi&#355;iilor legale cu privire la semnalizarea schimb&#259;rii direc&#355;iei de mers;\r\n</p><p id=\"v_b\"><b>b. </b> înc&#259;lcarea dispozi&#355;iilor legale cu privire la folosirea luminilor din dotare pe timp de noapte;\r\n</p><p id=\"v_c\"><b>c. </b> acumularea din nou a cel pu&#355;in 15 puncte de penalizare.</p>', 'nu', 'nu', 'da', ''),
(922, 'La ce distan&#355;&#259; de trecerea la nivel cu calea ferat&#259; sunt instalate panourile suplimentare de avertizare?', '', '<p id=\"v_a\"><b>a. </b> primul la 90 m, al doilea la 60 m &#351;i al treilea la 30 m;\r\n</p><p id=\"v_b\"><b>b. </b> primul la 200 m, al doilea la 150 m &#351;i al treilea la 100 m;\r\n</p><p id=\"v_c\"><b>c. </b> primul la 150 m, al doilea la 100 m &#351;i al treilea la 50 m.</p>', 'nu', 'nu', 'da', ''),
(923, 'Ce trebuie s&#259; aib&#259; în vedere prima interven&#355;ie, la fa&#355;a locului, pentru salvarea victimei?', '', '<p id=\"v_a\"><b>a. </b> sp&#259;larea r&#259;nilor vizibile;\r\n</p><p id=\"v_b\"><b>b. </b> degajarea c&#259;ilor respiratorii &#351;i asigurarea func&#355;iei respiratorii;\r\n</p><p id=\"v_c\"><b>c. </b> igienizarea &#351;i pansarea r&#259;nilor.</p>', 'nu', 'da', 'nu', ''),
(924, 'Pute&#355;i trece cu autovehiculul peste o linie continu&#259; dac&#259;:', '', '<p id=\"v_a\"><b>a. </b> linia continu&#259; este înso&#355;it&#259; de una discontinu&#259;, iar aceasta din urm&#259; este mai apropiat&#259; de sensul dvs. de deplasare;\r\n</p><p id=\"v_b\"><b>b. </b> aceasta este aplicat&#259; pe drumuri cu mai mult de o band&#259; pe sensul de mers;\r\n</p><p id=\"v_c\"><b>c. </b> aceasta separ&#259; sensurile de circula&#355;ie.</p>', 'da', 'nu', 'nu', ''),
(925, 'Care dintre autovehicule au dreptul s&#259; intre în intersec&#355;ie?', 'uploads/486.jpg', '<p id=\"v_a\"><b>a. </b> autocamionul &#351;i motocicleta;\r\n</p><p id=\"v_b\"><b>b. </b> autoturismul &#351;i autocamionul;\r\n</p><p id=\"v_c\"><b>c. </b> autoturismul &#351;i motocicleta.</p>', 'nu', 'nu', 'da', ''),
(926, 'Cum ve&#355;i proceda dac&#259; intersec&#355;ia care urmeaz&#259; este blocat&#259; din cauza aglomera&#355;iei?', 'uploads/523.jpg', '<p id=\"v_a\"><b>a. </b> opri&#355;i &#351;i a&#351;tepta&#355;i deblocarea intersec&#355;iei, dup&#259; care v&#259; continua&#355;i deplasarea numai la semnalul verde al semaforului;\r\n</p><p id=\"v_b\"><b>b. </b> v&#259; continua&#355;i deplasarea, deoarece semnalul verde v&#259; permite acest lucru;\r\n</p><p id=\"v_c\"><b>c. </b> claxona&#355;i &#351;i avansa&#355;i printre celelalte vehicule.</p>', 'da', 'nu', 'nu', ''),
(927, 'Care sunt vehiculele care au prioritate de trecere într-o intersec&#355;ie dirijat&#259;?', '', '<p id=\"v_a\"><b>a. </b> autocamioanele de mare tonaj;\r\n</p><p id=\"v_b\"><b>b. </b> vehiculele apar&#355;inând Protec&#355;iei Civile, semnalizate ca atare;\r\n</p><p id=\"v_c\"><b>c. </b> vehiculele apar&#355;inând Serviciului de Ambulan&#355;&#259;, când au în func&#355;iune semnalele speciale luminoase &#351;i sonore.</p>', 'nu', 'nu', 'da', ''),
(928, 'Când deasupra fiec&#259;rei benzi de circula&#355;ie se afl&#259; în func&#355;iune câte un semafor, conduc&#259;torul trebuie:', '', '<p id=\"v_a\"><b>a. </b> s&#259; respecte semnifica&#355;ia semnalului luminos al semaforului instalat deasupra benzii din stânga;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; respecte semnifica&#355;ia semnalului luminos al semaforului instalat pe trotuar;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; respecte semnifica&#355;ia semnalului luminos instalat deasupra benzii pe care se afl&#259;.</p>', 'nu', 'nu', 'da', ''),
(929, 'Indicatorul semnific&#259;:', 'uploads/266.jpg', '<p id=\"v_a\"><b>a. </b> accesul interzis vehiculelor cu o în&#259;l&#355;ime mai mare de 3,5 m;\r\n</p><p id=\"v_b\"><b>b. </b> accesul permis numai vehiculelor cu o l&#259;&#355;ime mai mic&#259; de 3,5 m;\r\n</p><p id=\"v_c\"><b>c. </b> distan&#355;a minim&#259; obligatorie dintre dou&#259; vehicule.</p>', 'da', 'nu', 'nu', ''),
(930, 'Care este ordinea de trecere prin intersec&#355;ie a celor trei vehicule?', 'uploads/935.jpg', '<p id=\"v_a\"><b>a. </b> autoturismul ro&#351;u, motocicleta, autoturismul albastru;\r\n</p><p id=\"v_b\"><b>b. </b> motocicleta, autoturismul albastru, autoturismul ro&#351;u;\r\n</p><p id=\"v_c\"><b>c. </b> autoturismul albastru, motocicleta, autoturismul ro&#351;u.</p>', 'da', 'nu', 'nu', ''),
(931, 'Semnalul poli&#355;istului indic&#259;:', 'uploads/628.jpg', '<p id=\"v_a\"><b>a. </b> reduce&#355;i viteza;\r\n</p><p id=\"v_b\"><b>b. </b> m&#259;ri&#355;i viteza;\r\n</p><p id=\"v_c\"><b>c. </b> merge&#355;i cu pruden&#355;&#259;.</p>', 'nu', 'da', 'nu', ''),
(932, 'Cum procedeaz&#259; conduc&#259;torul de autovehicul la întâlnirea indicatorului „Oprire“?', '', '<p id=\"v_a\"><b>a. </b> opre&#351;te unde are o bun&#259; vizibilitate pentru a se asigura &#351;i î&#351;i continu&#259; deplasarea, dac&#259; pe drumul transversal nu circul&#259; alte vehicule;\r\n</p><p id=\"v_b\"><b>b. </b> reduce viteza, se asigur&#259;, dup&#259; care intr&#259; în intersec&#355;ie;\r\n</p><p id=\"v_c\"><b>c. </b> reduce viteza, dup&#259; care traverseaz&#259; intersec&#355;ia.</p>', 'da', 'nu', 'nu', ''),
(933, 'Ave&#355;i obliga&#355;ia de a respecta semnifica&#355;ia unui indicator amplasat pe partea stâng&#259; a drumului?', 'uploads/838.jpg', '<p id=\"v_a\"><b>a. </b> da, dar numai dac&#259; pe sensul dumneavoastr&#259; de mers se execut&#259; lucr&#259;ri;\r\n</p><p id=\"v_b\"><b>b. </b> da, pentru c&#259; ac&#355;ioneaz&#259; pe sensul dumneavoastr&#259; de mers;\r\n</p><p id=\"v_c\"><b>c. </b> nu, pentru c&#259; se adreseaz&#259; circula&#355;iei din sens invers.</p>', 'nu', 'da', 'nu', ''),
(934, 'Ce trebuie s&#259; face&#355;i pentru a evita un accident de circula&#355;ie?', '', '<p id=\"v_a\"><b>a. </b> s&#259; nu conduce&#355;i autoturismul decât atunci când probleme grave impun acest lucru;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; v&#259; asigura&#355;i întotdeauna un partener care s&#259; cunoasc&#259; bine traseul parcurs;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; anticipa&#355;i &#351;i s&#259; contracara&#355;i la timp manevrele imprudente ale partenerilor de drum &#351;i s&#259; respecta&#355;i permanent regulile de circula&#355;ie.</p>', 'nu', 'nu', 'da', ''),
(935, 'Ce obliga&#355;ii ave&#355;i la trecerea la nivel cu calea ferat&#259; curent&#259; prev&#259;zut&#259; cu bariere, dac&#259; acestea sunt ridicate?', '', '<p id=\"v_a\"><b>a. </b> v&#259; asigura&#355;i c&#259; nu sunte&#355;i dep&#259;&#351;it de alt autovehicul &#351;i v&#259; angaja&#355;i în traversarea c&#259;ii ferate;\r\n</p><p id=\"v_b\"><b>b. </b> ziua da&#355;i semnale sonore, iar noaptea aprinde&#355;i faza de drum intermitent&#259;, apoi traversa&#355;i;\r\n</p><p id=\"v_c\"><b>c. </b> reduce&#355;i viteza, v&#259; asigura&#355;i c&#259; din partea stâng&#259; sau din partea dreapt&#259; nu se apropie un vehicul feroviar, apoi v&#259; angaja&#355;i în traversare.</p>', 'nu', 'nu', 'da', ''),
(936, 'Când sunte&#355;i obligat s&#259; opri&#355;i autovehiculul?', '', '<p id=\"v_a\"><b>a. </b> la semnalele îndrum&#259;torilor de circula&#355;ie ai Ministerului Ap&#259;r&#259;rii;\r\n</p><p id=\"v_b\"><b>b. </b> la semnalele conduc&#259;torilor de vehicule care vireaz&#259; la dreapta;\r\n</p><p id=\"v_c\"><b>c. </b> la semnalele conduc&#259;torilor vehiculelor care circul&#259; din sens invers.</p>', 'da', 'nu', 'nu', '');
INSERT INTO `intrebari_c` (`id`, `cerinta`, `imagine`, `variante`, `rasp_a`, `rasp_b`, `rasp_c`, `eticheta`) VALUES
(1, 'Transportul persoanelor accidentate având hemoragii externe se va face:', '', '<p id=\"v_a\"><b>a. </b> imediat dup&#259; sosirea unui mijloc de transport;\r\n</p><p id=\"v_b\"><b>b. </b> imediat dup&#259; oprirea hemoragiei;\r\n</p><p id=\"v_c\"><b>c. </b> imediat dup&#259; ce s-a acordat asisten&#355;&#259; medical&#259; de o c&#259;tre persoan&#259; calificat&#259;.</p>', 'nu', 'da', 'da', ''),
(2, 'Cum este definit transportul rutier?', '', '<p id=\"v_a\"><b>a. </b> orice deplasare efectuat&#259;, în totalitate sau par&#355;ial, în gol sau cu sarcin&#259;, pe drumurile deschise circula&#355;iei publice, de un vehicul utilizat pentru transportul de persoane sau de marf&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> orice deplasare efectuat&#259; pe drumurile jude&#355;ene, na&#355;ionale sau na&#355;ional europene;\r\n</p><p id=\"v_c\"><b>c. </b> transportul efectuat cu autovehicule ale c&#259;ror caracteristici sunt specifice atât în ceea ce prive&#351;te forma caroseriei, cât &#351;i puterea motorului.</p>', 'da', 'nu', 'da', ''),
(3, 'Ce reprezint&#259; „perioada de odihn&#259; zilnic&#259; normal&#259;“?', '', '<p id=\"v_a\"><b>a. </b> intervalul neîntrerupt de cel pu&#355;in 11 ore de odihn&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> perioada de odihn&#259; realizat&#259; pe timpul nop&#355;ii, de cel pu&#355;in 12 ore;\r\n</p><p id=\"v_c\"><b>c. </b> odihna realizat&#259; în dou&#259; intervale de timp, primul de cel pu&#355;in 3 ore, iar al doilea de cel pu&#355;in 9 ore.</p>', 'da', 'nu', 'da', ''),
(4, 'Arborele cu came antreneaz&#259;:', '', '<p id=\"v_a\"><b>a. </b> pompa de ulei;\r\n</p><p id=\"v_b\"><b>b. </b> ruptorul-distribuitor;\r\n</p><p id=\"v_c\"><b>c. </b> ventilatorul radiatorului.</p>', 'da', 'da', 'nu', ''),
(5, 'În ce condi&#355;ii v&#259; este interzis s&#259; circula&#355;i cu un autovehicul având masa sau gabaritul dep&#259;&#351;it?', '', '<p id=\"v_a\"><b>a. </b> când vizibilitatea este redus&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> când partea carosabil&#259; este acoperit&#259; cu polei, ghea&#355;&#259; sau z&#259;pad&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> când nu de&#355;ine&#355;i certificat ADR corespunzãtor.</p>', 'da', 'da', 'nu', ''),
(6, 'Cum trebuie s&#259; proceda&#355;i când circula&#355;i pe timp de noapte &#351;i din sens opus se apropie un alt autovehicul?', '', '<p id=\"v_a\"><b>a. </b> s&#259; reduce&#355;i viteza;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; folosi&#355;i luminile de întâlnire de la cel pu&#355;in 200 m;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; v&#259; angaja&#355;i cât mai aproape de marginea din dreapta a drumului.</p>', 'da', 'da', 'nu', ''),
(7, 'În care dintre situa&#355;ii consumul de carburant al unui motor cre&#351;te?', '', '<p id=\"v_a\"><b>a. </b> atunci când motorul nu atinge temperatura de func&#355;ionare;\r\n</p><p id=\"v_b\"><b>b. </b> atunci când fumul de e&#351;apament este de culoare neagr&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> atunci când motorul func&#355;ioneaz&#259; cu întreruperi.</p>', 'da', 'da', 'da', ''),
(8, 'Când trebuie scoas&#259; din vehicul diagrama tahograf?', '', '<p id=\"v_a\"><b>a. </b> ori de câte ori este nevoie;\r\n</p><p id=\"v_b\"><b>b. </b> nu înainte de sfâr&#351;itul perioadei zilnice de lucru;\r\n</p><p id=\"v_c\"><b>c. </b> pe orice perioad&#259; a zilei, dac&#259; acest lucru a fost autorizat (control rutier, schimbarea vehiculului, defectarea aparatului).</p>', 'nu', 'da', 'da', ''),
(9, 'Sl&#259;birea curelei de antrenare a ventilatorului &#351;i a pompei de ap&#259; determin&#259;:', '', '<p id=\"v_a\"><b>a. </b> mic&#351;orarea presiunii uleiului în carter;\r\n</p><p id=\"v_b\"><b>b. </b> deteriorarea curelei prin patinare;\r\n</p><p id=\"v_c\"><b>c. </b> supraînc&#259;lzirea motorului.</p>', 'nu', 'da', 'da', ''),
(10, 'În care dintre situa&#355;iile de mai jos este interzis&#259; oprirea autocamioanelor pe drumurile publice?', '', '<p id=\"v_a\"><b>a. </b> pe partea carosabil&#259; a drumurilor na&#355;ionale;\r\n</p><p id=\"v_b\"><b>b. </b> la o distan&#355;&#259; mai mic&#259; de 25 m înainte &#351;i dup&#259; indicatorul sta&#355;iei pentru mijloacele de transport public de persoane;\r\n</p><p id=\"v_c\"><b>c. </b> la mai pu&#355;in de 50 m de cel mai apropiat col&#355; al intersec&#355;iei.</p>', 'nu', 'da', 'nu', ''),
(11, 'Care sunt datele pe care le înregistreaz&#259; &#351;i/sau stocheaz&#259; în mod automat sau semiautomat tahograful?', '', '<p id=\"v_a\"><b>a. </b> distan&#355;a parcurs&#259; de vehicul &#351;i viteza de deplasare a acestuia;\r\n</p><p id=\"v_b\"><b>b. </b> durata orelor de conducere, pauzele &#351;i perioadele zilnice de odihn&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> timpii disponibili, precum &#351;i al&#355;i timpi de munc&#259;.</p>', 'da', 'da', 'da', ''),
(12, 'Transportarea victimelor la spital se poate realiza cu:', '', '<p id=\"v_a\"><b>a. </b> o ambulan&#355;&#259; special dotat&#259; în acest sens;\r\n</p><p id=\"v_b\"><b>b. </b> o ambulan&#355;&#259; înso&#355;it&#259; de un medic;\r\n</p><p id=\"v_c\"><b>c. </b> vehiculul care a provocat accidentul, dac&#259; starea victimei permite acest lucru.</p>', 'da', 'da', 'da', ''),
(13, 'În ce situa&#355;ii se reduce viteza în trafic?', '', '<p id=\"v_a\"><b>a. </b> în toate cazurile prev&#259;zute în legisla&#355;ia rutier&#259; pentru aplicarea legii privind circula&#355;ia pe drumurile publice;\r\n</p><p id=\"v_b\"><b>b. </b> preventiv, în toate situa&#355;iile în care se prevede o stare de pericol, care poate fi evitat&#259; rulând cu o vitez&#259; inferioar&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> atunci când sunte&#355;i obosit, indispus sau suferi&#355;i de o afec&#355;iune care v&#259; diminueaz&#259; capacitatea de a conduce.</p>', 'da', 'da', 'nu', ''),
(14, 'Avantajele func&#355;ion&#259;rii motorului cu gaz petrolier lichefiat (GPL) sunt:', '', '<p id=\"v_a\"><b>a. </b> arderea amestecului carburant este mai complet&#259;, reducându-se astfel poluarea;\r\n</p><p id=\"v_b\"><b>b. </b> lubrifiantul de pe cilindri nu se spal&#259;, prelungindu-se astfel durata de folosire a uleiului;\r\n</p><p id=\"v_c\"><b>c. </b> puterea motorului este mai mare cu aproximativ 8%.</p>', 'da', 'da', 'nu', ''),
(15, 'Indicatorii de apreciere a st&#259;rii sistemului de ungere sunt:', '', '<p id=\"v_a\"><b>a. </b> consumul de ulei;\r\n</p><p id=\"v_b\"><b>b. </b> u&#351;urin&#355;a cu care se deplaseaz&#259; vehiculul;\r\n</p><p id=\"v_c\"><b>c. </b> presiunea uleiului.</p>', 'da', 'nu', 'da', ''),
(16, 'Când se folosesc luminile de avarie?', '', '<p id=\"v_a\"><b>a. </b> când autocamionul a r&#259;mas în pan&#259; sau a fost avariat, fiind imobilizat pe carosabil;\r\n</p><p id=\"v_b\"><b>b. </b> când autocamionul se deplaseaz&#259; foarte lent &#351;i constituie un pericol pentru ceilal&#355;i participan&#355;i la trafic;\r\n</p><p id=\"v_c\"><b>c. </b> când se blocheaz&#259; circula&#355;ia pe sensul de mers.</p>', 'da', 'da', 'da', ''),
(17, 'Care dintre urm&#259;toarele cauze determin&#259; desc&#259;rcarea bateriei de acumulatori?', '', '<p id=\"v_a\"><b>a. </b> defectarea releului regulator;\r\n</p><p id=\"v_b\"><b>b. </b> arderera siguran&#355;elor farurilor;\r\n</p><p id=\"v_c\"><b>c. </b> folosirea excesiv&#259; a demarorului.</p>', 'da', 'nu', 'da', ''),
(18, 'Cum trebuie s&#259; fie atelele pentru imobilizarea unei fracturi?', '', '<p id=\"v_a\"><b>a. </b> suficient de lungi pentru a acoperi în întregime atât zona de deasupra, cât &#351;i cea de sub fractur&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> rigide;\r\n</p><p id=\"v_c\"><b>c. </b> u&#351;oare.</p>', 'da', 'da', 'da', ''),
(19, 'Care dintre urm&#259;torii factori influen&#355;eaz&#259; spa&#355;iul de frânare a autocamionului?', '', '<p id=\"v_a\"><b>a. </b> greutatea autovehiculului, uzura pneurilor &#351;i timpul de reac&#355;ie al conduc&#259;torului auto;\r\n</p><p id=\"v_b\"><b>b. </b> viteza de deplasare, aderen&#355;a pneurilor la sol &#351;i timpul de reac&#355;ie al conduc&#259;torului auto;\r\n</p><p id=\"v_c\"><b>c. </b> diametrul ro&#355;ilor, viteza autovehiculului &#351;i num&#259;rul de ro&#355;i.</p>', 'da', 'da', 'nu', ''),
(20, 'Ce categorii de vehicule au obliga&#355;ia de a folosi luminile de întâlnire pe timp de zi?', '', '<p id=\"v_a\"><b>a. </b> motocicletele &#351;i mopedele;\r\n</p><p id=\"v_b\"><b>b. </b> autovehiculele care înso&#355;esc coloane militare sau transport&#259; grupuri de persoane;\r\n</p><p id=\"v_c\"><b>c. </b> autovehiculele care tracteaz&#259; alte vehicule sau care transport&#259; produse periculoase.</p>', 'da', 'da', 'da', ''),
(21, 'Ce autovehicule nu au prioritate de trecere?', '', '<p id=\"v_a\"><b>a. </b> autovehiculele care întâlnesc indicatorul „Intersec&#355;ie cu un drum f&#259;r&#259; prioritate”;\r\n</p><p id=\"v_b\"><b>b. </b> autovehiculele care vireaz&#259; spre dreapta;\r\n</p><p id=\"v_c\"><b>c. </b> autovehiculele care vireaz&#259; spre stânga, fa&#355;&#259; de cele care circul&#259; din sens opus, în aliniament.</p>', 'nu', 'nu', 'da', ''),
(22, 'Care dintre ac&#355;iunile de mai jos sunt obligatorii în timpul ce efectua&#355;i o dep&#259;&#351;ire:', '', '<p id=\"v_a\"><b>a. </b> s&#259; semnaliza&#355;i &#351;i s&#259; v&#259; asigura&#355;i c&#259; pute&#355;i reveni în rândul ini&#355;ial, f&#259;r&#259; s&#259; pune&#355;i în pericol vehiculul dep&#259;&#351;it;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; p&#259;stra&#355;i în timpul dep&#259;&#351;irii o distan&#355;&#259; lateral&#259; suficient&#259; fa&#355;&#259; de vehiculul dep&#259;&#351;it;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; reintra&#355;i cât mai repede pe partea dreapt&#259;.</p>', 'da', 'da', 'nu', ''),
(23, 'Se consider&#259; contraven&#355;ie:', '', '<p id=\"v_a\"><b>a. </b> nerespectarea prevederilor privind utilizarea diagramelor tahograf sau a cartelelor tahografice atunci când vehiculul este condus de un echipaj format din mai mul&#355;i conduc&#259;tori auto;\r\n</p><p id=\"v_b\"><b>b. </b> nerespectarea obliga&#355;iei privind neintroducerea datelor în tahograf;\r\n</p><p id=\"v_c\"><b>c. </b> nerespectarea prevederilor privind reglarea ceasului tahografului &#351;i utilizarea comutatorului.</p>', 'da', 'da', 'da', ''),
(24, 'Care dintre urm&#259;toarele cauze determin&#259; sulfatarea pl&#259;cilor bateriei de acumulatori?', '', '<p id=\"v_a\"><b>a. </b> scurtcircuitarea acumulatorului în interiorul elementelor;\r\n</p><p id=\"v_b\"><b>b. </b> prezen&#355;a impurit&#259;&#355;ilor în electrolit;\r\n</p><p id=\"v_c\"><b>c. </b> nivelul sc&#259;zut al electrolitului sub partea superioar&#259; a pl&#259;cilor.</p>', 'da', 'nu', 'nu', ''),
(25, 'Care dintre urm&#259;torii factori reprezint&#259; baza conduitei preventive?', '', '<p id=\"v_a\"><b>a. </b> cunoa&#351;terea normelor de circula&#355;ie &#351;i a regulilor de conducere a autocamionului;\r\n</p><p id=\"v_b\"><b>b. </b> capacitatea de a fi politicos &#351;i disciplinat în traficul rutier;\r\n</p><p id=\"v_c\"><b>c. </b> încadrarea în baremurile legale privind s&#259;n&#259;tatea.</p>', 'da', 'da', 'nu', ''),
(26, 'Într-o intersec&#355;ie sunte&#355;i obligat:', '', '<p id=\"v_a\"><b>a. </b> s&#259; acorda&#355;i prioritate de trecere atunci când pietonii angaja&#355;i regulamentar în traversare se afl&#259; pe sensul dvs. de mers;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; circula&#355;i cu o vitez&#259; care s&#259; nu dep&#259;&#351;easc&#259; 30 km/h în localit&#259;&#355;i sau 50 km/h în afara acestora, dac&#259; drumul are cel mult o band&#259; pe sens, iar pietonii afla&#355;i pe trotuar în apropierea p&#259;r&#355;ii carosabile inten&#355;ioneaz&#259; s&#259; traverseze;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; circula&#355;i cât mai aproape de bordura din partea dreapt&#259; a trotuarului.</p>', 'da', 'da', 'nu', ''),
(27, 'Ce autovehicule au prioritate de trecere pe un drum îngust, în ramp&#259;?', '', '<p id=\"v_a\"><b>a. </b> autovehiculele cu gabarit dep&#259;&#351;it, fa&#355;&#259; de celelalte vehicule, indiferent dac&#259; urc&#259; sau coboar&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> autovehiculele care urc&#259;, fa&#355;&#259; de cele care coboar&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> autovehiculele care întâlnesc indicatorul „Prioritate pentru circula&#355;ia din sens invers“.</p>', 'da', 'nu', 'nu', ''),
(28, 'Atunci când transporta&#355;i m&#259;rfuri sau produse periculoase, v&#259; este interzis:', '', '<p id=\"v_a\"><b>a. </b> s&#259; dep&#259;&#351;i&#355;i alte vehicule;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; remorca&#355;i un vehicul r&#259;mas în pan&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; provoca&#355;i în mers &#351;ocuri autovehiculului.</p>', 'nu', 'da', 'da', ''),
(29, 'Ce func&#355;ii poate avea un bandaj de tifon aplicat pe o ran&#259; deschis&#259;?', '', '<p id=\"v_a\"><b>a. </b> protejeaz&#259; împotriva infect&#259;rii r&#259;nii;\r\n</p><p id=\"v_b\"><b>b. </b> opre&#351;te hemoragia;\r\n</p><p id=\"v_c\"><b>c. </b> scade tensiunea arterial&#259;.</p>', 'da', 'da', 'nu', ''),
(30, 'Care dintre indicatoarele al&#259;turate interzice accesul autocamioanelor a c&#259;ror în&#259;l&#355;ime este mai mare de 3,5 m?', 'uploads_c/1927.jpg', '<p id=\"v_a\"><b>a. </b> indicatorul 1;\r\n</p><p id=\"v_b\"><b>b. </b> indicatorul 2;\r\n</p><p id=\"v_c\"><b>c. </b> indicatorul 3.</p>', 'da', 'da', 'da', ''),
(31, 'Oprirea voluntar&#259; a unui autocamion este interzis&#259;:', '', '<p id=\"v_a\"><b>a. </b> pe drumurile cu un singur sens de circula&#355;ie;\r\n</p><p id=\"v_b\"><b>b. </b> pe drumurile cu o l&#259;&#355;ime mai mic&#259; de 6 m;\r\n</p><p id=\"v_c\"><b>c. </b> în locurile unde s-ar împiedica observarea unui indicator sau a unui semnal luminos.</p>', 'nu', 'nu', 'da', ''),
(32, 'Care dintre urm&#259;toarele piese fac parte din mecanismul de distribu&#355;ie?', '', '<p id=\"v_a\"><b>a. </b> carburatorul;\r\n</p><p id=\"v_b\"><b>b. </b> arborele cotit;\r\n</p><p id=\"v_c\"><b>c. </b> arborele cu came.</p>', 'nu', 'nu', 'da', ''),
(33, 'Oprirea este interzis&#259;:', '', '<p id=\"v_a\"><b>a. </b> la mai pu&#355;in de 25 m de col&#355;ul unei intersec&#355;ii;\r\n</p><p id=\"v_b\"><b>b. </b> în zona de ac&#355;iune a indicatorului „Sta&#355;ionarea interzis&#259;“;\r\n</p><p id=\"v_c\"><b>c. </b> în curbe &#351;i pe sectoarele de drum cu vizibilitate redus&#259; sub 50 m.</p>', 'da', 'nu', 'da', ''),
(34, 'Griparea pistonului unui element al pompei de injec&#355;ie poate conduce la:', '', '<p id=\"v_a\"><b>a. </b> spargerea carcasei pompei de injec&#355;ie;\r\n</p><p id=\"v_b\"><b>b. </b> supraturarea motorului;\r\n</p><p id=\"v_c\"><b>c. </b> fisurarea arborelui cu came al pompei de injec&#355;ie.</p>', 'nu', 'da', 'nu', ''),
(35, 'Ce trebuie s&#259; ave&#355;i în vedere la scoaterea persoanelor accidentate din autovehicul?', '', '<p id=\"v_a\"><b>a. </b> ca opera&#355;iunea s&#259; se desf&#259;&#351;oare rapid &#351;i f&#259;r&#259; s&#259; provoace alte traumatisme;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; nu se agraveze leziunile;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; nu se îndep&#259;rteze indiciile care ar putea ajuta la explicarea modului în care s-a produs accidentul.</p>', 'nu', 'da', 'nu', ''),
(36, 'Se consider&#259; contraven&#355;ie:', '', '<p id=\"v_a\"><b>a. </b> completarea diagramei tahograf cu pixul sau cu stiloul;\r\n</p><p id=\"v_b\"><b>b. </b> necompletarea pe diagrama tahograf a datelor prev&#259;zute de legisla&#355;ia în vigoare;\r\n</p><p id=\"v_c\"><b>c. </b> omisiunea de a introduce în tahograful digital datele prev&#259;zute de legisla&#355;ia în vigoare.</p>', 'nu', 'da', 'da', ''),
(37, 'În care dintre situa&#355;iile de mai jos este interzis&#259; întoarcerea autocamionului?', '', '<p id=\"v_a\"><b>a. </b> în locul unde este instalat indicatorul „Întoarcerea interzis&#259;“;\r\n</p><p id=\"v_b\"><b>b. </b> în dreptul unei institu&#355;ii;\r\n</p><p id=\"v_c\"><b>c. </b> în locurile unde oprirea este interzis&#259;, cu excep&#355;ia intersec&#355;iilor.</p>', 'da', 'nu', 'da', ''),
(38, 'Oprirea este interzis&#259;:', '', '<p id=\"v_a\"><b>a. </b> în zona de ac&#355;iune a indicatorului „Drum îngustat“;\r\n</p><p id=\"v_b\"><b>b. </b> în zona de ac&#355;iune a indicatoarelor care interzic dep&#259;&#351;irea;\r\n</p><p id=\"v_c\"><b>c. </b> în dreptul altui vehicul sta&#355;ionat, dac&#259; prin aceasta se stânjene&#351;te circula&#355;ia a dou&#259; vehicule venind din sensuri opuse.</p>', 'da', 'da', 'da', ''),
(39, 'Ce func&#355;ie are mecanismul de distribu&#355;ie?', '', '<p id=\"v_a\"><b>a. </b> faciliteaz&#259; formarea curentului electric de înalt&#259; tensiune &#351;i distribuirea acestuia la bujii;\r\n</p><p id=\"v_b\"><b>b. </b> faciliteaz&#259; uniformizarea func&#355;ion&#259;rii motorului prin acumularea surplusului de energie debitat în timpul motor &#351;i folosirea acestuia în ceilal&#355;i timpi;\r\n</p><p id=\"v_c\"><b>c. </b> asigur&#259; p&#259;trunderea amestecului carburant în cilindri &#351;i evacuarea gazelor arse.</p>', 'nu', 'nu', 'da', ''),
(40, 'Comportamentul ecologic în conducerea unui autovehiculul presupune:', '', '<p id=\"v_a\"><b>a. </b> planificarea traseului pentru a economisi timp &#351;i combustibil;\r\n</p><p id=\"v_b\"><b>b. </b> evitarea zonelor cu circula&#355;ie dens&#259;, cu lucr&#259;ri pe carosabil sau cu teren accidentat;\r\n</p><p id=\"v_c\"><b>c. </b> vitez&#259; &#351;i accelera&#355;ie mai mari, pentru a scurta timpul de deplasare.</p>', 'da', 'da', 'nu', ''),
(41, 'Conduc&#259;torul unui autocamion este obligat:', '', '<p id=\"v_a\"><b>a. </b> s&#259; permit&#259; controlul st&#259;rii tehnice a autocamionului, precum &#351;i al bunurilor transportate, în condi&#355;iile legii;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; se prezinte la verificarea medical&#259; periodic&#259;, potrivit legii;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; transporte poli&#355;i&#351;ti, la cererea acestora.</p>', 'da', 'da', 'nu', ''),
(42, 'Conduita ecologic&#259; în conducerea unui autovehicul înseamn&#259;:', '', '<p id=\"v_a\"><b>a. </b> s&#259; nu polua&#355;i fonic mediul înconjur&#259;tor, folosind sistemul de sonorizare la putere maxim&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; între&#355;ine&#355;i în permanen&#355;&#259; autovehiculul curat, prin sp&#259;lare &#351;i degresare zilnic&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; fi&#355;i calm &#351;i s&#259; anticipa&#355;i situa&#355;iile astfel încât s&#259; evita&#355;i accelerarea &#351;i frânarea brusc&#259;.</p>', 'nu', 'nu', 'da', ''),
(43, 'In ce conditii se poate face cuplarea unei remorci la un vehicul?', '', '<p id=\"v_a\"><b>a. </b> daca dimensiunile ansamblului de vehicule nu depasesc limitele prevazute de lege;\r\n</p><p id=\"v_b\"><b>b. </b> daca greutatea ansamblului nu depaseste 3,5 tone;\r\n</p><p id=\"v_c\"><b>c. </b> daca elementele de cuplare ale echipamentelor de franare, de iluminare si de semnalizare luminoasa sunt compatibile;</p>', 'da', 'nu', 'da', ''),
(44, 'În care dintre situa&#355;iile prezentate mai jos nu este permis&#259; oprirea voluntar&#259; a autocamioanelor?', '', '<p id=\"v_a\"><b>a. </b> la o distan&#355;&#259; mai mic&#259; de 50 m de trecerea la nivel cu calea ferat&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> pe pistele obligatorii pentru pietoni;\r\n</p><p id=\"v_c\"><b>c. </b> pe platforma c&#259;ii ferate sau de tramvai, dac&#259; prin aceasta este stânjenit&#259; circula&#355;ia vehiculelor pe &#351;ine.</p>', 'da', 'da', 'da', ''),
(45, 'Care dintre indicatoarele al&#259;turate interzice accesul autocamioanelor a c&#259;ror l&#259;&#355;ime este mai mare de 2,30 m?', 'uploads_c/1912.jpg', '<p id=\"v_a\"><b>a. </b> indicatorul 1;\r\n</p><p id=\"v_b\"><b>b. </b> indicatorul 2;\r\n</p><p id=\"v_c\"><b>c. </b> indicatorul 3.</p>', 'da', 'da', 'da', ''),
(46, 'În ce situa&#355;ie nu v&#259; este permis&#259; oprirea unui camion?', '', '<p id=\"v_a\"><b>a. </b> în curbe &#351;i pe sectoarele de drum cu vizibilitate redus&#259; sub 50 m;\r\n</p><p id=\"v_b\"><b>b. </b> pe drumurile publice unde circul&#259; troleibuze &#351;i tramvaie;\r\n</p><p id=\"v_c\"><b>c. </b> la mai pu&#355;in de 50 m de col&#355;ul unei intersec&#355;ii.</p>', 'da', 'nu', 'nu', ''),
(47, 'Oprirea voluntar&#259; a autocamioanelor este interzis&#259;:', '', '<p id=\"v_a\"><b>a. </b> pe platforma c&#259;ii ferate sau de tramvai, dac&#259; prin aceasta este stânjenit&#259; circula&#355;ia vehiculelor pe &#351;ine;\r\n</p><p id=\"v_b\"><b>b. </b> la mai pu&#355;in de 50 m de trecerile pentru pietoni;\r\n</p><p id=\"v_c\"><b>c. </b> la o distan&#355;&#259; mai mic&#259; de 150 m de trecerea la nivel cu calea ferat&#259;.</p>', 'da', 'nu', 'nu', ''),
(48, 'Conduc&#259;torul unui autocamion este obligat:', '', '<p id=\"v_a\"><b>a. </b> s&#259; nu conduc&#259; dup&#259; ce a consumat medicamente care sunt contraindicate;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; se prezinte la termen, la unitatea de poli&#355;ie care l-a citat, pentru solu&#355;ionarea oric&#259;rei probleme legate de calitatea sa de conduc&#259;tor;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; anun&#355;e imediat cea mai apropiat&#259; unitate de poli&#355;ie, dac&#259; s-a îmboln&#259;vit.</p>', 'nu', 'da', 'nu', ''),
(49, 'Se consider&#259; contraven&#355;ie:', '', '<p id=\"v_a\"><b>a. </b> conducerea unui vehicul dotat cu tahograf fara a utiliza diagrame tahograf;\n</p><p id=\"v_b\"><b>b. </b> utilizarea tahografului fara avizul politiei rutiere;\n</p><p id=\"v_c\"><b>c. </b> conducerea unui vehicul dotat cu tahograf digital fara a utiliza cartela tahografica.</p>', 'da', 'nu', 'da', ''),
(50, 'Ce obliga&#355;ii au conduc&#259;torii auto angaja&#355;i de c&#259;tre operatorii de transport rutier?', '', '<p id=\"v_a\"><b>a. </b> s&#259; respecte regulile privind înc&#259;rcarea, repartizarea înc&#259;rc&#259;turii, asigurarea &#351;i fixarea m&#259;rfurilor transportate;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; acopere înc&#259;rc&#259;tura cu prelate etan&#351;e &#351;i impermeabile;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; repartizeze înc&#259;rc&#259;tura &#351;i s&#259; fixeze marfa sub supravegherea unei persoane specializate.</p>', 'da', 'nu', 'nu', ''),
(51, 'În cazul motoarelor cu aprindere prin compresie, emisia fumului de culoare neagr&#259; este o urmare a:', '', '<p id=\"v_a\"><b>a. </b> regl&#259;rii incorecte a pompei de injec&#355;ie;\r\n</p><p id=\"v_b\"><b>b. </b> uzurii segmen&#355;ilor;\r\n</p><p id=\"v_c\"><b>c. </b> jocului mic dintre supape &#351;i culbutori.</p>', 'da', 'nu', 'nu', ''),
(52, 'Conduc&#259;torii auto angaja&#355;i ai operatorilor de transport rutier, care efectueaz&#259; opera&#355;iuni de transport, au obliga&#355;ia:', '', '<p id=\"v_a\"><b>a. </b> de a purta echipament de protec&#355;ie pentru prevenirea accidentelor de munc&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> de a nu circula în trafic f&#259;r&#259; a avea asupra lor fi&#351;a de protec&#355;ie a muncii;\r\n</p><p id=\"v_c\"><b>c. </b> de a respecta regulile privind prevenirea accidentelor de munc&#259; în circula&#355;ia rutier&#259;.</p>', 'nu', 'nu', 'da', ''),
(53, 'Piesele cele mai solicitate în timpul func&#355;ion&#259;rii transmisiei sunt:', '', '<p id=\"v_a\"><b>a. </b> planetarele;\r\n</p><p id=\"v_b\"><b>b. </b> diferen&#355;ialul;\r\n</p><p id=\"v_c\"><b>c. </b> ambreiajul.</p>', 'nu', 'nu', 'da', ''),
(54, 'Care sunt documentele înso&#355;itoare, în cazul unui vehicul de trasport rutier public de m&#259;rfuri în trafic na&#355;ional, de&#355;inut cu contract de închiriere sau de leasing, în afara documentului de transport?', '', '<p id=\"v_a\"><b>a. </b> foaia de parcurs vizat&#259; de c&#259;tre societatea de leasing;\r\n</p><p id=\"v_b\"><b>b. </b> actul notarial de încredin&#355;are a autovehiculului pentru fiecare conduc&#259;tor în parte;\r\n</p><p id=\"v_c\"><b>c. </b> contractul de închiriere, în original sau în copie conform&#259; cu originalul.</p>', 'nu', 'nu', 'da', ''),
(55, 'În care dintre situa&#355;ii este intezis s&#259; circula&#355;i pe drumurile publice?', '', '<p id=\"v_a\"><b>a. </b> atunci când certificatul de înmatriculare este deteriorat;\r\n</p><p id=\"v_b\"><b>b. </b> atunci când autocamionul nu este asigurat de r&#259;spundere civil&#259;, în caz de pagube materiale produse ter&#355;ilor prin accidente de circula&#355;ie, conform legii;\r\n</p><p id=\"v_c\"><b>c. </b> atunci când conduc&#259;torul auto nu a pl&#259;tit taxa de folosire a drumurilor publice.</p>', 'nu', 'da', 'nu', ''),
(56, 'Care dintre supapele mecanismului de distribu&#355;ie se uzeaz&#259; mai repede?', '', '<p id=\"v_a\"><b>a. </b> supapele de admisie;\r\n</p><p id=\"v_b\"><b>b. </b> supapele de evacuare;\r\n</p><p id=\"v_c\"><b>c. </b> ambele supape se uzeaz&#259; la fel în cazul unui motor bine între&#355;inut.</p>', 'nu', 'da', 'nu', ''),
(57, 'Care dintre indicatoarele al&#259;turate interzice conduc&#259;torilor de autocamioane s&#259; dep&#259;&#351;easc&#259; motociclete f&#259;r&#259; ata&#351;?', 'uploads_c/1467.jpg', '<p id=\"v_a\"><b>a. </b> numai indicatorul 1;\r\n</p><p id=\"v_b\"><b>b. </b> numai indicatorul 2;\r\n</p><p id=\"v_c\"><b>c. </b> ambele indicatoare.</p>', 'da', 'nu', 'nu', ''),
(58, 'Autovehiculul sau ansamblul de vehicule care, prin construc&#355;ie sau datorit&#259; înc&#259;rc&#259;turii transportate, dep&#259;&#351;e&#351;te masa total&#259; de 80 t &#351;i/sau lungimea de 40 m, sau în situa&#355;ia în care l&#259;&#355;imea sau în&#259;l&#355;imea acestuia dep&#259;&#351;es', '', '<p id=\"v_a\"><b>a. </b> f&#259;r&#259; nicio restric&#355;ie sau aprobare, îns&#259; cu o vitez&#259; de pân&#259; la 50 km/h;\r\n</p><p id=\"v_b\"><b>b. </b> numai în baza autoriza&#355;iei speciale emise de administratorul drumului &#351;i cu avizul poli&#355;iei rutiere;\r\n</p><p id=\"v_c\"><b>c. </b> numai înso&#355;i&#355;i de echipaje de poli&#355;ie rutier&#259;, precum &#351;i de persoane care cunosc bine caracteristicile înc&#259;rc&#259;turii transportate.</p>', 'nu', 'da', 'nu', ''),
(59, 'La cererea poli&#355;istului rutier, împreun&#259; cu reprezentantul autorit&#259;&#355;ilor abilitate, conduc&#259;torul unui autocamion este obligat:', '', '<p id=\"v_a\"><b>a. </b> s&#259; prezinte documentele de c&#259;l&#259;torie;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259;-l transporte la prima localitate;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; permit&#259; controlul st&#259;rii tehnice a autocamionului, precum &#351;i al bunurilor transportate.</p>', 'nu', 'nu', 'da', ''),
(60, 'În care dintre situa&#355;ii oprirea este interzis&#259;?', '', '<p id=\"v_a\"><b>a. </b> în zona de ac&#355;iune a indicatorului care interzice dep&#259;&#351;irea;\r\n</p><p id=\"v_b\"><b>b. </b> pe str&#259;zile unde circula&#355;ia se desf&#259;&#351;oar&#259; în sens unic;\r\n</p><p id=\"v_c\"><b>c. </b> în dreptul altui vehicul care se afl&#259; oprit, dac&#259; prin aceasta se stânjene&#351;te circula&#355;ia a dou&#259; vehicule venind din sensuri opuse.</p>', 'da', 'nu', 'da', ''),
(61, 'Care dintre urm&#259;toarele defec&#355;iuni ale instala&#355;iei de alimentare a motorului cu aprindere prin comprimare se manifest&#259; prin faptul c&#259; motorul merge neregulat, „bate“ &#351;i scoate mult fum?', '', '<p id=\"v_a\"><b>a. </b> desfacerea piuli&#355;elor conductelor de înalt&#259; presiune;\r\n</p><p id=\"v_b\"><b>b. </b> uzura injectoarelor;\r\n</p><p id=\"v_c\"><b>c. </b> ruperea arcului de reglare a presiunii de injectare.</p>', 'nu', 'da', 'nu', ''),
(62, 'Prin „raport de compresie“ se în&#355;elege:', '', '<p id=\"v_a\"><b>a. </b> raportul dintre volumul tuturor cilindrilor &#351;i volumul pistoanelor;\r\n</p><p id=\"v_b\"><b>b. </b> raportul dintre volumul camerei de ardere &#351;i volumul cilindrului;\r\n</p><p id=\"v_c\"><b>c. </b> raportul dintre volumul cilindrului &#351;i volumul camerei de ardere.</p>', 'nu', 'nu', 'da', ''),
(63, 'Masa total&#259; maxim&#259; admis&#259; pentru un tren rutier cu patru osii, compus dintr-un vehicul motor cu dou&#259; osii &#351;i o remorc&#259; cu dou&#259; osii, pe drumuri asfaltate, este:', '', '<p id=\"v_a\"><b>a. </b> 36 t;\r\n</p><p id=\"v_b\"><b>b. </b> 40 t;\r\n</p><p id=\"v_c\"><b>c. </b> 46 t.</p>', 'da', 'nu', 'nu', ''),
(64, 'Care este rulajul la care se recomand&#259; controlul bujiilor &#351;i reglarea distan&#355;ei dintre electrozi?', '', '<p id=\"v_a\"><b>a. </b> 10000 km;\r\n</p><p id=\"v_b\"><b>b. </b> 15000 km;\r\n</p><p id=\"v_c\"><b>c. </b> 5000 km.</p>', 'nu', 'nu', 'da', ''),
(65, 'Indicatorul interzice accesul:', 'uploads_c/1612.jpg', '<p id=\"v_a\"><b>a. </b> autovehiculelor &#351;i vehiculelor cu trac&#355;iune animal&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> motocicletelor, autoturismelor &#351;i vehiculelor cu trac&#355;iune animal&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> autoturismelor &#351;i vehiculelor cu trac&#355;iune animal&#259;.</p>', 'da', 'nu', 'nu', ''),
(66, 'În care dintre situa&#355;iile de mai jos este interzis&#259; oprirea autocamioanelor pe drumurile publice?', '', '<p id=\"v_a\"><b>a. </b> în intersec&#355;iile cu sens giratoriu;\r\n</p><p id=\"v_b\"><b>b. </b> în zona de ac&#355;iune a indicatorului „Sta&#355;ionarea interzis&#259;“;\r\n</p><p id=\"v_c\"><b>c. </b> pe arterele cu un singur sens de circula&#355;ie.</p>', 'da', 'nu', 'nu', ''),
(67, 'Care este circuitul de joas&#259; tensiune al instala&#355;iei de aprindere?', '', '<p id=\"v_a\"><b>a. </b> bateria de acumulatori, contactul, înf&#259;&#351;urarea secundar&#259; a bobinei de induc&#355;ie, ruptorul-distribuitor, masa;\r\n</p><p id=\"v_b\"><b>b. </b> bateria de acumulatori, contactul, înf&#259;&#351;urarea primar&#259; a bobinei de induc&#355;ie;\r\n</p><p id=\"v_c\"><b>c. </b> bateria de acumulatori, voltmetrul, ampermetrul, înf&#259;&#351;urarea secundar&#259; a bobinei de induc&#355;ie.</p>', 'nu', 'da', 'nu', ''),
(68, 'Exploziile caden&#355;ate în carburator sunt cauzate de:', '', '<p id=\"v_a\"><b>a. </b> supapele de evacuare defecte;\r\n</p><p id=\"v_b\"><b>b. </b> supapele de admisie defecte;\r\n</p><p id=\"v_c\"><b>c. </b> garnitura de chiulas&#259; rupt&#259; între doi cilindri.</p>', 'nu', 'da', 'nu', ''),
(69, 'În care dintre situa&#355;iile de mai jos este interzis&#259; întoarcerea unui camion?', '', '<p id=\"v_a\"><b>a. </b> în zona de ac&#355;iune a indicatorului „Drum îngustat“;\r\n</p><p id=\"v_b\"><b>b. </b> în curbe cu vizibilitatea redus&#259; sub 50 m;\r\n</p><p id=\"v_c\"><b>c. </b> la 100 m de sta&#355;iile de tramvai.</p>', 'da', 'da', 'nu', ''),
(70, 'Pe autostr&#259;zi, autovehiculul care, cu sau f&#259;r&#259; înc&#259;rc&#259;tur&#259;, dep&#259;&#351;e&#351;te l&#259;&#355;imea de 3,2 m, trebuie s&#259; fie:', '', '<p id=\"v_a\"><b>a. </b> dotat cu dispozitive speciale de semnalizare luminoas&#259; &#351;i sonor&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> urmat de un autovehicul de înso&#355;ire;\r\n</p><p id=\"v_c\"><b>c. </b> înso&#355;it de cel pu&#355;in dou&#259; echipaje de poli&#355;ie rutier&#259;.</p>', 'nu', 'da', 'nu', ''),
(71, 'Amestecul carburant foarte s&#259;rac provoac&#259; rateuri în:', '', '<p id=\"v_a\"><b>a. </b> galeria de admisie;\r\n</p><p id=\"v_b\"><b>b. </b> galeria de evacuare;\r\n</p><p id=\"v_c\"><b>c. </b> toba de e&#351;apament.</p>', 'da', 'nu', 'nu', ''),
(72, 'Hemoragia nazal&#259; se poate opri dac&#259; accidentatul:', '', '<p id=\"v_a\"><b>a. </b> st&#259; culcat cu capul pe spate;\r\n</p><p id=\"v_b\"><b>b. </b> st&#259; în picioare &#351;i î&#351;i strânge ambele n&#259;ri timp de aproximativ 5-10 minute;\r\n</p><p id=\"v_c\"><b>c. </b> ridic&#259; mâna dreapt&#259;.</p>', 'nu', 'da', 'nu', ''),
(73, 'Care dintre cauzele de mai jos provoac&#259; oscila&#355;ia ro&#355;ilor directoare la vitez&#259; redus&#259;?', '', '<p id=\"v_a\"><b>a. </b> griparea articula&#355;iilor mecanismului;\r\n</p><p id=\"v_b\"><b>b. </b> jocul prea mare între componentele casetei de direc&#355;ie;\r\n</p><p id=\"v_c\"><b>c. </b> griparea pivotului de fuzet&#259;.</p>', 'nu', 'da', 'nu', ''),
(74, 'C&#259;ile respiratorii pot fi deblocate:', '', '<p id=\"v_a\"><b>a. </b> numai în unit&#259;&#355;i medicale, folosindu-se instrumente speciale;\r\n</p><p id=\"v_b\"><b>b. </b> la locul accidentului, prin interven&#355;ii specifice;\r\n</p><p id=\"v_c\"><b>c. </b> indiferent unde, dac&#259; victima suport&#259; aceast&#259; interven&#355;ie.</p>', 'nu', 'da', 'nu', ''),
(75, 'Unghiul de c&#259;dere prea mare la o roat&#259; duce la uzura uniform&#259; a pneurilor:', '', '<p id=\"v_a\"><b>a. </b> pe marginea exterioar&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> pe marginea interioar&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> pe toat&#259; l&#259;&#355;imea.</p>', 'da', 'nu', 'nu', ''),
(76, 'Injec&#355;ia de benzin&#259; monopunct se realizeaz&#259; în:', '', '<p id=\"v_a\"><b>a. </b> interiorul camerei de nivel constant a carburatorului;\r\n</p><p id=\"v_b\"><b>b. </b> colectorul de admisie;\r\n</p><p id=\"v_c\"><b>c. </b> poarta supapelor de evacuare.</p>', 'nu', 'da', 'nu', ''),
(77, 'Ce m&#259;suri trebuie s&#259; lua&#355;i atunci când circula&#355;i în condi&#355;ii atmosferice ce împiedic&#259; mult vizibilitatea?', '', '<p id=\"v_a\"><b>a. </b> s&#259; circula&#355;i pe distan&#355;e scurte &#351;i cu luminile de pozi&#355;ie în func&#355;iune;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; folosi&#355;i luminile de pozi&#355;ie &#351;i s&#259; circula&#355;i cât mai aproape de marginea din dreapta a drumului;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; folosi&#355;i luminile de întâlnire.</p>', 'nu', 'nu', 'da', ''),
(78, 'Apari&#355;ia unor b&#259;t&#259;i sau zgomote la viraje sau la schimbarea brusc&#259; a vitezei de rulare se datoreaz&#259;:', '', '<p id=\"v_a\"><b>a. </b> defec&#355;iunilor la mecanismul de direc&#355;ie;\r\n</p><p id=\"v_b\"><b>b. </b> în&#355;epenirii sateli&#355;ilor diferen&#355;ialului pe axe;\r\n</p><p id=\"v_c\"><b>c. </b> înc&#259;rc&#259;rii neuniforme a autovehiculului.</p>', 'nu', 'da', 'nu', ''),
(79, 'Frâna de e&#351;apament îndepline&#351;te urm&#259;toarele func&#355;ii:', '', '<p id=\"v_a\"><b>a. </b> men&#355;ine temperatura de pornire a motorului &#351;i aerise&#351;te toba;\r\n</p><p id=\"v_b\"><b>b. </b> men&#355;ine motorul cald la ralanti &#351;i realizeaz&#259; frâna de motor;\r\n</p><p id=\"v_c\"><b>c. </b> ajut&#259; la re&#355;inerea noxelor din gazele e&#351;apate.</p>', 'nu', 'da', 'nu', ''),
(80, 'L&#259;&#355;imea maxim&#259; admis&#259; pentru toate vehiculele, cu excep&#355;ia celor frigorifice având caroserie izoterm&#259;, este:', '', '<p id=\"v_a\"><b>a. </b> 2,00 m;\r\n</p><p id=\"v_b\"><b>b. </b> 2,40 m;\r\n</p><p id=\"v_c\"><b>c. </b> 2,55 m.</p>', 'nu', 'nu', 'da', ''),
(81, 'Care dintre obliga&#355;iile de mai jos revin conduc&#259;torilor auto angaja&#355;i de c&#259;tre operatorii de transport rutier?', '', '<p id=\"v_a\"><b>a. </b> s&#259; respecte perioadele de conducere &#351;i perioadele de odihn&#259;, conform reglement&#259;rilor legale în vigoare, &#351;i s&#259; utilizeze corect aparatul tahograf;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; circule, numai pe timp de zi, f&#259;r&#259; a dep&#259;&#351;i viteza indicat&#259; de tahometru &#351;i s&#259; se odihneasc&#259; pe timpul nop&#355;ii;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; nu intervin&#259; asupra aparatului tahograf sau a limitatorului de vitez&#259; pentru scoaterea acestuia din uz sau pentru modificarea caracteristicilor de func&#355;ionare.</p>', 'da', 'nu', 'da', ''),
(82, 'La motorul în patru timpi, tura&#355;ia arborelui cu came, fa&#355;&#259; de tura&#355;ia arborelui cotit, este:', '', '<p id=\"v_a\"><b>a. </b> de dou&#259; ori mai mic&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> de dou&#259; ori mai mare;\r\n</p><p id=\"v_c\"><b>c. </b> egal&#259;.</p>', 'da', 'nu', 'nu', ''),
(83, '„Perioada de odihn&#259; zilnic&#259; redus&#259;“ se refer&#259; la:', '', '<p id=\"v_a\"><b>a. </b> perioada de somn de minimum 6 ore;\r\n</p><p id=\"v_b\"><b>b. </b> orice perioad&#259; de odihn&#259; de cel pu&#355;in 9 ore, dar care s&#259; nu dep&#259;&#351;easc&#259; 11 ore;\r\n</p><p id=\"v_c\"><b>c. </b> intervalul neîntrerupt de cel pu&#355;in 11 ore de odihn&#259;.</p>', 'nu', 'da', 'nu', ''),
(84, 'În&#259;l&#355;imea maxim&#259; admis&#259; pentru toate vehiculele este:', '', '<p id=\"v_a\"><b>a. </b> 3,50 m;\r\n</p><p id=\"v_b\"><b>b. </b> 4,00 m;\r\n</p><p id=\"v_c\"><b>c. </b> 4,50 m.</p>', 'nu', 'da', 'nu', ''),
(85, 'Care dintre piesele urm&#259;toare ac&#355;ioneaz&#259; pompa de înalt&#259; presiune din componen&#355;a sistemului de direc&#355;ie cu servomecanism hidraulic?', '', '<p id=\"v_a\"><b>a. </b> axul cardanic;\r\n</p><p id=\"v_b\"><b>b. </b> arborele cotit al compresorului de aer;\r\n</p><p id=\"v_c\"><b>c. </b> arborele cu came.</p>', 'nu', 'da', 'nu', ''),
(86, 'Conduc&#259;torul unui autocamion este obligat s&#259; opreasc&#259;:', '', '<p id=\"v_a\"><b>a. </b> la apropierea de o intersec&#355;ie;\r\n</p><p id=\"v_b\"><b>b. </b> la semnalul nev&#259;z&#259;torilor care traverseaz&#259; strada;\r\n</p><p id=\"v_c\"><b>c. </b> la semnalul conduc&#259;torului coloanei de pietoni.</p>', 'nu', 'da', 'nu', ''),
(87, 'Se consider&#259; contraven&#355;ie:', '', '<p id=\"v_a\"><b>a. </b> nerespectarea prevederilor în vigoare, privind utilizarea unei cartele tahografice defecte sau expirate;\r\n</p><p id=\"v_b\"><b>b. </b> utilizarea DVD-urilor sau a altor aparate electronice;\r\n</p><p id=\"v_c\"><b>c. </b> nerespectarea prevederilor privind utilizarea unor diagrame tahografice murdare sau deteriorate.</p>', 'da', 'nu', 'da', ''),
(88, 'Care este rolul principal al supapei de refulare a elementului pompei de injec&#355;ie?', '', '<p id=\"v_a\"><b>a. </b> s&#259; împiedice picurarea motorinei din injector, la sfâr&#351;itul injec&#355;iei;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; împiedice reîntoarcerea în pompa de injec&#355;ie a surplusului de motorin&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; creasc&#259; presiunea motorinei la sfâr&#351;itul injec&#355;iei.</p>', 'da', 'nu', 'nu', ''),
(89, 'Înc&#259;rc&#259;tura unui autocamion trebuie s&#259; fie a&#351;ezat&#259; astfel încât:', '', '<p id=\"v_a\"><b>a. </b> s&#259; nu pun&#259; în pericol persoane ori s&#259; cauzeze daune propriet&#259;&#355;ii publice sau private;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; nu fie târât&#259;, s&#259; nu curg&#259; &#351;i s&#259; nu cad&#259; pe drumul public;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; nu stânjeneasc&#259; vizibilitatea conduc&#259;torului auto &#351;i s&#259; nu pericliteze stabilitatea sau conducerea vehiculului.</p>', 'da', 'da', 'da', ''),
(90, 'Apari&#355;ia unui zgomot ascu&#355;it în momentul ap&#259;s&#259;rii pedalei de ambreiaj semnific&#259;:', '', '<p id=\"v_a\"><b>a. </b> ferodourile sunt uzate;\r\n</p><p id=\"v_b\"><b>b. </b> arcul de revenire a pedalei de ambreiaj este uzat;\r\n</p><p id=\"v_c\"><b>c. </b> rulmentul de presiune este defect.</p>', 'nu', 'nu', 'da', ''),
(91, 'Sc&#259;derea presiunii uleiului este determinat&#259; de una dintre urm&#259;toarele cauze:', '', '<p id=\"v_a\"><b>a. </b> îmbâcsirea sorbului pompei de ulei;;\r\n</p><p id=\"v_b\"><b>b. </b> func&#355;ionarea motorului la tura&#355;ii mici;\r\n</p><p id=\"v_c\"><b>c. </b> montarea incorect&#259; a filtrului de ulei.</p>', 'da', 'nu', 'nu', ''),
(92, 'Amestecul carburant foarte bogat provoac&#259; rateuri în:', '', '<p id=\"v_a\"><b>a. </b> galeria de evacuare sau în toba de e&#351;apament;\r\n</p><p id=\"v_b\"><b>b. </b> galeria de admisie;\r\n</p><p id=\"v_c\"><b>c. </b> carburator sau în pompa de benzin&#259;.</p>', 'da', 'nu', 'nu', ''),
(93, 'Care dintre urm&#259;toarele defec&#355;iuni face imposibil&#259;, la un moment dat, frânarea autovehiculului?', '', '<p id=\"v_a\"><b>a. </b> uzura exagerat&#259; a pneurilor;\r\n</p><p id=\"v_b\"><b>b. </b> ruperea curelei de antrenare a compresorului;\r\n</p><p id=\"v_c\"><b>c. </b> uzura pistoanelor compresorului.</p>', 'nu', 'da', 'nu', ''),
(94, 'Primul ajutor în caz de traumatism al capului const&#259; în:', '', '<p id=\"v_a\"><b>a. </b> aplicarea unor comprese reci pe frunte &#351;i bandajarea p&#259;r&#355;ilor lovite;\r\n</p><p id=\"v_b\"><b>b. </b> observarea respira&#355;iei &#351;i a circula&#355;iei sanguine &#351;i aplecarea capului c&#259;tre b&#259;rbie;\r\n</p><p id=\"v_c\"><b>c. </b> bandajarea fracturii deschise, deschiderea c&#259;ilor respiratorii dac&#259; se impune &#351;i culcarea victimei pe o targ&#259; plan&#259; pentru transport.</p>', 'nu', 'nu', 'da', ''),
(95, 'Conduce&#355;i, într-o localitate, un autocamion cu care transporta&#355;i înc&#259;rc&#259;turi periculoase. Cu ce vitez&#259; maxim&#259; ve&#355;i circula?', '', '<p id=\"v_a\"><b>a. </b> cu 30 km/h;\r\n</p><p id=\"v_b\"><b>b. </b> cu 40 km/h;\r\n</p><p id=\"v_c\"><b>c. </b> cu 50 km/h.</p>', 'nu', 'da', 'nu', ''),
(96, 'Cele mai importante caracteristici ale bujiilor sunt:', '', '<p id=\"v_a\"><b>a. </b> grosimea izolatorului ceramic &#351;i culoarea acestuia;\r\n</p><p id=\"v_b\"><b>b. </b> dimensiunea filetului &#351;i valoarea termic&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> termenul de garan&#355;ie &#351;i constan&#355;a scânteii.</p>', 'nu', 'da', 'nu', ''),
(97, 'Ce trebuie s&#259; folosi&#355;i pentru imobilizarea fracturilor de la membrele superioare?', '', '<p id=\"v_a\"><b>a. </b> atele improvizate;\r\n</p><p id=\"v_b\"><b>b. </b> o e&#351;arf&#259; simpl&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> în acest caz nu se intervine.</p>', 'nu', 'da', 'nu', ''),
(98, 'Ro&#355;ile directoare ale autovehiculelor cu trac&#355;iune pe spate sunt, de regul&#259;:', '', '<p id=\"v_a\"><b>a. </b> divergente sau paralele;\r\n</p><p id=\"v_b\"><b>b. </b> convergente sau paralele;\r\n</p><p id=\"v_c\"><b>c. </b> divergente.</p>', 'nu', 'da', 'nu', ''),
(99, 'Unde se formeaz&#259; amestecul carburant la motoarele cu aprindere prin compresie?', '', '<p id=\"v_a\"><b>a. </b> în galeria de admisie;\r\n</p><p id=\"v_b\"><b>b. </b> în cilindrii motorului;\r\n</p><p id=\"v_c\"><b>c. </b> în pompa de injec&#355;ie.</p>', 'nu', 'da', 'nu', ''),
(100, 'Ce cauze pot conduce la un consum exagerat de combustibil?', '', '<p id=\"v_a\"><b>a. </b> tura&#355;ia la ralanti prea mare;\r\n</p><p id=\"v_b\"><b>b. </b> temperatura mediului ambiant;\r\n</p><p id=\"v_c\"><b>c. </b> lipsa filtrului de benzin&#259;.</p>', 'da', 'nu', 'nu', ''),
(101, 'Indicatorul al&#259;turat interzice:', 'uploads_c/1468.jpg', '<p id=\"v_a\"><b>a. </b> accesul autovehiculelor cu remorci, cu excep&#355;ia celor cu o semiremorc&#259; sau cu o remorc&#259; cu o osie;\r\n</p><p id=\"v_b\"><b>b. </b> accesul tuturor autovehiculelor cu semiremorc&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> accesul tuturor autovehiculelor care tracteaz&#259; remorci sau semiremorci.</p>', 'da', 'nu', 'nu', ''),
(102, 'Furca de debreiere ac&#355;ioneaz&#259; pe:', '', '<p id=\"v_a\"><b>a. </b> discul de ambreiaj;\r\n</p><p id=\"v_b\"><b>b. </b> rulmentul de presiune;\r\n</p><p id=\"v_c\"><b>c. </b> placa de presiune.</p>', 'nu', 'da', 'nu', ''),
(103, 'Masa maxim&#259; admis&#259; pe osia simpl&#259; nemotoare, pe un drum european, este:', '', '<p id=\"v_a\"><b>a. </b> 10 t;\r\n</p><p id=\"v_b\"><b>b. </b> 15 t;\r\n</p><p id=\"v_c\"><b>c. </b> 20 t.</p>', 'da', 'nu', 'nu', ''),
(104, 'Neacordarea priorit&#259;&#355;ii înseamn&#259;:', '', '<p id=\"v_a\"><b>a. </b> nerespectarea semnifica&#355;iei semnaliz&#259;rii rutiere de obligare sau de avertizare;\r\n</p><p id=\"v_b\"><b>b. </b> nerespectarea dispozi&#355;iilor legale privind asigurarea &#351;i semnalizarea în intersec&#355;ii;\r\n</p><p id=\"v_c\"><b>c. </b> obligarea celor care au prioritate de trecere de a-&#351;i modifica brusc direc&#355;ia sau viteza de deplasare ori s&#259; opreasc&#259;.</p>', 'nu', 'nu', 'da', ''),
(105, 'Ce categorii de vehicule au obliga&#355;ia de a folosi în timpul zilei luminile de întâlnire?', '', '<p id=\"v_a\"><b>a. </b> autovehiculele care tracteaz&#259; alte vehicule sau care transport&#259; produse periculoase;\r\n</p><p id=\"v_b\"><b>b. </b> autovehiculele ce se deplaseaz&#259; cu vitez&#259; mare;\r\n</p><p id=\"v_c\"><b>c. </b> autovehiculele pentru transport public de persoane.</p>', 'da', 'nu', 'nu', ''),
(106, 'Care dintre urm&#259;toarele cauze duce la un consum m&#259;rit de combustibil?', '', '<p id=\"v_a\"><b>a. </b> obturarea jiclorului principal;\r\n</p><p id=\"v_b\"><b>b. </b> nivelul benzinei în camera de nivel constant este mediu;\r\n</p><p id=\"v_c\"><b>c. </b> p&#259;trunderea în exces a impurit&#259;&#355;ilor în filtrul de aer.</p>', 'nu', 'nu', 'da', ''),
(107, 'Fracturile rezultate în urma unui accident rutier pot fi recunoscute prin:', '', '<p id=\"v_a\"><b>a. </b> culoarea diferit&#259; a locului fracturii;\r\n</p><p id=\"v_b\"><b>b. </b> segmentul osos fracturat, devenit elastic;\r\n</p><p id=\"v_c\"><b>c. </b> segmentul fracturat, care are o mi&#351;care anormal&#259;.</p>', 'nu', 'nu', 'da', ''),
(108, 'Care dintre urm&#259;toarele cauze determin&#259; revenirea greoaie a ro&#355;ilor directoare pe direc&#355;ia înainte?', '', '<p id=\"v_a\"><b>a. </b> articula&#355;iile direc&#355;iei nu sunt gresate;\r\n</p><p id=\"v_b\"><b>b. </b> pneurile ro&#355;ilor directoare au o presiune superioar&#259; celei indicate de constructor;\r\n</p><p id=\"v_c\"><b>c. </b> jocul din angrenajul casetei de direc&#355;ie este prea mare.</p>', 'da', 'nu', 'nu', ''),
(109, '„Durata de conducere zilnic&#259;“ reprezint&#259;:', '', '<p id=\"v_a\"><b>a. </b> durata total&#259; de conducere acumulat&#259; între dou&#259; perioade de odihn&#259; zilnic&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> perioada de conducere în care autovehiculul ruleaz&#259; neîntrerupt;\r\n</p><p id=\"v_c\"><b>c. </b> perioada de conducere între dou&#259; puncte ale traseului, prev&#259;zute într-o singur&#259; zi.</p>', 'da', 'nu', 'nu', ''),
(110, 'Care dintre indicatoarele al&#259;turate interzice conduc&#259;torilor de autocamioane ce transport&#259; marf&#259; s&#259; dep&#259;&#351;easc&#259; autoturisme?', 'uploads_c/1533.jpg', '<p id=\"v_a\"><b>a. </b> numai indicatorul 1;\r\n</p><p id=\"v_b\"><b>b. </b> numai indicatorul 2;\r\n</p><p id=\"v_c\"><b>c. </b> ambele indicatoare.</p>', 'nu', 'nu', 'da', ''),
(111, 'Uzurile locale din zona benzii de rulare a pneurilor se produc din cauza:', '', '<p id=\"v_a\"><b>a. </b> ovaliz&#259;rii tamburului ro&#355;ii respective;\r\n</p><p id=\"v_b\"><b>b. </b> rul&#259;rii cu presiune sc&#259;zut&#259; în pneuri;\r\n</p><p id=\"v_c\"><b>c. </b> rul&#259;rii autovehiculului pe drumuri accidentate.</p>', 'da', 'nu', 'nu', ''),
(112, 'Când se folosesc luminile de avarie?', '', '<p id=\"v_a\"><b>a. </b> când se circul&#259; în coloan&#259;, cu vitez&#259; redus&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> când autocamionul se deplaseaz&#259; foarte lent, din motive obiective;\r\n</p><p id=\"v_c\"><b>c. </b> când se circul&#259; pe un drum alunecos.</p>', 'nu', 'da', 'nu', ''),
(113, '„Perioada de odihn&#259; s&#259;pt&#259;mânal&#259; normal&#259;“ reprezint&#259;:', '', '<p id=\"v_a\"><b>a. </b> perioada de odihn&#259; de cel pu&#355;in 24 de ore consecutive;\r\n</p><p id=\"v_b\"><b>b. </b> orice perioad&#259; de odihn&#259; de cel pu&#355;in 45 de ore consecutive;\r\n</p><p id=\"v_c\"><b>c. </b> perioada de odihn&#259; de cel pu&#355;in 3 zile consecutive.</p>', 'nu', 'da', 'nu', ''),
(114, 'Autocamionul sta&#355;ionat noaptea pe partea carosabil&#259; a unui drum public neiluminat se semnalizeaz&#259; prin:', '', '<p id=\"v_a\"><b>a. </b> plasarea, în partea din spate, a unui triunghi reflectorizant;\r\n</p><p id=\"v_b\"><b>b. </b> aprinderea luminilor de avarie;\r\n</p><p id=\"v_c\"><b>c. </b> aprinderea luminilor de pozi&#355;ie sau de sta&#355;ionare.</p>', 'nu', 'nu', 'da', ''),
(115, 'Pompa de înalt&#259; presiune din componen&#355;a sistemului de direc&#355;ie cu servomecanism hidraulic este antrenat&#259; de:', '', '<p id=\"v_a\"><b>a. </b> arborele cotit al motorului;\r\n</p><p id=\"v_b\"><b>b. </b> axul secundar al cutiei de viteze;\r\n</p><p id=\"v_c\"><b>c. </b> axa cu came.</p>', 'da', 'nu', 'nu', ''),
(116, 'Circuitul de înalt&#259; presiune al instala&#355;iei de alimentare a motoarelor Diesel are în componen&#355;&#259;:', '', '<p id=\"v_a\"><b>a. </b> pompa de alimentare, injectoarele &#351;i conductele de leg&#259;tur&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> pompa de injec&#355;ie, injectoarele &#351;i conductele de leg&#259;tur&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> pompa de alimentare, pompa de injec&#355;ie &#351;i filtrele.</p>', 'nu', 'da', 'nu', ''),
(117, 'În care dintre urm&#259;toarele situa&#355;ii pute&#355;i dep&#259;&#351;i un autoturism în afara localit&#259;&#355;ilor?', '', '<p id=\"v_a\"><b>a. </b> atunci când conduc&#259;torul autoturismului circul&#259; cu o vitez&#259; sub limita maxim&#259; admis&#259; pentru un autocamion;\r\n</p><p id=\"v_b\"><b>b. </b> când circula&#355;i pe un drum public cu mai mult de dou&#259; benzi pe sens;\r\n</p><p id=\"v_c\"><b>c. </b> atunci când autoturismul are semnul distinctiv al conduc&#259;torului cu vechime în conducere mai mic&#259; de un an.</p>', 'da', 'nu', 'nu', ''),
(118, 'Persoanele accidentate, cu arsuri multiple pe corp &#351;i membre, trebuie s&#259; fie:', '', '<p id=\"v_a\"><b>a. </b> dezbr&#259;cate rapid de haine, dup&#259; stingerea fl&#259;c&#259;rilor;\r\n</p><p id=\"v_b\"><b>b. </b> transportate urgent la o unitate medical&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> sp&#259;late, bandajate &#351;i apoi transportate la cea mai apropiat&#259; unitate sanitar&#259;.</p>', 'nu', 'da', 'nu', ''),
(119, 'Care dintre urm&#259;toarele defec&#355;iuni determin&#259; func&#355;ionarea motoarelor cu aprindere prin compresie cu fum alb excesiv?', '', '<p id=\"v_a\"><b>a. </b> arderea garniturii de chiulas&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> resortul injectorului blocat;\r\n</p><p id=\"v_c\"><b>c. </b> avansul la injec&#355;ie peste limita normal&#259;.</p>', 'da', 'nu', 'nu', '');
INSERT INTO `intrebari_c` (`id`, `cerinta`, `imagine`, `variante`, `rasp_a`, `rasp_b`, `rasp_c`, `eticheta`) VALUES
(120, 'În care dintre urm&#259;toarele situa&#355;ii este interzis&#259; oprirea?', '', '<p id=\"v_a\"><b>a. </b> pe sectoarele de drum neiluminate;\r\n</p><p id=\"v_b\"><b>b. </b> la o distan&#355;&#259; mai mic&#259; de 5 m fa&#355;&#259; de ultimul vagon al unui tramvai;\r\n</p><p id=\"v_c\"><b>c. </b> pe drumurile na&#355;ionale europene.</p>', 'nu', 'nu', 'da', ''),
(121, 'Ce se în&#355;elege prin „semiremorc&#259;”?', '', '<p id=\"v_a\"><b>a. </b> remorca a c&#259;rei mas&#259; maxim&#259; autorizat&#259; nu dep&#259;&#351;e&#351;te 750 kg;\r\n</p><p id=\"v_b\"><b>b. </b> remorca a c&#259;rei mas&#259; total&#259; maxim&#259; autorizat&#259; este preluat&#259; în parte de c&#259;tre un autovehicul sau de c&#259;tre un tractor;\r\n</p><p id=\"v_c\"><b>c. </b> remorca basculabil&#259;, destinat&#259; doar transporturilor de m&#259;rfuri neambalate.</p>', 'nu', 'da', 'nu', ''),
(122, 'Pentru a limita riscul de r&#259;sturnare, coletele grele trebuie înc&#259;rcate &#351;i a&#351;ezate:', '', '<p id=\"v_a\"><b>a. </b> în partea lateral&#259;, dreapt&#259; sau stâng&#259;, a caroseriei;\r\n</p><p id=\"v_b\"><b>b. </b> în partea superioar&#259; a caroseriei;\r\n</p><p id=\"v_c\"><b>c. </b> în partea inferioar&#259; a caroseriei.</p>', 'nu', 'nu', 'da', ''),
(123, 'Ce trebuie s&#259; face&#355;i pentru evitarea complica&#355;iilor în cazul unei fracturi?', '', '<p id=\"v_a\"><b>a. </b> s&#259; imobiliza&#355;i zona fracturat&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; steriliza&#355;i &#351;i s&#259; pansa&#355;i zona fracturat&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; aplica&#355;i un garou de cauciuc deasupra fracturii.</p>', 'da', 'nu', 'nu', ''),
(124, 'Ce se în&#355;elege prin „valoarea termic&#259;“ a bujiei?', '', '<p id=\"v_a\"><b>a. </b> timpul în secunde, pân&#259; când bujia ajunge la temperatura de autocur&#259;&#355;are a electrozilor;\r\n</p><p id=\"v_b\"><b>b. </b> capacitatea bujiei de a lucra mai bine la temperaturi înalte;\r\n</p><p id=\"v_c\"><b>c. </b> însu&#351;irea bujiei de a conecta &#351;i deconecta func&#355;ionarea termostatului.</p>', 'da', 'nu', 'nu', ''),
(125, 'Indicatorul al&#259;turat permite accesul:', 'uploads_c/1599.jpg', '<p id=\"v_a\"><b>a. </b> autocamioanelor;\r\n</p><p id=\"v_b\"><b>b. </b> motocicletelor f&#259;r&#259; ata&#351;;\r\n</p><p id=\"v_c\"><b>c. </b> bicicletelor.</p>', 'nu', 'nu', 'da', ''),
(126, 'Arborele cu came este ac&#355;ionat de:', '', '<p id=\"v_a\"><b>a. </b> arborele cotit;\r\n</p><p id=\"v_b\"><b>b. </b> diferen&#355;ial;\r\n</p><p id=\"v_c\"><b>c. </b> ruptor-distribuitor.</p>', 'da', 'nu', 'nu', ''),
(127, 'Uzura accentuat&#259; a p&#259;r&#355;ii interioare a benzii de rulare a pneurilor directoare denot&#259;:', '', '<p id=\"v_a\"><b>a. </b> pozi&#355;ia excesiv convergent&#259; a acestor ro&#355;i;\r\n</p><p id=\"v_b\"><b>b. </b> pozi&#355;ia excesiv divergent&#259; a acestor ro&#355;i;\r\n</p><p id=\"v_c\"><b>c. </b> rularea cu viteze foarte mari &#351;i cu presiune prea mare în pneuri.</p>', 'nu', 'da', 'nu', ''),
(128, 'Temperatura de intrare în func&#355;iune a termostatului este de:', '', '<p id=\"v_a\"><b>a. </b> 76-80 grade C;\r\n</p><p id=\"v_b\"><b>b. </b> 95-100 grade C;\r\n</p><p id=\"v_c\"><b>c. </b> 105-120 grade C.</p>', 'da', 'nu', 'nu', ''),
(129, 'Anvelopele ro&#355;ilor directoare se uzeaz&#259; excesiv pe partea interioar&#259; a benzii de rulare, dac&#259;:', '', '<p id=\"v_a\"><b>a. </b> divergen&#355;a acestora este m&#259;rit&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> convergen&#355;a acestora este m&#259;rit&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> s-a rulat îndelungat cu presiunea sc&#259;zut&#259; în pneuri.</p>', 'da', 'nu', 'nu', ''),
(130, 'Existen&#355;a aerului în instala&#355;ia de frânare hidraulic&#259; se manifest&#259;, la ap&#259;sarea pedalei de frân&#259;, prin:', '', '<p id=\"v_a\"><b>a. </b> atingerea podelei de c&#259;tre pedal&#259; la o ap&#259;sare u&#351;oar&#259;, f&#259;r&#259; a mai reveni;\r\n</p><p id=\"v_b\"><b>b. </b> rezisten&#355;a asem&#259;n&#259;toare ap&#259;s&#259;rii pe un arc, pe care o întâmpin&#259; pedala;\r\n</p><p id=\"v_c\"><b>c. </b> blocarea pedalei de frân&#259;.</p>', 'nu', 'da', 'nu', ''),
(131, 'Care dintre urm&#259;toarele obliga&#355;ii v&#259; revin la întâlnirea indicatorului al&#259;turat, în timp ce conduce&#355;i un autocamion?', 'uploads_c/1574.jpg', '<p id=\"v_a\"><b>a. </b> s&#259; acorda&#355;i prioritate de trecere numai autocamioanelor &#351;i autobuzelor care se apropie din sens invers;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; acorda&#355;i prioritate de trecere tuturor vehiculelor care se apropie din sens invers;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; circula&#355;i cu aten&#355;ie sporit&#259;, fiind un sector de drum îngustat intens circulat de autocamioane &#351;i autobuze.</p>', 'nu', 'da', 'nu', ''),
(132, 'Care dintre indicatoarele al&#259;turate interzice accesul autocamioanelor care transport&#259; înc&#259;rc&#259;turi u&#351;or inflamabile?', 'uploads_c/1459.jpg', '<p id=\"v_a\"><b>a. </b> indicatorul 1;\r\n</p><p id=\"v_b\"><b>b. </b> indicatorul 2;\r\n</p><p id=\"v_c\"><b>c. </b> indicatorul 3.</p>', 'da', 'nu', 'nu', ''),
(133, 'Viteza maxim&#259; admis&#259; pe drumurile na&#355;ionale europene (E) din afara localit&#259;&#355;ilor, pentru autovehiculele din subcategoria C1, este de:', '', '<p id=\"v_a\"><b>a. </b> 110 km/h;\r\n</p><p id=\"v_b\"><b>b. </b> 100 km/h;\r\n</p><p id=\"v_c\"><b>c. </b> 80 km/h.</p>', 'nu', 'nu', 'da', ''),
(134, 'Încetinitorul electromagnetic este mai eficient decât cel de pe e&#351;apament, mai ales la:', '', '<p id=\"v_a\"><b>a. </b> viteze ridicate;\r\n</p><p id=\"v_b\"><b>b. </b> opriri neprev&#259;zute;\r\n</p><p id=\"v_c\"><b>c. </b> viteze sc&#259;zute.</p>', 'da', 'nu', 'nu', ''),
(135, 'În ce pozi&#355;ie a cheii de contact intr&#259; în func&#355;iune, pe timp de iarn&#259;, elementul de pornire?', '', '<p id=\"v_a\"><b>a. </b> deschis;\r\n</p><p id=\"v_b\"><b>b. </b> dezaburire;\r\n</p><p id=\"v_c\"><b>c. </b> preînc&#259;lzire.</p>', 'nu', 'nu', 'da', ''),
(136, 'Mecanismul de distribu&#355;ie asigur&#259;:', '', '<p id=\"v_a\"><b>a. </b> transmiterea uniform&#259; a puterii motorului la ro&#355;ile autovehiculului;\r\n</p><p id=\"v_b\"><b>b. </b> deschiderea &#351;i închiderea supapelor, la momente bine precizate, pentru evacuarea gazelor arse &#351;i umplerea cilindrilor cu amestec carburant;\r\n</p><p id=\"v_c\"><b>c. </b> distribu&#355;ia c&#259;tre fiecare cilindru a curentului de joas&#259; tensiune, necesar aprinderii amestecului carburant.</p>', 'nu', 'da', 'nu', ''),
(137, 'Care dintre indicatoarele al&#259;turate interzice accesul autocamioanelor cu remorc&#259;, a c&#259;ror lungime total&#259; este mai mare de 10 m?', 'uploads_c/1699.jpg', '<p id=\"v_a\"><b>a. </b> numai indicatorul 1;\r\n</p><p id=\"v_b\"><b>b. </b> ambele indicatoare;\r\n</p><p id=\"v_c\"><b>c. </b> niciunul.</p>', 'da', 'nu', 'nu', ''),
(138, 'Uzura neuniform&#259; a benzii de rulare a unui pneu pe toat&#259; l&#259;&#355;imea acesteia este produs&#259; de:', '', '<p id=\"v_a\"><b>a. </b> folosirea constant&#259; a autovehiculului înc&#259;rcat;\r\n</p><p id=\"v_b\"><b>b. </b> rularea cu vitez&#259; mare;\r\n</p><p id=\"v_c\"><b>c. </b> neechilibrarea ro&#355;ii respective.</p>', 'nu', 'nu', 'da', ''),
(139, 'Cum este recomandat s&#259; proceda&#355;i pentru a dep&#259;&#351;i, în condi&#355;ii de siguran&#355;&#259;, un tramvai oprit într-o sta&#355;ie cu refugiu?', '', '<p id=\"v_a\"><b>a. </b> s&#259; avertiza&#355;i sonor persoanele aflate în zona sta&#355;iei;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; semnaliza&#355;i din timp inten&#355;ia de dep&#259;&#351;ire a tramvaiului;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; reduce&#355;i viteza de deplasare.</p>', 'nu', 'nu', 'da', ''),
(140, 'Ciclul de func&#355;ionare a motorului cu ardere intern&#259; în patru timpi se realizeaz&#259;:', '', '<p id=\"v_a\"><b>a. </b> într-o rota&#355;ie complet&#259; a arborelui motor;\r\n</p><p id=\"v_b\"><b>b. </b> în dou&#259; rota&#355;ii complete ale arborelui motor;\r\n</p><p id=\"v_c\"><b>c. </b> în patru rota&#355;ii complete ale arborelui motor.</p>', 'nu', 'da', 'nu', ''),
(141, 'Factorii principali care ajut&#259; un conduc&#259;tor de autocamioane s&#259; piloteze preventiv sunt:', '', '<p id=\"v_a\"><b>a. </b> atitudinea pozitiv&#259; în trafic, calmul, cuno&#351;tin&#355;ele dobândite &#351;i polite&#355;ea;\r\n</p><p id=\"v_b\"><b>b. </b> iscusin&#355;a &#351;i priceperea de a-&#351;i impune calit&#259;&#355;ile fizice dobândite;\r\n</p><p id=\"v_c\"><b>c. </b> mijloacele de semnalizare rutier&#259; &#351;i agen&#355;ii de poli&#355;ie rutier&#259;.</p>', 'da', 'nu', 'nu', ''),
(142, 'Care dintre urm&#259;toarele cauze duce la manevrarea greoaie a direc&#355;iei?', '', '<p id=\"v_a\"><b>a. </b> jocul prea mare între pivot &#351;i fuzeta de direc&#355;ie;\r\n</p><p id=\"v_b\"><b>b. </b> jocul prea mare la articula&#355;ii;\r\n</p><p id=\"v_c\"><b>c. </b> deteriorarea, griparea sau ruginirea pivo&#355;ilor fuzetelor.</p>', 'nu', 'nu', 'da', ''),
(143, 'În cazul rul&#259;rii cu un pneu umflat sub presiunea indicat&#259; de constructor, uzura neuniform&#259; a benzii de rulare se produce:', '', '<p id=\"v_a\"><b>a. </b> pe margini;\r\n</p><p id=\"v_b\"><b>b. </b> pe mijloc;\r\n</p><p id=\"v_c\"><b>c. </b> pe toat&#259; l&#259;&#355;imea.</p>', 'da', 'nu', 'nu', ''),
(144, 'Care dintre indicatoarele al&#259;turate interzice accesul autocamioanelor a c&#259;ror mas&#259; maxim&#259; autorizat&#259; dep&#259;&#351;e&#351;te 7 t?', 'uploads_c/1585.jpg', '<p id=\"v_a\"><b>a. </b> numai indicatorul 1;\r\n</p><p id=\"v_b\"><b>b. </b> ambele indicatoare;\r\n</p><p id=\"v_c\"><b>c. </b> niciunul.</p>', 'da', 'nu', 'nu', ''),
(145, 'Uleiurile pentru motoarele cu aprindere prin compresie sunt simbolizate cu litera:', '', '<p id=\"v_a\"><b>a. </b> D;\n</p><p id=\"v_b\"><b>b. </b> C;\n</p><p id=\"v_c\"><b>c. </b> U.</p>', 'nu', 'da', 'nu', '');
INSERT INTO `intrebari_d` (`id`, `cerinta`, `imagine`, `variante`, `rasp_a`, `rasp_b`, `rasp_c`, `eticheta`) VALUES
(1, 'Conduc&#259;torii autobuzelor sunt obliga&#355;i s&#259; circule cu o vitez&#259; maxim&#259; de 30 km/h în localit&#259;&#355;i sau 50 km/h în afara acestora, în urm&#259;toarele situa&#355;ii:', '', '<p id=\"v_a\"><b>a. </b> pe drumurile cu denivel&#259;ri, semnalizate ca atare;\r\n</p><p id=\"v_b\"><b>b. </b> la trecerile pentru pietoni nesemaforizate, semnalizate prin indicatoare &#351;i marcaje;\r\n</p><p id=\"v_c\"><b>c. </b> la schimbarea direc&#355;iei de mers prin viraje.</p>', 'da', 'nu', 'da', ''),
(2, 'Care dintre cauzele de mai jos determina incalzirea excesiva a pneurilor?', '', '<p id=\"v_a\"><b>a. </b> folosirea unei presiuni exagerat de mari în pneuri fata de cea recomandata;\n</p><p id=\"v_b\"><b>b. </b> rularea cu autovehiculul supraincarcat;\n</p><p id=\"v_c\"><b>c. </b> rularea cu o presiune mult mai mica decat cea indicata de uzina constructoare.</p>', 'nu', 'da', 'da', ''),
(3, 'Care dintre urm&#259;toarele defec&#355;iuni creeaz&#259; joc mare la volan?', '', '<p id=\"v_a\"><b>a. </b> pinionul nu are joc fa&#355;&#259; de cremalier&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> uzura pronun&#355;at&#259; a angrenajelor din caseta de direc&#355;ie;\r\n</p><p id=\"v_c\"><b>c. </b> uzura pronun&#355;at&#259; a capetelor de bar&#259;.</p>', 'nu', 'da', 'da', ''),
(4, 'Ce trebuie s&#259; fac&#259; conduc&#259;torul unui autobuz, atunci când sesizeaz&#259; c&#259; urmeaz&#259; s&#259; fie dep&#259;&#351;it?', '', '<p id=\"v_a\"><b>a. </b> s&#259; pun&#259; în func&#355;iune luminile indicatoare de direc&#355;ie intermitente;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; nu m&#259;reasc&#259; viteza;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; se angajeze cât mai aproape de marginea din dreapta a p&#259;r&#355;ii carosabile.</p>', 'nu', 'da', 'da', ''),
(5, 'Unde este interzis&#259; sta&#355;ionarea?', '', '<p id=\"v_a\"><b>a. </b> în zona de ac&#355;iune a indicatoarelor „Aten&#355;ie, copii“ &#351;i „Drum cu denivel&#259;ri“;\r\n</p><p id=\"v_b\"><b>b. </b> în pant&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> pe drumurile publice cu o l&#259;&#355;ime mai mic&#259; de 6 m.</p>', 'nu', 'da', 'da', ''),
(6, 'Cum se elibereaz&#259; licen&#355;a de traseu pentru transportul rutier public?', '', '<p id=\"v_a\"><b>a. </b> în trafic interjude&#355;ean pentru fiecare curs&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> în trafic na&#355;ional pentru fiecare categorie de drum;\r\n</p><p id=\"v_c\"><b>c. </b> în trafic jude&#355;ean pentru fiecare traseu.</p>', 'da', 'nu', 'da', ''),
(7, 'Cum trebuie s&#259; proceda&#355;i când circula&#355;i pe timp de noapte &#351;i din sens opus se apropie un alt autovehicul?', '', '<p id=\"v_a\"><b>a. </b> s&#259; reduce&#355;i viteza;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; folosi&#355;i luminile de întâlnire de la cel pu&#355;in 200 m;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; v&#259; angaja&#355;i cât mai aproape de marginea din dreapta a drumului.</p>', 'da', 'da', 'nu', ''),
(8, 'Arborele cu came antreneaz&#259;:', '', '<p id=\"v_a\"><b>a. </b> pompa de ulei;\r\n</p><p id=\"v_b\"><b>b. </b> ruptorul-distribuitor;\r\n</p><p id=\"v_c\"><b>c. </b> ventilatorul radiatorului.</p>', 'da', 'da', 'nu', ''),
(9, 'Într-o intersec&#355;ie sunte&#355;i obligat:', '', '<p id=\"v_a\"><b>a. </b> s&#259; acorda&#355;i prioritate de trecere atunci când pietonii angaja&#355;i regulamentar în traversare se afl&#259; pe sensul dvs. de mers;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; circula&#355;i cu o vitez&#259; care s&#259; nu dep&#259;&#351;easc&#259; 30 km/h în localit&#259;&#355;i sau 50 km/h în afara acestora, dac&#259; drumul are cel mult o band&#259; pe sens, iar pietonii afla&#355;i pe trotuar în apropierea p&#259;r&#355;ii carosabile inten&#355;ioneaz&#259; s&#259; traverseze;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; circula&#355;i cât mai aproape de bordura din partea dreapt&#259; a trotuarului.</p>', 'da', 'da', 'nu', ''),
(10, 'În ce situa&#355;ii se reduce viteza în trafic?', '', '<p id=\"v_a\"><b>a. </b> în toate cazurile prev&#259;zute în legisla&#355;ia rutier&#259; pentru aplicarea legii privind circula&#355;ia pe drumurile publice;\n</p><p id=\"v_b\"><b>b. </b> preventiv, în toate situa&#355;iile în care se prevede o stare de pericol, care poate fi evitat&#259; rulând cu o vitez&#259; inferioar&#259;;\n</p><p id=\"v_c\"><b>c. </b> atunci când sunte&#355;i obosit, indispus sau suferi&#355;i de o afec&#355;iune care v&#259; diminueaz&#259; capacitatea de a conduce.</p>', 'da', 'da', 'nu', ''),
(11, 'Ce reprezint&#259; „perioada de odihn&#259; zilnic&#259; normal&#259;“?', '', '<p id=\"v_a\"><b>a. </b> intervalul neîntrerupt de cel pu&#355;in 11 ore de odihn&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> perioada de odihn&#259; realizat&#259; pe timpul nop&#355;ii, de cel pu&#355;in 12 ore;\r\n</p><p id=\"v_c\"><b>c. </b> odihna realizat&#259; în dou&#259; intervale de timp, primul de cel pu&#355;in 3 ore, iar al doilea de cel pu&#355;in 9 ore.</p>', 'da', 'nu', 'da', ''),
(12, 'Care dintre urm&#259;torii factori reprezint&#259; baza conduitei preventive?', '', '<p id=\"v_a\"><b>a. </b> cunoa&#351;terea normelor de circula&#355;ie &#351;i a regulilor de conducere a autobuzului;\r\n</p><p id=\"v_b\"><b>b. </b> capacitatea de a fi politicos &#351;i disciplinat în traficul rutier;\r\n</p><p id=\"v_c\"><b>c. </b> încadrarea în baremurile legale privind s&#259;n&#259;tatea.</p>', 'da', 'da', 'nu', ''),
(13, 'Circula&#355;ia autovehiculelor destinate transportului public de persoane se desf&#259;&#351;oar&#259; pe banda din partea dreapt&#259; a autostr&#259;zii. Exist&#259; &#351;i excep&#355;ii de la aceast&#259; regul&#259;?', '', '<p id=\"v_a\"><b>a. </b> nu;\r\n</p><p id=\"v_b\"><b>b. </b> da, atunci când se efectueaz&#259; dep&#259;&#351;irea;\r\n</p><p id=\"v_c\"><b>c. </b> da, atunci când semnalizarea rutier&#259; existent&#259; instituie o alt&#259; reglementare de utilizare a benzilor.</p>', 'nu', 'da', 'da', ''),
(14, 'Oprirea voluntar&#259; a unui autobuz este interzis&#259;:', '', '<p id=\"v_a\"><b>a. </b> pe drumurile cu un singur sens de circula&#355;ie;\r\n</p><p id=\"v_b\"><b>b. </b> pe drumurile cu o l&#259;&#355;ime mai mic&#259; de 6 m;\r\n</p><p id=\"v_c\"><b>c. </b> în locurile unde s-ar împiedica observarea unui indicator sau a unui semnal luminos.</p>', 'nu', 'nu', 'da', ''),
(15, 'Care dintre vehicule nu au prioritate de trecere?', '', '<p id=\"v_a\"><b>a. </b> vehiculele care coboar&#259; o pant&#259;, fa&#355;&#259; de vehiculele care urc&#259;, dac&#259; sunt de aceea&#351;i categorie;\r\n</p><p id=\"v_b\"><b>b. </b> vehiculele care se pun în mi&#351;care sau care ies din garaje, cur&#355;i &#351;i ganguri, fa&#355;&#259; de cele care circul&#259; pe drumul public;\r\n</p><p id=\"v_c\"><b>c. </b> vehiculele care execut&#259; virajul spre stânga, fa&#355;&#259; de cele care circul&#259; din sens opus.</p>', 'nu', 'da', 'da', ''),
(16, 'Care dintre urm&#259;toarele cauze determin&#259; m&#259;rirea jocului la volan?', '', '<p id=\"v_a\"><b>a. </b> uzura articula&#355;iilor sferice ale mecanismului de direc&#355;ie;\r\n</p><p id=\"v_b\"><b>b. </b> ac&#355;ionarea brusc&#259; a volanului în mod repetat;\r\n</p><p id=\"v_c\"><b>c. </b> sl&#259;birea fix&#259;rii casetei de direc&#355;ie.</p>', 'nu', 'nu', 'da', ''),
(17, 'În care dintre situa&#355;iile de mai jos este interzis&#259; oprirea autobuzelor pe drumurile publice?', '', '<p id=\"v_a\"><b>a. </b> pe partea carosabil&#259; a drumurilor na&#355;ionale;\r\n</p><p id=\"v_b\"><b>b. </b> la o distan&#355;&#259; mai mic&#259; de 25 m înainte &#351;i dup&#259; indicatorul sta&#355;iei pentru mijloacele de transport public de persoane;\r\n</p><p id=\"v_c\"><b>c. </b> la mai pu&#355;in de 50 m de cel mai apropiat col&#355; al intersec&#355;iei.</p>', 'nu', 'da', 'nu', ''),
(18, 'Care este rolul mecanismului de distribu&#355;ie al motoarelor cu aprindere prin compresie?', '', '<p id=\"v_a\"><b>a. </b> asigur&#259; p&#259;trunderea amestecului carburant în cilindri &#351;i evacuarea gazelor arse;\r\n</p><p id=\"v_b\"><b>b. </b> asigur&#259; p&#259;trunderea aerului în cilindrii motorului &#351;i evacuarea gazelor arse;\r\n</p><p id=\"v_c\"><b>c. </b> asigur&#259; densitatea optim&#259; a amestecului carburant.</p>', 'nu', 'da', 'nu', ''),
(19, 'În care dintre situa&#355;ii consumul de carburant al unui motor cre&#351;te?', '', '<p id=\"v_a\"><b>a. </b> atunci când motorul nu atinge temperatura de func&#355;ionare;\r\n</p><p id=\"v_b\"><b>b. </b> atunci când fumul de e&#351;apament este de culoare neagr&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> atunci când motorul func&#355;ioneaz&#259; cu întreruperi.</p>', 'da', 'da', 'da', ''),
(20, 'Cum depista&#355;i c&#259; ambreiajul nu decupleaz&#259;?', '', '<p id=\"v_a\"><b>a. </b> garniturile de fric&#355;iune se supraînc&#259;lzesc &#351;i degaj&#259; miros specific;\r\n</p><p id=\"v_b\"><b>b. </b> autovehiculul se opre&#351;te sau merge cu trepida&#355;ii;\r\n</p><p id=\"v_c\"><b>c. </b> apar zgomote puternice de pinioane la schimbarea vitezelor.</p>', 'nu', 'nu', 'da', ''),
(21, 'Sl&#259;birea curelei de antrenare a ventilatorului &#351;i a pompei de ap&#259; determin&#259;:', '', '<p id=\"v_a\"><b>a. </b> mic&#351;orarea presiunii uleiului în carter;\r\n</p><p id=\"v_b\"><b>b. </b> deteriorarea curelei prin patinare;\r\n</p><p id=\"v_c\"><b>c. </b> supraînc&#259;lzirea motorului.</p>', 'nu', 'da', 'da', ''),
(22, 'Ce func&#355;ii poate avea un bandaj de tifon aplicat pe o ran&#259; deschis&#259;?', '', '<p id=\"v_a\"><b>a. </b> protejeaz&#259; împotriva infect&#259;rii r&#259;nii;\r\n</p><p id=\"v_b\"><b>b. </b> opre&#351;te hemoragia;\r\n</p><p id=\"v_c\"><b>c. </b> scade tensiunea arterial&#259;.</p>', 'da', 'da', 'nu', ''),
(23, 'În care dintre urm&#259;toarele situa&#355;ii conduc&#259;torul unui autobuz are obliga&#355;ia de a reduce viteza?', '', '<p id=\"v_a\"><b>a. </b> pe sectoarele de drum îngustat;\r\n</p><p id=\"v_b\"><b>b. </b> când este dep&#259;&#351;it de un alt autovehicul;\r\n</p><p id=\"v_c\"><b>c. </b> la trecerea la nivel cu calea ferat&#259;.</p>', 'nu', 'nu', 'da', ''),
(24, 'Care dintre urm&#259;toarele piese fac parte din mecanismul de distribu&#355;ie?', '', '<p id=\"v_a\"><b>a. </b> carburatorul;\r\n</p><p id=\"v_b\"><b>b. </b> arborele cotit;\r\n</p><p id=\"v_c\"><b>c. </b> arborele cu came.</p>', 'nu', 'nu', 'da', ''),
(25, 'Avantajele func&#355;ion&#259;rii motorului cu gaz petrolier lichefiat (GPL) sunt:', '', '<p id=\"v_a\"><b>a. </b> arderea amestecului carburant este mai complet&#259;, reducându-se astfel poluarea;\r\n</p><p id=\"v_b\"><b>b. </b> lubrifiantul de pe cilindri nu se spal&#259;, prelungindu-se astfel durata de folosire a uleiului;\r\n</p><p id=\"v_c\"><b>c. </b> puterea motorului este mai mare cu aproximativ 8%.</p>', 'da', 'da', 'nu', ''),
(26, 'Viteza maxim&#259; admis&#259; pe drumurile na&#355;ionale, altele decât cele europene (E), pentru autovehiculele din categoria D &#351;i subcategoria D1, este de:', '', '<p id=\"v_a\"><b>a. </b> 80 km/h;\r\n</p><p id=\"v_b\"><b>b. </b> 90 km/h;\r\n</p><p id=\"v_c\"><b>c. </b> 100 km/h.</p>', 'da', 'nu', 'nu', ''),
(27, 'Cum se realizeaz&#259; transportul rutier public de persoane cu autobuzul?', '', '<p id=\"v_a\"><b>a. </b> prin servicii regulate;\r\n</p><p id=\"v_b\"><b>b. </b> prin servicii regulate speciale;\r\n</p><p id=\"v_c\"><b>c. </b> prin servicii ocazionale.</p>', 'da', 'da', 'da', ''),
(28, 'Când trebuie scoas&#259; din vehicul diagrama tahograf?', '', '<p id=\"v_a\"><b>a. </b> ori de câte ori este nevoie;\r\n</p><p id=\"v_b\"><b>b. </b> nu înainte de sfâr&#351;itul perioadei zilnice de lucru;\r\n</p><p id=\"v_c\"><b>c. </b> pe orice perioad&#259; a zilei, dac&#259; acest lucru a fost autorizat (control rutier, schimbarea vehiculului, defectarea aparatului).</p>', 'nu', 'da', 'da', ''),
(29, 'Care sunt datele pe care le înregistreaz&#259; &#351;i/sau stocheaz&#259; în mod automat sau semiautomat tahograful?', '', '<p id=\"v_a\"><b>a. </b> distan&#355;a parcurs&#259; de vehicul &#351;i viteza de deplasare a acestuia;\r\n</p><p id=\"v_b\"><b>b. </b> durata orelor de conducere, pauzele &#351;i perioadele zilnice de odihn&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> timpii disponibili, precum &#351;i al&#355;i timpi de munc&#259;.</p>', 'da', 'da', 'da', ''),
(30, 'Care dintre urm&#259;toarele cauze determin&#259; sulfatarea pl&#259;cilor bateriei de acumulatori?', '', '<p id=\"v_a\"><b>a. </b> scurtcircuitarea acumulatorului în interiorul elementelor;\r\n</p><p id=\"v_b\"><b>b. </b> prezen&#355;a impurit&#259;&#355;ilor în electrolit;\r\n</p><p id=\"v_c\"><b>c. </b> nivelul sc&#259;zut al electrolitului sub partea superioar&#259; a pl&#259;cilor.</p>', 'da', 'nu', 'nu', ''),
(31, 'Care este circuitul de joas&#259; tensiune al instala&#355;iei de aprindere?', '', '<p id=\"v_a\"><b>a. </b> bateria de acumulatori, contactul, înf&#259;&#351;urarea secundar&#259; a bobinei de induc&#355;ie, ruptorul-distribuitor, masa;\r\n</p><p id=\"v_b\"><b>b. </b> bateria de acumulatori, contactul, înf&#259;&#351;urarea primar&#259; a bobinei de induc&#355;ie;\r\n</p><p id=\"v_c\"><b>c. </b> bateria de acumulatori, voltmetrul, ampermetrul, înf&#259;&#351;urarea secundar&#259; a bobinei de induc&#355;ie.</p>', 'nu', 'da', 'nu', ''),
(32, 'Conduc&#259;torului auto i se interzice?', '', '<p id=\"v_a\"><b>a. </b> s&#259; arunce din vehicul obiecte, substan&#355;e sau alte bunuri;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; deschid&#259; u&#351;ile, dac&#259; vehiculul este oprit sau sta&#355;ionat pe partea carosabil&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; foloseasc&#259; instala&#355;ia de sonorizare cu care este echipat autoturismul.</p>', 'da', 'nu', 'nu', ''),
(33, 'Nu au prioritate de trecere:', '', '<p id=\"v_a\"><b>a. </b> autovehiculele din serviciile regulate de transport public care se pun în mi&#351;care din sta&#355;ia prev&#259;zut&#259; cu alveol&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> vehiculele care urmeaz&#259; s&#259; intre într-o intersec&#355;ie cu sens giratoriu, fa&#355;&#259; de vehiculele care circul&#259; printr-o asemenea intersec&#355;ie;\r\n</p><p id=\"v_c\"><b>c. </b> vehiculele care execut&#259; manevra de întoarcere.</p>', 'nu', 'da', 'da', ''),
(34, 'Oprirea este interzis&#259;:', '', '<p id=\"v_a\"><b>a. </b> în zona de ac&#355;iune a indicatorului „Drum îngustat“;\r\n</p><p id=\"v_b\"><b>b. </b> în zona de ac&#355;iune a indicatoarelor care interzic dep&#259;&#351;irea;\r\n</p><p id=\"v_c\"><b>c. </b> în dreptul altui vehicul sta&#355;ionat, dac&#259; prin aceasta se stânjene&#351;te circula&#355;ia a dou&#259; vehicule venind din sensuri opuse.</p>', 'da', 'da', 'da', ''),
(35, 'Ce obliga&#355;ii revin conduc&#259;torului unui autobuz la plecarea din sta&#355;ii?', '', '<p id=\"v_a\"><b>a. </b> s&#259; nu porneasc&#259; din sta&#355;ie înainte de urcarea &#351;i de coborârea c&#259;l&#259;torilor &#351;i înainte de a lua m&#259;surile de precau&#355;ie necesare pentru evitarea oric&#259;rei situa&#355;ii periculoase previzibile;\r\n</p><p id=\"v_b\"><b>b. </b> nu are nicio obliga&#355;ie, deoarece autobuzul face parte din categoria vehiculelor destinate serviciilor regulate de transport public de persoane;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; semnalizeze inten&#355;ia de a se pune în mi&#351;care.</p>', 'da', 'nu', 'da', ''),
(36, 'Exploziile caden&#355;ate în carburator sunt cauzate de:', '', '<p id=\"v_a\"><b>a. </b> supapele de evacuare defecte;\r\n</p><p id=\"v_b\"><b>b. </b> supapele de admisie defecte;\r\n</p><p id=\"v_c\"><b>c. </b> garnitura de chiulas&#259; rupt&#259; între doi cilindri.</p>', 'nu', 'da', 'nu', ''),
(37, 'Transportarea victimelor la spital se poate realiza cu:', '', '<p id=\"v_a\"><b>a. </b> o ambulan&#355;&#259; special dotat&#259; în acest sens;\r\n</p><p id=\"v_b\"><b>b. </b> o ambulan&#355;&#259; înso&#355;it&#259; de un medic;\r\n</p><p id=\"v_c\"><b>c. </b> vehiculul care a provocat accidentul, dac&#259; starea victimei permite acest lucru.</p>', 'da', 'da', 'da', ''),
(38, 'Care este limita maxim&#259; de vitez&#259; în afara localit&#259;&#355;ilor, pentru categoria D &#351;i subcategoria D1, pe drumurile na&#355;ionale europene (E)?', '', '<p id=\"v_a\"><b>a. </b> 80 km/h;\r\n</p><p id=\"v_b\"><b>b. </b> 90 km/h;\r\n</p><p id=\"v_c\"><b>c. </b> 100 km/h.</p>', 'nu', 'da', 'nu', ''),
(39, 'Injec&#355;ia de benzin&#259; monopunct se realizeaz&#259; în:', '', '<p id=\"v_a\"><b>a. </b> interiorul camerei de nivel constant a carburatorului;\r\n</p><p id=\"v_b\"><b>b. </b> colectorul de admisie;\r\n</p><p id=\"v_c\"><b>c. </b> poarta supapelor de evacuare.</p>', 'nu', 'da', 'nu', ''),
(40, 'Ce func&#355;ie are mecanismul de distribu&#355;ie?', '', '<p id=\"v_a\"><b>a. </b> faciliteaz&#259; formarea curentului electric de înalt&#259; tensiune &#351;i distribuirea acestuia la bujii;\r\n</p><p id=\"v_b\"><b>b. </b> faciliteaz&#259; uniformizarea func&#355;ion&#259;rii motorului prin acumularea surplusului de energie debitat în timpul motor &#351;i folosirea acestuia în ceilal&#355;i timpi;\r\n</p><p id=\"v_c\"><b>c. </b> asigur&#259; p&#259;trunderea amestecului carburant în cilindri &#351;i evacuarea gazelor arse.</p>', 'nu', 'nu', 'da', ''),
(41, 'Comportamentul ecologic în conducerea unui autovehicul presupune:', '', '<p id=\"v_a\"><b>a. </b> planificarea traseului pentru a economisi timp &#351;i combustibil;\r\n</p><p id=\"v_b\"><b>b. </b> evitarea zonelor cu circula&#355;ie dens&#259;, cu lucr&#259;ri pe carosabil sau cu teren accidentat;\r\n</p><p id=\"v_c\"><b>c. </b> vitez&#259; &#351;i accelera&#355;ie mai mari, pentru a scurta timpul de deplasare.</p>', 'da', 'da', 'nu', ''),
(42, 'La motorul în patru timpi, tura&#355;ia arborelui cu came, fa&#355;&#259; de tura&#355;ia arborelui cotit, este:', '', '<p id=\"v_a\"><b>a. </b> de dou&#259; ori mai mic&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> de dou&#259; ori mai mare;\r\n</p><p id=\"v_c\"><b>c. </b> egal&#259;.</p>', 'da', 'nu', 'nu', ''),
(43, 'Prin „raport de compresie“ se în&#355;elege:', '', '<p id=\"v_a\"><b>a. </b> raportul dintre volumul tuturor cilindrilor &#351;i volumul pistoanelor;\r\n</p><p id=\"v_b\"><b>b. </b> raportul dintre volumul camerei de ardere &#351;i volumul cilindrului;\r\n</p><p id=\"v_c\"><b>c. </b> raportul dintre volumul cilindrului &#351;i volumul camerei de ardere.</p>', 'nu', 'nu', 'da', ''),
(44, 'Cum trebuie s&#259; fie atelele pentru imobilizarea unei fracturi?', '', '<p id=\"v_a\"><b>a. </b> suficient de lungi pentru a acoperi în întregime atât zona de deasupra, cât &#351;i cea de sub fractur&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> rigide;\r\n</p><p id=\"v_c\"><b>c. </b> u&#351;oare.</p>', 'da', 'da', 'da', ''),
(45, 'Unghiul de c&#259;dere prea mare la o roat&#259; duce la uzura uniform&#259; a pneurilor:', '', '<p id=\"v_a\"><b>a. </b> pe marginea exterioar&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> pe marginea interioar&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> pe toat&#259; l&#259;&#355;imea.</p>', 'da', 'nu', 'nu', ''),
(46, 'În cazul motoarelor cu aprindere prin compresie, emisia fumului de culoare neagr&#259; este o urmare a:', '', '<p id=\"v_a\"><b>a. </b> regl&#259;rii incorecte a pompei de injec&#355;ie;\r\n</p><p id=\"v_b\"><b>b. </b> uzurii segmen&#355;ilor;\r\n</p><p id=\"v_c\"><b>c. </b> jocului mic dintre supape &#351;i culbutori.</p>', 'da', 'nu', 'nu', ''),
(47, 'Care dintre supapele mecanismului de distribu&#355;ie se uzeaz&#259; mai repede?', '', '<p id=\"v_a\"><b>a. </b> supapele de admisie;\r\n</p><p id=\"v_b\"><b>b. </b> supapele de evacuare;\r\n</p><p id=\"v_c\"><b>c. </b> ambele supape se uzeaz&#259; la fel în cazul unui motor bine între&#355;inut.</p>', 'nu', 'da', 'nu', ''),
(48, 'Când se folosesc luminile de avarie?', '', '<p id=\"v_a\"><b>a. </b> când autobuzul a r&#259;mas în pan&#259; sau a fost avariat, fiind imobilizat pe carosabil;\r\n</p><p id=\"v_b\"><b>b. </b> când autobuzul se deplaseaz&#259; foarte lent &#351;i constituie un pericol pentru ceilal&#355;i participan&#355;i la trafic;\r\n</p><p id=\"v_c\"><b>c. </b> când autobuzul este remorcat.</p>', 'da', 'da', 'da', ''),
(49, 'Ce categorii de vehicule au obliga&#355;ia de a folosi luminile de întâlnire pe timp de zi?', '', '<p id=\"v_a\"><b>a. </b> motocicletele &#351;i mopedele;\r\n</p><p id=\"v_b\"><b>b. </b> autovehiculele care înso&#355;esc coloane militare sau transport&#259; grupuri de persoane;\r\n</p><p id=\"v_c\"><b>c. </b> autovehiculele care tracteaz&#259; alte vehicule sau care transport&#259; produse periculoase.</p>', 'da', 'da', 'da', ''),
(50, 'Care dintre cauzele de mai jos provoac&#259; oscila&#355;ia ro&#355;ilor directoare la vitez&#259; redus&#259;?', '', '<p id=\"v_a\"><b>a. </b> griparea articula&#355;iilor mecanismului;\r\n</p><p id=\"v_b\"><b>b. </b> jocul prea mare între organele casetei de direc&#355;ie;\r\n</p><p id=\"v_c\"><b>c. </b> griparea pivotului de fuzet&#259;.</p>', 'nu', 'da', 'nu', ''),
(51, 'Este permis&#259; sta&#355;ionarea autobuzelor pe timpul nop&#355;ii pe partea carosabil&#259; a unui drum comunal neiluminat?', '', '<p id=\"v_a\"><b>a. </b> da, dac&#259; se aprind luminile de pozi&#355;ie sau de sta&#355;ionare;\r\n</p><p id=\"v_b\"><b>b. </b> nu, întrucât în toate cazurile se interzice pe timpul nop&#355;ii sta&#355;ionarea pe partea carosabil&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> da, dac&#259; în afara luminilor de pozi&#355;ie, prezen&#355;a autobuzului este semnalizat&#259; &#351;i cu ajutorul triunghiurilor reflectorizante.</p>', 'da', 'nu', 'nu', ''),
(52, 'Amestecul carburant foarte bogat provoac&#259; rateuri în:', '', '<p id=\"v_a\"><b>a. </b> galeria de evacuare sau în toba de e&#351;apament;\r\n</p><p id=\"v_b\"><b>b. </b> galeria de admisie;\r\n</p><p id=\"v_c\"><b>c. </b> carburator sau în pompa de benzin&#259;.</p>', 'da', 'nu', 'nu', ''),
(53, 'Ce trebuie s&#259; ave&#355;i în vedere la scoaterea persoanelor accidentate din autovehicul?', '', '<p id=\"v_a\"><b>a. </b> ca opera&#355;iunea s&#259; se desf&#259;&#351;oare rapid &#351;i f&#259;r&#259; s&#259; provoace alte traumatisme;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; nu se agraveze leziunile;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; nu se îndep&#259;rteze indiciile care ar putea ajuta la explicarea modului în care s-a produs accidentul.</p>', 'nu', 'da', 'nu', ''),
(54, 'Conduc&#259;torul unui autobuz are voie s&#259; dep&#259;&#351;easc&#259; un alt autobuz oprit în sta&#355;ie?', '', '<p id=\"v_a\"><b>a. </b> da, numai dac&#259; l&#259;&#355;imea drumului este de cel pu&#355;in 10 m;\r\n</p><p id=\"v_b\"><b>b. </b> da, numai dac&#259; autobuzul dep&#259;&#351;it este oprit într-o sta&#355;ie prev&#259;zut&#259; cu alveol&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> da, legea nu interzice.</p>', 'nu', 'nu', 'da', ''),
(55, 'Amestecul carburant foarte s&#259;rac provoac&#259; rateuri în:', '', '<p id=\"v_a\"><b>a. </b> galeria de admisie;\r\n</p><p id=\"v_b\"><b>b. </b> galeria de evacuare;\r\n</p><p id=\"v_c\"><b>c. </b> toba de e&#351;apament.</p>', 'da', 'nu', 'nu', ''),
(56, 'Care este rolul principal al supapei de refulare a elementului pompei de injec&#355;ie?', '', '<p id=\"v_a\"><b>a. </b> s&#259; împiedice picurarea motorinei din injector, la sfâr&#351;itul injec&#355;iei;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; împiedice reîntoarcerea în pompa de injec&#355;ie a surplusului de motorin&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; creasc&#259; presiunea motorinei la sfâr&#351;itul injec&#355;iei.</p>', 'da', 'nu', 'nu', ''),
(57, 'Care dintre piesele urm&#259;toare ac&#355;ioneaz&#259; pompa de înalt&#259; presiune din componen&#355;a sistemului de direc&#355;ie cu servomecanism hidraulic?', '', '<p id=\"v_a\"><b>a. </b> axul cardanic;\r\n</p><p id=\"v_b\"><b>b. </b> arborele cotit al compresorului de aer;\r\n</p><p id=\"v_c\"><b>c. </b> arborele cu came.</p>', 'nu', 'da', 'nu', ''),
(58, 'Pompa de înalt&#259; presiune din componen&#355;a sistemului de direc&#355;ie cu servomecanism hidraulic este antrenat&#259; de:', '', '<p id=\"v_a\"><b>a. </b> arborele cotit al motorului;\r\n</p><p id=\"v_b\"><b>b. </b> axul secundar al cutiei de viteze;\r\n</p><p id=\"v_c\"><b>c. </b> axa cu came.</p>', 'da', 'nu', 'nu', ''),
(59, 'Care sunt legitima&#355;iile de c&#259;l&#259;torie individuale pentru serviciile regulate de transport de persoane?', '', '<p id=\"v_a\"><b>a. </b> biletele;\r\n</p><p id=\"v_b\"><b>b. </b> abonamentele;\r\n</p><p id=\"v_c\"><b>c. </b> legitima&#355;iile speciale.</p>', 'da', 'da', 'da', ''),
(60, 'Griparea pistonului unui element al pompei de injec&#355;ie poate conduce la:', '', '<p id=\"v_a\"><b>a. </b> spargerea carcasei pompei de injec&#355;ie;\r\n</p><p id=\"v_b\"><b>b. </b> supraturarea motorului;\r\n</p><p id=\"v_c\"><b>c. </b> fisurarea arborelui cu came al pompei de injec&#355;ie.</p>', 'nu', 'da', 'nu', ''),
(61, 'În care dintre situa&#355;ii oprirea este interzis&#259;?', '', '<p id=\"v_a\"><b>a. </b> în zona de ac&#355;iune a indicatorului care interzice dep&#259;&#351;irea;\r\n</p><p id=\"v_b\"><b>b. </b> pe str&#259;zile unde circula&#355;ia se desf&#259;&#351;oar&#259; în sens unic;\r\n</p><p id=\"v_c\"><b>c. </b> în dreptul altui vehicul care se afl&#259; oprit, dac&#259; prin aceasta se stânjene&#351;te circula&#355;ia a dou&#259; vehicule venind din sensuri opuse.</p>', 'da', 'nu', 'da', ''),
(62, 'Care dintre urm&#259;toarele cauze determin&#259; revenirea greoaie a ro&#355;ilor directoare pe direc&#355;ia înainte?', '', '<p id=\"v_a\"><b>a. </b> articula&#355;iile direc&#355;iei nu sunt gresate;\r\n</p><p id=\"v_b\"><b>b. </b> pneurile ro&#355;ilor directoare au o presiune superioar&#259; celei indicate de constructor;\r\n</p><p id=\"v_c\"><b>c. </b> jocul din angrenajul casetei de direc&#355;ie este prea mare.</p>', 'da', 'nu', 'nu', ''),
(63, 'Conduita ecologic&#259; în conducerea unui autovehicul înseamn&#259;:', '', '<p id=\"v_a\"><b>a. </b> s&#259; nu polua&#355;i fonic mediul înconjur&#259;tor, folosind sistemul de sonorizare la putere maxim&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; între&#355;ine&#355;i în permanen&#355;&#259; autovehiculul curat, prin sp&#259;lare &#351;i degresare zilnic&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; fi&#355;i calm &#351;i s&#259; anticipa&#355;i situa&#355;iile astfel încât s&#259; evita&#355;i accelerarea &#351;i frânarea brusc&#259;.</p>', 'nu', 'nu', 'da', ''),
(64, 'Care dintre urm&#259;toarele defec&#355;iuni ale instala&#355;iei de alimentare a motorului cu aprindere prin comprimare se manifest&#259; prin faptul c&#259; motorul merge neregulat, „bate“ &#351;i scoate mult fum?', '', '<p id=\"v_a\"><b>a. </b> desfacerea piuli&#355;elor conductelor de înalt&#259; presiune;\r\n</p><p id=\"v_b\"><b>b. </b> uzura injectoarelor;\r\n</p><p id=\"v_c\"><b>c. </b> ruperea arcului de reglare a presiunii de injectare.</p>', 'nu', 'da', 'nu', ''),
(65, 'Anvelopele ro&#355;ilor directoare se uzeaz&#259; excesiv pe partea interioar&#259; a benzii de rulare, dac&#259;:', '', '<p id=\"v_a\"><b>a. </b> divergen&#355;a acestora este m&#259;rit&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> convergen&#355;a acestora este m&#259;rit&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> s-a rulat îndelungat cu presiunea sc&#259;zut&#259; în pneuri.</p>', 'da', 'nu', 'nu', ''),
(66, 'Apari&#355;ia unor b&#259;t&#259;i sau zgomote la viraje sau la schimbarea brusc&#259; a vitezei de rulare se datoreaz&#259;:', '', '<p id=\"v_a\"><b>a. </b> defec&#355;iunilor la mecanismul de direc&#355;ie;\r\n</p><p id=\"v_b\"><b>b. </b> în&#355;epenirii sateli&#355;ilor diferen&#355;ialului pe axe;\r\n</p><p id=\"v_c\"><b>c. </b> înc&#259;rc&#259;rii neuniforme a autovehiculului.</p>', 'nu', 'da', 'nu', ''),
(67, '„Perioada de odihn&#259; zilnic&#259; redus&#259;“ se refer&#259; la:', '', '<p id=\"v_a\"><b>a. </b> perioada de somn de minimum 6 ore;\r\n</p><p id=\"v_b\"><b>b. </b> orice perioad&#259; de odihn&#259; de cel pu&#355;in 9 ore, dar care s&#259; nu dep&#259;&#351;easc&#259; 11 ore;\r\n</p><p id=\"v_c\"><b>c. </b> intervalul neîntrerupt de cel pu&#355;in 11 ore de odihn&#259;.</p>', 'nu', 'da', 'nu', ''),
(68, 'În care dintre situa&#355;iile prezentate mai jos nu este permis&#259; oprirea voluntar&#259; a autobuzelor?', '', '<p id=\"v_a\"><b>a. </b> la o distan&#355;&#259; mai mic&#259; de 50 m de trecerea la nivel cu calea ferat&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> pe pistele obligatorii pentru pietoni;\r\n</p><p id=\"v_c\"><b>c. </b> pe platforma c&#259;ii ferate sau de tramvai, dac&#259; prin aceasta este stânjenit&#259; circula&#355;ia vehiculelor pe &#351;ine.</p>', 'da', 'da', 'da', ''),
(69, 'Conduc&#259;torul autobuzului din imaginea al&#259;turat&#259; este surprins de schimbarea semnalului semaforului, dup&#259; ce a p&#259;truns în intersec&#355;ie. Ce trebuie s&#259; fac&#259; în acest caz?', 'uploads_d/1969.jpg', '<p id=\"v_a\"><b>a. </b> s&#259;-&#351;i continue deplasarea, deoarece are prioritate fa&#355;&#259; de autovehiculele care urmeaz&#259; s&#259; intre în intersec&#355;ie;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; opreasc&#259; imediat;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259;-&#351;i continue deplasarea cu vitez&#259; redus&#259;, deoarece blocheaz&#259; intersec&#355;ia.</p>', 'nu', 'nu', 'da', ''),
(70, 'Uzura accentuat&#259; a p&#259;r&#355;ii interioare a benzii de rulare a pneurilor directoare denot&#259;:', '', '<p id=\"v_a\"><b>a. </b> pozi&#355;ia excesiv convergent&#259; a acestor ro&#355;i;\r\n</p><p id=\"v_b\"><b>b. </b> pozi&#355;ia excesiv divergent&#259; a acestor ro&#355;i;\r\n</p><p id=\"v_c\"><b>c. </b> rularea cu viteze foarte mari &#351;i cu presiune prea mare în pneuri.</p>', 'nu', 'da', 'nu', ''),
(71, 'Ro&#355;ile directoare ale autovehiculelor cu trac&#355;iune pe spate sunt, de regul&#259;:', '', '<p id=\"v_a\"><b>a. </b> divergente sau paralele;\r\n</p><p id=\"v_b\"><b>b. </b> convergente sau paralele;\r\n</p><p id=\"v_c\"><b>c. </b> divergente.</p>', 'nu', 'da', 'nu', ''),
(72, 'Uzura neuniform&#259; a benzii de rulare a unui pneu pe toat&#259; l&#259;&#355;imea acesteia este produs&#259; de:', '', '<p id=\"v_a\"><b>a. </b> folosirea constant&#259; a autovehiculului înc&#259;rcat;\r\n</p><p id=\"v_b\"><b>b. </b> rularea cu vitez&#259; mare;\r\n</p><p id=\"v_c\"><b>c. </b> neechilibrarea ro&#355;ii respective.</p>', 'nu', 'nu', 'da', ''),
(73, 'Care dintre obliga&#355;iile de mai jos revin conduc&#259;torului de autobuz la trecerea prin intersec&#355;iile cu circula&#355;ie nedirijat&#259; din localit&#259;&#355;i?', '', '<p id=\"v_a\"><b>a. </b> s&#259; opreasc&#259;, chiar dac&#259; nu exist&#259; sta&#355;ie, pentru a permite urcarea &#351;i coborârea c&#259;l&#259;torilor;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; circule cu o vitez&#259; care s&#259; nu dep&#259;&#351;eascã 30 km/h;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; reduc&#259; viteza, pentru a nu fi implicat în accidente rutiere.</p>', 'nu', 'da', 'nu', ''),
(74, 'Care dintre urm&#259;toarele defec&#355;iuni ale instala&#355;iei de r&#259;cire duce la deteriorarea prematur&#259; a motorului?', '', '<p id=\"v_a\"><b>a. </b> deteriorarea rulmen&#355;ilor pompei de ap&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> înfundarea instala&#355;iei de înc&#259;lzire;\r\n</p><p id=\"v_c\"><b>c. </b> blocarea termostatului în pozi&#355;ia „deschis“.</p>', 'nu', 'nu', 'da', ''),
(75, 'Ce se în&#355;elege prin „valoarea termic&#259;“ a bujiei?', '', '<p id=\"v_a\"><b>a. </b> timpul în secunde, pân&#259; când bujia ajunge la temperatura de autocur&#259;&#355;are a electrozilor;\r\n</p><p id=\"v_b\"><b>b. </b> capacitatea bujiei de a lucra mai bine la temperaturi înalte;\r\n</p><p id=\"v_c\"><b>c. </b> însu&#351;irea bujiei de a conecta &#351;i deconecta func&#355;ionarea termostatului.</p>', 'da', 'nu', 'nu', ''),
(76, 'C&#259;ile respiratorii pot fi deblocate:', '', '<p id=\"v_a\"><b>a. </b> numai în unit&#259;&#355;i medicale, folosindu-se instrumente speciale;\r\n</p><p id=\"v_b\"><b>b. </b> la locul accidentului, prin interven&#355;ii specifice;\r\n</p><p id=\"v_c\"><b>c. </b> indiferent unde, dac&#259; victima suport&#259; aceast&#259; interven&#355;ie.</p>', 'nu', 'da', 'nu', ''),
(77, 'Primul ajutor în caz de traumatism al capului const&#259; în:', '', '<p id=\"v_a\"><b>a. </b> aplicarea unor comprese reci pe frunte &#351;i bandajarea p&#259;r&#355;ilor lovite;\r\n</p><p id=\"v_b\"><b>b. </b> observarea respira&#355;iei &#351;i a circula&#355;iei sanguine &#351;i aplecarea capului c&#259;tre b&#259;rbie;\r\n</p><p id=\"v_c\"><b>c. </b> bandajarea fracturii deschise, deschiderea c&#259;ilor respiratorii dac&#259; se impune &#351;i culcarea victimei pe o targ&#259; plan&#259; pentru transport.</p>', 'nu', 'nu', 'da', ''),
(78, 'Este interzis&#259; sta&#355;ionarea unui autobuz pe un drum public cu o l&#259;&#355;ime mai mic&#259; de 6 m?', '', '<p id=\"v_a\"><b>a. </b> da;\r\n</p><p id=\"v_b\"><b>b. </b> nu;\r\n</p><p id=\"v_c\"><b>c. </b> legal, nu este prev&#259;zut.</p>', 'da', 'nu', 'nu', ''),
(79, 'Ce obliga&#355;ii are conduc&#259;torul unui autobuz, atunci când sta&#355;ioneaz&#259;, pe timp de zi, pe partea carosabil&#259; a unui drum, în condi&#355;ii de cea&#355;&#259; dens&#259;?', '', '<p id=\"v_a\"><b>a. </b> s&#259; pun&#259; în func&#355;iune luminile de pozi&#355;ie sau de sta&#355;ionare;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; pun&#259; în func&#355;iune lumina galben&#259; intermitent&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; men&#355;in&#259; aprins&#259; lumina de întâlnire.</p>', 'da', 'nu', 'nu', ''),
(80, 'Uzura pronun&#355;at&#259; a p&#259;r&#355;ii exterioare a benzii de rulare a pneurilor directoare este cauzat&#259; de:', '', '<p id=\"v_a\"><b>a. </b> unghiul de convergen&#355;&#259; prea mare;\r\n</p><p id=\"v_b\"><b>b. </b> unghiul de divergen&#355;&#259; prea mare;\r\n</p><p id=\"v_c\"><b>c. </b> folosirea pneurilor antiderapante.</p>', 'da', 'nu', 'nu', ''),
(81, 'Ce trebuie s&#259; folosi&#355;i pentru imobilizarea fracturilor de la membrele superioare?', '', '<p id=\"v_a\"><b>a. </b> atele improvizate;\r\n</p><p id=\"v_b\"><b>b. </b> o e&#351;arf&#259; simpl&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> în acest caz nu se intervine.</p>', 'nu', 'da', 'nu', ''),
(82, 'Ce obliga&#355;ii revin conduc&#259;torilor de autobuze la trecerea autovehiculelor cu regim de circula&#355;ie prioritar&#259;, care au în func&#355;iune mijloacele speciale de avertizare luminoas&#259; de culoare albastr&#259; &#351;i pe cele de avertizare sonor&#259;?', '', '<p id=\"v_a\"><b>a. </b> s&#259; reduc&#259; viteza, s&#259; circule cât mai aproape de marginea drumului în sensul de deplasare &#351;i s&#259; acorde prioritate de trecere;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; m&#259;reasc&#259; viteza pentru a nu incomoda circula&#355;ia;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; opreasc&#259; imediat, pe acostament sau, în lipsa acestuia, cât mai aproape de marginea drumului sau de bordura trotuarului, în sensul de deplasare.</p>', 'da', 'nu', 'nu', ''),
(83, 'Piesele cele mai solicitate în timpul func&#355;ion&#259;rii transmisiei sunt:', '', '<p id=\"v_a\"><b>a. </b> planetarele;\r\n</p><p id=\"v_b\"><b>b. </b> diferen&#355;ialul;\r\n</p><p id=\"v_c\"><b>c. </b> ambreiajul.</p>', 'nu', 'nu', 'da', ''),
(84, 'Care este rulajul la care se recomand&#259; controlul bujiilor &#351;i reglarea distan&#355;ei dintre electrozi?', '', '<p id=\"v_a\"><b>a. </b> 10000 km;\r\n</p><p id=\"v_b\"><b>b. </b> 15000 km;\r\n</p><p id=\"v_c\"><b>c. </b> 5000 km.</p>', 'nu', 'nu', 'da', ''),
(85, 'Care dintre cele patru autovehicule din imaginea al&#259;turat&#259; au dreptul de a-&#351;i continua deplasarea prin intersec&#355;ie?', 'uploads_d/1981.jpg', '<p id=\"v_a\"><b>a. </b> autoturismul &#351;i motocicleta;\r\n</p><p id=\"v_b\"><b>b. </b> niciunul;\r\n</p><p id=\"v_c\"><b>c. </b> autoturismul.</p>', 'nu', 'da', 'nu', ''),
(86, 'Preciza&#355;i care este viteza maxim&#259; permis&#259; pe autostrad&#259;, dac&#259; autobuzul tracteaz&#259; o remorc&#259;:', '', '<p id=\"v_a\"><b>a. </b> 80 km/h;\r\n</p><p id=\"v_b\"><b>b. </b> 100 km/h;\r\n</p><p id=\"v_c\"><b>c. </b> 90 km/h.</p>', 'nu', 'da', 'nu', ''),
(87, 'Frâna de e&#351;apament îndepline&#351;te urm&#259;toarele func&#355;ii:', '', '<p id=\"v_a\"><b>a. </b> men&#355;ine temperatura de pornire a motorului &#351;i aerise&#351;te toba;\r\n</p><p id=\"v_b\"><b>b. </b> men&#355;ine motorul cald la ralanti &#351;i realizeaz&#259; frâna de motor;\r\n</p><p id=\"v_c\"><b>c. </b> ajut&#259; la re&#355;inerea noxelor din gazele e&#351;apate.</p>', 'nu', 'da', 'nu', ''),
(88, 'Cum proceda&#355;i în cazul unui accidentat care are puls, dar nu respir&#259;?', '', '<p id=\"v_a\"><b>a. </b> îi face&#355;i o resuscitare cardio-respiratorie;\r\n</p><p id=\"v_b\"><b>b. </b> îi da&#355;i câteva palme peste fa&#355;&#259;, pân&#259; î&#351;i revine;\r\n</p><p id=\"v_c\"><b>c. </b> începe&#355;i respira&#355;ia artificial&#259;.</p>', 'nu', 'nu', 'da', ''),
(89, 'Reglement&#259;rile referitoare la stabilirea perioadelor de conducere &#351;i de odihn&#259; a conduc&#259;torilor auto se refer&#259; la:', '', '<p id=\"v_a\"><b>a. </b> vehiculele sau ansamblurile de vehicule a c&#259;ror mas&#259; total&#259; maxim&#259; autorizat&#259; nu dep&#259;&#351;e&#351;te 7,5 tone, utilizate pentru transportul de m&#259;rfuri în scopuri necomerciale;\r\n</p><p id=\"v_b\"><b>b. </b> vehiculele destinate transportului de m&#259;rfuri, a c&#259;ror mas&#259; total&#259; maxim&#259; autorizat&#259;, inclusiv remorca sau semiremorca, dep&#259;&#351;e&#351;te 3,5 tone;\r\n</p><p id=\"v_c\"><b>c. </b> vehiculele destinate transportului de persoane, a c&#259;ror mas&#259; total&#259; maxim&#259; autorizat&#259; nu dep&#259;&#351;e&#351;te 7,5 tone.</p>', 'nu', 'da', 'nu', ''),
(90, 'Fracturile rezultate în urma unui accident rutier pot fi recunoscute prin:', '', '<p id=\"v_a\"><b>a. </b> culoarea diferit&#259; a locului fracturii;\r\n</p><p id=\"v_b\"><b>b. </b> segmentul osos fracturat, devenit elastic;\r\n</p><p id=\"v_c\"><b>c. </b> segmentul fracturat, care are o mi&#351;care anormal&#259;.</p>', 'nu', 'nu', 'da', ''),
(91, 'Care dintre urm&#259;toarele defec&#355;iuni produc vibra&#355;ii puternice în timpul deplas&#259;rii automobilului?', '', '<p id=\"v_a\"><b>a. </b> uzura pinioanelor cutiei de viteze;\r\n</p><p id=\"v_b\"><b>b. </b> ruperea transmisiei longitudinale;\r\n</p><p id=\"v_c\"><b>c. </b> dezechilibrarea arborilor cardanici.</p>', 'nu', 'nu', 'da', ''),
(92, 'Uzurile locale din zona benzii de rulare a pneurilor se produc din cauza:', '', '<p id=\"v_a\"><b>a. </b> ovaliz&#259;rii tamburului ro&#355;ii respective;\r\n</p><p id=\"v_b\"><b>b. </b> rul&#259;rii cu presiune sc&#259;zut&#259; în pneuri;\r\n</p><p id=\"v_c\"><b>c. </b> rul&#259;rii autovehiculului pe drumuri accidentate.</p>', 'da', 'nu', 'nu', ''),
(93, 'Unde se formeaz&#259; amestecul carburant la motoarele cu aprindere prin compresie?', '', '<p id=\"v_a\"><b>a. </b> în galeria de admisie;\r\n</p><p id=\"v_b\"><b>b. </b> în cilindrii motorului;\r\n</p><p id=\"v_c\"><b>c. </b> în pompa de injec&#355;ie.</p>', 'nu', 'da', 'nu', ''),
(94, 'Se consider&#259; contraven&#355;ie:', '', '<p id=\"v_a\"><b>a. </b> necompletarea pe diagrama tahograf a datelor prev&#259;zute de legisla&#355;ia în vigoare;\r\n</p><p id=\"v_b\"><b>b. </b> completarea diagramei tahograf cu pixul sau cu stiloul;\r\n</p><p id=\"v_c\"><b>c. </b> omisiunea de a introduce în tahograful digital datele prev&#259;zute de legisla&#355;ia în vigoare.</p>', 'da', 'nu', 'da', ''),
(95, 'Se consider&#259; contraven&#355;ie:', '', '<p id=\"v_a\"><b>a. </b> nerespectarea prevederilor privind utilizarea unor diagrame tahografice murdare sau deteriorate;\r\n</p><p id=\"v_b\"><b>b. </b> nerespectarea prevederilor în vigoare, privind utilizarea unei cartele tahografice defecte sau expirate;\r\n</p><p id=\"v_c\"><b>c. </b> utilizarea DVD-urilor sau a altor aparate electronice.</p>', 'da', 'da', 'nu', ''),
(96, 'Factorii principali care ajut&#259; un conduc&#259;tor de autobuze s&#259; piloteze preventiv sunt:\r\n', '', '<p id=\"v_a\"><b>a. </b> atitudinea pozitiv&#259; în trafic, calmul, cuno&#351;tin&#355;ele dobândite &#351;i polite&#355;ea;\r\n</p><p id=\"v_b\"><b>b. </b> iscusin&#355;a &#351;i priceperea de a-&#351;i impune calit&#259;&#355;ile fizice dobândite;\r\n</p><p id=\"v_c\"><b>c. </b> mijloacele de semnalizare rutier&#259; &#351;i agen&#355;ii de poli&#355;ie rutier&#259;.</p>', 'da', 'nu', 'nu', ''),
(97, 'Apari&#355;ia unui zgomot ascu&#355;it în momentul ap&#259;s&#259;rii pedalei de ambreiaj semnific&#259;:', '', '<p id=\"v_a\"><b>a. </b> ferodourile sunt uzate;\r\n</p><p id=\"v_b\"><b>b. </b> arcul de revenire a pedalei de ambreiaj este uzat;\r\n</p><p id=\"v_c\"><b>c. </b> rulmentul de presiune este defect.</p>', 'nu', 'nu', 'da', ''),
(98, 'Ce obliga&#355;ii are conduc&#259;torul unui autobuz care urmeaz&#259; s&#259; fie dep&#259;&#351;it?', '', '<p id=\"v_a\"><b>a. </b> s&#259; reduc&#259; viteza;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; nu m&#259;reasc&#259; viteza de deplasare;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; se deplaseze cât mai aproape de axul drumului.</p>', 'nu', 'da', 'nu', ''),
(99, 'Conduc&#259;torul unui autobuz este obligat s&#259; opreasc&#259;:', '', '<p id=\"v_a\"><b>a. </b> la apropierea de o intersec&#355;ie;\r\n</p><p id=\"v_b\"><b>b. </b> la semnalul nev&#259;z&#259;torilor care traverseaz&#259; strada;\r\n</p><p id=\"v_c\"><b>c. </b> la semnalul conduc&#259;torului coloanei de pietoni.</p>', 'nu', 'da', 'nu', ''),
(100, '„Perioada de odihn&#259; s&#259;pt&#259;mânal&#259; normal&#259;“ reprezint&#259;:', '', '<p id=\"v_a\"><b>a. </b> perioada de odihn&#259; de cel pu&#355;in 24 de ore consecutive;\r\n</p><p id=\"v_b\"><b>b. </b> orice perioad&#259; de odihn&#259; de cel pu&#355;in 45 de ore consecutive;\r\n</p><p id=\"v_c\"><b>c. </b> perioada de odihn&#259; de cel pu&#355;in 3 zile consecutive.</p>', 'nu', 'da', 'nu', ''),
(101, 'Circuitul de înalt&#259; presiune al instala&#355;iei de alimentare a motoarelor Diesel are în componen&#355;&#259;:', '', '<p id=\"v_a\"><b>a. </b> pompa de alimentare, injectoarele &#351;i conductele de leg&#259;tur&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> pompa de injec&#355;ie, injectoarele &#351;i conductele de leg&#259;tur&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> pompa de alimentare, pompa de injec&#355;ie &#351;i filtrele.</p>', 'nu', 'da', 'nu', ''),
(102, 'Reducerea vitezei este obligatorie:', '', '<p id=\"v_a\"><b>a. </b> numai la semnalul conduc&#259;torilor grupurilor de pietoni;\r\n</p><p id=\"v_b\"><b>b. </b> la semnalul personalului autorizat din zona lucr&#259;rilor de pe drumurile publice;\r\n</p><p id=\"v_c\"><b>c. </b> la semnalul bicicli&#351;tilor, atunci când ace&#351;tia opresc.</p>', 'nu', 'da', 'nu', ''),
(103, 'Dac&#259; motorul nu are putere suficient&#259;, acest fapt se poate datora:', '', '<p id=\"v_a\"><b>a. </b> sistemului de rulare;\r\n</p><p id=\"v_b\"><b>b. </b> sistemului de r&#259;cire;\r\n</p><p id=\"v_c\"><b>c. </b> instala&#355;iei de aprindere.</p>', 'nu', 'nu', 'da', ''),
(104, 'Cum este recomandat s&#259; proceda&#355;i pentru a dep&#259;&#351;i, în condi&#355;ii de siguran&#355;&#259;, un tramvai oprit într-o sta&#355;ie cu refugiu?', '', '<p id=\"v_a\"><b>a. </b> s&#259; avertiza&#355;i sonor persoanele aflate în zona sta&#355;iei;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; semnaliza&#355;i din timp inten&#355;ia de dep&#259;&#351;ire a tramvaiului;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; reduce&#355;i viteza de deplasare.</p>', 'nu', 'nu', 'da', ''),
(105, 'Când conduce&#355;i un autobuz, troleibuz sau tramvai, v&#259; este interzis:', '', '<p id=\"v_a\"><b>a. </b> s&#259; transporta&#355;i mai multe persoane decât num&#259;rul de locuri stabilite în certificatul de înmatriculare sau de înregistrare;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; deschide&#355;i u&#351;ile în timpul mersului sau s&#259; porni&#355;i de pe loc cu u&#351;ile deschise;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; ave&#355;i în timpul mersului preocup&#259;ri de natur&#259; a v&#259; distrage în mod periculos aten&#355;ia.</p>', 'da', 'da', 'da', ''),
(106, '„Durata de conducere zilnic&#259;“ reprezint&#259;:', '', '<p id=\"v_a\"><b>a. </b> durata total&#259; de conducere acumulat&#259; între dou&#259; perioade de odihn&#259; zilnic&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> perioada de conducere în care autovehiculul ruleaz&#259; neîntrerupt;\r\n</p><p id=\"v_c\"><b>c. </b> perioada de conducere între dou&#259; puncte ale traseului, prev&#259;zute într-o singur&#259; zi.</p>', 'da', 'nu', 'nu', ''),
(107, 'Cui trebuie s&#259; cedeze trecerea conduc&#259;torul autobuzului 1, dac&#259; circul&#259; pe direc&#355;ia înainte?', 'uploads_d/2005.jpg', '<p id=\"v_a\"><b>a. </b> niciunuia dintre autovehicule;\r\n</p><p id=\"v_b\"><b>b. </b> autocamionului;\r\n</p><p id=\"v_c\"><b>c. </b> autobuzului 2.</p>', 'da', 'nu', 'nu', ''),
(108, 'Ce trebuie s&#259; face&#355;i pentru evitarea complica&#355;iilor în cazul unei fracturi?', '', '<p id=\"v_a\"><b>a. </b> s&#259; imobiliza&#355;i zona fracturat&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; steriliza&#355;i &#351;i s&#259; pansa&#355;i zona fracturat&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; aplica&#355;i un garou de cauciuc deasupra fracturii.</p>', 'da', 'nu', 'nu', ''),
(109, 'Care dintre conduc&#259;torii celor dou&#259; autobuze trebuie s&#259; cedeze trecerea autocamionului?', 'uploads_d/2110.jpg', '<p id=\"v_a\"><b>a. </b> ambii conduc&#259;tori;\r\n</p><p id=\"v_b\"><b>b. </b> conduc&#259;torul autobuzului 2;\r\n</p><p id=\"v_c\"><b>c. </b> conduc&#259;torul autobuzului 1.</p>', 'da', 'nu', 'nu', ''),
(110, 'Care dintre obliga&#355;iile de mai jos revin conduc&#259;torilor auto angaja&#355;i de c&#259;tre operatorii de transport rutier?', '', '<p id=\"v_a\"><b>a. </b> s&#259; respecte perioadele de conducere &#351;i perioadele de odihn&#259;, conform reglement&#259;rilor legale în vigoare, &#351;i s&#259; utilizeze corect aparatul tahograf;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; circule, numai pe timp de zi, f&#259;r&#259; a dep&#259;&#351;i viteza indicat&#259; de tahometru &#351;i s&#259; se odihneasc&#259; pe timpul nop&#355;ii;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; nu intervin&#259; asupra aparatului tahograf sau a limitatorului de vitez&#259; pentru scoaterea acestuia din uz sau pentru modificarea caracteristicilor de func&#355;ionare.</p>', 'da', 'nu', 'da', ''),
(111, 'Ce obliga&#355;ie are conduc&#259;torul unui autobuz la întâlnirea indicatorului din imagine, înso&#355;it de panoul adi&#355;ional?', 'uploads_d/1948.jpg', '<p id=\"v_a\"><b>a. </b> s&#259; cedeze trecerea numai autocamioanelor &#351;i autobuzelor care vin din sens opus;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; cedeze trecerea celorlalte vehicule care nu sunt figurate pe panoul adi&#355;ional;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; cedeze trecerea tuturor vehiculelor care vin din sens opus.</p>', 'nu', 'nu', 'da', ''),
(112, 'Permisul de conducere valabil pentru categoria D v&#259; permite s&#259; conduce&#355;i:', '', '<p id=\"v_a\"><b>a. </b> autovehiculele destinate transportului de persoane a c&#259;rui mas&#259; dep&#259;&#351;e&#351;te 3500 kg;\r\n</p><p id=\"v_b\"><b>b. </b> autovehicule destinate transportului de persoane având mai mult de 8 locuri pe scaune, în afara locului conduc&#259;torului, la care se poate ata&#351;a o remorc&#259;, a c&#259;rei mas&#259; total&#259; maxim&#259; autorizat&#259; nu dep&#259;&#351;e&#351;te 750 kg;\r\n</p><p id=\"v_c\"><b>c. </b> autobuze &#351;i remorci destinate transportului de bunuri &#351;i de persoane.</p>', 'nu', 'da', 'nu', ''),
(113, 'Care dintre urm&#259;toarele defec&#355;iuni determin&#259; func&#355;ionarea motoarelor cu aprindere prin compresie cu fum alb excesiv?', '', '<p id=\"v_a\"><b>a. </b> arderea garniturii de chiulas&#259;;\r\n</p><p id=\"v_b\"><b>b. </b> resortul injectorului blocat;\r\n</p><p id=\"v_c\"><b>c. </b> avansul la injec&#355;ie peste limita normal&#259;.</p>', 'da', 'nu', 'nu', ''),
(114, 'În cazul rul&#259;rii cu un pneu umflat sub presiunea indicat&#259; de constructor, uzura neuniform&#259; a benzii de rulare se produce:', '', '<p id=\"v_a\"><b>a. </b> pe margini;\r\n</p><p id=\"v_b\"><b>b. </b> pe mijloc;\r\n</p><p id=\"v_c\"><b>c. </b> pe toat&#259; l&#259;&#355;imea.</p>', 'da', 'nu', 'nu', ''),
(115, 'În care dintre urm&#259;toarele situa&#355;ii sunte&#355;i obligat s&#259; circula&#355;i cu o vitez&#259; care s&#259; nu dep&#259;&#351;easc&#259; 30 km/h în localit&#259;&#355;i?', '', '<p id=\"v_a\"><b>a. </b> pe poduri, sub poduri sau în tuneluri;\r\n</p><p id=\"v_b\"><b>b. </b> la trecerea prin intersec&#355;iile cu circula&#355;ie dirijat&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> când partea carosabil&#259; este acoperit&#259; cu polei.</p>', 'nu', 'nu', 'da', ''),
(116, 'Dac&#259; într-o intersec&#355;ie conduc&#259;torul de autobuz întâlne&#351;te semnalul verde al semaforului, precum &#351;i indicatorul „Cedeaz&#259; trecerea“, el este obligat:', '', '<p id=\"v_a\"><b>a. </b> s&#259; respecte semnifica&#355;ia semnalului verde;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; respecte semnifica&#355;ia indicatorului;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; respecte semnifica&#355;ia semnalului verde, acordând prioritate de trecere vehiculelor care circul&#259; pe drumul prioritar.</p>', 'da', 'nu', 'nu', ''),
(117, 'Uleiurile pentru motoarele cu aprindere prin compresie sunt simbolizate cu litera:', '', '<p id=\"v_a\"><b>a. </b> D;\r\n</p><p id=\"v_b\"><b>b. </b> C;\r\n</p><p id=\"v_c\"><b>c. </b> U.</p>', 'nu', 'da', 'nu', ''),
(118, 'Care este rulajul la care se recomand&#259; schimbarea bujiilor, pentru a evita riscul unor c&#259;deri func&#355;ionale &#351;i de consum ale motorului?', '', '<p id=\"v_a\"><b>a. </b> dup&#259; fiecare 5000 km;\r\n</p><p id=\"v_b\"><b>b. </b> dup&#259; 15000-18000 km;\r\n</p><p id=\"v_c\"><b>c. </b> dup&#259; 30000-35000 km.</p>', 'nu', 'da', 'nu', '');
INSERT INTO `intrebari_d` (`id`, `cerinta`, `imagine`, `variante`, `rasp_a`, `rasp_b`, `rasp_c`, `eticheta`) VALUES
(119, 'Ce obliga&#355;ii ave&#355;i la semnalul membrilor patrulelor &#351;colare de circula&#355;ie, la trecerile pentru pietoni din apropierea unit&#259;&#355;ilor de înv&#259;&#355;&#259;mânt?', '', '<p id=\"v_a\"><b>a. </b> s&#259; reduce&#355;i viteza &#351;i s&#259; circula&#355;i cu aten&#355;ie;\r\n</p><p id=\"v_b\"><b>b. </b> s&#259; trece&#355;i pe banda de lâng&#259; axul drumului;\r\n</p><p id=\"v_c\"><b>c. </b> s&#259; opri&#355;i.</p>', 'nu', 'nu', 'da', ''),
(120, 'Arborele cu came este ac&#355;ionat de:', '', '<p id=\"v_a\"><b>a. </b> arborele cotit;\r\n</p><p id=\"v_b\"><b>b. </b> diferen&#355;ial;\r\n</p><p id=\"v_c\"><b>c. </b> ruptor-distribuitor.</p>', 'da', 'nu', 'nu', ''),
(121, 'Furca de debreiere ac&#355;ioneaz&#259; pe:', '', '<p id=\"v_a\"><b>a. </b> discul de ambreiaj;\r\n</p><p id=\"v_b\"><b>b. </b> rulmentul de presiune;\r\n</p><p id=\"v_c\"><b>c. </b> placa de presiune.</p>', 'nu', 'da', 'nu', ''),
(122, 'Cele mai importante caracteristici ale bujiilor sunt:', '', '<p id=\"v_a\"><b>a. </b> grosimea izolatorului ceramic &#351;i culoarea acestuia;\r\n</p><p id=\"v_b\"><b>b. </b> dimensiunea filetului &#351;i valoarea termic&#259;;\r\n</p><p id=\"v_c\"><b>c. </b> termenul de garan&#355;ie &#351;i constan&#355;a scânteii.</p>', 'nu', 'da', 'nu', ''),
(123, 'Microbuzele &#351;i autobuzele sunt supuse inspec&#355;iei tehnice la cel mult:', '', '<p id=\"v_a\"><b>a. </b> 6 luni;\r\n</p><p id=\"v_b\"><b>b. </b> 1 an;\r\n</p><p id=\"v_c\"><b>c. </b> 2 ani.</p>', 'da', 'nu', 'nu', ''),
(124, 'Se consider&#259; contraven&#355;ie:', '', '<p id=\"v_a\"><b>a. </b> conducerea unui vehicul dotat cu tahograf f&#259;r&#259; a utiliza diagrame tahograf;\r\n</p><p id=\"v_b\"><b>b. </b> utilizarea tahografului f&#259;r&#259; avizul poli&#355;iei rutiere;\r\n</p><p id=\"v_c\"><b>c. </b> conducerea unui vehicul dotat cu tahograf digital f&#259;r&#259; a utiliza cartela tahografic&#259;.</p>', 'da', 'nu', 'da', ''),
(125, 'Neacordarea priorit&#259;&#355;ii înseamn&#259;:', '', '<p id=\"v_a\"><b>a. </b> nerespectarea semnifica&#355;iei semnaliz&#259;rii rutiere de obligare sau de avertizare;\r\n</p><p id=\"v_b\"><b>b. </b> nerespectarea dispozi&#355;iilor legale privind asigurarea &#351;i semnalizarea în intersec&#355;ii;\r\n</p><p id=\"v_c\"><b>c. </b> obligarea celor care au prioritate de trecere de a-&#351;i modifica brusc direc&#355;ia sau viteza de deplasare ori s&#259; opreasc&#259;.</p>', 'nu', 'nu', 'da', ''),
(126, 'Încetinitorul electromagnetic este mai eficient decât cel de pe e&#351;apament, mai ales la:', '', '<p id=\"v_a\"><b>a. </b> viteze ridicate;\r\n</p><p id=\"v_b\"><b>b. </b> opriri neprev&#259;zute;\r\n</p><p id=\"v_c\"><b>c. </b> viteze sc&#259;zute.</p>', 'da', 'nu', 'nu', ''),
(127, 'Ce cauze pot conduce la un consum exagerat de combustibil?', '', '<p id=\"v_a\"><b>a. </b> tura&#355;ia la ralanti prea mare;\r\n</p><p id=\"v_b\"><b>b. </b> temperatura mediului ambiant;\r\n</p><p id=\"v_c\"><b>c. </b> lipsa filtrului de benzin&#259;.</p>', 'da', 'nu', 'nu', ''),
(128, 'Care dintre urm&#259;toarele defec&#355;iuni face imposibil&#259;, la un moment dat, frânarea autovehiculului?', '', '<p id=\"v_a\"><b>a. </b> uzura exagerat&#259; a pneurilor;\r\n</p><p id=\"v_b\"><b>b. </b> ruperea curelei de antrenare a compresorului;\r\n</p><p id=\"v_c\"><b>c. </b> uzura pistoanelor compresorului.</p>', 'nu', 'da', 'nu', ''),
(129, 'Hemoragia nazal&#259; se poate opri dac&#259; accidentatul:', '', '<p id=\"v_a\"><b>a. </b> st&#259; culcat cu capul pe spate;\r\n</p><p id=\"v_b\"><b>b. </b> st&#259; în picioare &#351;i î&#351;i strânge ambele n&#259;ri timp de aproximativ 5-10 minute;\r\n</p><p id=\"v_c\"><b>c. </b> ridic&#259; mâna dreapt&#259;.</p>', 'nu', 'da', 'nu', ''),
(130, 'Suspendarea exercit&#259;rii dreptului de a conduce se aplic&#259; în cazul:', '', '<p id=\"v_a\"><b>a. </b> nerespect&#259;rii interdic&#355;iei de a deschide u&#351;ile autovehiculului în timpul mersului;\r\n</p><p id=\"v_b\"><b>b. </b> utiliz&#259;rii detectoarelor antiradar;\r\n</p><p id=\"v_c\"><b>c. </b> nerespect&#259;rii regulilor de circula&#355;ie la trecerea unei coloane oficiale.</p>', 'nu', 'nu', 'da', ''),
(131, 'În care dintre situa&#355;ii este intezis s&#259; circula&#355;i cu autobuzul pe drumurile publice?', '', '<p id=\"v_a\"><b>a. </b> atunci când certificatul de înmatriculare este deteriorat;\r\n</p><p id=\"v_b\"><b>b. </b> atunci când autobuzul nu este asigurat de r&#259;spundere civil&#259;, în caz de pagube materiale produse ter&#355;ilor prin accidente de circula&#355;ie, conform legii;\r\n</p><p id=\"v_c\"><b>c. </b> atunci când posesorul vehiculului nu a pl&#259;tit taxa de folosire a drumurilor publice.</p>', 'nu', 'da', 'nu', ''),
(132, 'Care este durata maxim&#259; de conducere s&#259;pt&#259;mânal&#259;?', '', '<p id=\"v_a\"><b>a. </b> 48 de ore;\r\n</p><p id=\"v_b\"><b>b. </b> 56 de ore;\r\n</p><p id=\"v_c\"><b>c. </b> 40 de ore.</p>', 'nu', 'da', 'nu', '');
